INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'en', 'United States', 'United States of America', 'From iconic cities to stunning national parks, discover endless opportunities in the land of dreams and innovation.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'ar', 'الولايات المتحدة', 'الولايات المتحدة الأمريكية', 'من المدن الأيقونية إلى الحدائق الوطنية المذهلة، اكتشف فرصاً لا نهائية في أرض الأحلام والابتكار.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'es', 'Estados Unidos', 'Estados Unidos de América', 'Desde ciudades icónicas hasta parques nacionales impresionantes, descubre oportunidades infinitas en la tierra de los sueños e innovación.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'fr', 'États-Unis', 'États-Unis d''''Amérique', 'Des villes emblématiques aux parcs nationaux époustouflants, découvrez des opportunités infinies dans la terre des rêves et de l''''innovation.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'pt', 'Estados Unidos', 'Estados Unidos da América', 'De cidades icônicas a parques nacionais deslumbrantes, descubra oportunidades infinitas na terra dos sonhos e inovação.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'ru', 'Соединённые Штаты', 'Соединённые Штаты Америки', 'От знаковых городов до потрясающих национальных парков, откройте для себя бесконечные возможности в стране мечты и инноваций.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'de', 'Vereinigte Staaten', 'Vereinigte Staaten von Amerika', 'Von ikonischen Städten bis hin zu atemberaubenden Nationalparks, entdecken Sie endlose Möglichkeiten im Land der Träume und Innovation.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'it', 'Stati Uniti', 'Stati Uniti d''''America', 'Dalle città iconiche ai parchi nazionali mozzafiato, scopri opportunità infinite nella terra dei sogni e dell''''innovazione.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'en', 'Canada', 'Canada', 'Experience vast wilderness, vibrant cities, and warm hospitality in this diverse northern paradise perfect for outdoor adventures.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'ar', 'كندا', 'كندا', 'استمتع بالبرية الشاسعة والمدن النابضة بالحياة والضيافة الدافئة في هذه الجنة الشمالية المتنوعة المثالية للمغامرات الخارجية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'es', 'Canadá', 'Canadá', 'Experimenta vastos territorios salvajes, ciudades vibrantes y cálida hospitalidad en este paraíso norteño diverso perfecto para aventuras al aire libre.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'fr', 'Canada', 'Canada', 'Découvrez de vastes étendues sauvages, des villes dynamiques et une hospitalité chaleureuse dans ce paradis nordique diversifié parfait pour les aventures en plein air.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'pt', 'Canadá', 'Canadá', 'Experimente vastas regiões selvagens, cidades vibrantes e hospitalidade calorosa neste paraíso nórdico diversificado perfeito para aventuras ao ar livre.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'ru', 'Канада', 'Канада', 'Откройте для себя обширную дикую природу, яркие города и теплое гостеприимство в этом разнообразном северном раю, идеальном для приключений на свежем воздухе.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'de', 'Kanada', 'Kanada', 'Erleben Sie weite Wildnis, lebendige Städte und herzliche Gastfreundschaft in diesem vielfältigen nördlichen Paradies, perfekt für Outdoor-Abenteuer.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'it', 'Canada', 'Canada', 'Vivi vaste terre selvagge, città vibranti e calorosa ospitalità in questo paradiso settentrionale diversificato perfetto per avventure all''aria aperta.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'en', 'United Kingdom', 'United Kingdom of Great Britain and Northern Ireland', 'Explore historic castles, charming countryside, and vibrant cities where centuries of history blend with modern innovation and culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'ar', 'المملكة المتحدة', 'المملكة المتحدة لبريطانيا العظمى وأيرلندا الشمالية', 'استكشف القلاع التاريخية والريف الساحر والمدن النابضة بالحياة حيث تمتزج قرون من التاريخ مع الابتكار والثقافة الحديثة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'es', 'Reino Unido', 'Reino Unido de Gran Bretaña e Irlanda del Norte', 'Explora castillos históricos, campiña encantadora y ciudades vibrantes donde siglos de historia se mezclan con innovación y cultura moderna.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'fr', 'Royaume-Uni', 'Royaume-Uni de Grande-Bretagne et d''''Irlande du Nord', 'Explorez des châteaux historiques, une campagne charmante et des villes dynamiques où des siècles d''''histoire se mêlent à l''''innovation et à la culture modernes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'pt', 'Reino Unido', 'Reino Unido da Grã-Bretanha e Irlanda do Norte', 'Explore castelos históricos, campos encantadores e cidades vibrantes onde séculos de história se misturam com inovação e cultura moderna.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'ru', 'Великобритания', 'Соединённое Королевство Великобритании и Северной Ирландии', 'Исследуйте исторические замки, очаровательную сельскую местность и яркие города, где века истории сочетаются с современными инновациями и культурой.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'de', 'Vereinigtes Königreich', 'Vereinigtes Königreich Großbritannien und Nordirland', 'Erkunden Sie historische Schlösser, bezaubernde Landschaften und lebendige Städte, wo sich Jahrhunderte der Geschichte mit moderner Innovation und Kultur verbinden.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'it', 'Regno Unito', 'Regno Unito di Gran Bretagna e Irlanda del Nord', 'Esplora castelli storici, campagne incantevoli e città vibranti dove secoli di storia si fondono con innovazione e cultura moderna.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'en', 'Australia', 'Commonwealth of Australia', 'Experience the land down under with pristine beaches, unique wildlife, and vibrant cities. From the Great Barrier Reef to the Outback.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'ar', 'أستراليا', 'كومنولث أستراليا', 'اكتشف القارة الجنوبية بشواطئها البكر وحياتها البرية الفريدة ومدنها النابضة بالحياة. من الحاجز المرجاني العظيم إلى المناطق النائية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'es', 'Australia', 'Commonwealth de Australia', 'Vive la tierra de los antípodas con playas vírgenes, fauna única y ciudades vibrantes. Desde la Gran Barrera de Coral hasta el Outback.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'fr', 'Australie', 'Commonwealth d''''Australie', 'Découvrez la terre des antipodes avec ses plages immaculées, sa faune unique et ses villes dynamiques. De la Grande Barrière de Corail à l''''Outback.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'pt', 'Austrália', 'Commonwealth da Austrália', 'Descubra a terra dos antípodas com praias intocadas, fauna única e cidades vibrantes. Da Grande Barreira de Coral ao Outback.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'ru', 'Австралия', 'Содружество Австралии', 'Откройте для себя землю антиподов с девственными пляжами, уникальной дикой природой и яркими городами. От Большого Барьерного рифа до глубинки.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'de', 'Australien', 'Commonwealth von Australien', 'Erleben Sie das Land der Antipoden mit unberührten Stränden, einzigartiger Tierwelt und lebendigen Städten. Vom Great Barrier Reef bis zum Outback.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'it', 'Australia', 'Commonwealth d''''Australia', 'Scopri la terra degli antipodi con spiagge incontaminate, fauna unica e città vibranti. Dalla Grande Barriera Corallina all''''Outback.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'en', 'United Arab Emirates', 'United Arab Emirates', 'Modern oasis where ancient traditions meet futuristic innovation. Experience luxury shopping, stunning architecture, and pristine beaches in the heart of the Middle East.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'ar', 'الإمارات العربية المتحدة', 'دولة الإمارات العربية المتحدة', 'واحة عصرية تلتقي فيها التقاليد القديمة مع الابتكار المستقبلي. استمتع بتسوق فاخر وهندسة معمارية مذهلة وشواطئ نقية في قلب الشرق الأوسط.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'es', 'Emiratos Árabes Unidos', 'Emiratos Árabes Unidos', 'Oasis moderno donde las tradiciones ancestrales se encuentran con la innovación futurista. Experimenta compras de lujo, arquitectura impresionante y playas vírgenes en el corazón del Medio Oriente.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'fr', 'Émirats arabes unis', 'Émirats arabes unis', 'Oasis moderne où les traditions ancestrales rencontrent l''''innovation futuriste. Découvrez le shopping de luxe, l''''architecture époustouflante et des plages immaculées au cœur du Moyen-Orient.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'pt', 'Emirados Árabes Unidos', 'Emirados Árabes Unidos', 'Oásis moderno onde tradições antigas encontram inovação futurista. Experimente compras luxuosas, arquitetura deslumbrante e praias pristinas no coração do Oriente Médio.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'ru', 'Объединённые Арабские Эмираты', 'Объединённые Арабские Эмираты', 'Современный оазис, где древние традиции встречаются с футуристическими инновациями. Наслаждайтесь роскошным шопингом, потрясающей архитектурой и нетронутыми пляжами в сердце Ближнего Востока.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'de', 'Vereinigte Arabische Emirate', 'Vereinigte Arabische Emirate', 'Moderne Oase, wo alte Traditionen auf futuristische Innovation treffen. Erleben Sie luxuriöses Shopping, atemberaubende Architektur und unberührte Strände im Herzen des Nahen Ostens.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'it', 'Emirati Arabi Uniti', 'Emirati Arabi Uniti', 'Oasi moderna dove antiche tradizioni incontrano innovazione futuristica. Sperimenta shopping di lusso, architettura mozzafiato e spiagge incontaminate nel cuore del Medio Oriente.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'en', 'Algeria', 'Algeria', 'Explore North Africa''s largest country with stunning Sahara landscapes, ancient Roman ruins at Timgad, and vibrant Mediterranean coast. Discover Berber heritage and French colonial architecture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'ar', 'Algeria', 'Algeria', 'استكشف أكبر بلدان شمال أفريقيا مع مناظر الصحراء الخلابة وآثار رومانية قديمة في تيمغاد والساحل المتوسطي النابض. اكتشف التراث البربري والعمارة الاستعمارية الفرنسية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'es', 'Algeria', 'Algeria', 'Explora el país más grande del norte de África con impresionantes paisajes del Sahara, ruinas romanas ancestrales en Timgad y vibrante costa mediterránea. Descubre patrimonio bereber y arquitectura colonial francesa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'fr', 'Algeria', 'Algeria', 'Explorez le plus grand pays d''Afrique du Nord avec paysages sahariens époustouflants, ruines romaines antiques à Timgad et côte méditerranéenne vibrante. Découvrez héritage berbère et architecture coloniale française.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'pt', 'Algeria', 'Algeria', 'Explore o maior país do norte da África com paisagens deslumbrantes do Saara, ruínas romanas antigas em Timgad e costa mediterrânea vibrante. Descubra património berbere e arquitetura colonial francesa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'ru', 'Algeria', 'Algeria', 'Исследуйте крупнейшую страну Северной Африки с потрясающими пейзажами Сахары, древними римскими руинами в Тимгаде и ярким средиземноморским побережьем. Откройте берберское наследие и французскую колониальную архитектуру.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'de', 'Algeria', 'Algeria', 'Erkunden Sie Nordafrikas größtes Land mit atemberaubenden Sahara-Landschaften, antiken römischen Ruinen in Timgad und lebendiger Mittelmeerküste. Entdecken Sie Berber-Erbe und französische Kolonialarchitektur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'it', 'Algeria', 'Algeria', 'Esplora il più grande paese del Nord Africa con paesaggi sahariani mozzafiato, antiche rovine romane a Timgad e costa mediterranea vibrante. Scopri patrimonio berbero e architettura coloniale francese.');
