INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'en', 'Botswana', 'Botswana', 'Experience Africa''s wildlife paradise in the pristine Okavango Delta and vast Kalahari Desert. Discover world-class safari adventures, diamond heritage, and peaceful stability in southern Africa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'ar', 'Botswana', 'Botswana', 'استمتع بجنة الحياة البرية في أفريقيا في دلتا أوكافانغو البكر وصحراء كالاهاري الشاسعة. اكتشف مغامرات السافاري عالمية المستوى وتراث الالماس والاستقرار السلمي في جنوب أفريقيا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'es', 'Botswana', 'Botswana', 'Experimenta el paraíso de vida silvestre de África en el prístino Delta de Okavango y vasto Desierto de Kalahari. Descubre aventuras de safari de clase mundial, patrimonio de diamantes y estabilidad pacífica en África austral.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'fr', 'Botswana', 'Botswana', 'Vivez le paradis de la faune africaine dans le Delta d''Okavango vierge et vaste Désert du Kalahari. Découvrez aventures safari de classe mondiale, héritage diamantaire et stabilité pacifique en Afrique australe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'pt', 'Botswana', 'Botswana', 'Experimente o paraíso da vida selvagem da África no prístino Delta do Okavango e vasto Deserto de Kalahari. Descubra aventuras de safari de classe mundial, património de diamantes e estabilidade pacífica na África austral.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'ru', 'Botswana', 'Botswana', 'Ощутите рай дикой природы Африки в нетронутой дельте Окаванго и обширной пустыне Калахари. Откройте сафари мирового класса, алмазное наследие и мирную стабильность южной Африки.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'de', 'Botswana', 'Botswana', 'Erleben Sie Afrikas Wildtierparadies im unberührten Okavango-Delta und der weiten Kalahari-Wüste. Entdecken Sie Weltklasse-Safari-Abenteuer, Diamanten-Erbe und friedliche Stabilität im südlichen Afrika.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'it', 'Botswana', 'Botswana', 'Vivi il paradiso della fauna selvatica africana nel pristino Delta dell''Okavango e vasto Deserto del Kalahari. Scopri avventure safari di classe mondiale, patrimonio diamantifero e stabilità pacifica nell''Africa meridionale.');
