INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'en', 'Egypt', 'Egypt', 'Discover ancient wonders at the pyramids and temples. Experience the magic of the Nile, rich pharaonic history, and vibrant modern culture in Egypt.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'ar', 'Egypt', 'Egypt', 'اكتشف عجائب قديمة في الأهرامات والمعابد. استمتع بسحر نهر النيل والتاريخ الفرعوني الغني والثقافة الحديثة النابضة في مصر.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'es', 'Egypt', 'Egypt', 'Descubre maravillas antiguas en las pirámides y templos. Experimenta la magia del Nilo, rica historia faraónica y vibrante cultura moderna en Egipto.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'fr', 'Egypt', 'Egypt', 'Découvrez les merveilles antiques des pyramides et temples. Vivez la magie du Nil, riche histoire pharaonique et culture moderne vibrante en Égypte.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'pt', 'Egypt', 'Egypt', 'Descubra maravilhas antigas nas pirâmides e templos. Experimente a magia do Nilo, rica história faraônica e cultura moderna vibrante no Egito.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'ru', 'Egypt', 'Egypt', 'Откройте древние чудеса пирамид и храмов. Почувствуйте магию Нила, богатую фараонскую историю и яркую современную культуру в Египте.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'de', 'Egypt', 'Egypt', 'Entdecken Sie antike Wunder an Pyramiden und Tempeln. Erleben Sie die Magie des Nils, reiche Pharaonengeschichte und lebendige moderne Kultur in Ägypten.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'it', 'Egypt', 'Egypt', 'Scopri meraviglie antiche nelle piramidi e templi. Vivi la magia del Nilo, ricca storia faraonica e vibrante cultura moderna in Egitto.');
