INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'en', 'Ethiopia', 'Ethiopia', 'Discover Ethiopia''s ancient wonders from Lucy''s fossils to Lalibela''s rock-hewn churches. Experience the birthplace of coffee, diverse cultures, and dramatic landscapes in the Horn of Africa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'ar', 'Ethiopia', 'Ethiopia', 'اكتشف عجائب إثيوبيا القديمة من حفريات لوسي إلى كنائس لاليبيلا المنحوتة في الصخر. استمتع بموطن القهوة والثقافات المتنوعة والمناظر الدرامية في قرن أفريقيا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'es', 'Ethiopia', 'Ethiopia', 'Descubre las maravillas ancestrales de Etiopía desde fósiles de Lucy hasta iglesias talladas en roca de Lalibela. Experimenta la cuna del café, culturas diversas y paisajes dramáticos en el Cuerno de África.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'fr', 'Ethiopia', 'Ethiopia', 'Découvrez les merveilles antiques d''Ethiopie des fossiles de Lucy aux églises taillées dans la roche de Lalibela. Vivez le berceau du café, cultures diverses et paysages dramatiques dans la Corne de l''Afrique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'pt', 'Ethiopia', 'Ethiopia', 'Descubra as maravilhas antigas da Etiópia desde fósseis de Lucy até igrejas escavadas na rocha de Lalibela. Experimente o berço do café, culturas diversas e paisagens dramáticas no Chifre da África.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'ru', 'Ethiopia', 'Ethiopia', 'Откройте древние чудеса Эфиопии от окаменелостей Люси до высеченных в скале церквей Лалибэлы. Ощутите родину кофе, разнообразные культуры и драматические ландшафты Рога Африки.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'de', 'Ethiopia', 'Ethiopia', 'Entdecken Sie Äthiopiens antike Wunder von Lucys Fossilien bis zu Lalibelas in Fels gehauenen Kirchen. Erleben Sie die Wiege des Kaffees, vielfältige Kulturen und dramatische Landschaften im Horn von Afrika.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'it', 'Ethiopia', 'Ethiopia', 'Scopri le meraviglie antiche dell''Etiopia dai fossili di Lucy alle chiese scavate nella roccia di Lalibela. Vivi la culla del caffè, culture diverse e paesaggi drammatici nel Corno d''Africa.');
