INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'en', 'Kenya', 'Kenya', 'Experience the ultimate safari destination with the Great Migration, diverse wildlife, and stunning landscapes. Discover Mount Kenya, beautiful coastlines, and vibrant culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'ar', 'Kenya', 'Kenya', 'استمتع بوجهة السفاري المثالية مع الهجرة العظيمة والحياة البرية المتنوعة والمناظر الطبيعية الخلابة. اكتشف جبل كينيا والسواحل الجميلة والثقافة النابضة بالحياة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'es', 'Kenya', 'Kenya', 'Experimenta el destino de safari definitivo con la Gran Migración, fauna diversa y paisajes impresionantes. Descubre el Monte Kenia, hermosas costas y cultura vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'fr', 'Kenya', 'Kenya', 'Découvrez la destination safari ultime avec la Grande Migration, une faune diversifiée et des paysages époustouflants. Explorez le Mont Kenya, belles côtes et culture vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'pt', 'Kenya', 'Kenya', 'Experimente o destino de safari definitivo com a Grande Migração, vida selvagem diversificada e paisagens deslumbrantes. Descubra o Monte Quênia, belas costas e cultura vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'ru', 'Kenya', 'Kenya', 'Ощутите лучшее направление для сафари с Великой Миграцией, разнообразной дикой природой и потрясающими пейзажами. Откройте гору Кения, красивые побережья и яркую культуру.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'de', 'Kenya', 'Kenya', 'Erleben Sie das ultimative Safari-Ziel mit der Großen Migration, vielfältiger Tierwelt und atemberaubenden Landschaften. Entdecken Sie Mount Kenia, schöne Küsten und lebendige Kultur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'it', 'Kenya', 'Kenya', 'Vivi la destinazione safari definitiva con la Grande Migrazione, fauna diversificata e paesaggi mozzafiato. Scopri il Monte Kenya, belle coste e cultura vibrante.');
