INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'en', 'Morocco', 'Morocco', 'Experience the enchanting blend of Berber, Arab, and French cultures. From bustling souks to Atlas Mountains and Sahara dunes, Morocco captivates every traveler.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'ar', 'Morocco', 'Morocco', 'استمتع بالمزيج الساحر من الثقافات البربرية والعربية والفرنسية. من الأسواق النابضة إلى جبال الأطلس وكثبان الصحراء، يأسر المغرب كل مسافر.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'es', 'Morocco', 'Morocco', 'Experimenta la fascinante mezcla de culturas bereber, árabe y francesa. Desde zocos bulliciosos hasta las montañas del Atlas y dunas del Sahara, Marruecos cautiva a cada viajero.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'fr', 'Morocco', 'Morocco', 'Découvrez le mélange enchanteur des cultures berbère, arabe et française. Des souks animés aux montagnes de l''Atlas et dunes du Sahara, le Maroc captive chaque voyageur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'pt', 'Morocco', 'Morocco', 'Experimente a fascinante mistura de culturas berber, árabe e francesa. De souks movimentados às montanhas do Atlas e dunas do Saara, Marrocos cativa cada viajante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'ru', 'Morocco', 'Morocco', 'Окунитесь в очаровательное смешение берберских, арабских и французских культур. От шумных базаров до гор Атлас и дюн Сахары, Марокко пленяет каждого путешественника.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'de', 'Morocco', 'Morocco', 'Erleben Sie die bezaubernde Mischung aus Berber-, arabischen und französischen Kulturen. Von lebendigen Souks bis zu Atlas-Bergen und Sahara-Dünen, Marokko fesselt jeden Reisenden.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'it', 'Morocco', 'Morocco', 'Vivi l''incantevole fusione di culture berbere, arabe e francesi. Dai souk vivaci alle montagne dell''Atlante e dune del Sahara, il Marocco affascina ogni viaggiatore.');
