INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'en', 'Namibia', 'Namibia', 'Discover Namibia''s otherworldly landscapes from towering red dunes of Sossusvlei to dramatic Fish River Canyon. Experience Skeleton Coast mysteries, desert-adapted wildlife, and stargazing perfection.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'ar', 'Namibia', 'Namibia', 'اكتشف مناظر ناميبيا الغريبة من كثبان سوسفلي الحمراء الشاهقة إلى أخدود نهر السمك الدرامي. استمتع بأسرار ساحل الهياكل والحياة البرية المتكيفة مع الصحراء ومراقبة النجوم المثالية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'es', 'Namibia', 'Namibia', 'Descubre paisajes de otro mundo de Namibia desde dunas rojas imponentes de Sossusvlei hasta dramático Cañón del Río Pez. Experimenta misterios de Costa Esqueleto, vida silvestre adaptada al desierto y perfección para observar estrellas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'fr', 'Namibia', 'Namibia', 'Découvrez paysages d''un autre monde de Namibie des dunes rouges imposantes de Sossusvlei au dramatique Canyon de la Rivière aux Poissons. Vivez mystères de Côte des Squelettes, faune adaptée au désert et perfection d''observation des étoiles.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'pt', 'Namibia', 'Namibia', 'Descubra paisagens de outro mundo da Namíbia desde dunas vermelhas imponentes de Sossusvlei até dramático Canyon do Rio dos Peixes. Experimente mistérios da Costa do Esqueleto, vida selvagem adaptada ao deserto e perfeição para observar estrelas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'ru', 'Namibia', 'Namibia', 'Откройте потусторонние ландшафты Намибии от высоких красных дюн Соссусвлей до драматичного каньона реки Рыбной. Ощутите тайны Берега Скелетов, дикую природу, приспособленную к пустыне, и идеальное наблюдение за звёздами.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'de', 'Namibia', 'Namibia', 'Entdecken Sie Namibias andere Weltlandschaften von hoch aufragenden roten Dünen Sossusvlei bis zum dramatischen Fish River Canyon. Erleben Sie Skelettküsten-Geheimnisse, wüstenangepasste Tierwelt und perfekte Sternenbeobachtung.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'it', 'Namibia', 'Namibia', 'Scopri paesaggi di un altro mondo della Namibia dalle imponenti dune rosse di Sossusvlei al drammatico Canyon del Fiume dei Pesci. Vivi misteri della Costa degli Scheletri, fauna adattata al deserto e perfezione per osservare le stelle.');
