INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'en', 'South Africa', 'South Africa', 'Discover the Rainbow Nation with spectacular wildlife, stunning landscapes, and rich cultural diversity. From safari adventures to vibrant cities and beautiful coastlines.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'ar', 'South Africa', 'South Africa', 'اكتشف أمة قوس قزح مع الحياة البرية المذهلة والمناظر الطبيعية الخلابة والتنوع الثقافي الغني. من مغامرات السفاري إلى المدن النابضة بالحياة والسواحل الجميلة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'es', 'South Africa', 'South Africa', 'Descubre la Nación Arcoíris con fauna espectacular, paisajes impresionantes y rica diversidad cultural. Desde aventuras de safari hasta ciudades vibrantes y hermosas costas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'fr', 'South Africa', 'South Africa', 'Découvrez la Nation Arc-en-Ciel avec une faune spectaculaire, des paysages époustouflants et une riche diversité culturelle. Des aventures safari aux villes dynamiques et belles côtes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'pt', 'South Africa', 'South Africa', 'Descubra a Nação Arco-Íris com vida selvagem espetacular, paisagens deslumbrantes e rica diversidade cultural. De aventuras de safari a cidades vibrantes e belas costas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'ru', 'South Africa', 'South Africa', 'Откройте страну Радужной Нации с великолепной дикой природой, потрясающими ландшафтами и богатым культурным разнообразием. От сафари до ярких городов и красивых побережий.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'de', 'South Africa', 'South Africa', 'Entdecken Sie die Regenbogen-Nation mit spektakulärer Tierwelt, atemberaubenden Landschaften und reicher kultureller Vielfalt. Von Safari-Abenteuern bis zu lebendigen Städten und schönen Küsten.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'it', 'South Africa', 'South Africa', 'Scopri la Nazione Arcobaleno con fauna spettacolare, paesaggi mozzafiato e ricca diversità culturale. Da avventure safari a città vibranti e belle coste.');
