INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'en', 'Tunisia', 'Tunisia', 'Explore Tunisia where Mediterranean charm meets Saharan adventure. Discover ancient Carthage ruins, stunning coastlines, and authentic North African culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'ar', 'Tunisia', 'Tunisia', 'استكشف تونس حيث يلتقي سحر البحر الأبيض المتوسط مع مغامرة الصحراء. اكتشف آثار قرطاج القديمة والسواحل الخلابة والثقافة الشمال أفريقية الأصيلة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'es', 'Tunisia', 'Tunisia', 'Explora Túnez donde el encanto mediterráneo se encuentra con la aventura sahariana. Descubre ruinas de la antigua Cartago, costas impresionantes y auténtica cultura norteafricana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'fr', 'Tunisia', 'Tunisia', 'Explorez la Tunisie où le charme méditerranéen rencontre l''aventure saharienne. Découvrez les ruines antiques de Carthage, côtes magnifiques et culture nord-africaine authentique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'pt', 'Tunisia', 'Tunisia', 'Explore a Tunísia onde o charme mediterrâneo encontra a aventura saariana. Descubra ruínas da antiga Cartago, costas deslumbrantes e cultura norte-africana autêntica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'ru', 'Tunisia', 'Tunisia', 'Исследуйте Тунис, где средиземноморское очарование встречается с сахарским приключением. Откройте древние руины Карфагена, потрясающие побережья и аутентичную североафриканскую культуру.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'de', 'Tunisia', 'Tunisia', 'Erkunden Sie Tunesien, wo mediterraner Charme auf Sahara-Abenteuer trifft. Entdecken Sie antike Karthago-Ruinen, atemberaubende Küsten und authentische nordafrikanische Kultur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'it', 'Tunisia', 'Tunisia', 'Esplora la Tunisia dove il fascino mediterraneo incontra l''avventura sahariana. Scopri antiche rovine di Cartagine, coste mozzafiato e autentica cultura nordafricana.');
