INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'en', 'Mexico', 'Mexico', 'Discover vibrant Mexico with ancient Mayan ruins, pristine beaches, and rich traditions. From Cancun paradise to Mexico City culture, experience colorful festivals and authentic cuisine.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'ar', 'Mexico', 'Mexico', 'اكتشف المكسيك النابضة بالحياة مع آثار المايا القديمة والشواطئ البكر والتقاليد الغنية. من جنة كانكون إلى ثقافة مكسيكو سيتي، استمتع بالمهرجانات الملونة والمأكولات الأصيلة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'es', 'Mexico', 'Mexico', 'Descubre el vibrante México con ruinas mayas ancestrales, playas prístinas y tradiciones ricas. Desde el paraíso de Cancún hasta la cultura de Ciudad de México, experimenta festivales coloridos y cocina auténtica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'fr', 'Mexico', 'Mexico', 'Découvrez le Mexique vibrant avec ruines mayas anciennes, plages immaculées et riches traditions. Du paradis de Cancun à la culture de Mexico, vivez festivals colorés et cuisine authentique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'pt', 'Mexico', 'Mexico', 'Descubra o vibrante México com ruínas maias antigas, praias imaculadas e ricas tradições. Do paraíso de Cancun à cultura da Cidade do México, experimente festivais coloridos e culinária autêntica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'ru', 'Mexico', 'Mexico', 'Откройте яркую Мексику с древними руинами Майя, нетронутыми пляжами и богатыми традициями. От парадайза Канкуна до культуры Мехико-сити, ощутите яркие фестивали и аутентичную кухню.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'de', 'Mexico', 'Mexico', 'Entdecken Sie das lebendige Mexiko mit antiken Maya-Ruinen, unberührten Stränden und reichen Traditionen. Vom Cancun-Paradies bis zur Kultur von Mexiko-Stadt, erleben Sie bunte Festivals und authentische Küche.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'it', 'Mexico', 'Mexico', 'Scopri il vibrante Messico con antiche rovine Maya, spiagge incontaminate e ricche tradizioni. Dal paradiso di Cancun alla cultura di Città del Messico, vivi festival colorati e cucina autentica.');
