INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'en', 'Costa Rica', 'Costa Rica', 'Discover Costa Rica''s pure vida with pristine rainforests, stunning beaches, and incredible biodiversity. Experience adventure, wildlife, and sustainable tourism at its finest.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'ar', 'Costa Rica', 'Costa Rica', 'اكتشف الحياة النقية في كوستا ريكا مع الغابات المطيرة النقية والشواطئ الخلابة والتنوع الحيوي المدهش. استمتع بالمغامرة والحياة البرية والسياحة المستدامة في أفضل أشكالها.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'es', 'Costa Rica', 'Costa Rica', 'Descubre la pura vida de Costa Rica con bosques tropicales vírgenes, playas impresionantes y biodiversidad increíble. Experimenta aventura, vida silvestre y turismo sostenible en su máxima expresión.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'fr', 'Costa Rica', 'Costa Rica', 'Découvrez la pura vida du Costa Rica avec forêts tropicales vierges, plages magnifiques et biodiversité incroyable. Vivez aventure, faune sauvage et tourisme durable à son meilleur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'pt', 'Costa Rica', 'Costa Rica', 'Descubra a pura vida da Costa Rica com florestas tropicais intocadas, praias deslumbrantes e biodiversidade incrível. Experimente aventura, vida selvagem e turismo sustentável no seu melhor.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'ru', 'Costa Rica', 'Costa Rica', 'Откройте чистую жизнь Коста-Рики с нетронутыми тропическими лесами, потрясающими пляжами и невероятным биоразнообразием. Ощутите приключения, дикую природу и устойчивый туризм в лучшем виде.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'de', 'Costa Rica', 'Costa Rica', 'Entdecken Sie Costa Ricas pura vida mit unberührten Regenwäldern, atemberaubenden Stränden und unglaublicher Artenvielfalt. Erleben Sie Abenteuer, Tierwelt und nachhaltigen Tourismus vom Feinsten.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'it', 'Costa Rica', 'Costa Rica', 'Scopri la pura vida del Costa Rica con foreste tropicali incontaminate, spiagge mozzafiato e biodiversità incredibile. Vivi avventura, fauna selvatica e turismo sostenibile al suo meglio.');
