INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'en', 'Argentina', 'Argentina', 'Experience passionate Argentina with tango rhythms, world-class wine, and dramatic landscapes. From Buenos Aires sophistication to Patagonia wilderness, discover South American elegance.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'ar', 'Argentina', 'Argentina', 'استمتع بالأرجنتين العاطفية مع إيقاعات التانغو والنبيذ عالي الجودة والمناظر الطبيعية المذهلة. من أناقة بوينوس آيرس إلى برية باتاغونيا، اكتشف الأناقة الأمريكية الجنوبية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'es', 'Argentina', 'Argentina', 'Experimenta la apasionada Argentina con ritmos de tango, vinos de clase mundial y paisajes dramáticos. Desde la sofisticación de Buenos Aires hasta la naturaleza de la Patagonia, descubre elegancia sudamericana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'fr', 'Argentina', 'Argentina', 'Découvrez l''Argentine passionnée avec rythmes de tango, vins de classe mondiale et paysages dramatiques. De la sophistication de Buenos Aires à la nature sauvage de Patagonie, explorez l''élégance sud-américaine.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'pt', 'Argentina', 'Argentina', 'Experimente a apaixonada Argentina com ritmos de tango, vinhos de classe mundial e paisagens dramáticas. Da sofisticação de Buenos Aires à natureza selvagem da Patagônia, descubra elegância sul-americana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'ru', 'Argentina', 'Argentina', 'Ощутите страстную Аргентину с ритмами танго, мировыми винами и драматичными пейзажами. От современности Буэнос-Айреса до дикой Патагонии, откройте южноамериканскую элегантность.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'de', 'Argentina', 'Argentina', 'Erleben Sie das leidenschaftliche Argentinien mit Tango-Rhythmen, Weltklasse-Weinen und dramatischen Landschaften. Von der Raffinesse Buenos Aires bis zur Wildnis Patagoniens, entdecken Sie südamerikanische Eleganz.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'it', 'Argentina', 'Argentina', 'Vivi l''appassionata Argentina con ritmi di tango, vini di classe mondiale e paesaggi drammatici. Dalla sofisticazione di Buenos Aires alla natura selvaggia della Patagonia, scopri eleganza sudamericana.');
