INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'en', 'Brazil', 'Brazil', 'Experience vibrant Brazil with Amazon rainforest, stunning beaches, and infectious energy. From Rio carnival to São Paulo culture, discover the heart of South America.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'ar', 'Brazil', 'Brazil', 'استمتع ببرازيل النابضة بالحياة مع غابات الأمازون المطيرة والشواطئ الخلابة والطاقة المعدية. من كرنفال ريو إلى ثقافة سان باولو، اكتشف قلب أمريكا الجنوبية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'es', 'Brazil', 'Brazil', 'Experimenta el vibrante Brasil con la selva amazónica, playas impresionantes y energía contagiosa. Desde el carnaval de Río hasta la cultura de São Paulo, descubre el corazón de Sudamérica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'fr', 'Brazil', 'Brazil', 'Découvrez le Brésil vibrant avec la forêt amazonienne, plages magnifiques et énergie contagieuse. Du carnaval de Rio à la culture de São Paulo, explorez le cœur de l''Amérique du Sud.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'pt', 'Brazil', 'Brazil', 'Experimente o vibrante Brasil com floresta amazônica, praias deslumbrantes e energia contagiante. Do carnaval do Rio à cultura de São Paulo, descubra o coração da América do Sul.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'ru', 'Brazil', 'Brazil', 'Ощутите яркую Бразилию с тропическими лесами Амазонки, потрясающими пляжами и заразительной энергией. От карнавала в Рио до культуры Сан-Паулу, откройте сердце Южной Америки.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'de', 'Brazil', 'Brazil', 'Erleben Sie das lebendige Brasilien mit Amazonas-Regenwald, atemberaubenden Stränden und ansteckender Energie. Vom Rio-Karneval bis zur São Paulo-Kultur, entdecken Sie das Herz Südamerikas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'it', 'Brazil', 'Brazil', 'Vivi il vibrante Brasile con la foresta amazzonica, spiagge mozzafiato ed energia contagiosa. Dal carnevale di Rio alla cultura di São Paulo, scopri il cuore del Sud America.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'en', 'Chile', 'Chile', 'From the Atacama Desert to Patagonian glaciers, explore Chile''s dramatic landscapes, world-class wines, and vibrant cities like Santiago and Valparaíso.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'ar', 'Chile', 'Chile', 'من صحراء أتاكاما إلى أنهار باتاجونيا الجليدية، استكشف المناظر الطبيعية الدرامية في تشيلي والنبيذ عالمي المستوى والمدن النابضة مثل سانتياغو وفالبارايسو.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'es', 'Chile', 'Chile', 'Desde el desierto de Atacama hasta los glaciares patagónicos, explora los dramáticos paisajes de Chile, vinos de clase mundial y ciudades vibrantes como Santiago y Valparaíso.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'fr', 'Chile', 'Chile', 'Du désert d''Atacama aux glaciers patagons, explorez paysages dramatiques du Chili, vins de classe mondiale et villes vibrantes comme Santiago et Valparaíso.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'pt', 'Chile', 'Chile', 'Do deserto do Atacama às geleiras patagônicas, explore paisagens dramáticas do Chile, vinhos de classe mundial e cidades vibrantes como Santiago e Valparaíso.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'ru', 'Chile', 'Chile', 'От пустыни Атакама до патагонских ледников, исследуйте драматические пейзажи Чили, вина мирового класса и яркие города как Сантьяго и Вальпараисо.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'de', 'Chile', 'Chile', 'Von der Atacama-Wüste bis zu patagonischen Gletschern, erkunden Sie Chiles dramatische Landschaften, Weltklasse-Weine und lebendige Städte wie Santiago und Valparaíso.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'it', 'Chile', 'Chile', 'Dal deserto di Atacama ai ghiacciai della Patagonia, esplora i paesaggi drammatici del Cile, vini di classe mondiale e città vibranti come Santiago e Valparaíso.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'en', 'Colombia', 'Colombia', 'Experience vibrant Colombia from Caribbean beaches to coffee highlands. Explore colorful Cartagena, bustling Bogotá, and the magic of emerald country with warm hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'ar', 'Colombia', 'Colombia', 'استمتع بكولومبيا النابضة من شواطئ البحر الكاريبي إلى مرتفعات القهوة. استكشف كارتاخينا الملونة وبوغوتا الصاخبة وسحر بلاد الزمرد بضيافة دافئة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'es', 'Colombia', 'Colombia', 'Experimenta la vibrante Colombia desde playas caribeñas hasta tierras altas del café. Explora la colorida Cartagena, la bulliciosa Bogotá y la magia del país esmeralda con cálida hospitalidad.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'fr', 'Colombia', 'Colombia', 'Vivez la Colombie vibrante des plages caraïbes aux hauts plateaux de café. Explorez Cartagena colorée, Bogotá bouillonnante et la magie du pays d''émeraude avec hospitalité chaleureuse.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'pt', 'Colombia', 'Colombia', 'Experimente a vibrante Colômbia desde praias do Caribe até terras altas do café. Explore Cartagena colorida, Bogotá movimentada e a magia do país esmeralda com hospitalidade calorosa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'ru', 'Colombia', 'Colombia', 'Почувствуйте яркую Колумбию от карибских пляжей до кофейных высокогорий. Исследуйте яркую Картахену, шумную Боготу и магию изумрудной страны с тёплой гостеприимством.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'de', 'Colombia', 'Colombia', 'Erleben Sie das lebendige Kolumbien von karibischen Stränden bis zu Kaffee-Hochland. Erkunden Sie farbenfrohes Cartagena, geschäftiges Bogotá und die Magie des Smaragdlandes mit herzlicher Gastfreundschaft.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'it', 'Colombia', 'Colombia', 'Vivi la vibrante Colombia dalle spiagge caraibiche agli altipiani del caffè. Esplora la colorata Cartagena, la vivace Bogotá e la magia del paese dello smeraldo con calda ospitalità.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'en', 'Ecuador', 'Ecuador', 'Discover Ecuador''s incredible diversity from Amazon rainforest to Andean peaks and the legendary Galápagos Islands. Experience colonial Quito and vibrant indigenous culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'ar', 'Ecuador', 'Ecuador', 'اكتشف التنوع المدهش في الإكوادور من غابات الأمازون إلى قمم الأنديز وجزر غالاباغوس الأسطورية. استمتع بكيتو الاستعمارية والثقافة الأصيلة النابضة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'es', 'Ecuador', 'Ecuador', 'Descubre la increíble diversidad de Ecuador desde la selva amazónica hasta picos andinos y las legendarias islas Galápagos. Experimenta la colonial Quito y vibrante cultura indígena.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'fr', 'Ecuador', 'Ecuador', 'Découvrez l''incroyable diversité de l''Equateur de la forêt amazonienne aux pics andins et légendaires îles Galápagos. Vivez Quito coloniale et culture indigène vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'pt', 'Ecuador', 'Ecuador', 'Descubra a incrível diversidade do Equador desde floresta amazônica até picos andinos e as lendárias ilhas Galápagos. Experimente Quito colonial e cultura indígena vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'ru', 'Ecuador', 'Ecuador', 'Откройте невероятное разнообразие Эквадора от амазонских тропических лесов до андских пиков и легендарных островов Галапагос. Ощутите колониальное Кито и яркую культуру коренных народов.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'de', 'Ecuador', 'Ecuador', 'Entdecken Sie Ecuadors unglaubliche Vielfalt vom Amazonas-Regenwald bis zu Anden-Gipfeln und den legendären Galápagos-Inseln. Erleben Sie koloniales Quito und lebendige indigene Kultur.');
