INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'en', 'Peru', 'Peru', 'Discover ancient wonders like Machu Picchu, the mysterious Nazca Lines, and the vibrant culture of the Andes in this mystical land.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'ar', 'Peru', 'Peru', 'اكتشف العجائب القديمة مثل ماتشو بيتشو وخطوط النازكا الغامضة والثقافة النابضة بالحياة في الأنديز في هذه الأرض الساحرة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'es', 'Peru', 'Peru', 'Descubre maravillas ancestrales como Machu Picchu, las misteriosas Líneas de Nazca y la vibrante cultura andina en esta tierra mística.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'fr', 'Peru', 'Peru', 'Découvrez les merveilles antiques comme Machu Picchu, les mystérieuses lignes de Nazca et la culture vibrante des Andes dans cette terre mystique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'pt', 'Peru', 'Peru', 'Descubra maravilhas antigas como Machu Picchu, as misteriosas Linhas de Nazca e a cultura vibrante dos Andes nesta terra mística.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'ru', 'Peru', 'Peru', 'Откройте для себя древние чудеса, такие как Мачу-Пикчу, загадочные линии Наска и яркую культуру Анд в этой мистической стране.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'de', 'Peru', 'Peru', 'Entdecken Sie antike Wunder wie Machu Picchu, die geheimnisvollen Nazca-Linien und die lebendige Kultur der Anden in diesem mystischen Land.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'it', 'Peru', 'Peru', 'Scopri meraviglie antiche come Machu Picchu, le misteriose Linee di Nazca e la vibrante cultura andina in questa terra mistica.');
