INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'en', 'Cambodia', 'Cambodia', 'Explore the magnificent temples of Angkor Wat, floating villages, and vibrant markets. Experience ancient Khmer heritage and warm Cambodian hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'ar', 'Cambodia', 'Cambodia', 'استكشف معابد أنغكور وات الرائعة والقرى العائمة والأسواق النابضة بالحياة. اختبر التراث الخميري القديم والضيافة الكمبودية الدافئة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'es', 'Cambodia', 'Cambodia', 'Explora los magníficos templos de Angkor Wat, pueblos flotantes y mercados vibrantes. Experimenta el patrimonio jemer antiguo y la cálida hospitalidad camboyana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'fr', 'Cambodia', 'Cambodia', 'Explorez les magnifiques temples d''Angkor Vat, villages flottants et marchés vibrants. Découvrez l''héritage khmer ancien et l''hospitalité cambodgienne chaleureuse.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'pt', 'Cambodia', 'Cambodia', 'Explore os magníficos templos de Angkor Wat, aldeias flutuantes e mercados vibrantes. Experimente herança khmer antiga e hospitalidade cambojana calorosa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'ru', 'Cambodia', 'Cambodia', 'Исследуйте великолепные храмы Ангкор-Вата, плавучие деревни и яркие рынки. Ощутите древнее кхмерское наследие и теплое камбоджийское гостеприимство.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'de', 'Cambodia', 'Cambodia', 'Erkunden Sie die prächtigen Tempel von Angkor Wat, schwimmende Dörfer und lebendige Märkte. Erleben Sie altes Khmer-Erbe und warme kambodschanische Gastfreundschaft.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'it', 'Cambodia', 'Cambodia', 'Esplora i magnifici templi di Angkor Wat, villaggi galleggianti e mercati vivaci. Vivi l''eredità khmer antica e la calda ospitalità cambogiana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'en', 'China', 'China', 'Discover ancient wonders and modern marvels in the Middle Kingdom. From the Great Wall to bustling cities, experience 5,000 years of rich culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'ar', 'China', 'China', 'اكتشف عجائب قديمة وإنجازات حديثة في الإمبراطورية الوسطى. من السور العظيم إلى المدن النابضة بالحياة، استمتع بـ 5000 عام من الثقافة الغنية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'es', 'China', 'China', 'Descubre maravillas antiguas y logros modernos en el Reino Medio. Desde la Gran Muralla hasta ciudades vibrantes, experimenta 5,000 años de rica cultura.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'fr', 'China', 'China', 'Découvrez des merveilles anciennes et des exploits modernes dans l''Empire du Milieu. De la Grande Muraille aux villes dynamiques, vivez 5 000 ans de riche culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'pt', 'China', 'China', 'Descubra maravilhas antigas e conquistas modernas no Reino do Meio. Da Grande Muralha às cidades vibrantes, experimente 5.000 anos de rica cultura.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'ru', 'China', 'China', 'Откройте древние чудеса и современные достижения в Поднебесной. От Великой стены до живых городов, познайте 5000 лет богатой культуры.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'de', 'China', 'China', 'Entdecken Sie antike Wunder und moderne Errungenschaften im Reich der Mitte. Von der Großen Mauer bis zu pulsierenden Städten, erleben Sie 5.000 Jahre reiche Kultur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'it', 'China', 'China', 'Scopri meraviglie antiche e conquiste moderne nell''Impero di Mezzo. Dalla Grande Muraglia alle città vibranti, vivi 5.000 anni di ricca cultura.');
