INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'en', 'Kuwait', 'Kuwait', 'Experience the pearl of the Gulf with modern architecture, rich maritime heritage, and vibrant souks blending tradition with prosperity.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'ar', 'الكويت', 'الكويت', 'اختبر درة الخليج بهندستها المعمارية الحديثة وتراثها البحري الغني وأسواقها النابضة التي تمزج التراث مع الازدهار.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'es', 'Kuwait', 'Kuwait', 'Experimenta la perla del Golfo con arquitectura moderna, rico patrimonio marítimo y zocos vibrantes que combinan tradición con prosperidad.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'fr', 'Koweït', 'Koweït', 'Découvrez la perle du Golfe avec son architecture moderne, son riche patrimoine maritime et ses souks vibrants mélant tradition et prospérité.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'pt', 'Kuwait', 'Kuwait', 'Experimente a pérola do Golfo com arquitetura moderna, rico patrimônio marítimo e souks vibrantes que misturam tradição com prosperidade.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'ru', 'Кувейт', 'Кувейт', 'Ощутите жемчужину Персидского залива с современной архитектурой, богатым морским наследием и яркими базарами, сочетающими традиции с процветанием.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'de', 'Kuwait', 'Kuwait', 'Erleben Sie die Perle des Golfs mit moderner Architektur, reichem maritimen Erbe und lebendigen Souks, die Tradition mit Wohlstand verbinden.');
