INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'en', 'Nepal', 'Nepal', 'Experience the roof of the world with Mount Everest, ancient temples, and Himalayan adventures. Discover birthplace of Buddha, vibrant festivals, and warm mountain hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'ar', 'Nepal', 'Nepal', 'استمتع بسقف العالم مع جبل إفرست والمعابد القديمة ومغامرات هيمالايا. اكتشف موطن بوذا والمهرجانات النابضة وضيافة الجبل الدافئة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'es', 'Nepal', 'Nepal', 'Experimenta el techo del mundo con Monte Everest, templos ancestrales y aventuras himalayanas. Descubre cuna de Buda, festivales vibrantes y cálida hospitalidad montañesa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'fr', 'Nepal', 'Nepal', 'Vivez le toit du monde avec Mont Everest, temples antiques et aventures himalayennes. Découvrez berceau de Bouddha, festivals vibrants et chaleureuse hospitalité montagnarde.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'pt', 'Nepal', 'Nepal', 'Experimente o teto do mundo com Monte Everest, templos antigos e aventuras himalaias. Descubra berço de Buda, festivais vibrantes e hospitalidade montanhesa calorosa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'ru', 'Nepal', 'Nepal', 'Ощутите крышу мира с Эверестом, древними храмами и гималайскими приключениями. Откройте родину Будды, яркие фестивали и тёплое горное гостеприимство.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'de', 'Nepal', 'Nepal', 'Erleben Sie das Dach der Welt mit Mount Everest, antiken Tempeln und Himalaya-Abenteuern. Entdecken Sie Buddhas Geburtsort, lebendige Festivals und warme Berghospitalität.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'it', 'Nepal', 'Nepal', 'Vivi il tetto del mondo con Monte Everest, templi antichi e avventure himalayane. Scopri culla del Buddha, festival vibranti e calda ospitalità montana.');
