INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'en', 'Oman', 'Oman', 'Experience authentic Arabia with dramatic landscapes, ancient forts, pristine beaches, and the warm hospitality of the Sultanate.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'ar', 'عمان', 'عمان', 'اختبر الجزيرة العربية الأصيلة بمناظرها الدرامية وحصونها القديمة وشواطئها البكر وضيافة السلطنة الدافئة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'es', 'Omán', 'Omán', 'Experimenta la Arabia auténtica con paisajes dramáticos, fuertes antiguos, playas prístinas y la cálida hospitalidad del Sultanato.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'fr', 'Oman', 'Oman', 'Découvrez l''''Arabie authentique avec des paysages dramatiques, des forts anciens, des plages immaculées et la chaleureuse hospitalité du Sultanat.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'pt', 'Omã', 'Omã', 'Experimente a Arábia autêntica com paisagens dramáticas, fortes antigos, praias imaculadas e a calorosa hospitalidade do Sultanato.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'ru', 'Оман', 'Оман', 'Ощутите подлинную Аравию с драматичными пейзажами, древними крепостями, нетронутыми пляжами и тёплым гостеприимством Султаната.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'de', 'Oman', 'Oman', 'Erleben Sie das authentische Arabien mit dramatischen Landschaften, alten Festungen, unberührten Stränden und der warmen Gastfreundschaft des Sultanats.');
