INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'en', 'Vietnam', 'Vietnam', 'Journey through Vietnam from bustling cities to serene landscapes. Experience rich history, breathtaking natural beauty, and extraordinary street food culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'ar', 'Vietnam', 'Vietnam', 'انطلق في رحلة عبر فيتنام من المدن النابضة بالحياة إلى المناظر الطبيعية الهادئة. استمتع بالتاريخ الغني والجمال الطبيعي الخلاب وثقافة طعام الشارع المميزة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'es', 'Vietnam', 'Vietnam', 'Viaja por Vietnam desde ciudades bulliciosas hasta paisajes serenos. Experimenta rica historia, belleza natural impresionante y extraordinaria cultura de comida callejera.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'fr', 'Vietnam', 'Vietnam', 'Voyagez à travers le Vietnam des villes animées aux paysages sereins. Découvrez une riche histoire, une beauté naturelle à couper le souffle et une extraordinaire culture de street food.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'pt', 'Vietnam', 'Vietnam', 'Viaje pelo Vietnã de cidades agitadas a paisagens serenas. Experimente história rica, beleza natural deslumbrante e extraordinária cultura de comida de rua.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'ru', 'Vietnam', 'Vietnam', 'Путешествуйте по Вьетнаму от шумных городов до спокойных пейзажей. Ощутите богатую историю, захватывающую природную красоту и необыкновенную культуру уличной еды.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'de', 'Vietnam', 'Vietnam', 'Reisen Sie durch Vietnam von lebendigen Städten zu ruhigen Landschaften. Erleben Sie reiche Geschichte, atemberaubende Naturschönheit und außergewöhnliche Straßenesskultur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'it', 'Vietnam', 'Vietnam', 'Viaggia attraverso il Vietnam dalle città vivaci ai paesaggi sereni. Vivi una ricca storia, bellezza naturale mozzafiato e straordinaria cultura del cibo di strada.');
