INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'en', 'Finland', 'Finland', 'Experience Finnish sisu with pristine lakes, Northern Lights magic, and sauna culture. Discover Helsinki''s design scene, Lapland''s reindeer, and the land of a thousand lakes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'ar', 'Finland', 'Finland', 'استمتع بالسيسو الفنلندي مع البحيرات النقية وسحر الأضواء الشمالية وثقافة الساونا. اكتشف مشهد التصميم في هلسنكي ورنة لابلاند وأرض الألف بحيرة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'es', 'Finland', 'Finland', 'Experimenta el sisu finlandés con lagos prístinos, magia de auroras boreales y cultura de sauna. Descubre la escena de diseño de Helsinki, renos de Laponia y la tierra de los mil lagos.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'fr', 'Finland', 'Finland', 'Vivez le sisu finlandais avec lacs vierges, magie des aurores boréales et culture sauna. Découvrez scène design d''Helsinki, rennes de Laponie et pays des mille lacs.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'pt', 'Finland', 'Finland', 'Experimente o sisu finlandês com lagos imaculados, magia da aurora boreal e cultura da sauna. Descubra cena de design de Helsínquia, renas da Lapônia e a terra dos mil lagos.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'ru', 'Finland', 'Finland', 'Ощутите финский сису с нетронутыми озерами, магией северного сияния и культурой сауны. Откройте дизайн-сцену Хельсинки, оленей Лапландии и страну тысячи озёр.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'de', 'Finland', 'Finland', 'Erleben Sie finnische Sisu mit unberührten Seen, Nordlichter-Magie und Sauna-Kultur. Entdecken Sie Helsinkis Designszene, Lapplands Rentiere und das Land der tausend Seen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'it', 'Finland', 'Finland', 'Vivi il sisu finlandese con laghi incontaminati, magia dell''aurora boreale e cultura della sauna. Scopri la scena del design di Helsinki, renne della Lapponia e la terra dei mille laghi.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'en', 'France', 'France', 'Experience the epitome of elegance with world-class cuisine, iconic landmarks, and romantic countryside. From the Eiffel Tower to the French Riviera.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'ar', 'فرنسا', 'فرنسا', 'اختبر قمة الأناقة مع المأكولات الراقية والمعالم الشهيرة والريف الرومانسي. من برج إيفل إلى الريفييرا الفرنسية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'es', 'Francia', 'Francia', 'Experimenta el epítome de la elegancia con cocina de clase mundial, monumentos icónicos y paisajes románticos. Desde la Torre Eiffel hasta la Riviera Francesa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'fr', 'France', 'France', 'Découvrez l''''apogée de l''''élégance avec une cuisine de classe mondiale, des monuments emblématiques et des campagnes romantiques. De la Tour Eiffel à la Côte d''''Azur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'pt', 'França', 'França', 'Experimente o epítome da elegância com culinária de classe mundial, monumentos icônicos e paisagens românticas. Da Torre Eiffel à Riviera Francesa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'ru', 'Франция', 'Франция', 'Откройте для себя воплощение элегантности с кухней мирового класса, знаковыми достопримечательностями и романтической сельской местностью. От Эйфелевой башни до Французской Ривьеры.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'de', 'Frankreich', 'Frankreich', 'Erleben Sie den Inbegriff der Eleganz mit Weltklasse-Küche, ikonischen Wahrzeichen und romantischer Landschaft. Vom Eiffelturm bis zur Côte d''''Azur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'it', 'Francia', 'Francia', 'Scopri l''''epitome dell''''eleganza con cucina di classe mondiale, monumenti iconici e campagne romantiche. Dalla Torre Eiffel alla Costa Azzurra.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'en', 'Germany', 'Germany', 'Experience the heart of Europe with fairy-tale castles, vibrant cities, and world-renowned cultural heritage. From the Alps to the Baltic Sea.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'ar', 'ألمانيا', 'ألمانيا', 'اختبر قلب أوروبا بقلاعها الخيالية ومدنها النابضة بالحياة وتراثها الثقافي المشهور عالمياً. من جبال الألب إلى بحر البلطيق.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'es', 'Alemania', 'Alemania', 'Experimenta el corazón de Europa con castillos de cuento de hadas, ciudades vibrantes y patrimonio cultural de renombre mundial. Desde los Alpes hasta el Mar Báltico.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'fr', 'Allemagne', 'Allemagne', 'Découvrez le cœur de l''''Europe avec ses châteaux de conte de fées, ses villes dynamiques et son patrimoine culturel de renommée mondiale. Des Alpes à la mer Baltique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'pt', 'Alemanha', 'Alemanha', 'Experimente o coração da Europa com castelos de contos de fadas, cidades vibrantes e patrimônio cultural de renome mundial. Dos Alpes ao Mar Báltico.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'ru', 'Германия', 'Германия', 'Откройте для себя сердце Европы со сказочными замками, яркими городами и всемирно известным культурным наследием. От Альп до Балтийского моря.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'de', 'Deutschland', 'Deutschland', 'Erleben Sie das Herz Europas mit märchenhaften Schlössern, lebendigen Städten und weltberühmtem Kulturerbe. Von den Alpen bis zur Ostsee.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'it', 'Germania', 'Germania', 'Scopri il cuore dell''''Europa con castelli fiabeschi, città vivaci e patrimonio culturale di fama mondiale. Dalle Alpi al Mar Baltico.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'en', 'Greece', 'Greece', 'Discover ancient Greece with stunning islands, rich mythology, and Mediterranean charm. From Acropolis to Santorini sunsets, experience the cradle of Western civilization.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'ar', 'Greece', 'Greece', 'اكتشف اليونان القديمة مع الجزر الخلابة والأساطير الغنية والسحر المتوسطي. من الأكروبوليس إلى غروب الشمس في سانتوريني، استمتع بمهد الحضارة الغربية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'es', 'Greece', 'Greece', 'Descubre la antigua Grecia con islas impresionantes, rica mitología y encanto mediterráneo. Desde la Acrópolis hasta los atardeceres de Santorini, experimenta la cuna de la civilización occidental.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'fr', 'Greece', 'Greece', 'Découvrez la Grèce antique avec îles magnifiques, riche mythologie et charme méditerranéen. De l''Acropole aux couchers de soleil de Santorin, vivez le berceau de la civilisation occidentale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'pt', 'Greece', 'Greece', 'Descubra a Grécia antiga com ilhas deslumbrantes, rica mitologia e charme mediterrâneo. Da Acrópole aos pôres do sol de Santorini, experimente o berço da civilização ocidental.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'ru', 'Greece', 'Greece', 'Откройте древнюю Грецию с потрясающими островами, богатой мифологией и средиземноморским очарованием. От Акрополя до закатов Санторини, ощутите колыбель западной цивилизации.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'de', 'Greece', 'Greece', 'Entdecken Sie das antike Griechenland mit atemberaubenden Inseln, reicher Mythologie und mediterranem Charme. Von der Akropolis bis zu Santorins Sonnenuntergängen, erleben Sie die Wiege der westlichen Zivilisation.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'it', 'Greece', 'Greece', 'Scopri l''antica Grecia con isole mozzafiato, ricca mitologia e fascino mediterraneo. Dall''Acropoli ai tramonti di Santorini, vivi la culla della civiltà occidentale.');
