INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'en', 'Italy', 'Italy', 'Immerse yourself in timeless beauty with Renaissance art, ancient ruins, and world-famous cuisine. From Rome to the Amalfi Coast.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'ar', 'إيطاليا', 'إيطاليا', 'انغمس في الجمال الخالد مع فن عصر النهضة والآثار القديمة والمأكولات المشهورة عالمياً. من روما إلى ساحل أمالفي.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'es', 'Italia', 'Italia', 'Sumérgete en la belleza atemporal con arte renacentista, ruinas antiguas y cocina mundialmente famosa. Desde Roma hasta la Costa Amalfitana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'fr', 'Italie', 'Italie', 'Plongez dans une beauté intemporelle avec l''''art de la Renaissance, des ruines antiques et une cuisine de renommée mondiale. De Rome à la côte amalfitaine.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'pt', 'Itália', 'Itália', 'Mergulhe na beleza atemporal com arte renascentista, ruínas antigas e culinária mundialmente famosa. De Roma à Costa Amalfitana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'ru', 'Италия', 'Италия', 'Погрузитесь в вечную красоту с искусством Возрождения, древними руинами и всемирно известной кухней. От Рима до побережья Амальфи.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'de', 'Italien', 'Italien', 'Tauchen Sie ein in zeitlose Schönheit mit Renaissance-Kunst, antiken Ruinen und weltberühmter Küche. Von Rom bis zur Amalfiküste.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'it', 'Italia', 'Italia', 'Immergiti nella bellezza senza tempo con arte rinascimentale, rovine antiche e cucina di fama mondiale. Da Roma alla Costiera Amalfitana.');
