INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'en', 'Netherlands', 'Netherlands', 'Discover the Netherlands with iconic canals, vibrant tulip fields, and world-class museums. From Amsterdam charm to innovative design, experience Dutch culture and cycling paradise.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'ar', 'Netherlands', 'Netherlands', 'اكتشف هولندا مع القنوات الرمزية وحقول التوليب النابضة بالحياة والمتاحف عالمية المستوى. من سحر أمستردام إلى التصميم المبتكر، استمتع بالثقافة الهولندية وجنة الدراجات.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'es', 'Netherlands', 'Netherlands', 'Descubre los Países Bajos con canales icónicos, campos de tulipanes vibrantes y museos de clase mundial. Desde el encanto de Ámsterdam hasta el diseño innovador, experimenta la cultura holandesa y el paraíso ciclista.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'fr', 'Netherlands', 'Netherlands', 'Découvrez les Pays-Bas avec canaux iconiques, champs de tulipes vibrants et musées de classe mondiale. Du charme d''Amsterdam au design innovant, vivez la culture néerlandaise et le paradis du cyclisme.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'pt', 'Netherlands', 'Netherlands', 'Descubra os Países Baixos com canais icônicos, campos de tulipas vibrantes e museus de classe mundial. Do charme de Amsterdã ao design inovador, experimente a cultura holandesa e o paraíso das bicicletas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'ru', 'Netherlands', 'Netherlands', 'Откройте Нидерланды с иконическими каналами, яркими тюльпановыми полями и музеями мирового уровня. От очарования Амстердама до инновационного дизайна, ощутите голландскую культуру и велосипедный рай.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'de', 'Netherlands', 'Netherlands', 'Entdecken Sie die Niederlande mit ikonischen Kanälen, lebendigen Tulpenfeldern und Weltklasse-Museen. Von Amsterdams Charme bis zu innovativem Design, erleben Sie niederländische Kultur und Fahrradparadies.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'it', 'Netherlands', 'Netherlands', 'Scopri i Paesi Bassi con canali iconici, campi di tulipani vibranti e musei di classe mondiale. Dal fascino di Amsterdam al design innovativo, vivi la cultura olandese e il paradiso delle biciclette.');
