INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'en', 'Portugal', 'Portugal', 'Discover Portugal''s timeless charm from historic Lisbon to stunning Porto, sun-soaked Algarve beaches, and world-famous port wine valleys. Experience centuries of maritime heritage.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'ar', 'Portugal', 'Portugal', 'اكتشف سحر البرتغال الخالد من لشبونة التاريخية إلى بورتو الرائعة، وشواطئ الغارف المشمسة، ووديان نبيذ البورت الشهيرة عالمياً. استمتع بقرون من التراث البحري.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'es', 'Portugal', 'Portugal', 'Descubre el encanto eterno de Portugal desde la histórica Lisboa hasta la impresionante Oporto, playas soleadas del Algarve y famosos valles de vino de oporto. Experimenta siglos de patrimonio marítimo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'fr', 'Portugal', 'Portugal', 'Découvrez le charme intemporel du Portugal de Lisbonne historique à magnifique Porto, plages ensoleillées de l''Algarve et vallées de vin de Porto mondialement célèbres. Vivez des siècles d''héritage maritime.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'pt', 'Portugal', 'Portugal', 'Descubra o charme eterno de Portugal desde a histórica Lisboa ao deslumbrante Porto, praias ensolaradas do Algarve e famosos vales do vinho do Porto. Experimente séculos de património marítimo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'ru', 'Portugal', 'Portugal', 'Откройте вечное очарование Португалии от исторической Лиссабона до потрясающего Порту, солнечных пляжей Алгарви и всемирно известных долин портвейна. Ощутите века морского наследия.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'de', 'Portugal', 'Portugal', 'Entdecken Sie Portugals zeitlosen Charme vom historischen Lissabon bis zum atemberaubenden Porto, sonnigen Algarve-Stränden und weltberühmten Portwein-Tälern. Erleben Sie jahrhundertealtes Seefahrtserbe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'it', 'Portugal', 'Portugal', 'Scopri il fascino senza tempo del Portogallo dalla storica Lisbona alla splendida Porto, spiagge soleggiate dell''Algarve e famose valli del vino di Porto. Vivi secoli di patrimonio marittimo.');
