INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'en', 'Spain', 'Spain', 'Discover passionate culture with flamenco rhythms, stunning architecture, and vibrant festivals. From Barcelona to Seville.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'ar', 'إسبانيا', 'إسبانيا', 'اكتشف الثقافة العاطفية مع إيقاعات الفلامنكو والهندسة المعمارية المذهلة والمهرجانات النابضة بالحياة. من برشلونة إلى إشبيلية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'es', 'España', 'España', 'Descubre la cultura apasionada con ritmos de flamenco, arquitectura impresionante y festivales vibrantes. Desde Barcelona hasta Sevilla.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'fr', 'Espagne', 'Espagne', 'Découvrez une culture passionnée avec les rythmes du flamenco, une architecture époustouflante et des festivals vibrants. De Barcelone à Séville.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'pt', 'Espanha', 'Espanha', 'Descubra a cultura apaixonada com ritmos de flamenco, arquitetura deslumbrante e festivais vibrantes. De Barcelona a Sevilha.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'ru', 'Испания', 'Испания', 'Откройте для себя страстную культуру с ритмами фламенко, потрясающей архитектурой и яркими фестивалями. От Барселоны до Севильи.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'de', 'Spanien', 'Spanien', 'Entdecken Sie leidenschaftliche Kultur mit Flamenco-Rhythmen, atemberaubender Architektur und lebendigen Festivals. Von Barcelona bis Sevilla.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'it', 'Spagna', 'Spagna', 'Scopri la cultura appassionata con ritmi di flamenco, architettura mozzafiato e festival vivaci. Da Barcellona a Siviglia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'en', 'Sweden', 'Sweden', 'Discover Sweden''s perfect balance of innovation and nature. Experience Stockholm''s archipelago, Lapland''s wilderness, ABBA legacy, and renowned design culture with sustainable living.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'ar', 'Sweden', 'Sweden', 'اكتشف التوازن المثالي بين الابتكار والطبيعة في السويد. استمتع بأرخبيل ستوكهولم وبرية لابلاند وتراث أبا وثقافة التصميم المشهورة مع الحياة المستدامة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'es', 'Sweden', 'Sweden', 'Descubre el equilibrio perfecto de Suecia entre innovación y naturaleza. Experimenta el archipiélago de Estocolmo, naturaleza de Laponia, legado de ABBA y reconocida cultura de diseño con vida sostenible.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'fr', 'Sweden', 'Sweden', 'Découvrez l''équilibre parfait de la Suède entre innovation et nature. Vivez archipel de Stockholm, nature sauvage de Laponie, héritage ABBA et culture design renommée avec vie durable.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'pt', 'Sweden', 'Sweden', 'Descubra o equilíbrio perfeito da Suécia entre inovação e natureza. Experimente arquipélago de Estocolmo, natureza da Lapônia, legado do ABBA e renomada cultura de design com vida sustentável.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'ru', 'Sweden', 'Sweden', 'Откройте идеальный баланс Швеции между инновациями и природой. Ощутите архипелаг Стокгольма, дикую природу Лапландии, наследие ABBA и знаменитую культуру дизайна с устойчивой жизнью.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'de', 'Sweden', 'Sweden', 'Entdecken Sie Schwedens perfekte Balance zwischen Innovation und Natur. Erleben Sie Stockholms Schärengarten, Lapplands Wildnis, ABBA-Erbe und renommierte Designkultur mit nachhaltigem Leben.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'it', 'Sweden', 'Sweden', 'Scopri il perfetto equilibrio della Svezia tra innovazione e natura. Vivi arcipelago di Stoccolma, natura selvaggia della Lapponia, eredità degli ABBA e rinomata cultura del design con vita sostenibile.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'en', 'Switzerland', 'Switzerland', 'Experience Swiss perfection with majestic Alps, pristine lakes, and world-renowned precision. From skiing adventures to luxury watches and chocolate, discover Alpine excellence.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'ar', 'Switzerland', 'Switzerland', 'استمتع بالكمال السويسري مع جبال الألب الهيبة والبحيرات البكر والدقة العالمية الشهرة. من مغامرات التزلج إلى الساعات الفاخرة والشوكولاته، اكتشف التميز الألبي.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'es', 'Switzerland', 'Switzerland', 'Experimenta la perfección suiza con majestuosos Alpes, lagos prístinos y precisión mundialmente reconocida. Desde aventuras de esquí hasta relojes de lujo y chocolate, descubre la excelencia alpina.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'fr', 'Switzerland', 'Switzerland', 'Découvrez la perfection suisse avec Alpes majestueuses, lacs immaculés et précision mondialement reconnue. Des aventures de ski aux montres de luxe et chocolat, explorez l''excellence alpine.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'pt', 'Switzerland', 'Switzerland', 'Experimente a perfeição suíça com Alpes majestosos, lagos imaculados e precisão mundialmente reconhecida. De aventuras de esqui a relógios de luxo e chocolate, descubra excelência alpina.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'ru', 'Switzerland', 'Switzerland', 'Ощутите швейцарское совершенство с величественными Альпами, пристинными озёрами и всемирно известной точностью. От лыжных приключений до роскошных часов и шоколада, откройте альпийское совершенство.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'de', 'Switzerland', 'Switzerland', 'Erleben Sie Schweizer Perfektion mit majestätischen Alpen, makellosen Seen und weltberühmter Präzision. Von Skiabenteuer bis zu Luxusuhren und Schokolade, entdecken Sie alpine Exzellenz.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'it', 'Switzerland', 'Switzerland', 'Vivi la perfezione svizzera con Alpi maestose, laghi incontaminati e precisione di fama mondiale. Dalle avventure sciistiche agli orologi di lusso e cioccolato, scopri eccellenza alpina.');
