INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'en', 'United States', 'United States of America', 'From iconic cities to stunning national parks, discover endless opportunities in the land of dreams and innovation.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'ar', 'الولايات المتحدة', 'الولايات المتحدة الأمريكية', 'من المدن الأيقونية إلى الحدائق الوطنية المذهلة، اكتشف فرصاً لا نهائية في أرض الأحلام والابتكار.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'es', 'Estados Unidos', 'Estados Unidos de América', 'Desde ciudades icónicas hasta parques nacionales impresionantes, descubre oportunidades infinitas en la tierra de los sueños e innovación.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'fr', 'États-Unis', 'États-Unis d''''Amérique', 'Des villes emblématiques aux parcs nationaux époustouflants, découvrez des opportunités infinies dans la terre des rêves et de l''''innovation.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'pt', 'Estados Unidos', 'Estados Unidos da América', 'De cidades icônicas a parques nacionais deslumbrantes, descubra oportunidades infinitas na terra dos sonhos e inovação.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'ru', 'Соединённые Штаты', 'Соединённые Штаты Америки', 'От знаковых городов до потрясающих национальных парков, откройте для себя бесконечные возможности в стране мечты и инноваций.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'de', 'Vereinigte Staaten', 'Vereinigte Staaten von Amerika', 'Von ikonischen Städten bis hin zu atemberaubenden Nationalparks, entdecken Sie endlose Möglichkeiten im Land der Träume und Innovation.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('USA', 'it', 'Stati Uniti', 'Stati Uniti d''''America', 'Dalle città iconiche ai parchi nazionali mozzafiato, scopri opportunità infinite nella terra dei sogni e dell''''innovazione.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'en', 'Canada', 'Canada', 'Experience vast wilderness, vibrant cities, and warm hospitality in this diverse northern paradise perfect for outdoor adventures.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'ar', 'كندا', 'كندا', 'استمتع بالبرية الشاسعة والمدن النابضة بالحياة والضيافة الدافئة في هذه الجنة الشمالية المتنوعة المثالية للمغامرات الخارجية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'es', 'Canadá', 'Canadá', 'Experimenta vastos territorios salvajes, ciudades vibrantes y cálida hospitalidad en este paraíso norteño diverso perfecto para aventuras al aire libre.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'fr', 'Canada', 'Canada', 'Découvrez de vastes étendues sauvages, des villes dynamiques et une hospitalité chaleureuse dans ce paradis nordique diversifié parfait pour les aventures en plein air.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'pt', 'Canadá', 'Canadá', 'Experimente vastas regiões selvagens, cidades vibrantes e hospitalidade calorosa neste paraíso nórdico diversificado perfeito para aventuras ao ar livre.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'ru', 'Канада', 'Канада', 'Откройте для себя обширную дикую природу, яркие города и теплое гостеприимство в этом разнообразном северном раю, идеальном для приключений на свежем воздухе.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'de', 'Kanada', 'Kanada', 'Erleben Sie weite Wildnis, lebendige Städte und herzliche Gastfreundschaft in diesem vielfältigen nördlichen Paradies, perfekt für Outdoor-Abenteuer.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAN', 'it', 'Canada', 'Canada', 'Vivi vaste terre selvagge, città vibranti e calorosa ospitalità in questo paradiso settentrionale diversificato perfetto per avventure all''aria aperta.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'en', 'United Kingdom', 'United Kingdom of Great Britain and Northern Ireland', 'Explore historic castles, charming countryside, and vibrant cities where centuries of history blend with modern innovation and culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'ar', 'المملكة المتحدة', 'المملكة المتحدة لبريطانيا العظمى وأيرلندا الشمالية', 'استكشف القلاع التاريخية والريف الساحر والمدن النابضة بالحياة حيث تمتزج قرون من التاريخ مع الابتكار والثقافة الحديثة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'es', 'Reino Unido', 'Reino Unido de Gran Bretaña e Irlanda del Norte', 'Explora castillos históricos, campiña encantadora y ciudades vibrantes donde siglos de historia se mezclan con innovación y cultura moderna.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'fr', 'Royaume-Uni', 'Royaume-Uni de Grande-Bretagne et d''''Irlande du Nord', 'Explorez des châteaux historiques, une campagne charmante et des villes dynamiques où des siècles d''''histoire se mêlent à l''''innovation et à la culture modernes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'pt', 'Reino Unido', 'Reino Unido da Grã-Bretanha e Irlanda do Norte', 'Explore castelos históricos, campos encantadores e cidades vibrantes onde séculos de história se misturam com inovação e cultura moderna.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'ru', 'Великобритания', 'Соединённое Королевство Великобритании и Северной Ирландии', 'Исследуйте исторические замки, очаровательную сельскую местность и яркие города, где века истории сочетаются с современными инновациями и культурой.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'de', 'Vereinigtes Königreich', 'Vereinigtes Königreich Großbritannien und Nordirland', 'Erkunden Sie historische Schlösser, bezaubernde Landschaften und lebendige Städte, wo sich Jahrhunderte der Geschichte mit moderner Innovation und Kultur verbinden.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GBR', 'it', 'Regno Unito', 'Regno Unito di Gran Bretagna e Irlanda del Nord', 'Esplora castelli storici, campagne incantevoli e città vibranti dove secoli di storia si fondono con innovazione e cultura moderna.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'en', 'Australia', 'Commonwealth of Australia', 'Experience the land down under with pristine beaches, unique wildlife, and vibrant cities. From the Great Barrier Reef to the Outback.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'ar', 'أستراليا', 'كومنولث أستراليا', 'اكتشف القارة الجنوبية بشواطئها البكر وحياتها البرية الفريدة ومدنها النابضة بالحياة. من الحاجز المرجاني العظيم إلى المناطق النائية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'es', 'Australia', 'Commonwealth de Australia', 'Vive la tierra de los antípodas con playas vírgenes, fauna única y ciudades vibrantes. Desde la Gran Barrera de Coral hasta el Outback.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'fr', 'Australie', 'Commonwealth d''''Australie', 'Découvrez la terre des antipodes avec ses plages immaculées, sa faune unique et ses villes dynamiques. De la Grande Barrière de Corail à l''''Outback.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'pt', 'Austrália', 'Commonwealth da Austrália', 'Descubra a terra dos antípodas com praias intocadas, fauna única e cidades vibrantes. Da Grande Barreira de Coral ao Outback.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'ru', 'Австралия', 'Содружество Австралии', 'Откройте для себя землю антиподов с девственными пляжами, уникальной дикой природой и яркими городами. От Большого Барьерного рифа до глубинки.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'de', 'Australien', 'Commonwealth von Australien', 'Erleben Sie das Land der Antipoden mit unberührten Stränden, einzigartiger Tierwelt und lebendigen Städten. Vom Great Barrier Reef bis zum Outback.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUS', 'it', 'Australia', 'Commonwealth d''''Australia', 'Scopri la terra degli antipodi con spiagge incontaminate, fauna unica e città vibranti. Dalla Grande Barriera Corallina all''''Outback.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'en', 'United Arab Emirates', 'United Arab Emirates', 'Modern oasis where ancient traditions meet futuristic innovation. Experience luxury shopping, stunning architecture, and pristine beaches in the heart of the Middle East.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'ar', 'الإمارات العربية المتحدة', 'دولة الإمارات العربية المتحدة', 'واحة عصرية تلتقي فيها التقاليد القديمة مع الابتكار المستقبلي. استمتع بتسوق فاخر وهندسة معمارية مذهلة وشواطئ نقية في قلب الشرق الأوسط.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'es', 'Emiratos Árabes Unidos', 'Emiratos Árabes Unidos', 'Oasis moderno donde las tradiciones ancestrales se encuentran con la innovación futurista. Experimenta compras de lujo, arquitectura impresionante y playas vírgenes en el corazón del Medio Oriente.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'fr', 'Émirats arabes unis', 'Émirats arabes unis', 'Oasis moderne où les traditions ancestrales rencontrent l''''innovation futuriste. Découvrez le shopping de luxe, l''''architecture époustouflante et des plages immaculées au cœur du Moyen-Orient.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'pt', 'Emirados Árabes Unidos', 'Emirados Árabes Unidos', 'Oásis moderno onde tradições antigas encontram inovação futurista. Experimente compras luxuosas, arquitetura deslumbrante e praias pristinas no coração do Oriente Médio.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'ru', 'Объединённые Арабские Эмираты', 'Объединённые Арабские Эмираты', 'Современный оазис, где древние традиции встречаются с футуристическими инновациями. Наслаждайтесь роскошным шопингом, потрясающей архитектурой и нетронутыми пляжами в сердце Ближнего Востока.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'de', 'Vereinigte Arabische Emirate', 'Vereinigte Arabische Emirate', 'Moderne Oase, wo alte Traditionen auf futuristische Innovation treffen. Erleben Sie luxuriöses Shopping, atemberaubende Architektur und unberührte Strände im Herzen des Nahen Ostens.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARE', 'it', 'Emirati Arabi Uniti', 'Emirati Arabi Uniti', 'Oasi moderna dove antiche tradizioni incontrano innovazione futuristica. Sperimenta shopping di lusso, architettura mozzafiato e spiagge incontaminate nel cuore del Medio Oriente.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'en', 'Algeria', 'Algeria', 'Explore North Africa''s largest country with stunning Sahara landscapes, ancient Roman ruins at Timgad, and vibrant Mediterranean coast. Discover Berber heritage and French colonial architecture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'ar', 'Algeria', 'Algeria', 'استكشف أكبر بلدان شمال أفريقيا مع مناظر الصحراء الخلابة وآثار رومانية قديمة في تيمغاد والساحل المتوسطي النابض. اكتشف التراث البربري والعمارة الاستعمارية الفرنسية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'es', 'Algeria', 'Algeria', 'Explora el país más grande del norte de África con impresionantes paisajes del Sahara, ruinas romanas ancestrales en Timgad y vibrante costa mediterránea. Descubre patrimonio bereber y arquitectura colonial francesa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'fr', 'Algeria', 'Algeria', 'Explorez le plus grand pays d''Afrique du Nord avec paysages sahariens époustouflants, ruines romaines antiques à Timgad et côte méditerranéenne vibrante. Découvrez héritage berbère et architecture coloniale française.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'pt', 'Algeria', 'Algeria', 'Explore o maior país do norte da África com paisagens deslumbrantes do Saara, ruínas romanas antigas em Timgad e costa mediterrânea vibrante. Descubra património berbere e arquitetura colonial francesa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'ru', 'Algeria', 'Algeria', 'Исследуйте крупнейшую страну Северной Африки с потрясающими пейзажами Сахары, древними римскими руинами в Тимгаде и ярким средиземноморским побережьем. Откройте берберское наследие и французскую колониальную архитектуру.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'de', 'Algeria', 'Algeria', 'Erkunden Sie Nordafrikas größtes Land mit atemberaubenden Sahara-Landschaften, antiken römischen Ruinen in Timgad und lebendiger Mittelmeerküste. Entdecken Sie Berber-Erbe und französische Kolonialarchitektur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DZA', 'it', 'Algeria', 'Algeria', 'Esplora il più grande paese del Nord Africa con paesaggi sahariani mozzafiato, antiche rovine romane a Timgad e costa mediterranea vibrante. Scopri patrimonio berbero e architettura coloniale francese.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AGO', 'en', 'Angola', 'Angola', 'Explore vast landscapes, from tropical Atlantic beaches to sub-Saharan desert. Discover vibrant culture, colonial history, and the rhythms of kizomba.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AGO', 'ar', 'أنغولا', 'أنغولا', 'استكشف المناظر الطبيعية الشاسعة، من شواطئ المحيط الأطلسي الاستوائية إلى الصحراء جنوب الصحراء الكبرى. اكتشف الثقافة النابضة بالحياة والتاريخ الاستعماري وإيقاعات الكيزومبا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AGO', 'es', 'Angola', 'Angola', 'Explora vastos paisajes, desde las playas tropicales del Atlántico hasta el desierto subsahariano. Descubre una cultura vibrante, historia colonial y los ritmos de la kizomba.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AGO', 'fr', 'Angola', 'Angola', 'Explorez de vastes paysages, des plages tropicales de l''Atlantique au désert subsaharien. Découvrez une culture vibrante, une histoire coloniale et les rythmes de la kizomba.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AGO', 'pt', 'Angola', 'Angola', 'Explore vastas paisagens, das praias tropicais do Atlântico ao deserto subsaariano. Descubra a cultura vibrante, a história colonial e os ritmos da kizomba.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AGO', 'ru', 'Ангола', 'Ангола', 'Исследуйте обширные ландшафты, от тропических пляжей Атлантики до пустыни к югу от Сахары. Откройте для себя яркую культуру, колониальную историю и ритмы кизомбы.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AGO', 'de', 'Angola', 'Angola', 'Erkunden Sie weite Landschaften, von tropischen Atlantikstränden bis zur Subsahara-Wüste. Entdecken Sie eine lebendige Kultur, Kolonialgeschichte und die Rhythmen von Kizomba.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AGO', 'it', 'Angola', 'Angola', 'Esplora vasti paesaggi, dalle spiagge tropicali dell''Atlantico al deserto sub-sahariano. Scopri una cultura vibrante, la storia coloniale e i ritmi della kizomba.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEN', 'en', 'Benin', 'Benin', 'Discover the birthplace of Voodoo, the slave coast, and the stilt village of Ganvié. Experience vibrant culture and history in West Africa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEN', 'ar', 'بنين', 'بنين', 'اكتشف مهد الفودو، وساحل العبيد، وقرية غانفي على ركائز. استمتع بالثقافة والتاريخ النابضين بالحياة في غرب أفريقيا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEN', 'es', 'Benín', 'Benín', 'Descubre la cuna del vudú, la costa de los esclavos y el pueblo sobre pilotes de Ganvié. Experimenta una cultura e historia vibrantes en África Occidental.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEN', 'fr', 'Bénin', 'Bénin', 'Découvrez le berceau du vaudou, la côte des esclaves et le village sur pilotis de Ganvié. Vivez une culture et une histoire vibrantes en Afrique de l''Ouest.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEN', 'pt', 'Benin', 'Benin', 'Descubra o berço do Vodu, a costa dos escravos e a aldeia sobre palafitas de Ganvié. Experimente a cultura e história vibrantes na África Ocidental.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEN', 'ru', 'Бенин', 'Бенин', 'Откройте для себя родину вуду, побережье рабов и деревню на сваях Ганвье. Познакомьтесь с яркой культурой и историей Западной Африки.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEN', 'de', 'Benin', 'Benin', 'Entdecken Sie den Geburtsort des Voodoo, die Sklavenküste und das Stelzendorf Ganvié. Erleben Sie eine lebendige Kultur und Geschichte in Westafrika.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEN', 'it', 'Benin', 'Benin', 'Scopri la culla del Voodoo, la costa degli schiavi e il villaggio su palafitte di Ganvié. Vivi la vibrante cultura e storia dell''Africa occidentale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'en', 'Botswana', 'Botswana', 'Experience Africa''s wildlife paradise in the pristine Okavango Delta and vast Kalahari Desert. Discover world-class safari adventures, diamond heritage, and peaceful stability in southern Africa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'ar', 'Botswana', 'Botswana', 'استمتع بجنة الحياة البرية في أفريقيا في دلتا أوكافانغو البكر وصحراء كالاهاري الشاسعة. اكتشف مغامرات السافاري عالمية المستوى وتراث الالماس والاستقرار السلمي في جنوب أفريقيا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'es', 'Botswana', 'Botswana', 'Experimenta el paraíso de vida silvestre de África en el prístino Delta de Okavango y vasto Desierto de Kalahari. Descubre aventuras de safari de clase mundial, patrimonio de diamantes y estabilidad pacífica en África austral.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'fr', 'Botswana', 'Botswana', 'Vivez le paradis de la faune africaine dans le Delta d''Okavango vierge et vaste Désert du Kalahari. Découvrez aventures safari de classe mondiale, héritage diamantaire et stabilité pacifique en Afrique australe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'pt', 'Botswana', 'Botswana', 'Experimente o paraíso da vida selvagem da África no prístino Delta do Okavango e vasto Deserto de Kalahari. Descubra aventuras de safari de classe mundial, património de diamantes e estabilidade pacífica na África austral.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'ru', 'Botswana', 'Botswana', 'Ощутите рай дикой природы Африки в нетронутой дельте Окаванго и обширной пустыне Калахари. Откройте сафари мирового класса, алмазное наследие и мирную стабильность южной Африки.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'de', 'Botswana', 'Botswana', 'Erleben Sie Afrikas Wildtierparadies im unberührten Okavango-Delta und der weiten Kalahari-Wüste. Entdecken Sie Weltklasse-Safari-Abenteuer, Diamanten-Erbe und friedliche Stabilität im südlichen Afrika.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BWA', 'it', 'Botswana', 'Botswana', 'Vivi il paradiso della fauna selvatica africana nel pristino Delta dell''Okavango e vasto Deserto del Kalahari. Scopri avventure safari di classe mondiale, patrimonio diamantifero e stabilità pacifica nell''Africa meridionale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BFA', 'en', 'Burkina Faso', 'Burkina Faso', 'Discover the ''Land of Honest Men'' with its vibrant music scene, traditional Mossi culture, and stunning Sahelian landscapes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BFA', 'ar', 'بوركينا فاسو', 'بوركينا فاسو', 'اكتشف ''أرض الرجال النزيهين'' بمشهدها الموسيقي النابض بالحياة، وثقافة موسي التقليدية، ومناظرها الطبيعية الساحلية الخلابة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BFA', 'es', 'Burkina Faso', 'Burkina Faso', 'Descubre la ''Tierra de los Hombres Honestos'' con su vibrante escena musical, la cultura tradicional Mossi y sus impresionantes paisajes sahelianos.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BFA', 'fr', 'Burkina Faso', 'Burkina Faso', 'Découvrez le ''Pays des Hommes Intègres'' avec sa scène musicale vibrante, la culture traditionnelle Mossi et ses paysages sahéliens époustouflants.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BFA', 'pt', 'Burkina Faso', 'Burkina Faso', 'Descubra a ''Terra dos Homens Honestos'' com sua vibrante cena musical, a cultura tradicional Mossi e suas deslumbrantes paisagens sahelianas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BFA', 'ru', 'Буркина-Фасо', 'Буркина-Фасо', 'Откройте для себя ''Землю честных людей'' с ее яркой музыкальной сценой, традиционной культурой Моси и потрясающими сахелианскими пейзажами.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BFA', 'de', 'Burkina Faso', 'Burkina Faso', 'Entdecken Sie das ''Land der ehrlichen Menschen'' mit seiner lebendigen Musikszene, der traditionellen Mossi-Kultur und den atemberaubenden Sahel-Landschaften.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BFA', 'it', 'Burkina Faso', 'Burkina Faso', 'Scopri la ''Terra degli Uomini Onesti'' con la sua vibrante scena musicale, la cultura tradizionale Mossi e i suoi splendidi paesaggi saheliani.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BDI', 'en', 'Burundi', 'Burundi', 'Discover the ''Heart of Africa'' with its stunning Lake Tanganyika, lush highlands, and vibrant culture. Experience the rhythm of Burundian drums and warm hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BDI', 'ar', 'بوروندي', 'بوروندي', 'اكتشف ''قلب أفريقيا'' ببحيرة تنجانيقا الخلابة، ومرتفعاتها الخضراء، وثقافتها النابضة بالحياة. استمتع بإيقاع الطبول البوروندية والضيافة الدافئة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BDI', 'es', 'Burundi', 'Burundi', 'Descubre el ''Corazón de África'' con su impresionante lago Tanganica, sus exuberantes tierras altas y su vibrante cultura. Experimenta el ritmo de los tambores burundeses y la cálida hospitalidad.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BDI', 'fr', 'Burundi', 'Burundi', 'Découvrez le ''Cœur de l''Afrique'' avec son magnifique lac Tanganyika, ses hauts plateaux verdoyants et sa culture vibrante. Vivez le rythme des tambours burundais et l''hospitalité chaleureuse.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BDI', 'pt', 'Burundi', 'Burundi', 'Descubra o ''Coração da África'' com o seu deslumbrante Lago Tanganica, as suas terras altas exuberantes e a sua cultura vibrante. Experimente o ritmo dos tambores do Burundi e a calorosa hospitalidade.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BDI', 'ru', 'Бурунди', 'Бурунди', 'Откройте для себя ''Сердце Африки'' с его потрясающим озером Танганьика, пышными высокогорьями и яркой культурой. Почувствуйте ритм бурундийских барабанов и теплое гостеприимство.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BDI', 'de', 'Burundi', 'Burundi', 'Entdecken Sie das ''Herz Afrikas'' mit dem atemberaubenden Tanganjikasee, den üppigen Hochebenen und der lebendigen Kultur. Erleben Sie den Rhythmus der burundischen Trommeln und die herzliche Gastfreundschaft.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BDI', 'it', 'Burundi', 'Burundi', 'Scopri il ''Cuore dell''Africa'' con il suo splendido Lago Tanganica, i suoi altopiani lussureggianti e la sua vibrante cultura. Vivi il ritmo dei tamburi burundesi e la calda ospitalità.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CMR', 'en', 'Cameroon', 'Cameroon', 'Discover ''Africa in Miniature'' with its incredible diversity of landscapes, cultures, and wildlife, from rainforests to savannas and mountains.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CMR', 'ar', 'الكاميرون', 'الكاميرون', 'اكتشف ''أفريقيا المصغرة'' بتنوعها المذهل من المناظر الطبيعية والثقافات والحياة البرية، من الغابات المطيرة إلى السافانا والجبال.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CMR', 'es', 'Camerún', 'Camerún', 'Descubre ''África en Miniatura'' con su increíble diversidad de paisajes, culturas y vida silvestre, desde selvas tropicales hasta sabanas y montañas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CMR', 'fr', 'Cameroun', 'Cameroun', 'Découvrez l''''Afrique en miniature'' avec son incroyable diversité de paysages, de cultures et de faune, des forêts tropicales aux savanes et montagnes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CMR', 'pt', 'Camarões', 'Camarões', 'Descubra a ''África em Miniatura'' com sua incrível diversidade de paisagens, culturas e vida selvagem, das florestas tropicais às savanas e montanhas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CMR', 'ru', 'Камерун', 'Камерун', 'Откройте для себя ''Африку в миниатюре'' с ее невероятным разнообразием ландшафтов, культур и дикой природы, от тропических лесов до саванн и гор.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CMR', 'de', 'Kamerun', 'Kamerun', 'Entdecken Sie ''Afrika in Miniatur'' mit seiner unglaublichen Vielfalt an Landschaften, Kulturen und Wildtieren, von Regenwäldern über Savannen bis hin zu Bergen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CMR', 'it', 'Camerun', 'Camerun', 'Scopri l''''Africa in miniatura'' con la sua incredibile diversità di paesaggi, culture e fauna selvatica, dalle foreste pluviali alle savane and montagne.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CPV', 'en', 'Cape Verde', 'Cape Verde', 'Discover a volcanic archipelago of stunning beaches, vibrant music, and a unique Creole culture. Experience the ''morabeza'' spirit of the islands.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CPV', 'ar', 'الرأس الأخضر', 'الرأس الأخضر', 'اكتشف أرخبيلاً بركانياً من الشواطئ الخلابة والموسيقى النابضة بالحياة وثقافة الكريول الفريدة. استمتع بروح ''مورابيزا'' في الجزر.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CPV', 'es', 'Cabo Verde', 'Cabo Verde', 'Descubre un archipiélago volcánico de playas impresionantes, música vibrante y una cultura criolla única. Experimenta el espíritu ''morabeza'' de las islas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CPV', 'fr', 'Cap-Vert', 'Cap-Vert', 'Découvrez un archipel volcanique de plages magnifiques, de musique vibrante et d''une culture créole unique. Vivez l''esprit ''morabeza'' des îles.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CPV', 'pt', 'Cabo Verde', 'Cabo Verde', 'Descubra um arquipélago vulcânico de praias deslumbrantes, música vibrante e uma cultura crioula única. Experimente o espírito de ''morabeza'' das ilhas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CPV', 'ru', 'Кабо-Верде', 'Кабо-Верде', 'Откройте для себя вулканический архипелаг с потрясающими пляжами, яркой музыкой и уникальной креольской культурой. Почувствуйте дух ''морабезы'' островов.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CPV', 'de', 'Kap Verde', 'Kap Verde', 'Entdecken Sie einen vulkanischen Archipel mit atemberaubenden Stränden, lebendiger Musik und einer einzigartigen kreolischen Kultur. Erleben Sie den ''Morabeza''-Geist der Inseln.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CPV', 'it', 'Capo Verde', 'Capo Verde', 'Scopri un arcipelago vulcanico di spiagge meravigliose, musica vibrante e una cultura creola unica. Vivi lo spirito ''morabeza'' delle isole.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAF', 'en', 'Central African Republic', 'Central African Republic', 'Discover a land of immense natural beauty, from dense rainforests to sprawling savannas, and encounter unique wildlife and vibrant traditions.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAF', 'ar', 'جمهورية أفريقيا الوسطى', 'جمهورية أفريقيا الوسطى', 'اكتشف أرضًا ذات جمال طبيعي هائل، من الغابات المطيرة الكثيفة إلى السافانا المترامية الأطراف، وتعرّف على الحياة البرية الفريدة والتقاليد النابضة بالحياة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAF', 'es', 'República Centroafricana', 'República Centroafricana', 'Descubre una tierra de inmensa belleza natural, desde densas selvas tropicales hasta extensas sabanas, y encuentra una vida silvestre única y tradiciones vibrantes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAF', 'fr', 'République centrafricaine', 'République centrafricaine', 'Découvrez une terre d''une immense beauté naturelle, des forêts tropicales denses aux savanes tentaculaires, et rencontrez une faune unique et des traditions vibrantes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAF', 'pt', 'República Centro-Africana', 'República Centro-Africana', 'Descubra uma terra de imensa beleza natural, desde densas florestas tropicais a vastas savanas, e encontre uma vida selvagem única e tradições vibrantes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAF', 'ru', 'Центральноафриканская Республика', 'Центральноафриканская Республика', 'Откройте для себя землю огромной природной красоты, от густых тропических лесов до раскидистых саванн, и познакомьтесь с уникальной дикой природой и яркими традициями.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAF', 'de', 'Zentralafrikanische Republik', 'Zentralafrikanische Republik', 'Entdecken Sie ein Land von immenser natürlicher Schönheit, von dichten Regenwäldern bis zu weitläufigen Savannen, und begegnen Sie einer einzigartigen Tierwelt und lebendigen Traditionen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CAF', 'it', 'Repubblica Centrafricana', 'Repubblica Centrafricana', 'Scopri una terra di immensa bellezza naturale, dalle fitte foreste pluviali alle vaste savane, e incontra una fauna selvatica unica e tradizioni vibranti.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TCD', 'en', 'Chad', 'Chad', 'Discover the heart of the Sahara with stunning desert landscapes, ancient rock art, and the unique biodiversity of Lake Chad.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TCD', 'ar', 'تشاد', 'تشاد', 'اكتشف قلب الصحراء الكبرى بمناظرها الطبيعية الصحراوية الخلابة، والفن الصخري القديم، والتنوع البيولوجي الفريد لبحيرة تشاد.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TCD', 'es', 'Chad', 'Chad', 'Descubre el corazón del Sáhara con impresionantes paisajes desérticos, arte rupestre antiguo y la biodiversidad única del lago Chad.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TCD', 'fr', 'Tchad', 'Tchad', 'Découvrez le cœur du Sahara avec des paysages désertiques époustouflants, de l''art rupestre ancien et la biodiversité unique du lac Tchad.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TCD', 'pt', 'Chade', 'Chade', 'Descubra o coração do Saara com paisagens desérticas deslumbrantes, arte rupestre antiga e a biodiversidade única do Lago Chade.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TCD', 'ru', 'Чад', 'Чад', 'Откройте для себя сердце Сахары с потрясающими пустынными пейзажами, древним наскальным искусством и уникальным биоразнообразием озера Чад.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TCD', 'de', 'Tschad', 'Tschad', 'Entdecken Sie das Herz der Sahara mit atemberaubenden Wüstenlandschaften, alter Felskunst und der einzigartigen Artenvielfalt des Tschadsees.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TCD', 'it', 'Ciad', 'Ciad', 'Scopri il cuore del Sahara con paesaggi desertici mozzafiato, arte rupestre antica e la biodiversità unica del Lago Ciad.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COM', 'en', 'Comoros', 'Comoros', 'Discover the ''Perfumed Islands'' with their volcanic landscapes, pristine beaches, and unique blend of African, Arab, and French cultures.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COM', 'ar', 'جزر القمر', 'جزر القمر', 'اكتشف ''الجزر المعطرة'' بمناظرها الطبيعية البركانية، وشواطئها البكر، ومزيجها الفريد من الثقافات الأفريقية والعربية والفرنسية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COM', 'es', 'Comoras', 'Comoras', 'Descubre las ''Islas Perfumadas'' con sus paisajes volcánicos, playas vírgenes y una mezcla única de culturas africanas, árabes y francesas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COM', 'fr', 'Comores', 'Comores', 'Découvrez les ''Îles aux Parfums'' avec leurs paysages volcaniques, leurs plages immaculées et leur mélange unique de cultures africaines, arabes et françaises.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COM', 'pt', 'Comores', 'Comores', 'Descubra as ''Ilhas Perfumadas'' com as suas paisagens vulcânicas, praias imaculadas e uma mistura única de culturas africanas, árabes e francesas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COM', 'ru', 'Коморские острова', 'Коморские острова', 'Откройте для себя ''Парфюмерные острова'' с их вулканическими пейзажами, нетронутыми пляжами и уникальным сочетанием африканской, арабской и французской культур.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COM', 'de', 'Komoren', 'Komoren', 'Entdecken Sie die ''Parfümierten Inseln'' mit ihren Vulkanlandschaften, unberührten Stränden und einer einzigartigen Mischung aus afrikanischen, arabischen und französischen Kulturen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COM', 'it', 'Comore', 'Comore', 'Scopri le ''Isole Profumate'' con i loro paesaggi vulcanici, le spiagge incontaminate e una miscela unica di culture africane, arabe e francesi.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COG', 'en', 'Congo', 'Congo', 'Explore the vast Congo Basin, with its dense rainforests, incredible biodiversity, and the mighty Congo River. Discover vibrant music and resilient cultures.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COG', 'ar', 'الكونغو', 'الكونغو', 'استكشف حوض الكونغو الشاسع، بغاباته المطيرة الكثيفة، وتنوعه البيولوجي المذهل، ونهر الكونغو العظيم. اكتشف الموسيقى النابضة بالحياة والثقافات الصامدة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COG', 'es', 'Congo', 'Congo', 'Explora la vasta cuenca del Congo, con sus densas selvas tropicales, su increíble biodiversidad y el poderoso río Congo. Descubre una música vibrante y culturas resilientes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COG', 'fr', 'Congo', 'Congo', 'Explorez le vaste bassin du Congo, avec ses forêts tropicales denses, son incroyable biodiversité et le puissant fleuve Congo. Découvrez une musique vibrante et des cultures résilientes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COG', 'pt', 'Congo', 'Congo', 'Explore a vasta Bacia do Congo, com as suas densas florestas tropicais, a sua incrível biodiversidade e o poderoso Rio Congo. Descubra uma música vibrante e culturas resilientes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COG', 'ru', 'Конго', 'Конго', 'Исследуйте обширный бассейн Конго с его густыми тропическими лесами, невероятным биоразнообразием и могучей рекой Конго. Откройте для себя яркую музыку и стойкие культуры.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COG', 'de', 'Kongo', 'Kongo', 'Erkunden Sie das riesige Kongobecken mit seinen dichten Regenwäldern, seiner unglaublichen Artenvielfalt und dem mächtigen Kongo-Fluss. Entdecken Sie lebendige Musik und widerstandsfähige Kulturen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COG', 'it', 'Congo', 'Congo', 'Esplora il vasto bacino del Congo, con le sue fitte foreste pluviali, la sua incredibile biodiversità e il possente fiume Congo. Scopri la musica vibrante e le culture resilienti.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COD', 'en', 'Democratic Republic of the Congo', 'Democratic Republic of the Congo', 'Explore a land of immense scale and natural wealth, from the Congo River to the Virunga Mountains, home to endangered mountain gorillas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COD', 'ar', 'جمهورية الكونغو الديمقراطية', 'جمهورية الكونغو الديمقراطية', 'استكشف أرضًا ذات حجم هائل وثروة طبيعية، من نهر الكونغو إلى جبال فيرونجا، موطن الغوريلا الجبلية المهددة بالانقراض.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COD', 'es', 'República Democrática del Congo', 'República Democrática del Congo', 'Explora una tierra de inmensa escala y riqueza natural, desde el río Congo hasta las montañas Virunga, hogar de los gorilas de montaña en peligro de extinción.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COD', 'fr', 'République démocratique du Congo', 'République démocratique du Congo', 'Explorez une terre d''une ampleur et d''une richesse naturelle immenses, du fleuve Congo aux montagnes des Virunga, qui abritent des gorilles de montagne en voie de disparition.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COD', 'pt', 'República Democrática do Congo', 'República Democrática do Congo', 'Explore uma terra de imensa escala e riqueza natural, do Rio Congo às Montanhas Virunga, lar dos gorilas da montanha em perigo de extinção.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COD', 'ru', 'Демократическая Республика Конго', 'Демократическая Республика Конго', 'Исследуйте землю огромных масштабов и природных богатств, от реки Конго до гор Вирунга, где обитают находящиеся под угрозой исчезновения горные гориллы.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COD', 'de', 'Demokratische Republik Kongo', 'Demokratische Republik Kongo', 'Erkunden Sie ein Land von immenser Größe und natürlichem Reichtum, vom Kongo-Fluss bis zu den Virunga-Bergen, der Heimat der vom Aussterben bedrohten Berggorillas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COD', 'it', 'Repubblica Democratica del Congo', 'Repubblica Democratica del Congo', 'Esplora una terra di immense dimensioni e ricchezza naturale, dal fiume Congo ai monti Virunga, patria dei gorilla di montagna in via di estinzione.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CIV', 'en', 'Ivory Coast', 'Ivory Coast', 'Discover a land of contrasts, from bustling Abidjan to pristine rainforests and cocoa plantations. Experience a rich tapestry of cultures and traditions.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CIV', 'ar', 'ساحل العاج', 'ساحل العاج', 'اكتشف أرض التناقضات، من أبيدجان الصاخبة إلى الغابات المطيرة البكر ومزارع الكاكاو. استمتع بنسيج غني من الثقافات والتقاليد.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CIV', 'es', 'Costa de Marfil', 'Costa de Marfil', 'Descubre una tierra de contrastes, desde la bulliciosa Abiyán hasta las selvas vírgenes y las plantaciones de cacao. Experimenta un rico tapiz de culturas y tradiciones.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CIV', 'fr', 'Côte d''Ivoire', 'Côte d''Ivoire', 'Découvrez une terre de contrastes, de l''effervescence d''Abidjan aux forêts tropicales vierges et aux plantations de cacao. Découvrez une riche tapisserie de cultures et de traditions.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CIV', 'pt', 'Costa do Marfim', 'Costa do Marfim', 'Descubra uma terra de contrastes, da movimentada Abidjan às florestas tropicais intocadas e plantações de cacau. Experimente uma rica tapeçaria de culturas e tradições.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CIV', 'ru', 'Кот-д''Ивуар', 'Кот-д''Ивуар', 'Откройте для себя страну контрастов, от шумного Абиджана до нетронутых тропических лесов и плантаций какао. Познакомьтесь с богатым разнообразием культур и традиций.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CIV', 'de', 'Elfenbeinküste', 'Elfenbeinküste', 'Entdecken Sie ein Land der Kontraste, vom geschäftigen Abidjan bis zu unberührten Regenwäldern und Kakaoplantagen. Erleben Sie ein reiches Geflecht von Kulturen und Traditionen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CIV', 'it', 'Costa d''Avorio', 'Costa d''Avorio', 'Scopri una terra di contrasti, dalla vivace Abidjan alle foreste pluviali incontaminate e alle piantagioni di cacao. Vivi un ricco arazzo di culture e tradizioni.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DJI', 'en', 'Djibouti', 'Djibouti', 'Discover a unique landscape of volcanic formations, salt lakes, and stunning coastlines on the Horn of Africa. A crossroads of cultures and trade.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DJI', 'ar', 'جيبوتي', 'جيبوتي', 'اكتشف منظرًا طبيعيًا فريدًا من التكوينات البركانية والبحيرات المالحة والسواحل الخلابة على القرن الأفريقي. ملتقى للثقافات والتجارة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DJI', 'es', 'Yibuti', 'Yibuti', 'Descubre un paisaje único de formaciones volcánicas, lagos salados y costas impresionantes en el Cuerno de África. Un cruce de culturas y comercio.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DJI', 'fr', 'Djibouti', 'Djibouti', 'Découvrez un paysage unique de formations volcaniques, de lacs salés et de côtes magnifiques sur la Corne de l''Afrique. Un carrefour de cultures et de commerce.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DJI', 'pt', 'Djibuti', 'Djibuti', 'Descubra uma paisagem única de formações vulcânicas, lagos salgados e costas deslumbrantes no Corno de África. Uma encruzilhada de culturas e comércio.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DJI', 'ru', 'Джибути', 'Джибути', 'Откройте для себя уникальный ландшафт вулканических образований, соленых озер и потрясающих береговых линий на Африканском Роге. Перекресток культур и торговли.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DJI', 'de', 'Dschibuti', 'Dschibuti', 'Entdecken Sie eine einzigartige Landschaft aus vulkanischen Formationen, Salzseen und atemberaubenden Küsten am Horn von Afrika. Ein Knotenpunkt der Kulturen und des Handels.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DJI', 'it', 'Gibuti', 'Gibuti', 'Scopri un paesaggio unico di formazioni vulcaniche, laghi salati and coste meravigliose sul Corno d''Africa. Un crocevia di culture e commercio.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'en', 'Egypt', 'Egypt', 'Discover ancient wonders at the pyramids and temples. Experience the magic of the Nile, rich pharaonic history, and vibrant modern culture in Egypt.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'ar', 'Egypt', 'Egypt', 'اكتشف عجائب قديمة في الأهرامات والمعابد. استمتع بسحر نهر النيل والتاريخ الفرعوني الغني والثقافة الحديثة النابضة في مصر.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'es', 'Egypt', 'Egypt', 'Descubre maravillas antiguas en las pirámides y templos. Experimenta la magia del Nilo, rica historia faraónica y vibrante cultura moderna en Egipto.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'fr', 'Egypt', 'Egypt', 'Découvrez les merveilles antiques des pyramides et temples. Vivez la magie du Nil, riche histoire pharaonique et culture moderne vibrante en Égypte.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'pt', 'Egypt', 'Egypt', 'Descubra maravilhas antigas nas pirâmides e templos. Experimente a magia do Nilo, rica história faraônica e cultura moderna vibrante no Egito.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'ru', 'Egypt', 'Egypt', 'Откройте древние чудеса пирамид и храмов. Почувствуйте магию Нила, богатую фараонскую историю и яркую современную культуру в Египте.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'de', 'Egypt', 'Egypt', 'Entdecken Sie antike Wunder an Pyramiden und Tempeln. Erleben Sie die Magie des Nils, reiche Pharaonengeschichte und lebendige moderne Kultur in Ägypten.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EGY', 'it', 'Egypt', 'Egypt', 'Scopri meraviglie antiche nelle piramidi e templi. Vivi la magia del Nilo, ricca storia faraonica e vibrante cultura moderna in Egitto.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNQ', 'en', 'Equatorial Guinea', 'Equatorial Guinea', 'Discover a nation of tropical rainforests, volcanic islands, and Spanish colonial architecture. Explore the biodiversity of Monte Alen National Park.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNQ', 'ar', 'غينيا الاستوائية', 'غينيا الاستوائية', 'اكتشف أمة من الغابات الاستوائية المطيرة والجزر البركانية والهندسة المعمارية الإسبانية الاستعمارية. استكشف التنوع البيولوجي لمنتزه مونتي ألين الوطني.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNQ', 'es', 'Guinea Ecuatorial', 'Guinea Ecuatorial', 'Descubre una nación de selvas tropicales, islas volcánicas y arquitectura colonial española. Explora la biodiversidad del Parque Nacional de Monte Alén.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNQ', 'fr', 'Guinée équatoriale', 'Guinée équatoriale', 'Découvrez une nation de forêts tropicales, d''îles volcaniques et d''architecture coloniale espagnole. Explorez la biodiversité du parc national de Monte Alen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNQ', 'pt', 'Guiné Equatorial', 'Guiné Equatorial', 'Descubra uma nação de florestas tropicais, ilhas vulcânicas e arquitetura colonial espanhola. Explore a biodiversidade do Parque Nacional Monte Alen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNQ', 'ru', 'Экваториальная Гвинея', 'Экваториальная Гвинея', 'Откройте для себя страну тропических лесов, вулканических островов и испанской колониальной архитектуры. Исследуйте биоразнообразие национального парка Монте-Ален.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNQ', 'de', 'Äquatorialguinea', 'Äquatorialguinea', 'Entdecken Sie eine Nation aus tropischen Regenwäldern, Vulkaninseln und spanischer Kolonialarchitektur. Erkunden Sie die Artenvielfalt des Monte-Alen-Nationalparks.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNQ', 'it', 'Guinea Equatoriale', 'Guinea Equatoriale', 'Scopri una nazione di foreste pluviali tropicali, isole vulcaniche e architettura coloniale spagnola. Esplora la biodiversità del Parco Nazionale di Monte Alen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ERI', 'en', 'Eritrea', 'Eritrea', 'Discover a land of ancient history and diverse cultures on the Red Sea. Explore modernist architecture in Asmara and the Dahlak Archipelago.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ERI', 'ar', 'إريتريا', 'إريتريا', 'اكتشف أرضًا ذات تاريخ قديم وثقافات متنوعة على البحر الأحمر. استكشف العمارة الحداثية في أسمرة وأرخبيل دهلك.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ERI', 'es', 'Eritrea', 'Eritrea', 'Descubre una tierra de historia antigua y culturas diversas en el Mar Rojo. Explora la arquitectura modernista en Asmara and el archipiélago de Dahlak.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ERI', 'fr', 'Érythrée', 'Érythrée', 'Découvrez une terre d''histoire ancienne et de cultures diverses sur la mer Rouge. Explorez l''architecture moderniste d''Asmara et l''archipel des Dahlak.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ERI', 'pt', 'Eritreia', 'Eritreia', 'Descubra uma terra de história antiga e culturas diversas no Mar Vermelho. Explore a arquitetura modernista em Asmara e o Arquipélago de Dahlak.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ERI', 'ru', 'Эритрея', 'Эритрея', 'Откройте для себя землю древней истории и разнообразных культур на Красном море. Исследуйте модернистскую архитектуру в Асмэре и архипелаг Дахлак.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ERI', 'de', 'Eritrea', 'Eritrea', 'Entdecken Sie ein Land mit alter Geschichte und vielfältigen Kulturen am Roten Meer. Erkunden Sie die modernistische Architektur in Asmara und den Dahlak-Archipel.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ERI', 'it', 'Eritrea', 'Eritrea', 'Scopri una terra di storia antica e culture diverse sul Mar Rosso. Esplora l''architettura modernista di Asmara e l''arcipelago di Dahlak.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWZ', 'en', 'Eswatini', 'Eswatini', 'Discover the Kingdom of Eswatini, a small yet vibrant nation of rich culture, stunning mountain scenery, and abundant wildlife reserves.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWZ', 'ar', 'إسواتيني', 'إسواتيني', 'اكتشف مملكة إسواتيني، وهي دولة صغيرة ولكنها نابضة بالحياة ذات ثقافة غنية ومناظر جبلية خلابة ومحميات حياة برية وفيرة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWZ', 'es', 'Esuatini', 'Esuatini', 'Descubre el Reino de Esuatini, una nación pequeña pero vibrante de rica cultura, impresionantes paisajes de montaña y abundantes reservas de vida silvestre.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWZ', 'fr', 'Eswatini', 'Eswatini', 'Découvrez le Royaume d''Eswatini, une petite nation dynamique dotée d''une culture riche, de paysages de montagne magnifiques et de réserves fauniques abondantes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWZ', 'pt', 'Essuatíni', 'Essuatíni', 'Descubra o Reino de Essuatíni, uma nação pequena mas vibrante, de cultura rica, paisagens montanhosas deslumbrantes e abundantes reservas de vida selvagem.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWZ', 'ru', 'Эсватини', 'Эсватини', 'Откройте для себя Королевство Эсватини, небольшую, но яркую страну с богатой культурой, потрясающими горными пейзажами и обильными заповедниками дикой природы.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWZ', 'de', 'Eswatini', 'Eswatini', 'Entdecken Sie das Königreich Eswatini, eine kleine, aber lebendige Nation mit reicher Kultur, atemberaubender Berglandschaft und üppigen Wildreservaten.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWZ', 'it', 'Eswatini', 'Eswatini', 'Scopri il Regno di Eswatini, una nazione piccola ma vibrante, ricca di cultura, paesaggi montani mozzafiato e abbondanti riserve di fauna selvatica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'en', 'Ethiopia', 'Ethiopia', 'Discover Ethiopia''s ancient wonders from Lucy''s fossils to Lalibela''s rock-hewn churches. Experience the birthplace of coffee, diverse cultures, and dramatic landscapes in the Horn of Africa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'ar', 'Ethiopia', 'Ethiopia', 'اكتشف عجائب إثيوبيا القديمة من حفريات لوسي إلى كنائس لاليبيلا المنحوتة في الصخر. استمتع بموطن القهوة والثقافات المتنوعة والمناظر الدرامية في قرن أفريقيا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'es', 'Ethiopia', 'Ethiopia', 'Descubre las maravillas ancestrales de Etiopía desde fósiles de Lucy hasta iglesias talladas en roca de Lalibela. Experimenta la cuna del café, culturas diversas y paisajes dramáticos en el Cuerno de África.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'fr', 'Ethiopia', 'Ethiopia', 'Découvrez les merveilles antiques d''Ethiopie des fossiles de Lucy aux églises taillées dans la roche de Lalibela. Vivez le berceau du café, cultures diverses et paysages dramatiques dans la Corne de l''Afrique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'pt', 'Ethiopia', 'Ethiopia', 'Descubra as maravilhas antigas da Etiópia desde fósseis de Lucy até igrejas escavadas na rocha de Lalibela. Experimente o berço do café, culturas diversas e paisagens dramáticas no Chifre da África.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'ru', 'Ethiopia', 'Ethiopia', 'Откройте древние чудеса Эфиопии от окаменелостей Люси до высеченных в скале церквей Лалибэлы. Ощутите родину кофе, разнообразные культуры и драматические ландшафты Рога Африки.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'de', 'Ethiopia', 'Ethiopia', 'Entdecken Sie Äthiopiens antike Wunder von Lucys Fossilien bis zu Lalibelas in Fels gehauenen Kirchen. Erleben Sie die Wiege des Kaffees, vielfältige Kulturen und dramatische Landschaften im Horn von Afrika.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ETH', 'it', 'Ethiopia', 'Ethiopia', 'Scopri le meraviglie antiche dell''Etiopia dai fossili di Lucy alle chiese scavate nella roccia di Lalibela. Vivi la culla del caffè, culture diverse e paesaggi drammatici nel Corno d''Africa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GAB', 'en', 'Gabon', 'Gabon', 'Discover a paradise of pristine rainforests, abundant wildlife including lowland gorillas and forest elephants, and a coastline of unspoiled beaches.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GAB', 'ar', 'الغابون', 'الغابون', 'اكتشف جنة من الغابات المطيرة البكر، والحياة البرية الوفيرة بما في ذلك الغوريلا السهلية وفيلة الغابات، وساحل من الشواطئ البكر.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GAB', 'es', 'Gabón', 'Gabón', 'Descubre un paraíso de selvas vírgenes, abundante vida silvestre que incluye gorilas de llanura y elefantes de bosque, y una costa de playas vírgenes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GAB', 'fr', 'Gabon', 'Gabon', 'Découvrez un paradis de forêts tropicales vierges, une faune abondante comprenant des gorilles des plaines et des éléphants de forêt, et un littoral de plages préservées.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GAB', 'pt', 'Gabão', 'Gabão', 'Descubra um paraíso de florestas tropicais intocadas, vida selvagem abundante, incluindo gorilas-das-planícies e elefantes-da-floresta, e uma costa de praias virgens.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GAB', 'ru', 'Габон', 'Габон', 'Откройте для себя рай нетронутых тропических лесов, обильной дикой природы, включая равнинных горилл и лесных слонов, и побережье с нетронутыми пляжами.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GAB', 'de', 'Gabun', 'Gabun', 'Entdecken Sie ein Paradies aus unberührten Regenwäldern, einer reichen Tierwelt mit Flachlandgorillas und Waldelefanten und einer Küste mit unberührten Stränden.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GAB', 'it', 'Gabon', 'Gabon', 'Scopri un paradiso di foreste pluviali incontaminate, un''abbondante fauna selvatica tra cui gorilla di pianura ed elefanti della foresta, e una costa di spiagge incontaminate.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GMB', 'en', 'Gambia', 'Gambia', 'Discover ''The Smiling Coast of Africa'' with its beautiful beaches, diverse birdlife, and the historic Kunta Kinteh Island.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GMB', 'ar', 'غامبيا', 'غامبيا', 'اكتشف ''ساحل أفريقيا المبتسم'' بشواطئه الجميلة، وحياة الطيور المتنوعة، وجزيرة كونتا كينتي التاريخية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GMB', 'es', 'Gambia', 'Gambia', 'Descubre ''La Costa Sonriente de África'' con sus hermosas playas, su diversa avifauna y la histórica isla de Kunta Kinteh.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GMB', 'fr', 'Gambie', 'Gambie', 'Découvrez ''La Côte Souriante de l''Afrique'' avec ses belles plages, sa faune aviaire diversifiée et l''île historique de Kunta Kinteh.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GMB', 'pt', 'Gâmbia', 'Gâmbia', 'Descubra ''A Costa Sorridente da África'' com as suas belas praias, a sua diversificada avifauna e a histórica Ilha Kunta Kinteh.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GMB', 'ru', 'Гамбия', 'Гамбия', 'Откройте для себя ''Улыбающееся побережье Африки'' с его прекрасными пляжами, разнообразной орнитофауной и историческим островом Кунта-Кинте.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GMB', 'de', 'Gambia', 'Gambia', 'Entdecken Sie ''Die lächelnde Küste Afrikas'' mit ihren wunderschönen Stränden, der vielfältigen Vogelwelt und der historischen Insel Kunta Kinteh.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GMB', 'it', 'Gambia', 'Gambia', 'Scopri ''La costa sorridente dell''Africa'' con le sue bellissime spiagge, la sua variegata avifauna e la storica isola di Kunta Kinteh.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GHA', 'en', 'Ghana', 'Ghana', 'Experience the warmth of Ghanaian hospitality and rich cultural heritage. Discover historic forts, vibrant markets, beautiful beaches, and the gateway to West Africa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GHA', 'ar', 'Ghana', 'Ghana', 'استمتع بدفء الضيافة الغانية والتراث الثقافي الغني. اكتشف الحصون التاريخية والأسواق النابضة بالحياة والشواطئ الجميلة وبوابة غرب أفريقيا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GHA', 'es', 'Ghana', 'Ghana', 'Experimenta la calidez de la hospitalidad ghanesa y el rico patrimonio cultural. Descubre fuertes históricos, mercados vibrantes, hermosas playas y la puerta de entrada a África Occidental.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GHA', 'fr', 'Ghana', 'Ghana', 'Découvrez la chaleur de l''hospitalité ghanéenne et riche patrimoine culturel. Explorez forts historiques, marchés vibrants, belles plages et la porte d''entrée de l''Afrique de l''Ouest.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GHA', 'pt', 'Ghana', 'Ghana', 'Experimente o calor da hospitalidade ganense e rico patrimônio cultural. Descubra fortes históricos, mercados vibrantes, belas praias e o portal da África Ocidental.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GHA', 'ru', 'Ghana', 'Ghana', 'Ощутите теплоту ганского гостеприимства и богатое культурное наследие. Откройте исторические крепости, яркие рынки, красивые пляжи и ворота в Западную Африку.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GHA', 'de', 'Ghana', 'Ghana', 'Erleben Sie die Wärme ghanaischer Gastfreundschaft und reiches kulturelles Erbe. Entdecken Sie historische Forts, lebendige Märkte, schöne Strände und das Tor zu Westafrika.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GHA', 'it', 'Ghana', 'Ghana', 'Vivi il calore dell''ospitalità ghanese e il ricco patrimonio culturale. Scopri forti storici, mercati vibranti, belle spiagge e la porta dell''Africa Occidentale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GIN', 'en', 'Guinea', 'Guinea', 'Discover the source of the Niger River, the stunning Fouta Djallon highlands, and a rich tradition of West African music and dance.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GIN', 'ar', 'غينيا', 'غينيا', 'اكتشف مصدر نهر النيجر، ومرتفعات فوتا جالون الخلابة، وتقاليد غنية من موسيقى ورقص غرب أفريقيا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GIN', 'es', 'Guinea', 'Guinea', 'Descubre el nacimiento del río Níger, las impresionantes tierras altas de Fouta Djallon y una rica tradición de música y danza de África Occidental.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GIN', 'fr', 'Guinée', 'Guinée', 'Découvrez la source du fleuve Niger, les magnifiques hauts plateaux du Fouta Djallon et une riche tradition de musique et de danse ouest-africaines.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GIN', 'pt', 'Guiné', 'Guiné', 'Descubra a nascente do rio Níger, as deslumbrantes terras altas de Fouta Djallon e uma rica tradição de música e dança da África Ocidental.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GIN', 'ru', 'Гвинея', 'Гвинея', 'Откройте для себя исток реки Нигер, потрясающие высокогорья Фута-Джаллон и богатые традиции западноафриканской музыки и танцев.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GIN', 'de', 'Guinea', 'Guinea', 'Entdecken Sie die Quelle des Niger, das atemberaubende Fouta-Djallon-Hochland und eine reiche Tradition westafrikanischer Musik und Tänze.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GIN', 'it', 'Guinea', 'Guinea', 'Scopri la sorgente del fiume Niger, gli splendidi altopiani del Fouta Djallon e una ricca tradizione di musica e danze dell''Africa occidentale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNB', 'en', 'Guinea-Bissau', 'Guinea-Bissau', 'Explore a nation of tropical islands, coastal savannas, and a rich blend of African and Portuguese cultures. Discover the Bijagós Archipelago, a UNESCO Biosphere Reserve.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNB', 'ar', 'غينيا بيساو', 'غينيا بيساو', 'استكشف أمة من الجزر الاستوائية، والسافانا الساحلية، ومزيج غني من الثقافات الأفريقية والبرتغالية. اكتشف أرخبيل بيجاغوس، وهو محمية محيط حيوي تابعة لليونسكو.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNB', 'es', 'Guinea-Bisáu', 'Guinea-Bisáu', 'Explora una nación de islas tropicales, sabanas costeras y una rica mezcla de culturas africanas y portuguesas. Descubre el archipiélago de Bijagós, una reserva de la biosfera de la UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNB', 'fr', 'Guinée-Bissau', 'Guinée-Bissau', 'Explorez une nation d''îles tropicales, de savanes côtières et d''un riche mélange de cultures africaines et portugaises. Découvrez l''archipel des Bijagos, une réserve de biosphère de l''UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNB', 'pt', 'Guiné-Bissau', 'Guiné-Bissau', 'Explore uma nação de ilhas tropicais, savanas costeiras e uma rica mistura de culturas africanas e portuguesas. Descubra o Arquipélago dos Bijagós, uma Reserva da Biosfera da UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNB', 'ru', 'Гвинея-Бисау', 'Гвинея-Бисау', 'Исследуйте страну тропических островов, прибрежных саванн и богатого сочетания африканской и португальской культур. Откройте для себя архипелаг Бижагош, биосферный заповедник ЮНЕСКО.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNB', 'de', 'Guinea-Bissau', 'Guinea-Bissau', 'Entdecken Sie eine Nation aus tropischen Inseln, Küstensavannen und einer reichen Mischung aus afrikanischen und portugiesischen Kulturen. Entdecken Sie den Bissagos-Archipel, ein UNESCO-Biosphärenreservat.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GNB', 'it', 'Guinea-Bissau', 'Guinea-Bissau', 'Esplora una nazione di isole tropicali, savane costiere e una ricca miscela di culture africane e portoghesi. Scopri l''arcipelago delle Bijagós, una riserva della biosfera dell''UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'en', 'Kenya', 'Kenya', 'Experience the ultimate safari destination with the Great Migration, diverse wildlife, and stunning landscapes. Discover Mount Kenya, beautiful coastlines, and vibrant culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'ar', 'Kenya', 'Kenya', 'استمتع بوجهة السفاري المثالية مع الهجرة العظيمة والحياة البرية المتنوعة والمناظر الطبيعية الخلابة. اكتشف جبل كينيا والسواحل الجميلة والثقافة النابضة بالحياة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'es', 'Kenya', 'Kenya', 'Experimenta el destino de safari definitivo con la Gran Migración, fauna diversa y paisajes impresionantes. Descubre el Monte Kenia, hermosas costas y cultura vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'fr', 'Kenya', 'Kenya', 'Découvrez la destination safari ultime avec la Grande Migration, une faune diversifiée et des paysages époustouflants. Explorez le Mont Kenya, belles côtes et culture vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'pt', 'Kenya', 'Kenya', 'Experimente o destino de safari definitivo com a Grande Migração, vida selvagem diversificada e paisagens deslumbrantes. Descubra o Monte Quênia, belas costas e cultura vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'ru', 'Kenya', 'Kenya', 'Ощутите лучшее направление для сафари с Великой Миграцией, разнообразной дикой природой и потрясающими пейзажами. Откройте гору Кения, красивые побережья и яркую культуру.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'de', 'Kenya', 'Kenya', 'Erleben Sie das ultimative Safari-Ziel mit der Großen Migration, vielfältiger Tierwelt und atemberaubenden Landschaften. Entdecken Sie Mount Kenia, schöne Küsten und lebendige Kultur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KEN', 'it', 'Kenya', 'Kenya', 'Vivi la destinazione safari definitiva con la Grande Migrazione, fauna diversificata e paesaggi mozzafiato. Scopri il Monte Kenya, belle coste e cultura vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LSO', 'en', 'Lesotho', 'Lesotho', 'Discover the ''Kingdom in the Sky'', a land of breathtaking mountain ranges, stunning waterfalls, and unique Basotho culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LSO', 'ar', 'ليسوتو', 'ليسوتو', 'اكتشف ''المملكة في السماء''، أرض السلاسل الجبلية الخلابة، والشلالات المذهلة، وثقافة الباسوتو الفريدة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LSO', 'es', 'Lesoto', 'Lesoto', 'Descubre el ''Reino en el Cielo'', una tierra de impresionantes cadenas montañosas, cascadas espectaculares y la cultura única de los basutos.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LSO', 'fr', 'Lesotho', 'Lesotho', 'Découvrez le ''Royaume dans le Ciel'', une terre de chaînes de montagnes à couper le souffle, de cascades magnifiques et d''une culture basotho unique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LSO', 'pt', 'Lesoto', 'Lesoto', 'Descubra o ''Reino no Céu'', uma terra de cordilheiras de tirar o fôlego, cachoeiras deslumbrantes e a cultura única do povo basoto.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LSO', 'ru', 'Лесото', 'Лесото', 'Откройте для себя ''Королевство в небесах'', страну захватывающих дух горных хребтов, потрясающих водопадов и уникальной культуры басуто.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LSO', 'de', 'Lesotho', 'Lesotho', 'Entdecken Sie das ''Königreich im Himmel'', ein Land mit atemberaubenden Bergketten, beeindruckenden Wasserfällen und der einzigartigen Kultur der Basotho.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LSO', 'it', 'Lesotho', 'Lesotho', 'Scopri il ''Regno nel Cielo'', una terra di catene montuose mozzafiato, cascate spettacolari e la cultura unica dei Basotho.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBR', 'en', 'Liberia', 'Liberia', 'Discover Africa''s oldest republic, with a rich history, vibrant culture, and stunning coastline. Explore Sapo National Park, one of West Africa''s most pristine rainforests.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBR', 'ar', 'ليبيريا', 'ليبيريا', 'اكتشف أقدم جمهورية في أفريقيا، ذات التاريخ الغني والثقافة النابضة بالحياة والساحل المذهل. استكشف حديقة سابو الوطنية، إحدى أكثر الغابات المطيرة بكرًا في غرب أفريقيا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBR', 'es', 'Liberia', 'Liberia', 'Descubre la república más antigua de África, con una rica historia, una cultura vibrante y una costa impresionante. Explora el Parque Nacional Sapo, una de las selvas tropicales más prístinas de África Occidental.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBR', 'fr', 'Libéria', 'Libéria', 'Découvrez la plus ancienne république d''Afrique, avec une histoire riche, une culture vibrante et un littoral magnifique. Explorez le parc national de Sapo, l''une des forêts tropicales les plus vierges d''Afrique de l''Ouest.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBR', 'pt', 'Libéria', 'Libéria', 'Descubra a república mais antiga da África, com uma história rica, uma cultura vibrante e uma costa deslumbrante. Explore o Parque Nacional Sapo, uma das florestas tropicais mais intocadas da África Ocidental.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBR', 'ru', 'Либерия', 'Либерия', 'Откройте для себя старейшую республику Африки с богатой историей, яркой культурой и потрясающим побережьем. Исследуйте национальный парк Сапо, один из самых нетронутых тропических лесов Западной Африки.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBR', 'de', 'Liberia', 'Liberia', 'Entdecken Sie die älteste Republik Afrikas mit einer reichen Geschichte, einer lebendigen Kultur und einer atemberaubenden Küste. Erkunden Sie den Sapo-Nationalpark, einen der unberührtesten Regenwälder Westafrikas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBR', 'it', 'Liberia', 'Liberia', 'Scopri la repubblica più antica dell''Africa, con una ricca storia, una cultura vibrante e una costa mozzafiato. Esplora il Parco Nazionale di Sapo, una delle foreste pluviali più incontaminate dell''Africa occidentale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBY', 'en', 'Libya', 'Libya', 'Discover a land of ancient wonders, from the Roman ruins of Leptis Magna to the vast Sahara desert and its hidden oases.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBY', 'ar', 'ليبيا', 'ليبيا', 'اكتشف أرض العجائب القديمة، من أطلال لبدة الكبرى الرومانية إلى الصحراء الكبرى الشاسعة وواحاتها الخفية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBY', 'es', 'Libia', 'Libia', 'Descubre una tierra de maravillas antiguas, desde las ruinas romanas de Leptis Magna hasta el vasto desierto del Sahara y sus oasis escondidos.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBY', 'fr', 'Libye', 'Libye', 'Découvrez une terre de merveilles antiques, des ruines romaines de Leptis Magna au vaste désert du Sahara et ses oasis cachées.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBY', 'pt', 'Líbia', 'Líbia', 'Descubra uma terra de maravilhas antigas, das ruínas romanas de Leptis Magna ao vasto deserto do Saara e seus oásis escondidos.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBY', 'ru', 'Ливия', 'Ливия', 'Откройте для себя землю древних чудес, от римских руин Лептис-Магны до огромной пустыни Сахара и ее скрытых оазисов.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBY', 'de', 'Libyen', 'Libyen', 'Entdecken Sie ein Land antiker Wunder, von den römischen Ruinen von Leptis Magna bis zur riesigen Sahara und ihren verborgenen Oasen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBY', 'it', 'Libia', 'Libia', 'Scopri una terra di antiche meraviglie, dalle rovine romane di Leptis Magna al vasto deserto del Sahara e alle sue oasi nascoste.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDG', 'en', 'Madagascar', 'Madagascar', 'Explore a world of unique biodiversity, from lemurs and baobabs to stunning rainforests and pristine beaches. Discover the eighth continent.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDG', 'ar', 'مدغشقر', 'مدغشقر', 'استكشف عالمًا من التنوع البيولوجي الفريد، من الليمور وأشجار الباوباب إلى الغابات المطيرة الخلابة والشواطئ البكر. اكتشف القارة الثامنة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDG', 'es', 'Madagascar', 'Madagascar', 'Explora un mundo de biodiversidad única, desde lémures y baobabs hasta impresionantes selvas tropicales y playas vírgenes. Descubre el octavo continente.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDG', 'fr', 'Madagascar', 'Madagascar', 'Explorez un monde de biodiversité unique, des lémuriens et des baobabs aux forêts tropicales magnifiques et aux plages immaculées. Découvrez le huitième continent.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDG', 'pt', 'Madagáscar', 'Madagáscar', 'Explore um mundo de biodiversidade única, de lémures e embondeiros a deslumbrantes florestas tropicais e praias imaculadas. Descubra o oitavo continente.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDG', 'ru', 'Мадагаскар', 'Мадагаскар', 'Исследуйте мир уникального биоразнообразия, от лемуров и баобабов до потрясающих тропических лесов и нетронутых пляжей. Откройте для себя восьмой континент.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDG', 'de', 'Madagaskar', 'Madagaskar', 'Entdecken Sie eine Welt einzigartiger Artenvielfalt, von Lemuren und Affenbrotbäumen bis hin zu atemberaubenden Regenwäldern und unberührten Stränden. Entdecken Sie den achten Kontinent.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDG', 'it', 'Madagascar', 'Madagascar', 'Esplora un mondo di biodiversità unica, dai lemuri e i baobab alle splendide foreste pluviali e alle spiagge incontaminate. Scopri l''ottavo continente.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MWI', 'en', 'Malawi', 'Malawi', 'Discover the ''Warm Heart of Africa'' with its stunning Lake Malawi, friendly people, and diverse landscapes, from highlands to national parks.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MWI', 'ar', 'ملاوي', 'ملاوي', 'اكتشف ''قلب أفريقيا الدافئ'' ببحيرة ملاوي المذهلة، وشعبها الودود، ومناظرها الطبيعية المتنوعة، من المرتفعات إلى المتنزهات الوطنية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MWI', 'es', 'Malaui', 'Malaui', 'Descubre el ''Corazón Cálido de África'' con su impresionante lago Malaui, su gente amable y sus diversos paisajes, desde las tierras altas hasta los parques nacionales.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MWI', 'fr', 'Malawi', 'Malawi', 'Découvrez le ''Cœur Chaud de l''Afrique'' avec son magnifique lac Malawi, ses habitants sympathiques et ses paysages variés, des hauts plateaux aux parcs nationaux.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MWI', 'pt', 'Malawi', 'Malawi', 'Descubra o ''Coração Quente da África'' com o seu deslumbrante Lago Malawi, o seu povo amigável e as suas paisagens diversificadas, das terras altas aos parques nacionais.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MWI', 'ru', 'Малави', 'Малави', 'Откройте для себя ''Теплое сердце Африки'' с его потрясающим озером Малави, дружелюбными людьми и разнообразными ландшафтами, от высокогорья до национальных парков.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MWI', 'de', 'Malawi', 'Malawi', 'Entdecken Sie das ''Warme Herz Afrikas'' mit dem atemberaubenden Malawisee, den freundlichen Menschen und den vielfältigen Landschaften, von den Hochebenen bis zu den Nationalparks.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MWI', 'it', 'Malawi', 'Malawi', 'Scopri il ''Cuore Caldo dell''Africa'' con il suo splendido Lago Malawi, la sua gente amichevole e i suoi diversi paesaggi, dagli altopiani ai parchi nazionali.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLI', 'en', 'Mali', 'Mali', 'Discover the legendary city of Timbuktu, the vibrant Dogon country, and the rich musical heritage of this ancient Sahelian empire.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLI', 'ar', 'مالي', 'مالي', 'اكتشف مدينة تمبكتو الأسطورية، وبلاد الدوجون النابضة بالحياة، والتراث الموسيقي الغني لهذه الإمبراطورية الساحلية القديمة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLI', 'es', 'Malí', 'Malí', 'Descubre la legendaria ciudad de Tombuctú, el vibrante país Dogón y el rico patrimonio musical de este antiguo imperio saheliano.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLI', 'fr', 'Mali', 'Mali', 'Découvrez la ville légendaire de Tombouctou, le pays Dogon dynamique et le riche patrimoine musical de cet ancien empire sahélien.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLI', 'pt', 'Mali', 'Mali', 'Descubra a lendária cidade de Timbuktu, o vibrante país Dogon e a rica herança musical deste antigo império saheliano.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLI', 'ru', 'Мали', 'Мали', 'Откройте для себя легендарный город Томбукту, яркую страну догонов и богатое музыкальное наследие этой древней сахельской империи.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLI', 'de', 'Mali', 'Mali', 'Entdecken Sie die legendäre Stadt Timbuktu, das pulsierende Dogon-Land und das reiche musikalische Erbe dieses alten Sahel-Reiches.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLI', 'it', 'Mali', 'Mali', 'Scopri la leggendaria città di Timbuctù, il vibrante paese Dogon e il ricco patrimonio musicale di questo antico impero saheliano.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MRT', 'en', 'Mauritania', 'Mauritania', 'Discover a land of vast desert landscapes, ancient caravan cities, and a rich Moorish culture. Explore the Banc d''Arguin National Park, a UNESCO World Heritage site.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MRT', 'ar', 'موريتانيا', 'موريتانيا', 'اكتشف أرضًا من المناظر الطبيعية الصحراوية الشاسعة، ومدن القوافل القديمة، والثقافة المغاربية الغنية. استكشف حديقة بانك دارغين الوطنية، وهي أحد مواقع التراث العالمي لليونسكو.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MRT', 'es', 'Mauritania', 'Mauritania', 'Descubre una tierra de vastos paisajes desérticos, antiguas ciudades caravaneras y una rica cultura árabe. Explora el Parque Nacional de Banc d''Arguin, declarado Patrimonio de la Humanidad por la UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MRT', 'fr', 'Mauritanie', 'Mauritanie', 'Découvrez une terre de vastes paysages désertiques, d''anciennes cités caravanières et d''une riche culture maure. Explorez le parc national du Banc d''Arguin, un site du patrimoine mondial de l''UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MRT', 'pt', 'Mauritânia', 'Mauritânia', 'Descubra uma terra de vastas paisagens desérticas, antigas cidades de caravanas e uma rica cultura moura. Explore o Parque Nacional Banc d''Arguin, um Património Mundial da UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MRT', 'ru', 'Мавритания', 'Мавритания', 'Откройте для себя страну бескрайних пустынных пейзажей, древних караванных городов и богатой мавританской культуры. Исследуйте национальный парк Банк-д''Арген, объект Всемирного наследия ЮНЕСКО.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MRT', 'de', 'Mauretanien', 'Mauretanien', 'Entdecken Sie ein Land mit riesigen Wüstenlandschaften, alten Karawanenstädten und einer reichen maurischen Kultur. Erkunden Sie den Nationalpark Banc d''Arguin, ein UNESCO-Weltkulturerbe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MRT', 'it', 'Mauritania', 'Mauritania', 'Scopri una terra di vasti paesaggi desertici, antiche città carovaniere e una ricca cultura moresca. Esplora il Parco Nazionale del Banc d''Arguin, un sito del patrimonio mondiale dell''UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MUS', 'en', 'Mauritius', 'Mauritius', 'Discover an island paradise of turquoise waters, white sandy beaches, and lush volcanic landscapes. A melting pot of cultures and cuisines.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MUS', 'ar', 'موريشيوس', 'موريشيوس', 'اكتشف جنة جزيرة من المياه الفيروزية، والشواطئ الرملية البيضاء، والمناظر الطبيعية البركانية الخصبة. بوتقة تنصهر فيها الثقافات والمأكولات.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MUS', 'es', 'Mauricio', 'Mauricio', 'Descubre una isla paradisíaca de aguas turquesas, playas de arena blanca y exuberantes paisajes volcánicos. Un crisol de culturas y cocinas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MUS', 'fr', 'Maurice', 'Maurice', 'Découvrez une île paradisiaque aux eaux turquoise, aux plages de sable blanc et aux paysages volcaniques luxuriants. Un creuset de cultures et de cuisines.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MUS', 'pt', 'Maurícia', 'Maurícia', 'Descubra uma ilha paradisíaca de águas azul-turquesa, praias de areia branca e paisagens vulcânicas exuberantes. Um caldeirão de culturas e culinárias.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MUS', 'ru', 'Маврикий', 'Маврикий', 'Откройте для себя райский остров с бирюзовыми водами, белыми песчаными пляжами и пышными вулканическими пейзажами. Плавильный котел культур и кухонь.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MUS', 'de', 'Mauritius', 'Mauritius', 'Entdecken Sie ein Inselparadies mit türkisfarbenem Wasser, weißen Sandstränden und üppigen Vulkanlandschaften. Ein Schmelztiegel der Kulturen und Küchen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MUS', 'it', 'Mauritius', 'Mauritius', 'Scopri un''isola paradisiaca con acque turchesi, spiagge di sabbia bianca e lussureggianti paesaggi vulcanici. Un crogiolo di culture e cucine.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'en', 'Morocco', 'Morocco', 'Experience the enchanting blend of Berber, Arab, and French cultures. From bustling souks to Atlas Mountains and Sahara dunes, Morocco captivates every traveler.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'ar', 'Morocco', 'Morocco', 'استمتع بالمزيج الساحر من الثقافات البربرية والعربية والفرنسية. من الأسواق النابضة إلى جبال الأطلس وكثبان الصحراء، يأسر المغرب كل مسافر.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'es', 'Morocco', 'Morocco', 'Experimenta la fascinante mezcla de culturas bereber, árabe y francesa. Desde zocos bulliciosos hasta las montañas del Atlas y dunas del Sahara, Marruecos cautiva a cada viajero.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'fr', 'Morocco', 'Morocco', 'Découvrez le mélange enchanteur des cultures berbère, arabe et française. Des souks animés aux montagnes de l''Atlas et dunes du Sahara, le Maroc captive chaque voyageur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'pt', 'Morocco', 'Morocco', 'Experimente a fascinante mistura de culturas berber, árabe e francesa. De souks movimentados às montanhas do Atlas e dunas do Saara, Marrocos cativa cada viajante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'ru', 'Morocco', 'Morocco', 'Окунитесь в очаровательное смешение берберских, арабских и французских культур. От шумных базаров до гор Атлас и дюн Сахары, Марокко пленяет каждого путешественника.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'de', 'Morocco', 'Morocco', 'Erleben Sie die bezaubernde Mischung aus Berber-, arabischen und französischen Kulturen. Von lebendigen Souks bis zu Atlas-Bergen und Sahara-Dünen, Marokko fesselt jeden Reisenden.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MAR', 'it', 'Morocco', 'Morocco', 'Vivi l''incantevole fusione di culture berbere, arabe e francesi. Dai souk vivaci alle montagne dell''Atlante e dune del Sahara, il Marocco affascina ogni viaggiatore.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MOZ', 'en', 'Mozambique', 'Mozambique', 'Discover a coastline of idyllic beaches, vibrant coral reefs, and the historic Island of Mozambique, a UNESCO World Heritage site.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MOZ', 'ar', 'موزمبيق', 'موزمبيق', 'اكتشف ساحلًا من الشواطئ المثالية، والشعاب المرجانية النابضة بالحياة، وجزيرة موزمبيق التاريخية، وهي أحد مواقع التراث العالمي لليونسكو.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MOZ', 'es', 'Mozambique', 'Mozambique', 'Descubre una costa de playas idílicas, vibrantes arrecifes de coral y la histórica Isla de Mozambique, declarada Patrimonio de la Humanidad por la UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MOZ', 'fr', 'Mozambique', 'Mozambique', 'Découvrez un littoral de plages idylliques, de récifs coralliens dynamiques et l''île historique du Mozambique, un site du patrimoine mondial de l''UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MOZ', 'pt', 'Moçambique', 'Moçambique', 'Descubra uma costa de praias idílicas, recifes de coral vibrantes e a histórica Ilha de Moçambique, um Património Mundial da UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MOZ', 'ru', 'Мозамбик', 'Мозамбик', 'Откройте для себя побережье с идиллическими пляжами, яркими коралловыми рифами и историческим островом Мозамбик, объектом Всемирного наследия ЮНЕСКО.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MOZ', 'de', 'Mosambik', 'Mosambik', 'Entdecken Sie eine Küste mit idyllischen Stränden, lebhaften Korallenriffen und der historischen Insel Mosambik, einem UNESCO-Weltkulturerbe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MOZ', 'it', 'Mozambico', 'Mozambico', 'Scopri una costa di spiagge idilliache, vivaci barriere coralline e la storica Isola del Mozambico, un sito del patrimonio mondiale dell''UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'en', 'Namibia', 'Namibia', 'Discover Namibia''s otherworldly landscapes from towering red dunes of Sossusvlei to dramatic Fish River Canyon. Experience Skeleton Coast mysteries, desert-adapted wildlife, and stargazing perfection.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'ar', 'Namibia', 'Namibia', 'اكتشف مناظر ناميبيا الغريبة من كثبان سوسفلي الحمراء الشاهقة إلى أخدود نهر السمك الدرامي. استمتع بأسرار ساحل الهياكل والحياة البرية المتكيفة مع الصحراء ومراقبة النجوم المثالية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'es', 'Namibia', 'Namibia', 'Descubre paisajes de otro mundo de Namibia desde dunas rojas imponentes de Sossusvlei hasta dramático Cañón del Río Pez. Experimenta misterios de Costa Esqueleto, vida silvestre adaptada al desierto y perfección para observar estrellas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'fr', 'Namibia', 'Namibia', 'Découvrez paysages d''un autre monde de Namibie des dunes rouges imposantes de Sossusvlei au dramatique Canyon de la Rivière aux Poissons. Vivez mystères de Côte des Squelettes, faune adaptée au désert et perfection d''observation des étoiles.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'pt', 'Namibia', 'Namibia', 'Descubra paisagens de outro mundo da Namíbia desde dunas vermelhas imponentes de Sossusvlei até dramático Canyon do Rio dos Peixes. Experimente mistérios da Costa do Esqueleto, vida selvagem adaptada ao deserto e perfeição para observar estrelas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'ru', 'Namibia', 'Namibia', 'Откройте потусторонние ландшафты Намибии от высоких красных дюн Соссусвлей до драматичного каньона реки Рыбной. Ощутите тайны Берега Скелетов, дикую природу, приспособленную к пустыне, и идеальное наблюдение за звёздами.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'de', 'Namibia', 'Namibia', 'Entdecken Sie Namibias andere Weltlandschaften von hoch aufragenden roten Dünen Sossusvlei bis zum dramatischen Fish River Canyon. Erleben Sie Skelettküsten-Geheimnisse, wüstenangepasste Tierwelt und perfekte Sternenbeobachtung.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NAM', 'it', 'Namibia', 'Namibia', 'Scopri paesaggi di un altro mondo della Namibia dalle imponenti dune rosse di Sossusvlei al drammatico Canyon del Fiume dei Pesci. Vivi misteri della Costa degli Scheletri, fauna adattata al deserto e perfezione per osservare le stelle.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NER', 'en', 'Niger', 'Niger', 'Discover the gateway to the Sahara, with its vast desert landscapes, the historic city of Agadez, and the unique Wodaabe culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NER', 'ar', 'النيجر', 'النيجر', 'اكتشف بوابة الصحراء الكبرى، بمناظرها الطبيعية الصحراوية الشاسعة، ومدينة أغاديز التاريخية، وثقافة Wodaabe الفريدة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NER', 'es', 'Níger', 'Níger', 'Descubre la puerta de entrada al Sáhara, con sus vastos paisajes desérticos, la histórica ciudad de Agadez y la cultura única de los Wodaabe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NER', 'fr', 'Niger', 'Niger', 'Découvrez la porte du Sahara, avec ses vastes paysages désertiques, la ville historique d''Agadez et la culture unique des Wodaabe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NER', 'pt', 'Níger', 'Níger', 'Descubra a porta de entrada para o Saara, com as suas vastas paisagens desérticas, a cidade histórica de Agadez e a cultura única dos Wodaabe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NER', 'ru', 'Нигер', 'Нигер', 'Откройте для себя ворота в Сахару с ее обширными пустынными ландшафтами, историческим городом Агадес и уникальной культурой Водаабе.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NER', 'de', 'Niger', 'Niger', 'Entdecken Sie das Tor zur Sahara mit seinen riesigen Wüstenlandschaften, der historischen Stadt Agadez und der einzigartigen Kultur der Wodaabe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NER', 'it', 'Niger', 'Niger', 'Scopri la porta del Sahara, con i suoi vasti paesaggi desertici, la storica città di Agadez e la cultura unica dei Wodaabe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NGA', 'en', 'Nigeria', 'Nigeria', 'Discover the ''Giant of Africa'', a land of vibrant cities, diverse cultures, and a booming arts and music scene. Explore the ancient city of Benin and the stunning Yankari National Park.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NGA', 'ar', 'نيجيريا', 'نيجيريا', 'اكتشف ''عملاق أفريقيا''، أرض المدن النابضة بالحياة، والثقافات المتنوعة، ومشهد الفنون والموسيقى المزدهر. استكشف مدينة بنين القديمة ومنتزه يانكاري الوطني المذهل.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NGA', 'es', 'Nigeria', 'Nigeria', 'Descubre el ''Gigante de África'', una tierra de ciudades vibrantes, culturas diversas y una floreciente escena artística y musical. Explora la antigua ciudad de Benín y el impresionante Parque Nacional de Yankari.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NGA', 'fr', 'Nigéria', 'Nigéria', 'Découvrez le ''Géant de l''Afrique'', une terre de villes dynamiques, de cultures diverses et d''une scène artistique et musicale en plein essor. Explorez l''ancienne ville de Bénin et le magnifique parc national de Yankari.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NGA', 'pt', 'Nigéria', 'Nigéria', 'Descubra o ''Gigante da África'', uma terra de cidades vibrantes, culturas diversas e uma cena artística e musical em expansão. Explore a antiga cidade de Benin e o deslumbrante Parque Nacional de Yankari.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NGA', 'ru', 'Нигерия', 'Нигерия', 'Откройте для себя ''Гиганта Африки'', страну ярких городов, разнообразных культур и процветающей сцены искусства и музыки. Исследуйте древний город Бенин и потрясающий национальный парк Янкари.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NGA', 'de', 'Nigeria', 'Nigeria', 'Entdecken Sie den ''Giganten Afrikas'', ein Land mit pulsierenden Städten, vielfältigen Kulturen und einer boomenden Kunst- und Musikszene. Erkunden Sie die antike Stadt Benin und den atemberaubenden Yankari-Nationalpark.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NGA', 'it', 'Nigeria', 'Nigeria', 'Scopri il ''Gigante dell''Africa'', una terra di città vivaci, culture diverse e una fiorente scena artistica e musicale. Esplora l''antica città del Benin e lo splendido Parco Nazionale di Yankari.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RWA', 'en', 'Rwanda', 'Rwanda', 'Discover the ''Land of a Thousand Hills'', a nation of stunning beauty, vibrant culture, and remarkable resilience. Track mountain gorillas in their natural habitat.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RWA', 'ar', 'رواندا', 'رواندا', 'اكتشف ''أرض الألف تل''، أمة ذات جمال مذهل، وثقافة نابضة بالحياة، ومرونة ملحوظة. تتبع الغوريلا الجبلية في بيئتها الطبيعية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RWA', 'es', 'Ruanda', 'Ruanda', 'Descubre la ''Tierra de las Mil Colinas'', una nación de una belleza impresionante, una cultura vibrante y una notable resiliencia. Sigue a los gorilas de montaña en su hábitat natural.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RWA', 'fr', 'Rwanda', 'Rwanda', 'Découvrez le ''Pays des Mille Collines'', une nation d''une beauté époustouflante, d''une culture vibrante et d''une résilience remarquable. Suivez les gorilles de montagne dans leur habitat naturel.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RWA', 'pt', 'Ruanda', 'Ruanda', 'Descubra a ''Terra das Mil Colinas'', uma nação de uma beleza estonteante, uma cultura vibrante e uma resiliência notável. Siga os gorilas da montanha no seu habitat natural.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RWA', 'ru', 'Руанда', 'Руанда', 'Откройте для себя ''Страну тысячи холмов'', страну потрясающей красоты, яркой культуры и удивительной стойкости. Проследите за горными гориллами в их естественной среде обитания.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RWA', 'de', 'Ruanda', 'Ruanda', 'Entdecken Sie das ''Land der tausend Hügel'', eine Nation von atemberaubender Schönheit, lebendiger Kultur und bemerkenswerter Widerstandsfähigkeit. Verfolgen Sie Berggorillas in ihrem natürlichen Lebensraum.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RWA', 'it', 'Ruanda', 'Ruanda', 'Scopri la ''Terra delle Mille Colline'', una nazione di straordinaria bellezza, cultura vibrante e notevole resilienza. Segui i gorilla di montagna nel loro habitat naturale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('STP', 'en', 'São Tomé and Príncipe', 'São Tomé and Príncipe', 'Discover the ''Chocolate Islands'', a paradise of volcanic peaks, lush rainforests, and pristine beaches. Explore a unique blend of African and Portuguese cultures.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('STP', 'ar', 'ساو تومي وبرينسيب', 'ساو تومي وبرينسيب', 'اكتشف ''جزر الشوكولاتة''، جنة من القمم البركانية، والغابات المطيرة المورقة، والشواطئ البكر. استكشف مزيجًا فريدًا من الثقافات الأفريقية والبرتغالية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('STP', 'es', 'Santo Tomé y Príncipe', 'Santo Tomé y Príncipe', 'Descubre las ''Islas del Chocolate'', un paraíso de picos volcánicos, exuberantes selvas tropicales y playas vírgenes. Explora una mezcla única de culturas africanas y portuguesas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('STP', 'fr', 'São Tomé et Príncipe', 'São Tomé et Príncipe', 'Découvrez les ''îles du chocolat'', un paradis de pics volcaniques, de forêts tropicales luxuriantes et de plages immaculées. Explorez un mélange unique de cultures africaines et portugaises.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('STP', 'pt', 'São Tomé e Príncipe', 'São Tomé e Príncipe', 'Descubra as ''Ilhas de Chocolate'', um paraíso de picos vulcânicos, florestas tropicais exuberantes e praias imaculadas. Explore uma mistura única de culturas africanas e portuguesas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('STP', 'ru', 'Сан-Томе и Принсипи', 'Сан-Томе и Принсипи', 'Откройте для себя ''Шоколадные острова'', рай вулканических вершин, пышных тропических лесов и нетронутых пляжей. Исследуйте уникальное сочетание африканской и португальской культур.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('STP', 'de', 'São Tomé und Príncipe', 'São Tomé und Príncipe', 'Entdecken Sie die ''Schokoladeninseln'', ein Paradies aus Vulkangipfeln, üppigen Regenwäldern und unberührten Stränden. Erkunden Sie eine einzigartige Mischung aus afrikanischen und portugiesischen Kulturen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('STP', 'it', 'São Tomé e Príncipe', 'São Tomé e Príncipe', 'Scopri le ''Isole del Cioccolato'', un paradiso di cime vulcaniche, lussureggianti foreste pluviali e spiagge incontaminate. Esplora una miscela unica di culture africane e portoghesi.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SEN', 'en', 'Senegal', 'Senegal', 'Discover the vibrant culture of West Africa, from the bustling markets of Dakar to the historic slave island of Gorée and the beautiful beaches of the Petite Côte.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SEN', 'ar', 'السنغال', 'السنغال', 'اكتشف الثقافة النابضة بالحياة في غرب أفريقيا، من أسواق داكار الصاخبة إلى جزيرة جوريه التاريخية للعبيد وشواطئ Petite Côte الجميلة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SEN', 'es', 'Senegal', 'Senegal', 'Descubre la vibrante cultura de África Occidental, desde los bulliciosos mercados de Dakar hasta la histórica isla de esclavos de Gorea y las hermosas playas de la Petite Côte.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SEN', 'fr', 'Sénégal', 'Sénégal', 'Découvrez la culture vibrante de l''Afrique de l''Ouest, des marchés animés de Dakar à l''île historique des esclaves de Gorée et aux belles plages de la Petite Côte.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SEN', 'pt', 'Senegal', 'Senegal', 'Descubra a cultura vibrante da África Ocidental, dos mercados movimentados de Dakar à histórica ilha de escravos de Gorée e às belas praias da Petite Côte.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SEN', 'ru', 'Сенегал', 'Сенегал', 'Откройте для себя яркую культуру Западной Африки, от шумных рынков Дакара до исторического острова рабов Горе и прекрасных пляжей Петит-Кот.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SEN', 'de', 'Senegal', 'Senegal', 'Entdecken Sie die lebendige Kultur Westafrikas, von den geschäftigen Märkten von Dakar über die historische Sklaveninsel Gorée bis zu den wunderschönen Stränden der Petite Côte.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SEN', 'it', 'Senegal', 'Senegal', 'Scopri la vibrante cultura dell''Africa occidentale, dai vivaci mercati di Dakar alla storica isola degli schiavi di Gorée e alle splendide spiagge della Petite Côte.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYC', 'en', 'Seychelles', 'Seychelles', 'Discover an archipelago of idyllic islands, with turquoise waters, white-sand beaches, and unique wildlife, including the giant Aldabra tortoise.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYC', 'ar', 'سيشل', 'سيشل', 'اكتشف أرخبيلاً من الجزر المثالية، بمياهها الفيروزية، وشواطئها ذات الرمال البيضاء، وحياتها البرية الفريدة، بما في ذلك سلحفاة ألدابرا العملاقة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYC', 'es', 'Seychelles', 'Seychelles', 'Descubre un archipiélago de islas idílicas, con aguas turquesas, playas de arena blanca y una fauna única, que incluye la tortuga gigante de Aldabra.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYC', 'fr', 'Seychelles', 'Seychelles', 'Découvrez un archipel d''îles idylliques, aux eaux turquoise, aux plages de sable blanc et à la faune unique, dont la tortue géante d''Aldabra.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYC', 'pt', 'Seicheles', 'Seicheles', 'Descubra um arquipélago de ilhas idílicas, com águas azul-turquesa, praias de areia branca e uma vida selvagem única, incluindo a tartaruga gigante de Aldabra.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYC', 'ru', 'Сейшельские острова', 'Сейшельские острова', 'Откройте для себя архипелаг идиллических островов с бирюзовыми водами, белоснежными пляжами и уникальной дикой природой, включая гигантскую черепаху Альдабра.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYC', 'de', 'Seychellen', 'Seychellen', 'Entdecken Sie einen Archipel idyllischer Inseln mit türkisfarbenem Wasser, weißen Sandstränden und einer einzigartigen Tierwelt, darunter die Aldabra-Riesenschildkröte.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYC', 'it', 'Seychelles', 'Seychelles', 'Scopri un arcipelago di isole idilliache, con acque turchesi, spiagge di sabbia bianca e una fauna selvatica unica, tra cui la tartaruga gigante di Aldabra.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLE', 'en', 'Sierra Leone', 'Sierra Leone', 'Discover a nation of stunning beaches, lush rainforests, and a resilient culture. Explore the Tacugama Chimpanzee Sanctuary and the historic Bunce Island.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLE', 'ar', 'سيراليون', 'سيراليون', 'اكتشف أمة من الشواطئ المذهلة، والغابات المطيرة المورقة، والثقافة المرنة. استكشف محمية تاكوجاما للشمبانزي وجزيرة بونس التاريخية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLE', 'es', 'Sierra Leona', 'Sierra Leona', 'Descubre una nación de playas impresionantes, selvas tropicales exuberantes y una cultura resiliente. Explora el Santuario de Chimpancés de Tacugama y la histórica isla de Bunce.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLE', 'fr', 'Sierra Leone', 'Sierra Leone', 'Découvrez une nation de plages magnifiques, de forêts tropicales luxuriantes et d''une culture résiliente. Explorez le sanctuaire de chimpanzés de Tacugama et l''île historique de Bunce.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLE', 'pt', 'Serra Leoa', 'Serra Leoa', 'Descubra uma nação de praias deslumbrantes, florestas tropicais exuberantes e uma cultura resiliente. Explore o Santuário de Chimpanzés de Tacugama e a histórica Ilha de Bunce.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLE', 'ru', 'Сьерра-Леоне', 'Сьерра-Леоне', 'Откройте для себя страну потрясающих пляжей, пышных тропических лесов и стойкой культуры. Исследуйте заповедник шимпанзе Такугама и исторический остров Банс.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLE', 'de', 'Sierra Leone', 'Sierra Leone', 'Entdecken Sie eine Nation mit atemberaubenden Stränden, üppigen Regenwäldern und einer widerstandsfähigen Kultur. Erkunden Sie das Tacugama-Schimpansen-Schutzgebiet und die historische Insel Bunce.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLE', 'it', 'Sierra Leone', 'Sierra Leone', 'Scopri una nazione di spiagge meravigliose, lussureggianti foreste pluviali e una cultura resiliente. Esplora il Santuario degli scimpanzé di Tacugama e la storica isola di Bunce.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SOM', 'en', 'Somalia', 'Somalia', 'Discover a land of poets and nomads, with a long coastline on the Indian Ocean and a rich history of trade and culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SOM', 'ar', 'الصومال', 'الصومال', 'اكتشف أرض الشعراء والبدو، بساحلها الطويل على المحيط الهندي وتاريخها الغني في التجارة والثقافة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SOM', 'es', 'Somalia', 'Somalia', 'Descubre una tierra de poetas y nómadas, con una larga costa en el Océano Índico y una rica historia de comercio y cultura.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SOM', 'fr', 'Somalie', 'Somalie', 'Découvrez une terre de poètes et de nomades, avec une longue côte sur l''océan Indien et une riche histoire de commerce et de culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SOM', 'pt', 'Somália', 'Somália', 'Descubra uma terra de poetas e nómadas, com uma longa costa no Oceano Índico e uma rica história de comércio e cultura.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SOM', 'ru', 'Сомали', 'Сомали', 'Откройте для себя землю поэтов и кочевников с длинной береговой линией в Индийском океане и богатой историей торговли и культуры.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SOM', 'de', 'Somalia', 'Somalia', 'Entdecken Sie ein Land der Dichter und Nomaden mit einer langen Küste am Indischen Ozean und einer reichen Geschichte von Handel und Kultur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SOM', 'it', 'Somalia', 'Somalia', 'Scopri una terra di poeti e nomadi, con una lunga costa sull''Oceano Indiano e una ricca storia di commercio e cultura.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'en', 'South Africa', 'South Africa', 'Discover the Rainbow Nation with spectacular wildlife, stunning landscapes, and rich cultural diversity. From safari adventures to vibrant cities and beautiful coastlines.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'ar', 'South Africa', 'South Africa', 'اكتشف أمة قوس قزح مع الحياة البرية المذهلة والمناظر الطبيعية الخلابة والتنوع الثقافي الغني. من مغامرات السفاري إلى المدن النابضة بالحياة والسواحل الجميلة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'es', 'South Africa', 'South Africa', 'Descubre la Nación Arcoíris con fauna espectacular, paisajes impresionantes y rica diversidad cultural. Desde aventuras de safari hasta ciudades vibrantes y hermosas costas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'fr', 'South Africa', 'South Africa', 'Découvrez la Nation Arc-en-Ciel avec une faune spectaculaire, des paysages époustouflants et une riche diversité culturelle. Des aventures safari aux villes dynamiques et belles côtes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'pt', 'South Africa', 'South Africa', 'Descubra a Nação Arco-Íris com vida selvagem espetacular, paisagens deslumbrantes e rica diversidade cultural. De aventuras de safari a cidades vibrantes e belas costas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'ru', 'South Africa', 'South Africa', 'Откройте страну Радужной Нации с великолепной дикой природой, потрясающими ландшафтами и богатым культурным разнообразием. От сафари до ярких городов и красивых побережий.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'de', 'South Africa', 'South Africa', 'Entdecken Sie die Regenbogen-Nation mit spektakulärer Tierwelt, atemberaubenden Landschaften und reicher kultureller Vielfalt. Von Safari-Abenteuern bis zu lebendigen Städten und schönen Küsten.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZAF', 'it', 'South Africa', 'South Africa', 'Scopri la Nazione Arcobaleno con fauna spettacolare, paesaggi mozzafiato e ricca diversità culturale. Da avventure safari a città vibranti e belle coste.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SSD', 'en', 'South Sudan', 'South Sudan', 'Discover the world''s newest nation, a land of vast savannas, wetlands, and diverse wildlife. Explore the Sudd, one of the world''s largest wetlands.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SSD', 'ar', 'جنوب السودان', 'جنوب السودان', 'اكتشف أحدث دولة في العالم، أرض السافانا الشاسعة والأراضي الرطبة والحياة البرية المتنوعة. استكشف السد، إحدى أكبر الأراضي الرطبة في العالم.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SSD', 'es', 'Sudán del Sur', 'Sudán del Sur', 'Descubre la nación más nueva del mundo, una tierra de vastas sabanas, humedales y diversa vida silvestre. Explora el Sudd, uno de los humedales más grandes del mundo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SSD', 'fr', 'Soudan du Sud', 'Soudan du Sud', 'Découvrez la plus jeune nation du monde, une terre de vastes savanes, de zones humides et d''une faune diversifiée. Explorez le Sudd, l''une des plus grandes zones humides du monde.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SSD', 'pt', 'Sudão do Sul', 'Sudão do Sul', 'Descubra a nação mais nova do mundo, uma terra de vastas savanas, pântanos e vida selvagem diversificada. Explore o Sudd, uma das maiores zonas húmidas do mundo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SSD', 'ru', 'Южный Судан', 'Южный Судан', 'Откройте для себя самую молодую нацию в мире, страну обширных саванн, водно-болотных угодий и разнообразной дикой природы. Исследуйте Судд, одно из крупнейших водно-болотных угодий в мире.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SSD', 'de', 'Südsudan', 'Südsudan', 'Entdecken Sie die jüngste Nation der Welt, ein Land mit riesigen Savannen, Feuchtgebieten und einer vielfältigen Tierwelt. Erkunden Sie den Sudd, eines der größten Feuchtgebiete der Welt.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SSD', 'it', 'Sudan del Sud', 'Sudan del Sud', 'Scopri la nazione più giovane del mondo, una terra di vaste savane, zone umide e una fauna selvatica diversificata. Esplora il Sudd, una delle zone umide più grandi del mondo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SDN', 'en', 'Sudan', 'Sudan', 'Discover a land of ancient civilizations, with more pyramids than Egypt. Explore the Nile Valley and the rich history of the Nubian kings.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SDN', 'ar', 'السودان', 'السودان', 'اكتشف أرض الحضارات القديمة، التي تضم أهرامات أكثر من مصر. استكشف وادي النيل والتاريخ الغني للملوك النوبيين.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SDN', 'es', 'Sudán', 'Sudán', 'Descubre una tierra de civilizaciones antiguas, con más pirámides que Egipto. Explora el valle del Nilo y la rica historia de los reyes nubios.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SDN', 'fr', 'Soudan', 'Soudan', 'Découvrez une terre de civilisations anciennes, avec plus de pyramides que l''Égypte. Explorez la vallée du Nil et la riche histoire des rois nubiens.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SDN', 'pt', 'Sudão', 'Sudão', 'Descubra uma terra de civilizações antigas, com mais pirâmides do que o Egito. Explore o Vale do Nilo e a rica história dos reis núbios.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SDN', 'ru', 'Судан', 'Судан', 'Откройте для себя землю древних цивилизаций, где больше пирамид, чем в Египте. Исследуйте долину Нила и богатую историю нубийских царей.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SDN', 'de', 'Sudan', 'Sudan', 'Entdecken Sie ein Land der alten Zivilisationen mit mehr Pyramiden als Ägypten. Erkunden Sie das Niltal und die reiche Geschichte der nubischen Könige.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SDN', 'it', 'Sudan', 'Sudan', 'Scopri una terra di antiche civiltà, con più piramidi dell''Egitto. Esplora la valle del Nilo e la ricca storia dei re nubiani.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TZA', 'en', 'Tanzania', 'Tanzania', 'Home to Mount Kilimanjaro, the Serengeti, and the spice islands of Zanzibar, Tanzania offers unparalleled wildlife safaris and stunning natural beauty.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TZA', 'ar', 'تنزانيا', 'تنزانيا', 'موطن جبل كليمنجارو، وسيرينجيتي، وجزر زنجبار الغنية بالتوابل، تقدم تنزانيا رحلات سفاري للحياة البرية لا مثيل لها وجمالًا طبيعيًا مذهلاً.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TZA', 'es', 'Tanzania', 'Tanzania', 'Hogar del Monte Kilimanjaro, el Serengeti y las islas de las especias de Zanzíbar, Tanzania ofrece safaris de vida silvestre inigualables y una belleza natural impresionante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TZA', 'fr', 'Tanzanie', 'Tanzanie', 'Abritant le mont Kilimandjaro, le Serengeti et les îles aux épices de Zanzibar, la Tanzanie offre des safaris animaliers inégalés et une beauté naturelle époustouflante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TZA', 'pt', 'Tanzânia', 'Tanzânia', 'Lar do Monte Kilimanjaro, do Serengeti e das ilhas de especiarias de Zanzibar, a Tanzânia oferece safaris de vida selvagem inigualáveis e uma beleza natural deslumbrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TZA', 'ru', 'Танзания', 'Танзания', 'Родина горы Килиманджаро, Серенгети и островов пряностей Занзибара, Танзания предлагает непревзойденные сафари по дикой природе и потрясающую природную красоту.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TZA', 'de', 'Tansania', 'Tansania', 'Tansania, die Heimat des Kilimandscharo, der Serengeti und der Gewürzinseln von Sansibar, bietet unvergleichliche Wildtiersafaris und atemberaubende Naturschönheiten.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TZA', 'it', 'Tanzania', 'Tanzania', 'Sede del Monte Kilimangiaro, del Serengeti e delle isole delle spezie di Zanzibar, la Tanzania offre safari naturalistici senza pari e una straordinaria bellezza naturale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TGO', 'en', 'Togo', 'Togo', 'Discover a country of diverse landscapes, from palm-fringed beaches to rolling savannas and forested hills. Explore the vibrant markets and rich cultural heritage.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TGO', 'ar', 'توغو', 'توغو', 'اكتشف بلدًا ذا مناظر طبيعية متنوعة، من الشواطئ التي تصطف على جانبيها أشجار النخيل إلى السافانا المتموجة والتلال المشجرة. استكشف الأسواق النابضة بالحياة والتراث الثقافي الغني.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TGO', 'es', 'Togo', 'Togo', 'Descubre un país de paisajes diversos, desde playas bordeadas de palmeras hasta sabanas onduladas y colinas boscosas. Explora los vibrantes mercados y el rico patrimonio cultural.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TGO', 'fr', 'Togo', 'Togo', 'Découvrez un pays aux paysages variés, des plages bordées de palmiers aux savanes vallonnées et aux collines boisées. Explorez les marchés animés et le riche patrimoine culturel.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TGO', 'pt', 'Togo', 'Togo', 'Descubra um país de paisagens diversas, desde praias orladas de palmeiras a savanas ondulantes e colinas arborizadas. Explore os mercados vibrantes e o rico património cultural.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TGO', 'ru', 'Того', 'Того', 'Откройте для себя страну разнообразных ландшафтов, от пляжей, окаймленных пальмами, до холмистых саванн и лесистых холмов. Исследуйте оживленные рынки и богатое культурное наследие.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TGO', 'de', 'Togo', 'Togo', 'Entdecken Sie ein Land mit vielfältigen Landschaften, von palmengesäumten Stränden über sanfte Savannen bis hin zu bewaldeten Hügeln. Erkunden Sie die pulsierenden Märkte und das reiche kulturelle Erbe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TGO', 'it', 'Togo', 'Togo', 'Scopri un paese dai paesaggi diversi, dalle spiagge orlate di palme alle savane ondulate e alle colline boscose. Esplora i vivaci mercati e il ricco patrimonio culturale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'en', 'Tunisia', 'Tunisia', 'Explore Tunisia where Mediterranean charm meets Saharan adventure. Discover ancient Carthage ruins, stunning coastlines, and authentic North African culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'ar', 'Tunisia', 'Tunisia', 'استكشف تونس حيث يلتقي سحر البحر الأبيض المتوسط مع مغامرة الصحراء. اكتشف آثار قرطاج القديمة والسواحل الخلابة والثقافة الشمال أفريقية الأصيلة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'es', 'Tunisia', 'Tunisia', 'Explora Túnez donde el encanto mediterráneo se encuentra con la aventura sahariana. Descubre ruinas de la antigua Cartago, costas impresionantes y auténtica cultura norteafricana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'fr', 'Tunisia', 'Tunisia', 'Explorez la Tunisie où le charme méditerranéen rencontre l''aventure saharienne. Découvrez les ruines antiques de Carthage, côtes magnifiques et culture nord-africaine authentique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'pt', 'Tunisia', 'Tunisia', 'Explore a Tunísia onde o charme mediterrâneo encontra a aventura saariana. Descubra ruínas da antiga Cartago, costas deslumbrantes e cultura norte-africana autêntica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'ru', 'Tunisia', 'Tunisia', 'Исследуйте Тунис, где средиземноморское очарование встречается с сахарским приключением. Откройте древние руины Карфагена, потрясающие побережья и аутентичную североафриканскую культуру.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'de', 'Tunisia', 'Tunisia', 'Erkunden Sie Tunesien, wo mediterraner Charme auf Sahara-Abenteuer trifft. Entdecken Sie antike Karthago-Ruinen, atemberaubende Küsten und authentische nordafrikanische Kultur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUN', 'it', 'Tunisia', 'Tunisia', 'Esplora la Tunisia dove il fascino mediterraneo incontra l''avventura sahariana. Scopri antiche rovine di Cartagine, coste mozzafiato e autentica cultura nordafricana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UGA', 'en', 'Uganda', 'Uganda', 'Discover the ''Pearl of Africa'', a land of stunning biodiversity, from the source of the Nile to the snow-capped Rwenzori Mountains and the iconic mountain gorillas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UGA', 'ar', 'أوغندا', 'أوغندا', 'اكتشف ''لؤلؤة أفريقيا''، أرض التنوع البيولوجي المذهل، من منبع النيل إلى جبال روينزوري المغطاة بالثلوج والغوريلا الجبلية الشهيرة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UGA', 'es', 'Uganda', 'Uganda', 'Descubre la ''Perla de África'', una tierra de una biodiversidad impresionante, desde el nacimiento del Nilo hasta las montañas Rwenzori nevadas y los icónicos gorilas de montaña.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UGA', 'fr', 'Ouganda', 'Ouganda', 'Découvrez la ''Perle de l''Afrique'', une terre d''une biodiversité époustouflante, de la source du Nil aux montagnes enneigées du Rwenzori et aux emblématiques gorilles de montagne.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UGA', 'pt', 'Uganda', 'Uganda', 'Descubra a ''Pérola de África'', uma terra de uma biodiversidade deslumbrante, desde a nascente do Nilo até às montanhas Rwenzori cobertas de neve e aos icónicos gorilas da montanha.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UGA', 'ru', 'Уганда', 'Уганда', 'Откройте для себя ''Жемчужину Африки'', страну потрясающего биоразнообразия, от истока Нила до заснеженных гор Рувензори и знаменитых горных горилл.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UGA', 'de', 'Uganda', 'Uganda', 'Entdecken Sie die ''Perle Afrikas'', ein Land von atemberaubender Artenvielfalt, von der Quelle des Nils bis zu den schneebedeckten Rwenzori-Bergen und den berühmten Berggorillas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UGA', 'it', 'Uganda', 'Uganda', 'Scopri la ''Perla d''Africa'', una terra di straordinaria biodiversità, dalla sorgente del Nilo alle innevate montagne del Rwenzori e agli iconici gorilla di montagna.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZMB', 'en', 'Zambia', 'Zambia', 'Discover the home of the legendary Victoria Falls, a nation of incredible wildlife, and the untamed wilderness of the Luangwa Valley.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZMB', 'ar', 'زامبيا', 'زامبيا', 'اكتشف موطن شلالات فيكتوريا الأسطورية، أمة الحياة البرية المذهلة، وبرية وادي لوانغوا التي لم تمس.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZMB', 'es', 'Zambia', 'Zambia', 'Descubre el hogar de las legendarias cataratas Victoria, una nación de increíble vida silvestre y la naturaleza indómita del valle de Luangwa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZMB', 'fr', 'Zambie', 'Zambie', 'Découvrez la patrie des légendaires chutes Victoria, une nation à la faune incroyable et à la nature sauvage et indomptée de la vallée de la Luangwa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZMB', 'pt', 'Zâmbia', 'Zâmbia', 'Descubra a casa das lendárias Cataratas Vitória, uma nação de vida selvagem incrível e a natureza selvagem do Vale do Luangwa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZMB', 'ru', 'Замбия', 'Замбия', 'Откройте для себя родину легендарного водопада Виктория, страну невероятной дикой природы и нетронутой дикой природы долины Луангва.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZMB', 'de', 'Sambia', 'Sambia', 'Entdecken Sie die Heimat der legendären Victoriafälle, eine Nation mit einer unglaublichen Tierwelt und der ungezähmten Wildnis des Luangwa-Tals.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZMB', 'it', 'Zambia', 'Zambia', 'Scopri la patria delle leggendarie Cascate Vittoria, una nazione di incredibile fauna selvatica e la natura selvaggia e incontaminata della Luangwa Valley.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZWE', 'en', 'Zimbabwe', 'Zimbabwe', 'Discover a land of dramatic landscapes, from the mighty Victoria Falls to the ancient Great Zimbabwe ruins. Explore diverse wildlife and a rich cultural heritage.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZWE', 'ar', 'زيمبابوي', 'زيمبابوي', 'اكتشف أرض المناظر الطبيعية الدرامية، من شلالات فيكتوريا العظيمة إلى أطلال زيمبابوي العظمى القديمة. استكشف الحياة البرية المتنوعة والتراث الثقافي الغني.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZWE', 'es', 'Zimbabue', 'Zimbabue', 'Descubre una tierra de paisajes espectaculares, desde las majestuosas cataratas Victoria hasta las antiguas ruinas del Gran Zimbabue. Explora una fauna diversa y un rico patrimonio cultural.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZWE', 'fr', 'Zimbabwe', 'Zimbabwe', 'Découvrez une terre de paysages spectaculaires, des puissantes chutes Victoria aux anciennes ruines du Grand Zimbabwe. Explorez une faune diversifiée et un riche patrimoine culturel.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZWE', 'pt', 'Zimbabué', 'Zimbabué', 'Descubra uma terra de paisagens dramáticas, das poderosas Cataratas Vitória às antigas ruínas do Grande Zimbabué. Explore a vida selvagem diversificada e um rico património cultural.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZWE', 'ru', 'Зимбабве', 'Зимбабве', 'Откройте для себя страну с драматическими пейзажами, от могучего водопада Виктория до древних руин Великого Зимбабве. Исследуйте разнообразную дикую природу и богатое культурное наследие.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZWE', 'de', 'Simbabwe', 'Simbabwe', 'Entdecken Sie ein Land mit dramatischen Landschaften, von den mächtigen Victoriafällen bis zu den antiken Ruinen von Groß-Simbabwe. Erkunden Sie die vielfältige Tierwelt und ein reiches kulturelles Erbe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ZWE', 'it', 'Zimbabwe', 'Zimbabwe', 'Scopri una terra di paesaggi spettacolari, dalle possenti Cascate Vittoria alle antiche rovine del Grande Zimbabwe. Esplora una fauna selvatica variegata e un ricco patrimonio culturale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'en', 'Mexico', 'Mexico', 'Discover vibrant Mexico with ancient Mayan ruins, pristine beaches, and rich traditions. From Cancun paradise to Mexico City culture, experience colorful festivals and authentic cuisine.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'ar', 'Mexico', 'Mexico', 'اكتشف المكسيك النابضة بالحياة مع آثار المايا القديمة والشواطئ البكر والتقاليد الغنية. من جنة كانكون إلى ثقافة مكسيكو سيتي، استمتع بالمهرجانات الملونة والمأكولات الأصيلة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'es', 'Mexico', 'Mexico', 'Descubre el vibrante México con ruinas mayas ancestrales, playas prístinas y tradiciones ricas. Desde el paraíso de Cancún hasta la cultura de Ciudad de México, experimenta festivales coloridos y cocina auténtica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'fr', 'Mexico', 'Mexico', 'Découvrez le Mexique vibrant avec ruines mayas anciennes, plages immaculées et riches traditions. Du paradis de Cancun à la culture de Mexico, vivez festivals colorés et cuisine authentique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'pt', 'Mexico', 'Mexico', 'Descubra o vibrante México com ruínas maias antigas, praias imaculadas e ricas tradições. Do paraíso de Cancun à cultura da Cidade do México, experimente festivais coloridos e culinária autêntica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'ru', 'Mexico', 'Mexico', 'Откройте яркую Мексику с древними руинами Майя, нетронутыми пляжами и богатыми традициями. От парадайза Канкуна до культуры Мехико-сити, ощутите яркие фестивали и аутентичную кухню.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'de', 'Mexico', 'Mexico', 'Entdecken Sie das lebendige Mexiko mit antiken Maya-Ruinen, unberührten Stränden und reichen Traditionen. Vom Cancun-Paradies bis zur Kultur von Mexiko-Stadt, erleben Sie bunte Festivals und authentische Küche.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MEX', 'it', 'Mexico', 'Mexico', 'Scopri il vibrante Messico con antiche rovine Maya, spiagge incontaminate e ricche tradizioni. Dal paradiso di Cancun alla cultura di Città del Messico, vivi festival colorati e cucina autentica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLZ', 'en', 'Belize', 'Belize', 'Discover a jewel in Central America with pristine reefs, lush jungles, and ancient Mayan ruins. Experience world-class diving, caving, and a melting pot of cultures.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLZ', 'ar', 'بليز', 'بليز', 'اكتشف جوهرة في أمريكا الوسطى بشعابها المرجانية البكر وأدغالها المورقة وأطلال المايا القديمة. استمتع بتجربة غوص وكهوف عالمية المستوى وبوتقة تنصهر فيها الثقافات.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLZ', 'es', 'Belice', 'Belice', 'Descubre una joya en Centroamérica con arrecifes vírgenes, selvas exuberantes y antiguas ruinas mayas. Experimenta buceo de clase mundial, espeleología y un crisol de culturas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLZ', 'fr', 'Belize', 'Belize', 'Découvrez un joyau en Amérique centrale avec des récifs immaculés, des jungles luxuriantes et d''anciennes ruines mayas. Faites l''expérience de la plongée de classe mondiale, de la spéléologie et d''un creuset de cultures.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLZ', 'pt', 'Belize', 'Belize', 'Descubra uma joia na América Central com recifes imaculados, selvas exuberantes e antigas ruínas maias. Experimente mergulho de classe mundial, espeleologia e um caldeirão de culturas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLZ', 'ru', 'Белиз', 'Белиз', 'Откройте для себя жемчужину Центральной Америки с нетронутыми рифами, пышными джунглями и древними руинами майя. Испытайте дайвинг мирового класса, спелеологию и плавильный котел культур.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLZ', 'de', 'Belize', 'Belize', 'Entdecken Sie ein Juwel in Mittelamerika mit unberührten Riffen, üppigen Dschungeln und alten Maya-Ruinen. Erleben Sie Weltklasse-Tauchen, Höhlenforschung und einen Schmelztiegel der Kulturen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLZ', 'it', 'Belize', 'Belize', 'Scopri un gioiello in America Centrale con barriere coralline incontaminate, giungle lussureggianti e antiche rovine Maya. Sperimenta immersioni di livello mondiale, speleologia e un crogiolo di culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'en', 'Costa Rica', 'Costa Rica', 'Discover Costa Rica''s pure vida with pristine rainforests, stunning beaches, and incredible biodiversity. Experience adventure, wildlife, and sustainable tourism at its finest.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'ar', 'Costa Rica', 'Costa Rica', 'اكتشف الحياة النقية في كوستا ريكا مع الغابات المطيرة النقية والشواطئ الخلابة والتنوع الحيوي المدهش. استمتع بالمغامرة والحياة البرية والسياحة المستدامة في أفضل أشكالها.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'es', 'Costa Rica', 'Costa Rica', 'Descubre la pura vida de Costa Rica con bosques tropicales vírgenes, playas impresionantes y biodiversidad increíble. Experimenta aventura, vida silvestre y turismo sostenible en su máxima expresión.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'fr', 'Costa Rica', 'Costa Rica', 'Découvrez la pura vida du Costa Rica avec forêts tropicales vierges, plages magnifiques et biodiversité incroyable. Vivez aventure, faune sauvage et tourisme durable à son meilleur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'pt', 'Costa Rica', 'Costa Rica', 'Descubra a pura vida da Costa Rica com florestas tropicais intocadas, praias deslumbrantes e biodiversidade incrível. Experimente aventura, vida selvagem e turismo sustentável no seu melhor.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'ru', 'Costa Rica', 'Costa Rica', 'Откройте чистую жизнь Коста-Рики с нетронутыми тропическими лесами, потрясающими пляжами и невероятным биоразнообразием. Ощутите приключения, дикую природу и устойчивый туризм в лучшем виде.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'de', 'Costa Rica', 'Costa Rica', 'Entdecken Sie Costa Ricas pura vida mit unberührten Regenwäldern, atemberaubenden Stränden und unglaublicher Artenvielfalt. Erleben Sie Abenteuer, Tierwelt und nachhaltigen Tourismus vom Feinsten.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CRI', 'it', 'Costa Rica', 'Costa Rica', 'Scopri la pura vida del Costa Rica con foreste tropicali incontaminate, spiagge mozzafiato e biodiversità incredibile. Vivi avventura, fauna selvatica e turismo sostenibile al suo meglio.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLV', 'en', 'El Salvador', 'El Salvador', 'Discover the ''Land of Volcanoes'' with its stunning landscapes, Mayan ruins, and vibrant surf culture. Explore colonial towns and enjoy the warmth of its people.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLV', 'ar', 'السلفادور', 'السلفادور', 'اكتشف ''أرض البراكين'' بمناظرها الطبيعية الخلابة، وأطلال المايا، وثقافة ركوب الأمواج النابضة بالحياة. استكشف المدن الاستعمارية واستمتع بدفء شعبها.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLV', 'es', 'El Salvador', 'El Salvador', 'Descubre la ''Tierra de Volcanes'' con sus impresionantes paisajes, ruinas mayas y vibrante cultura del surf. Explora pueblos coloniales y disfruta de la calidez de su gente.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLV', 'fr', 'El Salvador', 'El Salvador', 'Découvrez le ''Pays des Volcans'' avec ses paysages magnifiques, ses ruines mayas et sa culture du surf dynamique. Explorez les villes coloniales et profitez de la chaleur de ses habitants.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLV', 'pt', 'El Salvador', 'El Salvador', 'Descubra a ''Terra dos Vulcões'' com as suas paisagens deslumbrantes, ruínas maias e cultura de surf vibrante. Explore cidades coloniais e desfrute do calor do seu povo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLV', 'ru', 'Сальвадор', 'Сальвадор', 'Откройте для себя ''Землю вулканов'' с ее потрясающими пейзажами, руинами майя и яркой культурой серфинга. Исследуйте колониальные города и наслаждайтесь теплотой его жителей.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLV', 'de', 'El Salvador', 'El Salvador', 'Entdecken Sie das ''Land der Vulkane'' mit seinen atemberaubenden Landschaften, Maya-Ruinen und einer lebendigen Surfkultur. Erkunden Sie Kolonialstädte und genießen Sie die Herzlichkeit seiner Menschen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLV', 'it', 'El Salvador', 'El Salvador', 'Scopri la ''Terra dei Vulcani'' con i suoi paesaggi mozzafiato, le rovine Maya e la vibrante cultura del surf. Esplora le città coloniali e goditi il calore della sua gente.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GTM', 'en', 'Guatemala', 'Guatemala', 'Discover ancient Mayan ruins, colorful markets, and breathtaking volcanoes in the heart of Central America.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GTM', 'ar', 'غواتيمالا', 'غواتيمالا', 'اكتشف حضارة المايا القديمة، الأسواق الملونة، والبراكين الخلابة في قلب أمريكا الوسطى.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GTM', 'es', 'Guatemala', 'Guatemala', 'Descubre las antiguas ruinas mayas, mercados coloridos y volcanes impresionantes en el corazón de Centroamérica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GTM', 'fr', 'Guatemala', 'Guatemala', 'Découvrez les ruines mayas, les marchés colorés et les volcans spectaculaires au cœur de l’Amérique centrale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GTM', 'pt', 'Guatemala', 'Guatemala', 'Descubra ruínas maias antigas, mercados coloridos e vulcões impressionantes no coração da América Central.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GTM', 'ru', 'Гватемала', 'Гватемала', 'Откройте для себя древние руины майя, красочные рынки и впечатляющие вулканы в самом сердце Центральной Америки.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GTM', 'de', 'Guatemala', 'Guatemala', 'Entdecken Sie alte Maya-Ruinen, bunte Märkte und atemberaubende Vulkane im Herzen Mittelamerikas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GTM', 'it', 'Guatemala', 'Guatemala', 'Scopri antiche rovine Maya, mercati colorati e vulcani mozzafiato nel cuore dell’America Centrale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HND', 'en', 'Honduras', 'Honduras', 'Discover a land of ancient Mayan ruins, pristine Caribbean beaches, and lush rainforests. Explore the Bay Islands, a world-renowned diving destination.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HND', 'ar', 'هندوراس', 'هندوراس', 'اكتشف أرضًا من أطلال المايا القديمة، وشواطئ الكاريبي البكر، والغابات المطيرة المورقة. استكشف جزر الخليج، وهي وجهة غوص مشهورة عالميًا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HND', 'es', 'Honduras', 'Honduras', 'Descubre una tierra de antiguas ruinas mayas, playas vírgenes del Caribe y exuberantes selvas tropicales. Explora las Islas de la Bahía, un destino de buceo de renombre mundial.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HND', 'fr', 'Honduras', 'Honduras', 'Découvrez une terre d''anciennes ruines mayas, de plages immaculées des Caraïbes et de forêts tropicales luxuriantes. Explorez les îles de la Baie, une destination de plongée de renommée mondiale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HND', 'pt', 'Honduras', 'Honduras', 'Descubra uma terra de antigas ruínas maias, praias imaculadas do Caribe e exuberantes florestas tropicais. Explore as Ilhas da Baía, um destino de mergulho de renome mundial.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HND', 'ru', 'Гондурас', 'Гондурас', 'Откройте для себя землю древних руин майя, нетронутых пляжей Карибского моря и пышных тропических лесов. Исследуйте острова Бэй, всемирно известный дайвинг-курорт.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HND', 'de', 'Honduras', 'Honduras', 'Entdecken Sie ein Land mit alten Maya-Ruinen, unberührten Karibikstränden und üppigen Regenwäldern. Erkunden Sie die Bay Islands, ein weltberühmtes Tauchziel.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HND', 'it', 'Honduras', 'Honduras', 'Scopri una terra di antiche rovine Maya, spiagge caraibiche incontaminate e lussureggianti foreste pluviali. Esplora le Isole della Baia, una destinazione per immersioni di fama mondiale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NIC', 'en', 'Nicaragua', 'Nicaragua', 'Discover the ''Land of Lakes and Volcanoes'' with its stunning colonial architecture, pristine crater lakes, and vibrant culture. Explore the rich biodiversity of its rainforests and the charm of its cities.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NIC', 'ar', 'نيكاراغوا', 'نيكاراغوا', 'اكتشف ''أرض البحيرات والبراكين'' بهندستها المعمارية الاستعمارية المذهلة، وبحيرات الفوهات البكر، وثقافتها النابضة بالحياة. استكشف التنوع البيولوجي الغني لغاباتها المطيرة وسحر مدنها.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NIC', 'es', 'Nicaragua', 'Nicaragua', 'Descubre la ''Tierra de Lagos y Volcanes'' con su impresionante arquitectura colonial, lagos de cráter vírgenes y una cultura vibrante. Explora la rica biodiversidad de sus selvas tropicales y el encanto de sus ciudades.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NIC', 'fr', 'Nicaragua', 'Nicaragua', 'Découvrez la ''Terre des Lacs et des Volcans'' avec son architecture coloniale magnifique, ses lacs de cratère immaculés et sa culture dynamique. Explorez la riche biodiversité de ses forêts tropicales et le charme de ses villes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NIC', 'pt', 'Nicarágua', 'Nicarágua', 'Descubra a ''Terra de Lagos e Vulcões'' com a sua deslumbrante arquitetura colonial, lagos de cratera imaculados e cultura vibrante. Explore a rica biodiversidade das suas florestas tropicais e o encanto das suas cidades.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NIC', 'ru', 'Никарагуа', 'Никарагуа', 'Откройте для себя ''Землю озер и вулканов'' с ее потрясающей колониальной архитектурой, нетронутыми кратерными озерами и яркой культурой. Исследуйте богатое биоразнообразие ее тропических лесов и очарование ее городов.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NIC', 'de', 'Nicaragua', 'Nicaragua', 'Entdecken Sie das ''Land der Seen und Vulkane'' mit seiner atemberaubenden Kolonialarchitektur, unberührten Kraterseen und lebendigen Kultur. Erkunden Sie die reiche Artenvielfalt seiner Regenwälder und den Charme seiner Städte.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NIC', 'it', 'Nicaragua', 'Nicaragua', 'Scopri la ''Terra dei Laghi e dei Vulcani'' con la sua splendida architettura coloniale, i laghi vulcanici incontaminati e la cultura vibrante. Esplora la ricca biodiversità delle sue foreste pluviali e il fascino delle sue città.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAN', 'en', 'Panama', 'Panama', 'Explore the crossroads of the Americas in Panama, where the world-famous canal connects oceans. Discover its vibrant city life, lush rainforests, and beautiful beaches.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAN', 'ar', 'بنما', 'بنما', 'استكشف مفترق طرق الأمريكتين في بنما، حيث تربط القناة المشهورة عالميًا المحيطات. اكتشف حياة المدينة النابضة بالحياة والغابات المطيرة المورقة والشواطئ الجميلة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAN', 'es', 'Panamá', 'Panamá', 'Explore el cruce de las Américas en Panamá, donde el mundialmente famoso canal conecta océanos. Descubra su vibrante vida urbana, sus exuberantes selvas tropicales y sus hermosas playas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAN', 'fr', 'Panama', 'Panama', 'Explorez le carrefour des Amériques au Panama, où le célèbre canal relie les océans. Découvrez sa vie urbaine animée, ses forêts tropicales luxuriantes et ses belles plages.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAN', 'pt', 'Panamá', 'Panamá', 'Explore a encruzilhada das Américas no Panamá, onde o mundialmente famoso canal conecta oceanos. Descubra sua vibrante vida urbana, florestas tropicais exuberantes e belas praias.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAN', 'ru', 'Панама', 'Панама', 'Исследуйте перекресток Америк в Панаме, где всемирно известный канал соединяет океаны. Откройте для себя его яркую городскую жизнь, пышные тропические леса и прекрасные пляжи.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAN', 'de', 'Panama', 'Panama', 'Erkunden Sie die Kreuzung Amerikas in Panama, wo der weltberühmte Kanal die Ozeane verbindet. Entdecken Sie das pulsierende Stadtleben, die üppigen Regenwälder und die wunderschönen Strände.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAN', 'it', 'Panama', 'Panama', 'Esplora il crocevia delle Americhe a Panama, dove il famoso canale collega gli oceani. Scopri la sua vibrante vita cittadina, le lussureggianti foreste pluviali e le bellissime spiagge.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ATG', 'en', 'Antigua and Barbuda', 'Antigua and Barbuda', 'Discover a paradise of 365 beaches, one for every day of the year. Explore historic harbors, vibrant coral reefs, and the laid-back island life of the Caribbean.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ATG', 'ar', 'أنتيغوا وبربودا', 'أنتيغوا وبربودا', 'اكتشف جنة من 365 شاطئًا، واحد لكل يوم من أيام السنة. استكشف المرافئ التاريخية والشعاب المرجانية النابضة بالحياة وحياة الجزيرة المريحة في منطقة البحر الكاريبي.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ATG', 'es', 'Antigua y Barbuda', 'Antigua y Barbuda', 'Descubra un paraíso de 365 playas, una para cada día del año. Explore puertos históricos, vibrantes arrecifes de coral y la relajada vida isleña del Caribe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ATG', 'fr', 'Antigua-et-Barbuda', 'Antigua-et-Barbuda', 'Découvrez un paradis de 365 plages, une pour chaque jour de l''''année. Explorez les ports historiques, les récifs coralliens animés et la vie insulaire décontractée des Caraïbes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ATG', 'pt', 'Antígua e Barbuda', 'Antígua e Barbuda', 'Descubra um paraíso de 365 praias, uma para cada dia do ano. Explore portos históricos, recifes de corais vibrantes e a descontraída vida insular do Caribe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ATG', 'ru', 'Антигуа и Барбуда', 'Антигуа и Барбуда', 'Откройте для себя рай из 365 пляжей, по одному на каждый день года. Исследуйте исторические гавани, яркие коралловые рифы и непринужденную островную жизнь Карибского моря.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ATG', 'de', 'Antigua und Barbuda', 'Antigua und Barbuda', 'Entdecken Sie ein Paradies mit 365 Stränden, einen für jeden Tag des Jahres. Erkunden Sie historische Häfen, lebendige Korallenriffe und das entspannte Inselleben der Karibik.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ATG', 'it', 'Antigua e Barbuda', 'Antigua e Barbuda', 'Scopri un paradiso di 365 spiagge, una per ogni giorno dell''''anno. Esplora porti storici, vivaci barriere coralline e la rilassata vita isolana dei Caraibi.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BHS', 'en', 'Bahamas', 'Bahamas', 'Discover an archipelago of over 700 islands and cays, with crystal-clear turquoise waters, vibrant coral reefs, and a lively culture. From swimming with pigs to exploring historic Nassau, the Bahamas offers endless adventure.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BHS', 'ar', 'جزر البهاما', 'جزر البهاما', 'اكتشف أرخبيلاً يضم أكثر من 700 جزيرة وجزيرة صغيرة، بمياهه الفيروزية الصافية، وشعابه المرجانية النابضة بالحياة، وثقافته المفعمة بالحيوية. من السباحة مع الخنازير إلى استكشاف ناسو التاريخية، توفر جزر البهاما مغامرة لا نهاية لها.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BHS', 'es', 'Bahamas', 'Bahamas', 'Descubra un archipiélago de más de 700 islas y cayos, con aguas turquesas y cristalinas, vibrantes arrecifes de coral y una animada cultura. Desde nadar con cerdos hasta explorar la histórica Nassau, las Bahamas ofrecen una aventura sin fin.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BHS', 'fr', 'Bahamas', 'Bahamas', 'Découvrez un archipel de plus de 700 îles et cayes, avec des eaux turquoise cristallines, des récifs coralliens animés et une culture vivante. De la nage avec les cochons à l''''exploration de la ville historique de Nassau, les Bahamas offrent une aventure sans fin.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BHS', 'pt', 'Bahamas', 'Bahamas', 'Descubra um arquipélago de mais de 700 ilhas e ilhotas, com águas azul-turquesa cristalinas, recifes de corais vibrantes e uma cultura animada. De nadar com porcos a explorar a histórica Nassau, as Bahamas oferecem uma aventura sem fim.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BHS', 'ru', 'Багамы', 'Багамы', 'Откройте для себя архипелаг из более чем 700 островов и рифов с кристально чистой бирюзовой водой, яркими коралловыми рифами и живой культурой. От плавания со свиньями до изучения исторического Нассау, Багамы предлагают бесконечные приключения.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BHS', 'de', 'Bahamas', 'Bahamas', 'Entdecken Sie einen Archipel mit über 700 Inseln und Cays, mit kristallklarem türkisfarbenem Wasser, lebhaften Korallenriffen und einer lebendigen Kultur. Vom Schwimmen mit Schweinen bis zur Erkundung des historischen Nassau bieten die Bahamas endlose Abenteuer.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BHS', 'it', 'Bahamas', 'Bahamas', 'Scopri un arcipelago di oltre 700 isole e isolotti, con acque turchesi cristalline, vivaci barriere coralline e una cultura vivace. Dal nuoto con i maiali all''''esplorazione della storica Nassau, le Bahamas offrono un''''avventura senza fine.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRB', 'en', 'Barbados', 'Barbados', 'Discover the birthplace of rum, with its stunning beaches, vibrant festivals, and a unique blend of British and West African heritage. Explore the historic Bridgetown and its Garrison, a UNESCO World Heritage site.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRB', 'ar', 'بربادوس', 'بربادوس', 'اكتشف مهد الروم، بشواطئه الخلابة، ومهرجاناته النابضة بالحياة، ومزيجه الفريد من التراث البريطاني وغرب أفريقيا. استكشف بريدجتاون التاريخية وحاميتها، وهي أحد مواقع التراث العالمي لليونسكو.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRB', 'es', 'Barbados', 'Barbados', 'Descubra la cuna del ron, con sus impresionantes playas, vibrantes festivales y una mezcla única de herencia británica y de África Occidental. Explore la histórica Bridgetown y su guarnición, declarada Patrimonio de la Humanidad por la UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRB', 'fr', 'Barbade', 'Barbade', 'Découvrez le berceau du rhum, avec ses plages magnifiques, ses festivals animés et un mélange unique d''''héritage britannique et ouest-africain. Explorez la ville historique de Bridgetown et sa garnison, un site du patrimoine mondial de l''''UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRB', 'pt', 'Barbados', 'Barbados', 'Descubra o berço do rum, com as suas praias deslumbrantes, festivais vibrantes e uma mistura única de herança britânica e da África Ocidental. Explore a histórica Bridgetown e a sua guarnição, um Património Mundial da UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRB', 'ru', 'Барбадос', 'Барбадос', 'Откройте для себя родину рома с ее потрясающими пляжами, яркими фестивалями и уникальным сочетанием британского и западноафриканского наследия. Исследуйте исторический Бриджтаун и его гарнизон, объект Всемирного наследия ЮНЕСКО.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRB', 'de', 'Barbados', 'Barbados', 'Entdecken Sie den Geburtsort des Rums mit seinen atemberaubenden Stränden, lebhaften Festivals und einer einzigartigen Mischung aus britischem und westafrikanischem Erbe. Erkunden Sie das historische Bridgetown und seine Garnison, ein UNESCO-Weltkulturerbe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRB', 'it', 'Barbados', 'Barbados', 'Scopri la culla del rum, con le sue spiagge mozzafiato, i festival vivaci e una miscela unica di eredità britannica e dell''''Africa occidentale. Esplora la storica Bridgetown e la sua guarnigione, un sito del patrimonio mondiale dell''''UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CUB', 'en', 'Cuba', 'Cuba', 'Discover a Caribbean island of vibrant culture, classic cars, and colonial architecture. From the lively streets of Havana to the pristine beaches of Varadero, experience a nation frozen in time.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CUB', 'ar', 'كوبا', 'كوبا', 'اكتشف جزيرة كاريبية ذات ثقافة نابضة بالحياة وسيارات كلاسيكية وهندسة معمارية استعمارية. من شوارع هافانا المفعمة بالحياة إلى شواطئ فاراديرو البكر، جرب أمة مجمدة في الزمن.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CUB', 'es', 'Cuba', 'Cuba', 'Descubra una isla caribeña de cultura vibrante, coches clásicos y arquitectura colonial. Desde las animadas calles de La Habana hasta las playas vírgenes de Varadero, experimente una nación congelada en el tiempo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CUB', 'fr', 'Cuba', 'Cuba', 'Découvrez une île des Caraïbes à la culture vibrante, aux voitures classiques et à l''''architecture coloniale. Des rues animées de La Havane aux plages immaculées de Varadero, découvrez une nation figée dans le temps.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CUB', 'pt', 'Cuba', 'Cuba', 'Descubra uma ilha caribenha de cultura vibrante, carros clássicos e arquitetura colonial. Das ruas animadas de Havana às praias imaculadas de Varadero, experimente uma nação congelada no tempo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CUB', 'ru', 'Куба', 'Куба', 'Откройте для себя карибский остров с яркой культурой, классическими автомобилями и колониальной архитектурой. От оживленных улиц Гаваны до нетронутых пляжей Варадеро, познакомьтесь со страной, застывшей во времени.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CUB', 'de', 'Kuba', 'Kuba', 'Entdecken Sie eine Karibikinsel mit lebendiger Kultur, klassischen Autos und kolonialer Architektur. Von den belebten Straßen Havannas bis zu den unberührten Stränden von Varadero erleben Sie eine Nation, die in der Zeit eingefroren ist.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CUB', 'it', 'Cuba', 'Cuba', 'Scopri un''''isola caraibica dalla cultura vibrante, auto d''''epoca e architettura coloniale. Dalle vivaci strade de L''''Avana alle spiagge incontaminate di Varadero, vivi una nazione congelata nel tempo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DMA', 'en', 'Dominica', 'Dominica', 'Explore the Nature Island of the Caribbean, with lush rainforests, hot springs, and dramatic waterfalls.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DMA', 'ar', 'دومينيكا', 'دومينيكا', 'استكشف جزيرة الطبيعة في الكاريبي، بغاباتها المطيرة وينابيعها الحارة وشلالاتها الرائعة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DMA', 'es', 'Dominica', 'Dominica', 'Explora la Isla de la Naturaleza del Caribe, con selvas exuberantes, aguas termales y cascadas espectaculares.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DMA', 'fr', 'Dominique', 'Dominique', 'Explorez l’île Nature des Caraïbes, avec ses forêts tropicales luxuriantes, ses sources chaudes et ses cascades spectaculaires.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DMA', 'pt', 'Dominica', 'Dominica', 'Explore a Ilha da Natureza do Caribe, com florestas tropicais exuberantes, fontes termais e cachoeiras impressionantes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DMA', 'ru', 'Доминика', 'Доминика', 'Исследуйте остров природы Карибского моря с его пышными тропическими лесами, горячими источниками и впечатляющими водопадами.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DMA', 'de', 'Dominica', 'Dominica', 'Entdecken Sie die Naturinsel der Karibik mit üppigen Regenwäldern, heißen Quellen und dramatischen Wasserfällen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DMA', 'it', 'Dominica', 'Dominica', 'Esplora l’Isola della Natura dei Caraibi, con foreste pluviali lussureggianti, sorgenti termali e cascate spettacolari.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DOM', 'en', 'Dominican Republic', 'Dominican Republic', 'Experience vibrant Caribbean culture, white-sand beaches, and the historic charm of Santo Domingo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DOM', 'ar', 'جمهورية الدومينيكان', 'جمهورية الدومينيكان', 'استمتع بالثقافة الكاريبية النابضة بالحياة، الشواطئ الرملية البيضاء، وسحر سانتو دومينغو التاريخي.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DOM', 'es', 'República Dominicana', 'República Dominicana', 'Vive la vibrante cultura caribeña, playas de arena blanca y el encanto histórico de Santo Domingo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DOM', 'fr', 'République dominicaine', 'République dominicaine', 'Découvrez la culture caribéenne vibrante, les plages de sable blanc et le charme historique de Saint-Domingue.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DOM', 'pt', 'República Dominicana', 'República Dominicana', 'Viva a vibrante cultura caribenha, praias de areia branca e o charme histórico de Santo Domingo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DOM', 'ru', 'Доминиканская Республика', 'Доминиканская Республика', 'Погрузитесь в яркую карибскую культуру, белоснежные пляжи и исторический шарм Санто-Доминго.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DOM', 'de', 'Dominikanische Republik', 'Dominikanische Republik', 'Erleben Sie die lebendige karibische Kultur, weiße Sandstrände und den historischen Charme von Santo Domingo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DOM', 'it', 'Repubblica Dominicana', 'Repubblica Dominicana', 'Vivi la vibrante cultura caraibica, spiagge di sabbia bianca e il fascino storico di Santo Domingo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRD', 'en', 'Grenada', 'Grenada', 'Known as the Spice Island, Grenada offers stunning beaches, rainforests, and a rich blend of African, French, and British heritage.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRD', 'ar', 'غرينادا', 'غرينادا', 'تعرف بجزيرة التوابل، وتضم شواطئ خلابة، وغابات مطيرة، ومزيجاً غنياً من التراث الأفريقي والفرنسي والبريطاني.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRD', 'es', 'Granada', 'Granada', 'Conocida como la Isla de las Especias, ofrece playas impresionantes, selvas y una rica herencia africana, francesa y británica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRD', 'fr', 'Grenade', 'Grenade', 'Surnommée l’île aux épices, Grenade offre des plages superbes, des forêts tropicales et un riche héritage africain, français et britannique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRD', 'pt', 'Granada', 'Granada', 'Conhecida como Ilha das Especiarias, oferece praias deslumbrantes, florestas e uma rica herança africana, francesa e britânica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRD', 'ru', 'Гренада', 'Гренада', 'Известна как остров специй: потрясающие пляжи, тропические леса и богатое смешение африканского, французского и британского наследия.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRD', 'de', 'Grenada', 'Grenada', 'Bekannt als Gewürzinsel, bietet Grenada traumhafte Strände, Regenwälder und ein reiches afrikanisches, französisches und britisches Erbe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRD', 'it', 'Grenada', 'Grenada', 'Conosciuta come l’Isola delle Spezie, offre spiagge stupende, foreste pluviali e un ricco patrimonio africano, francese e britannico.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HTI', 'en', 'Haiti', 'Haiti', 'Discover a land of mountains, vibrant art, and a resilient spirit, where history and culture meet the Caribbean Sea.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HTI', 'ar', 'هايتي', 'هايتي', 'اكتشف أرض الجبال والفن النابض بالحياة والروح الصامدة، حيث يلتقي التاريخ والثقافة ببحر الكاريبي.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HTI', 'es', 'Haití', 'Haití', 'Descubre una tierra de montañas, arte vibrante y espíritu resiliente, donde la historia y la cultura se encuentran con el mar Caribe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HTI', 'fr', 'Haïti', 'Haïti', 'Découvrez une terre de montagnes, d’art vibrant et d’esprit résilient, où l’histoire et la culture rencontrent la mer des Caraïbes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HTI', 'pt', 'Haiti', 'Haiti', 'Descubra uma terra de montanhas, arte vibrante e espírito resiliente, onde história e cultura encontram o mar do Caribe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HTI', 'ru', 'Гаити', 'Гаити', 'Откройте для себя страну гор, яркого искусства и стойкого духа, где история и культура встречаются с Карибским морем.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HTI', 'de', 'Haiti', 'Haiti', 'Entdecken Sie ein Land der Berge, lebendiger Kunst und eines widerstandsfähigen Geistes, wo Geschichte und Kultur auf das Karibische Meer treffen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HTI', 'it', 'Haiti', 'Haiti', 'Scopri una terra di montagne, arte vibrante e spirito resiliente, dove storia e cultura incontrano il Mar dei Caraibi.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JAM', 'en', 'Jamaica', 'Jamaica', 'Feel the rhythm of reggae, relax on white-sand beaches, and explore lush mountains in vibrant Jamaica, the heart of the Caribbean.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JAM', 'ar', 'Jamaica', 'Jamaica', 'استمتع بإيقاع الريغي، والشواطئ البيضاء، والطبيعة الخضراء في جامايكا، قلب الكاريبي النابض.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JAM', 'es', 'Jamaica', 'Jamaica', 'Siente el ritmo del reggae, relájate en playas de arena blanca y explora las montañas de la vibrante Jamaica, el corazón del Caribe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JAM', 'fr', 'Jamaica', 'Jamaica', 'Vivez le reggae, les plages de sable blanc et les montagnes luxuriantes de la Jamaïque, cœur vibrant des Caraïbes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JAM', 'pt', 'Jamaica', 'Jamaica', 'Sinta o ritmo do reggae, relaxe em praias de areia branca e explore as montanhas exuberantes da vibrante Jamaica, o coração do Caribe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JAM', 'ru', 'Jamaica', 'Jamaica', 'Почувствуйте ритм регги, белоснежные пляжи и зелёные горы Ямайки — сердца Карибского региона.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JAM', 'de', 'Jamaica', 'Jamaica', 'Erleben Sie Reggae, weiße Sandstrände und grüne Berge im lebendigen Jamaika, dem Herzen der Karibik.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JAM', 'it', 'Jamaica', 'Jamaica', 'Vivi il ritmo del reggae, rilassati su spiagge bianche ed esplora le montagne rigogliose della vibrante Giamaica, cuore dei Caraibi.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KNA', 'en', 'Saint Kitts and Nevis', 'Saint Kitts and Nevis', 'Discover twin islands with volcanic peaks, golden beaches, and a warm Caribbean welcome in Saint Kitts and Nevis.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KNA', 'ar', 'Saint Kitts and Nevis', 'Saint Kitts and Nevis', 'اكتشف الجزر التوأم ذات القمم البركانية والشواطئ الذهبية والترحاب الكاريبي الدافئ في سانت كيتس ونيفيس.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KNA', 'es', 'Saint Kitts and Nevis', 'Saint Kitts and Nevis', 'Descubre islas gemelas con picos volcánicos, playas doradas y una cálida bienvenida caribeña en San Cristóbal y Nieves.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KNA', 'fr', 'Saint Kitts and Nevis', 'Saint Kitts and Nevis', 'Découvrez des îles jumelles aux pics volcaniques, plages dorées et accueil chaleureux des Caraïbes à Saint-Christophe-et-Niévès.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KNA', 'pt', 'Saint Kitts and Nevis', 'Saint Kitts and Nevis', 'Descubra ilhas gêmeas com picos vulcânicos, praias douradas e uma calorosa recepção caribenha em São Cristóvão e Neves.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KNA', 'ru', 'Saint Kitts and Nevis', 'Saint Kitts and Nevis', 'Откройте для себя острова-близнецы с вулканическими вершинами, золотыми пляжами и тёплым карибским гостеприимством в Сент-Китс и Невис.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KNA', 'de', 'Saint Kitts and Nevis', 'Saint Kitts and Nevis', 'Entdecken Sie Zwillingsinseln mit Vulkanbergen, goldenen Stränden und herzlicher karibischer Gastfreundschaft in St. Kitts und Nevis.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KNA', 'it', 'Saint Kitts and Nevis', 'Saint Kitts and Nevis', 'Scopri isole gemelle con vette vulcaniche, spiagge dorate e una calorosa accoglienza caraibica a Saint Kitts e Nevis.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LCA', 'en', 'Saint Lucia', 'Saint Lucia', 'Marvel at the iconic Pitons, lush rainforests, and vibrant Creole culture in the tropical paradise of Saint Lucia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LCA', 'ar', 'Saint Lucia', 'Saint Lucia', 'استمتع بجمال بيتونز الشهير، والغابات المطيرة، والثقافة الكريولية النابضة في جنة سانت لوسيا الاستوائية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LCA', 'es', 'Saint Lucia', 'Saint Lucia', 'Admira los icónicos Pitons, selvas tropicales y la vibrante cultura criolla en el paraíso tropical de Santa Lucía.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LCA', 'fr', 'Saint Lucia', 'Saint Lucia', 'Admirez les Pitons emblématiques, les forêts tropicales luxuriantes et la culture créole vibrante dans le paradis tropical de Sainte-Lucie.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LCA', 'pt', 'Saint Lucia', 'Saint Lucia', 'Maravilhe-se com os icônicos Pitons, florestas tropicais e a vibrante cultura crioula no paraíso tropical de Santa Lúcia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LCA', 'ru', 'Saint Lucia', 'Saint Lucia', 'Полюбуйтесь знаменитыми Питонами, тропическими лесами и яркой креольской культурой в тропическом раю Сент-Люсии.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LCA', 'de', 'Saint Lucia', 'Saint Lucia', 'Bewundern Sie die berühmten Pitons, üppige Regenwälder und die lebendige kreolische Kultur im tropischen Paradies St. Lucia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LCA', 'it', 'Saint Lucia', 'Saint Lucia', 'Ammira i famosi Pitons, le foreste pluviali e la vibrante cultura creola nel paradiso tropicale di Santa Lucia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VCT', 'en', 'Saint Vincent and the Grenadines', 'Saint Vincent and the Grenadines', 'Sail turquoise waters, relax on secluded beaches, and experience island charm in Saint Vincent and the Grenadines.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VCT', 'ar', 'Saint Vincent and the Grenadines', 'Saint Vincent and the Grenadines', 'أبحر في المياه الفيروزية، واسترخِ على الشواطئ المنعزلة، واختبر سحر الجزر في سانت فنسنت والغرينادين.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VCT', 'es', 'Saint Vincent and the Grenadines', 'Saint Vincent and the Grenadines', 'Navega por aguas turquesas, relájate en playas apartadas y vive el encanto isleño en San Vicente y las Granadinas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VCT', 'fr', 'Saint Vincent and the Grenadines', 'Saint Vincent and the Grenadines', 'Naviguez sur des eaux turquoise, détendez-vous sur des plages isolées et découvrez le charme insulaire à Saint-Vincent-et-les-Grenadines.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VCT', 'pt', 'Saint Vincent and the Grenadines', 'Saint Vincent and the Grenadines', 'Navegue por águas turquesa, relaxe em praias isoladas e experimente o charme das ilhas em São Vicente e Granadinas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VCT', 'ru', 'Saint Vincent and the Grenadines', 'Saint Vincent and the Grenadines', 'Путешествуйте по бирюзовым водам, отдыхайте на уединённых пляжах и ощутите очарование островов Сент-Винсент и Гренадины.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VCT', 'de', 'Saint Vincent and the Grenadines', 'Saint Vincent and the Grenadines', 'Segeln Sie auf türkisfarbenem Wasser, entspannen Sie an abgelegenen Stränden und erleben Sie den Inselcharme von St. Vincent und die Grenadinen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VCT', 'it', 'Saint Vincent and the Grenadines', 'Saint Vincent and the Grenadines', 'Naviga in acque turchesi, rilassati su spiagge appartate e vivi il fascino isolano di Saint Vincent e Grenadine.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TTO', 'en', 'Trinidad and Tobago', 'Trinidad and Tobago', 'Celebrate Carnival, savor diverse cuisine, and explore rainforests and coral reefs in lively Trinidad and Tobago.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TTO', 'ar', 'Trinidad and Tobago', 'Trinidad and Tobago', 'احتفل بالكرنفال، وتذوق المأكولات المتنوعة، واستكشف الغابات المطيرة والشعاب المرجانية في ترينيداد وتوباغو النابضة بالحياة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TTO', 'es', 'Trinidad and Tobago', 'Trinidad and Tobago', 'Celebra el Carnaval, disfruta de la cocina diversa y explora selvas y arrecifes de coral en la animada Trinidad y Tobago.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TTO', 'fr', 'Trinidad and Tobago', 'Trinidad and Tobago', 'Célébrez le carnaval, savourez une cuisine variée et explorez forêts tropicales et récifs coralliens à Trinidad-et-Tobago.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TTO', 'pt', 'Trinidad and Tobago', 'Trinidad and Tobago', 'Celebre o Carnaval, saboreie a culinária diversificada e explore florestas tropicais e recifes de coral na animada Trinidad e Tobago.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TTO', 'ru', 'Trinidad and Tobago', 'Trinidad and Tobago', 'Погрузитесь в карнавал, попробуйте разнообразную кухню и исследуйте тропические леса и коралловые рифы в ярком Тринидаде и Тобаго.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TTO', 'de', 'Trinidad and Tobago', 'Trinidad and Tobago', 'Feiern Sie Karneval, genießen Sie vielfältige Küche und erkunden Sie Regenwälder und Korallenriffe im lebhaften Trinidad und Tobago.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TTO', 'it', 'Trinidad and Tobago', 'Trinidad and Tobago', 'Celebra il Carnevale, gusta la cucina variegata ed esplora foreste pluviali e barriere coralline nella vivace Trinidad e Tobago.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'en', 'Argentina', 'Argentina', 'Experience passionate Argentina with tango rhythms, world-class wine, and dramatic landscapes. From Buenos Aires sophistication to Patagonia wilderness, discover South American elegance.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'ar', 'Argentina', 'Argentina', 'استمتع بالأرجنتين العاطفية مع إيقاعات التانغو والنبيذ عالي الجودة والمناظر الطبيعية المذهلة. من أناقة بوينوس آيرس إلى برية باتاغونيا، اكتشف الأناقة الأمريكية الجنوبية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'es', 'Argentina', 'Argentina', 'Experimenta la apasionada Argentina con ritmos de tango, vinos de clase mundial y paisajes dramáticos. Desde la sofisticación de Buenos Aires hasta la naturaleza de la Patagonia, descubre elegancia sudamericana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'fr', 'Argentina', 'Argentina', 'Découvrez l''Argentine passionnée avec rythmes de tango, vins de classe mondiale et paysages dramatiques. De la sophistication de Buenos Aires à la nature sauvage de Patagonie, explorez l''élégance sud-américaine.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'pt', 'Argentina', 'Argentina', 'Experimente a apaixonada Argentina com ritmos de tango, vinhos de classe mundial e paisagens dramáticas. Da sofisticação de Buenos Aires à natureza selvagem da Patagônia, descubra elegância sul-americana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'ru', 'Argentina', 'Argentina', 'Ощутите страстную Аргентину с ритмами танго, мировыми винами и драматичными пейзажами. От современности Буэнос-Айреса до дикой Патагонии, откройте южноамериканскую элегантность.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'de', 'Argentina', 'Argentina', 'Erleben Sie das leidenschaftliche Argentinien mit Tango-Rhythmen, Weltklasse-Weinen und dramatischen Landschaften. Von der Raffinesse Buenos Aires bis zur Wildnis Patagoniens, entdecken Sie südamerikanische Eleganz.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ARG', 'it', 'Argentina', 'Argentina', 'Vivi l''appassionata Argentina con ritmi di tango, vini di classe mondiale e paesaggi drammatici. Dalla sofisticazione di Buenos Aires alla natura selvaggia della Patagonia, scopri eleganza sudamericana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BOL', 'en', 'Bolivia', 'Bolivia', 'Explore Bolivia’s Andean peaks, salt flats, and vibrant indigenous cultures in the heart of South America.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BOL', 'ar', 'Bolivia', 'Bolivia', 'استكشف قمم الأنديز، والملّاحات، والثقافات الأصلية النابضة في قلب أمريكا الجنوبية في بوليفيا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BOL', 'es', 'Bolivia', 'Bolivia', 'Explora los picos andinos, los salares y las culturas indígenas vibrantes en el corazón de Sudamérica: Bolivia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BOL', 'fr', 'Bolivia', 'Bolivia', 'Explorez les sommets andins, les salines et les cultures indigènes vibrantes au cœur de l’Amérique du Sud en Bolivie.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BOL', 'pt', 'Bolivia', 'Bolivia', 'Explore os picos andinos, salares e culturas indígenas vibrantes no coração da América do Sul: Bolívia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BOL', 'ru', 'Bolivia', 'Bolivia', 'Исследуйте анды, солончаки и яркие культуры коренных народов в самом сердце Южной Америки — Боливии.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BOL', 'de', 'Bolivia', 'Bolivia', 'Erkunden Sie Andengipfel, Salzseen und lebendige indigene Kulturen im Herzen Südamerikas: Bolivien.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BOL', 'it', 'Bolivia', 'Bolivia', 'Esplora le vette andine, i salar e le culture indigene vibranti nel cuore del Sud America: la Bolivia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'en', 'Brazil', 'Brazil', 'Experience vibrant Brazil with Amazon rainforest, stunning beaches, and infectious energy. From Rio carnival to São Paulo culture, discover the heart of South America.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'ar', 'Brazil', 'Brazil', 'استمتع ببرازيل النابضة بالحياة مع غابات الأمازون المطيرة والشواطئ الخلابة والطاقة المعدية. من كرنفال ريو إلى ثقافة سان باولو، اكتشف قلب أمريكا الجنوبية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'es', 'Brazil', 'Brazil', 'Experimenta el vibrante Brasil con la selva amazónica, playas impresionantes y energía contagiosa. Desde el carnaval de Río hasta la cultura de São Paulo, descubre el corazón de Sudamérica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'fr', 'Brazil', 'Brazil', 'Découvrez le Brésil vibrant avec la forêt amazonienne, plages magnifiques et énergie contagieuse. Du carnaval de Rio à la culture de São Paulo, explorez le cœur de l''Amérique du Sud.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'pt', 'Brazil', 'Brazil', 'Experimente o vibrante Brasil com floresta amazônica, praias deslumbrantes e energia contagiante. Do carnaval do Rio à cultura de São Paulo, descubra o coração da América do Sul.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'ru', 'Brazil', 'Brazil', 'Ощутите яркую Бразилию с тропическими лесами Амазонки, потрясающими пляжами и заразительной энергией. От карнавала в Рио до культуры Сан-Паулу, откройте сердце Южной Америки.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'de', 'Brazil', 'Brazil', 'Erleben Sie das lebendige Brasilien mit Amazonas-Regenwald, atemberaubenden Stränden und ansteckender Energie. Vom Rio-Karneval bis zur São Paulo-Kultur, entdecken Sie das Herz Südamerikas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRA', 'it', 'Brazil', 'Brazil', 'Vivi il vibrante Brasile con la foresta amazzonica, spiagge mozzafiato ed energia contagiosa. Dal carnevale di Rio alla cultura di São Paulo, scopri il cuore del Sud America.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'en', 'Chile', 'Chile', 'From the Atacama Desert to Patagonian glaciers, explore Chile''s dramatic landscapes, world-class wines, and vibrant cities like Santiago and Valparaíso.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'ar', 'Chile', 'Chile', 'من صحراء أتاكاما إلى أنهار باتاجونيا الجليدية، استكشف المناظر الطبيعية الدرامية في تشيلي والنبيذ عالمي المستوى والمدن النابضة مثل سانتياغو وفالبارايسو.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'es', 'Chile', 'Chile', 'Desde el desierto de Atacama hasta los glaciares patagónicos, explora los dramáticos paisajes de Chile, vinos de clase mundial y ciudades vibrantes como Santiago y Valparaíso.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'fr', 'Chile', 'Chile', 'Du désert d''Atacama aux glaciers patagons, explorez paysages dramatiques du Chili, vins de classe mondiale et villes vibrantes comme Santiago et Valparaíso.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'pt', 'Chile', 'Chile', 'Do deserto do Atacama às geleiras patagônicas, explore paisagens dramáticas do Chile, vinhos de classe mundial e cidades vibrantes como Santiago e Valparaíso.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'ru', 'Chile', 'Chile', 'От пустыни Атакама до патагонских ледников, исследуйте драматические пейзажи Чили, вина мирового класса и яркие города как Сантьяго и Вальпараисо.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'de', 'Chile', 'Chile', 'Von der Atacama-Wüste bis zu patagonischen Gletschern, erkunden Sie Chiles dramatische Landschaften, Weltklasse-Weine und lebendige Städte wie Santiago und Valparaíso.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHL', 'it', 'Chile', 'Chile', 'Dal deserto di Atacama ai ghiacciai della Patagonia, esplora i paesaggi drammatici del Cile, vini di classe mondiale e città vibranti come Santiago e Valparaíso.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'en', 'Colombia', 'Colombia', 'Experience vibrant Colombia from Caribbean beaches to coffee highlands. Explore colorful Cartagena, bustling Bogotá, and the magic of emerald country with warm hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'ar', 'Colombia', 'Colombia', 'استمتع بكولومبيا النابضة من شواطئ البحر الكاريبي إلى مرتفعات القهوة. استكشف كارتاخينا الملونة وبوغوتا الصاخبة وسحر بلاد الزمرد بضيافة دافئة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'es', 'Colombia', 'Colombia', 'Experimenta la vibrante Colombia desde playas caribeñas hasta tierras altas del café. Explora la colorida Cartagena, la bulliciosa Bogotá y la magia del país esmeralda con cálida hospitalidad.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'fr', 'Colombia', 'Colombia', 'Vivez la Colombie vibrante des plages caraïbes aux hauts plateaux de café. Explorez Cartagena colorée, Bogotá bouillonnante et la magie du pays d''émeraude avec hospitalité chaleureuse.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'pt', 'Colombia', 'Colombia', 'Experimente a vibrante Colômbia desde praias do Caribe até terras altas do café. Explore Cartagena colorida, Bogotá movimentada e a magia do país esmeralda com hospitalidade calorosa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'ru', 'Colombia', 'Colombia', 'Почувствуйте яркую Колумбию от карибских пляжей до кофейных высокогорий. Исследуйте яркую Картахену, шумную Боготу и магию изумрудной страны с тёплой гостеприимством.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'de', 'Colombia', 'Colombia', 'Erleben Sie das lebendige Kolumbien von karibischen Stränden bis zu Kaffee-Hochland. Erkunden Sie farbenfrohes Cartagena, geschäftiges Bogotá und die Magie des Smaragdlandes mit herzlicher Gastfreundschaft.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('COL', 'it', 'Colombia', 'Colombia', 'Vivi la vibrante Colombia dalle spiagge caraibiche agli altipiani del caffè. Esplora la colorata Cartagena, la vivace Bogotá e la magia del paese dello smeraldo con calda ospitalità.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'en', 'Ecuador', 'Ecuador', 'Discover Ecuador''s incredible diversity from Amazon rainforest to Andean peaks and the legendary Galápagos Islands. Experience colonial Quito and vibrant indigenous culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'ar', 'Ecuador', 'Ecuador', 'اكتشف التنوع المدهش في الإكوادور من غابات الأمازون إلى قمم الأنديز وجزر غالاباغوس الأسطورية. استمتع بكيتو الاستعمارية والثقافة الأصيلة النابضة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'es', 'Ecuador', 'Ecuador', 'Descubre la increíble diversidad de Ecuador desde la selva amazónica hasta picos andinos y las legendarias islas Galápagos. Experimenta la colonial Quito y vibrante cultura indígena.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'fr', 'Ecuador', 'Ecuador', 'Découvrez l''incroyable diversité de l''Equateur de la forêt amazonienne aux pics andins et légendaires îles Galápagos. Vivez Quito coloniale et culture indigène vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'pt', 'Ecuador', 'Ecuador', 'Descubra a incrível diversidade do Equador desde floresta amazônica até picos andinos e as lendárias ilhas Galápagos. Experimente Quito colonial e cultura indígena vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'ru', 'Ecuador', 'Ecuador', 'Откройте невероятное разнообразие Эквадора от амазонских тропических лесов до андских пиков и легендарных островов Галапагос. Ощутите колониальное Кито и яркую культуру коренных народов.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'de', 'Ecuador', 'Ecuador', 'Entdecken Sie Ecuadors unglaubliche Vielfalt vom Amazonas-Regenwald bis zu Anden-Gipfeln und den legendären Galápagos-Inseln. Erleben Sie koloniales Quito und lebendige indigene Kultur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ECU', 'it', 'Ecuador', 'Ecuador', 'Scopri la straordinaria diversità dell’Ecuador: dalla foresta amazzonica alle Ande e alle leggendarie Galápagos. Vivi Quito coloniale e la vibrante cultura indigena.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GUY', 'en', 'Guyana', 'Guyana', 'Journey to Guyana’s wild rainforests, mighty rivers, and multicultural heritage in South America’s hidden gem.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GUY', 'ar', 'Guyana', 'Guyana', 'اكتشف غابات غيانا المطيرة البرية، والأنهار القوية، والتراث متعدد الثقافات في جوهرة أمريكا الجنوبية الخفية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GUY', 'es', 'Guyana', 'Guyana', 'Viaja a las selvas salvajes, los grandes ríos y el patrimonio multicultural de Guyana, la joya escondida de Sudamérica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GUY', 'fr', 'Guyana', 'Guyana', 'Explorez les forêts tropicales sauvages, les puissants fleuves et le patrimoine multiculturel de la Guyana, joyau caché de l’Amérique du Sud.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GUY', 'pt', 'Guyana', 'Guyana', 'Explore as florestas tropicais selvagens, rios poderosos e o patrimônio multicultural da Guiana, joia escondida da América do Sul.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GUY', 'ru', 'Guyana', 'Guyana', 'Откройте для себя дикие тропические леса, могучие реки и мультикультурное наследие Гайаны — скрытой жемчужины Южной Америки.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GUY', 'de', 'Guyana', 'Guyana', 'Erkunden Sie wilde Regenwälder, mächtige Flüsse und das multikulturelle Erbe Guyanas, dem verborgenen Juwel Südamerikas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GUY', 'it', 'Guyana', 'Guyana', 'Scopri le foreste pluviali selvagge, i grandi fiumi e il patrimonio multiculturale della Guyana, gioiello nascosto del Sud America.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRY', 'en', 'Paraguay', 'Paraguay', 'Experience Paraguay’s rivers, subtropical forests, and unique blend of Guaraní and Spanish cultures in the heart of South America.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRY', 'ar', 'Paraguay', 'Paraguay', 'استمتع بنهري باراغواي، والغابات شبه الاستوائية، والمزيج الفريد بين ثقافة الغواراني والإسبانية في قلب أمريكا الجنوبية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRY', 'es', 'Paraguay', 'Paraguay', 'Vive los ríos, los bosques subtropicales y la mezcla única de culturas guaraní y española en el corazón de Sudamérica: Paraguay.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRY', 'fr', 'Paraguay', 'Paraguay', 'Découvrez les rivières, forêts subtropicales et le mélange unique des cultures guarani et espagnole au cœur de l’Amérique du Sud : le Paraguay.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRY', 'pt', 'Paraguay', 'Paraguay', 'Viva os rios, as florestas subtropicais e a mistura única das culturas guarani e espanhola no coração da América do Sul: Paraguai.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRY', 'ru', 'Paraguay', 'Paraguay', 'Познайте реки, субтропические леса и уникальное сочетание культур гуарани и испанцев в самом сердце Южной Америки — Парагвае.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRY', 'de', 'Paraguay', 'Paraguay', 'Erleben Sie Paraguays Flüsse, subtropische Wälder und die einzigartige Mischung aus Guaraní- und spanischer Kultur im Herzen Südamerikas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRY', 'it', 'Paraguay', 'Paraguay', 'Vivi i fiumi, le foreste subtropicali e l’unico mix di culture guaraní e spagnola nel cuore del Sud America: il Paraguay.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'en', 'Peru', 'Peru', 'Discover ancient wonders like Machu Picchu, the mysterious Nazca Lines, and the vibrant culture of the Andes in this mystical land.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'ar', 'Peru', 'Peru', 'اكتشف العجائب القديمة مثل ماتشو بيتشو وخطوط النازكا الغامضة والثقافة النابضة بالحياة في الأنديز في هذه الأرض الساحرة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'es', 'Peru', 'Peru', 'Descubre maravillas ancestrales como Machu Picchu, las misteriosas Líneas de Nazca y la vibrante cultura andina en esta tierra mística.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'fr', 'Peru', 'Peru', 'Découvrez les merveilles antiques comme Machu Picchu, les mystérieuses lignes de Nazca et la culture vibrante des Andes dans cette terre mystique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'pt', 'Peru', 'Peru', 'Descubra maravilhas antigas como Machu Picchu, as misteriosas Linhas de Nazca e a cultura vibrante dos Andes nesta terra mística.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'ru', 'Peru', 'Peru', 'Откройте для себя древние чудеса, такие как Мачу-Пикчу, загадочные линии Наска и яркую культуру Анд в этой мистической стране.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'de', 'Peru', 'Peru', 'Entdecken Sie antike Wunder wie Machu Picchu, die geheimnisvollen Nazca-Linien und die lebendige Kultur der Anden in diesem mystischen Land.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PER', 'it', 'Peru', 'Peru', 'Scopri meraviglie antiche come Machu Picchu, le misteriose Linee di Nazca e la vibrante cultura andina in questa terra mistica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SUR', 'en', 'Suriname', 'Suriname', 'Explore Suriname’s tropical rainforests, diverse wildlife, and a unique blend of cultures in South America’s smallest country.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SUR', 'ar', 'Suriname', 'Suriname', 'استكشف الغابات المطيرة الاستوائية في سورينام، والحياة البرية المتنوعة، والمزيج الثقافي الفريد في أصغر دولة في أمريكا الجنوبية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SUR', 'es', 'Suriname', 'Suriname', 'Explora las selvas tropicales, la fauna diversa y la mezcla única de culturas en Surinam, el país más pequeño de Sudamérica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SUR', 'fr', 'Suriname', 'Suriname', 'Explorez les forêts tropicales, la faune variée et le mélange unique de cultures du Suriname, le plus petit pays d’Amérique du Sud.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SUR', 'pt', 'Suriname', 'Suriname', 'Explore as florestas tropicais, a vida selvagem diversa e a mistura única de culturas do Suriname, o menor país da América do Sul.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SUR', 'ru', 'Suriname', 'Suriname', 'Исследуйте тропические леса, разнообразную природу и уникальное смешение культур Суринама — самой маленькой страны Южной Америки.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SUR', 'de', 'Suriname', 'Suriname', 'Erkunden Sie tropische Regenwälder, vielfältige Tierwelt und die einzigartige Mischung von Kulturen in Suriname, dem kleinsten Land Südamerikas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SUR', 'it', 'Suriname', 'Suriname', 'Esplora le foreste pluviali tropicali, la fauna variegata e l’unico mix di culture del Suriname, il più piccolo paese del Sud America.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('URY', 'en', 'Uruguay', 'Uruguay', 'Relax on Uruguay’s Atlantic beaches, enjoy Montevideo’s vibrant culture, and savor world-famous beef in this laid-back South American nation.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('URY', 'ar', 'Uruguay', 'Uruguay', 'استرخِ على شواطئ الأوروغواي الأطلسية، واستمتع بثقافة مونتيفيديو النابضة، وتذوق اللحم البقري الشهير في هذه الدولة الهادئة بأمريكا الجنوبية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('URY', 'es', 'Uruguay', 'Uruguay', 'Relájate en las playas atlánticas de Uruguay, disfruta de la cultura vibrante de Montevideo y saborea la carne de res famosa en este país relajado de Sudamérica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('URY', 'fr', 'Uruguay', 'Uruguay', 'Détendez-vous sur les plages atlantiques de l’Uruguay, profitez de la culture vibrante de Montevideo et savourez le bœuf renommé dans cette nation sud-américaine paisible.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('URY', 'pt', 'Uruguay', 'Uruguay', 'Relaxe nas praias do Atlântico do Uruguai, aproveite a cultura vibrante de Montevidéu e saboreie a famosa carne bovina neste país descontraído da América do Sul.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('URY', 'ru', 'Uruguay', 'Uruguay', 'Отдохните на атлантических пляжах Уругвая, насладитесь яркой культурой Монтевидео и попробуйте знаменитую говядину в этой спокойной стране Южной Америки.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('URY', 'de', 'Uruguay', 'Uruguay', 'Entspannen Sie an den atlantischen Stränden Uruguays, genießen Sie die lebendige Kultur von Montevideo und kosten Sie das weltberühmte Rindfleisch in dieser entspannten südamerikanischen Nation.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('URY', 'it', 'Uruguay', 'Uruguay', 'Rilassati sulle spiagge atlantiche dell’Uruguay, goditi la cultura vibrante di Montevideo e assapora la famosa carne bovina in questa nazione sudamericana tranquilla.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VEN', 'en', 'Venezuela', 'Venezuela', 'Marvel at Angel Falls, explore Caribbean beaches, and experience vibrant cities and natural wonders in Venezuela.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VEN', 'ar', 'Venezuela', 'Venezuela', 'اندهش من شلالات آنجل، واستكشف الشواطئ الكاريبية، واختبر المدن النابضة والعجائب الطبيعية في فنزويلا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VEN', 'es', 'Venezuela', 'Venezuela', 'Admira el Salto Ángel, explora playas caribeñas y vive ciudades vibrantes y maravillas naturales en Venezuela.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VEN', 'fr', 'Venezuela', 'Venezuela', 'Admirez le Salto Ángel, explorez les plages caribéennes et vivez des villes vibrantes et des merveilles naturelles au Venezuela.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VEN', 'pt', 'Venezuela', 'Venezuela', 'Maravilhe-se com o Salto Ángel, explore praias caribenhas e experimente cidades vibrantes e maravilhas naturais na Venezuela.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VEN', 'ru', 'Venezuela', 'Venezuela', 'Полюбуйтесь водопадом Анхель, исследуйте карибские пляжи и ощутите яркие города и природные чудеса Венесуэлы.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VEN', 'de', 'Venezuela', 'Venezuela', 'Bestaunen Sie den Salto Ángel, erkunden Sie karibische Strände und erleben Sie lebendige Städte und Naturwunder in Venezuela.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VEN', 'it', 'Venezuela', 'Venezuela', 'Ammira il Salto Ángel, esplora spiagge caraibiche e vivi città vibranti e meraviglie naturali in Venezuela.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AFG', 'en', 'Afghanistan', 'Afghanistan', 'Discover a land of ancient history and stunning landscapes, from the Hindu Kush mountains to the historic city of Balkh.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AFG', 'ar', 'أفغانستان', 'أفغانستان', 'اكتشف أرضًا ذات تاريخ قديم ومناظر طبيعية خلابة، من جبال هندو كوش إلى مدينة بلخ التاريخية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AFG', 'es', 'Afganistán', 'Afganistán', 'Descubre una tierra de historia antigua y paisajes impresionantes, desde las montañas del Hindu Kush hasta la histórica ciudad de Balj.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AFG', 'fr', 'Afghanistan', 'Afghanistan', 'Découvrez une terre d''histoire ancienne et de paysages magnifiques, des montagnes de l''Hindu Kush à la ville historique de Balkh.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AFG', 'pt', 'Afeganistão', 'Afeganistão', 'Descubra uma terra de história antiga e paisagens deslumbrantes, das montanhas do Hindu Kush à cidade histórica de Balkh.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AFG', 'ru', 'Афганистан', 'Афганистан', 'Откройте для себя землю древней истории и потрясающих пейзажей, от гор Гиндукуш до исторического города Балх.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AFG', 'de', 'Afghanistan', 'Afghanistan', 'Entdecken Sie ein Land mit alter Geschichte und atemberaubenden Landschaften, vom Hindukusch-Gebirge bis zur historischen Stadt Balch.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AFG', 'it', 'Afghanistan', 'Afghanistan', 'Scopri una terra di storia antica e paesaggi mozzafiato, dalle montagne dell''Hindu Kush alla storica città di Balkh.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGD', 'en', 'Bangladesh', 'Bangladesh', 'Discover a land of vibrant culture, lush green landscapes, and the world''s largest river delta. Explore the Sundarbans mangrove forest, home of the Bengal tiger.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGD', 'ar', 'بنغلاديش', 'بنغلاديش', 'اكتشف أرضًا ذات ثقافة نابضة بالحياة، ومناظر طبيعية خضراء مورقة، وأكبر دلتا نهر في العالم. استكشف غابة سونداربانس للمانجروف، موطن نمر البنغال.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGD', 'es', 'Bangladés', 'Bangladés', 'Descubre una tierra de cultura vibrante, paisajes verdes y exuberantes y el delta fluvial más grande del mundo. Explora el bosque de manglares de Sundarbans, hogar del tigre de Bengala.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGD', 'fr', 'Bangladesh', 'Bangladesh', 'Découvrez une terre de culture vibrante, de paysages verdoyants et du plus grand delta fluvial du monde. Explorez la forêt de mangroves des Sundarbans, qui abrite le tigre du Bengale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGD', 'pt', 'Bangladexe', 'Bangladexe', 'Descubra uma terra de cultura vibrante, paisagens verdejantes e o maior delta de rio do mundo. Explore a floresta de mangue de Sundarbans, lar do tigre de Bengala.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGD', 'ru', 'Бангладеш', 'Бангладеш', 'Откройте для себя страну яркой культуры, пышных зеленых пейзажей и крупнейшей в мире речной дельты. Исследуйте мангровый лес Сундарбан, родину бенгальского тигра.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGD', 'de', 'Bangladesch', 'Bangladesch', 'Entdecken Sie ein Land mit lebendiger Kultur, üppig grünen Landschaften und dem größten Flussdelta der Welt. Erkunden Sie den Sundarbans-Mangrovenwald, die Heimat des bengalischen Tigers.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGD', 'it', 'Bangladesh', 'Bangladesh', 'Scopri una terra di cultura vibrante, paesaggi lussureggianti e il delta del fiume più grande del mondo. Esplora la foresta di mangrovie delle Sundarbans, patria della tigre del Bengala.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BTN', 'en', 'Bhutan', 'Bhutan', 'Discover the last great Himalayan kingdom, a land of monasteries, fortresses (dzongs) and dramatic landscapes. Experience a culture where Gross National Happiness is paramount.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BTN', 'ar', 'بوتان', 'بوتان', 'اكتشف آخر مملكة عظيمة في جبال الهيمالايا، أرض الأديرة والحصون (دزونغ) والمناظر الطبيعية الدرامية. جرب ثقافة حيث السعادة الوطنية الإجمالية هي الأهم.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BTN', 'es', 'Bután', 'Bután', 'Descubre el último gran reino del Himalaya, una tierra de monasterios, fortalezas (dzongs) y paisajes espectaculares. Experimenta una cultura donde la Felicidad Nacional Bruta es primordial.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BTN', 'fr', 'Bhoutan', 'Bhoutan', 'Découvrez le dernier grand royaume de l''Himalaya, une terre de monastères, de forteresses (dzongs) et de paysages spectaculaires. Découvrez une culture où le bonheur national brut est primordial.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BTN', 'pt', 'Butão', 'Butão', 'Descubra o último grande reino do Himalaia, uma terra de mosteiros, fortalezas (dzongs) e paisagens dramáticas. Experimente uma cultura onde a Felicidade Nacional Bruta é primordial.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BTN', 'ru', 'Бутан', 'Бутан', 'Откройте для себя последнее великое гималайское королевство, страну монастырей, крепостей (дзонгов) и драматических пейзажей. Познакомьтесь с культурой, где валовое национальное счастье имеет первостепенное значение.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BTN', 'de', 'Bhutan', 'Bhutan', 'Entdecken Sie das letzte große Himalaya-Königreich, ein Land der Klöster, Festungen (Dzongs) und dramatischen Landschaften. Erleben Sie eine Kultur, in der das Bruttonationalglück an erster Stelle steht.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BTN', 'it', 'Bhutan', 'Bhutan', 'Scopri l''ultimo grande regno himalayano, una terra di monasteri, fortezze (dzong) e paesaggi spettacolari. Vivi una cultura in cui la Felicità Nazionale Lorda è fondamentale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRN', 'en', 'Brunei', 'Brunei', 'Discover a nation of pristine rainforests, opulent mosques, and the world''s largest water village. Experience the tranquility and rich Islamic heritage of the ''Abode of Peace''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRN', 'ar', 'بروناي', 'بروناي', 'اكتشف أمة من الغابات المطيرة البكر، والمساجد الفخمة، وأكبر قرية مائية في العالم. استمتع بالهدوء والتراث الإسلامي الغني في ''دار السلام''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRN', 'es', 'Brunéi', 'Brunéi', 'Descubre una nación de selvas tropicales vírgenes, opulentas mezquitas y el pueblo acuático más grande del mundo. Experimenta la tranquilidad y el rico patrimonio islámico de la ''Morada de la Paz''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRN', 'fr', 'Brunéi', 'Brunéi', 'Découvrez une nation de forêts tropicales vierges, de mosquées opulentes et du plus grand village sur l''eau du monde. Découvrez la tranquillité et le riche patrimoine islamique de la ''Demeure de la Paix''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRN', 'pt', 'Brunei', 'Brunei', 'Descubra uma nação de florestas tropicais intocadas, mesquitas opulentas e a maior vila aquática do mundo. Experimente a tranquilidade e a rica herança islâmica da ''Morada da Paz''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRN', 'ru', 'Бруней', 'Бруней', 'Откройте для себя страну нетронутых тропических лесов, роскошных мечетей и крупнейшей в мире водной деревни. Ощутите спокойствие и богатое исламское наследие ''Обители мира''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRN', 'de', 'Brunei', 'Brunei', 'Entdecken Sie eine Nation aus unberührten Regenwäldern, opulenten Moscheen und dem größten Wasserdorf der Welt. Erleben Sie die Ruhe und das reiche islamische Erbe der ''Wohnstätte des Friedens''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BRN', 'it', 'Brunei', 'Brunei', 'Scopri una nazione di foreste pluviali incontaminate, opulente moschee e il più grande villaggio sull''acqua del mondo. Vivi la tranquillità e il ricco patrimonio islamico della ''Dimora della Pace''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'en', 'Cambodia', 'Cambodia', 'Explore the magnificent temples of Angkor Wat, floating villages, and vibrant markets. Experience ancient Khmer heritage and warm Cambodian hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'ar', 'Cambodia', 'Cambodia', 'استكشف معابد أنغكور وات الرائعة والقرى العائمة والأسواق النابضة بالحياة. اختبر التراث الخميري القديم والضيافة الكمبودية الدافئة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'es', 'Cambodia', 'Cambodia', 'Explora los magníficos templos de Angkor Wat, pueblos flotantes y mercados vibrantes. Experimenta el patrimonio jemer antiguo y la cálida hospitalidad camboyana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'fr', 'Cambodia', 'Cambodia', 'Explorez les magnifiques temples d''Angkor Vat, villages flottants et marchés vibrants. Découvrez l''héritage khmer ancien et l''hospitalité cambodgienne chaleureuse.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'pt', 'Cambodia', 'Cambodia', 'Explore os magníficos templos de Angkor Wat, aldeias flutuantes e mercados vibrantes. Experimente herança khmer antiga e hospitalidade cambojana calorosa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'ru', 'Cambodia', 'Cambodia', 'Исследуйте великолепные храмы Ангкор-Вата, плавучие деревни и яркие рынки. Ощутите древнее кхмерское наследие и теплое камбоджийское гостеприимство.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'de', 'Cambodia', 'Cambodia', 'Erkunden Sie die prächtigen Tempel von Angkor Wat, schwimmende Dörfer und lebendige Märkte. Erleben Sie altes Khmer-Erbe und warme kambodschanische Gastfreundschaft.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KHM', 'it', 'Cambodia', 'Cambodia', 'Esplora i magnifici templi di Angkor Wat, villaggi galleggianti e mercati vivaci. Vivi l''eredità khmer antica e la calda ospitalità cambogiana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'en', 'China', 'China', 'Discover ancient wonders and modern marvels in the Middle Kingdom. From the Great Wall to bustling cities, experience 5,000 years of rich culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'ar', 'China', 'China', 'اكتشف عجائب قديمة وإنجازات حديثة في الإمبراطورية الوسطى. من السور العظيم إلى المدن النابضة بالحياة، استمتع بـ 5000 عام من الثقافة الغنية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'es', 'China', 'China', 'Descubre maravillas antiguas y logros modernos en el Reino Medio. Desde la Gran Muralla hasta ciudades vibrantes, experimenta 5,000 años de rica cultura.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'fr', 'China', 'China', 'Découvrez des merveilles anciennes et des exploits modernes dans l''Empire du Milieu. De la Grande Muraille aux villes dynamiques, vivez 5 000 ans de riche culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'pt', 'China', 'China', 'Descubra maravilhas antigas e conquistas modernas no Reino do Meio. Da Grande Muralha às cidades vibrantes, experimente 5.000 anos de rica cultura.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'ru', 'China', 'China', 'Откройте древние чудеса и современные достижения в Поднебесной. От Великой стены до живых городов, познайте 5000 лет богатой культуры.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'de', 'China', 'China', 'Entdecken Sie antike Wunder und moderne Errungenschaften im Reich der Mitte. Von der Großen Mauer bis zu pulsierenden Städten, erleben Sie 5.000 Jahre reiche Kultur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHN', 'it', 'China', 'China', 'Scopri meraviglie antiche e conquiste moderne nell''Impero di Mezzo. Dalla Grande Muraglia alle città vibranti, vivi 5.000 anni di ricca cultura.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TLS', 'en', 'East Timor', 'East Timor', 'Discover one of the world''s newest countries, with rugged mountains, pristine coral reefs, and a unique cultural blend of Southeast Asian and Portuguese influences.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TLS', 'ar', 'تيمور الشرقية', 'تيمور الشرقية', 'اكتشف واحدة من أحدث دول العالم، بجبالها الوعرة، وشعابها المرجانية البكر، ومزيجها الثقافي الفريد من التأثيرات الجنوبية الشرقية الآسيوية والبرتغالية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TLS', 'es', 'Timor Oriental', 'Timor Oriental', 'Descubre uno de los países más nuevos del mundo, con montañas escarpadas, arrecifes de coral vírgenes y una mezcla cultural única de influencias del sudeste asiático y portuguesas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TLS', 'fr', 'Timor oriental', 'Timor oriental', 'Découvrez l''un des plus jeunes pays du monde, avec ses montagnes escarpées, ses récifs coralliens immaculés et son mélange culturel unique d''influences sud-est asiatiques and portugaises.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TLS', 'pt', 'Timor-Leste', 'Timor-Leste', 'Descubra um dos países mais novos do mundo, com montanhas escarpadas, recifes de coral imaculados e uma mistura cultural única de influências do Sudeste Asiático e portuguesas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TLS', 'ru', 'Восточный Тимор', 'Восточный Тимор', 'Откройте для себя одну из самых молодых стран мира с ее скалистыми горами, нетронутыми коралловыми рифами и уникальным культурным сочетанием юго-восточноазиатских и португальских влияний.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TLS', 'de', 'Osttimor', 'Osttimor', 'Entdecken Sie eines der neuesten Länder der Welt mit schroffen Bergen, unberührten Korallenriffen und einer einzigartigen kulturellen Mischung aus südostasiatischen und portugiesischen Einflüssen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TLS', 'it', 'Timor Est', 'Timor Est', 'Scopri uno dei paesi più nuovi al mondo, con montagne aspre, barriere coralline incontaminate e una miscela culturale unica di influenze del sud-est asiatico e portoghesi.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GEO', 'en', 'Georgia', 'Georgia', 'Discover the birthplace of wine, a country of ancient monasteries, stunning Caucasus mountain scenery, and vibrant cities like Tbilisi.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GEO', 'ar', 'جورجيا', 'جورجيا', 'اكتشف مهد النبيذ، بلد الأديرة القديمة، ومناظر جبال القوقاز الخلابة، والمدن النابضة بالحياة مثل تبليسي.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GEO', 'es', 'Georgia', 'Georgia', 'Descubre la cuna del vino, un país de antiguos monasterios, impresionantes paisajes de las montañas del Cáucaso y ciudades vibrantes como Tiflis.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GEO', 'fr', 'Géorgie', 'Géorgie', 'Découvrez le berceau du vin, un pays d''anciens monastères, de paysages montagneux du Caucase époustouflants et de villes dynamiques comme Tbilissi.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GEO', 'pt', 'Geórgia', 'Geórgia', 'Descubra o berço do vinho, um país de mosteiros antigos, paisagens deslumbrantes das montanhas do Cáucaso e cidades vibrantes como Tbilisi.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GEO', 'ru', 'Грузия', 'Грузия', 'Откройте для себя родину вина, страну древних монастырей, потрясающих пейзажей Кавказских гор и ярких городов, таких как Тбилиси.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GEO', 'de', 'Georgien', 'Georgien', 'Entdecken Sie die Wiege des Weins, ein Land der alten Klöster, der atemberaubenden Landschaft des Kaukasus und der pulsierenden Städte wie Tiflis.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GEO', 'it', 'Georgia', 'Georgia', 'Scopri la culla del vino, un paese di antichi monasteri, paesaggi mozzafiato sulle montagne del Caucaso e città vivaci come Tbilisi.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IND', 'en', 'India', 'India', 'Experience the incredible diversity of India, from the Himalayas to tropical beaches. Discover ancient temples, vibrant festivals, and warm hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IND', 'ar', 'India', 'India', 'استمتع بالتنوع المذهل في الهند، من جبال الهيمالايا إلى الشواطئ الاستوائية. اكتشف المعابد القديمة والمهرجانات النابضة بالحياة والضيافة الدافئة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IND', 'es', 'India', 'India', 'Experimenta la increíble diversidad de India, desde el Himalaya hasta playas tropicales. Descubre templos antiguos, festivales vibrantes y cálida hospitalidad.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IND', 'fr', 'India', 'India', 'Vivez l''incroyable diversité de l''Inde, de l''Himalaya aux plages tropicales. Découvrez temples anciens, festivals vibrants et chaleureuse hospitalité.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IND', 'pt', 'India', 'India', 'Experimente a incrível diversidade da Índia, do Himalaia às praias tropicais. Descubra templos antigos, festivais vibrantes e hospitalidade calorosa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IND', 'ru', 'India', 'India', 'Почувствуйте невероятное разнообразие Индии, от Гималаев до тропических пляжей. Откройте древние храмы, яркие фестивали и тёплое гостеприимство.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IND', 'de', 'India', 'India', 'Erleben Sie die unglaubliche Vielfalt Indiens, vom Himalaya bis zu tropischen Stränden. Entdecken Sie alte Tempel, lebendige Festivals und herzliche Gastfreundschaft.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IND', 'it', 'India', 'India', 'Vivi l''incredibile diversità dell''India, dall''Himalaya alle spiagge tropicali. Scopri templi antichi, festival vibranti e calda ospitalità.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IDN', 'en', 'Indonesia', 'Indonesia', 'Discover the world''s largest archipelago with 17,000 islands of paradise. From Bali''s temples to Jakarta''s energy, experience diverse cultures, pristine beaches, and volcanic adventures.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IDN', 'ar', 'Indonesia', 'Indonesia', 'اكتشف أكبر أرخبيل في العالم مع 17,000 جزيرة من الجنة. من معابد بالي إلى طاقة جاكرتا، استمتع بالثقافات المتنوعة والشواطئ البرية ومغامرات البراكين.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IDN', 'es', 'Indonesia', 'Indonesia', 'Descubre el archipiélago más grande del mundo con 17,000 islas del paraíso. Desde templos de Bali hasta energía de Yakarta, experimenta culturas diversas, playas prístinas y aventuras volcánicas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IDN', 'fr', 'Indonesia', 'Indonesia', 'Découvrez le plus grand archipel du monde avec 17,000 îles paradisiaques. Des temples de Bali à l''énergie de Jakarta, vivez cultures diverses, plages vierges et aventures volcaniques.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IDN', 'pt', 'Indonesia', 'Indonesia', 'Descubra o maior arquipélago do mundo com 17,000 ilhas do paraíso. Dos templos de Bali à energia de Jakarta, experimente culturas diversas, praias imaculadas e aventuras vulcânicas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IDN', 'ru', 'Indonesia', 'Indonesia', 'Откройте крупнейший в мире архипелаг с 17,000 островами рая. От храмов Бали до энергии Джакарты, ощутите разнообразные культуры, нетронутые пляжи и вулканические приключения.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IDN', 'de', 'Indonesia', 'Indonesia', 'Entdecken Sie den weltgrößten Archipel mit 17,000 paradiesischen Inseln. Von Balis Tempeln bis Jakartas Energie, erleben Sie vielfältige Kulturen, unberührte Strände und vulkanische Abenteuer.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IDN', 'it', 'Indonesia', 'Indonesia', 'Scopri l''arcipelago più grande del mondo con 17,000 isole paradisiache. Dai templi di Bali all''energia di Jakarta, vivi culture diverse, spiagge incontaminate e avventure vulcaniche.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRN', 'en', 'Iran', 'Iran', 'Discover a land of ancient Persian civilization, with stunning mosques, bustling bazaars, and a rich artistic and literary heritage.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRN', 'ar', 'إيران', 'إيران', 'اكتشف أرض الحضارة الفارسية القديمة، بمساجدها المذهلة، وأسواقها الصاخبة، وتراثها الفني والأدبي الغني.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRN', 'es', 'Irán', 'Irán', 'Descubre una tierra de la antigua civilización persa, con impresionantes mezquitas, bulliciosos bazares y un rico patrimonio artístico y literario.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRN', 'fr', 'Iran', 'Iran', 'Découvrez une terre de l''ancienne civilisation perse, avec des mosquées magnifiques, des bazars animés et un riche patrimoine artistique et littéraire.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRN', 'pt', 'Irão', 'Irão', 'Descubra uma terra da antiga civilização persa, com mesquitas deslumbrantes, bazares movimentados e um rico património artístico e literário.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRN', 'ru', 'Иран', 'Иран', 'Откройте для себя землю древней персидской цивилизации с ее потрясающими мечетями, шумными базарами и богатым художественным и литературным наследием.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRN', 'de', 'Iran', 'Iran', 'Entdecken Sie ein Land der alten persischen Zivilisation mit atemberaubenden Moscheen, geschäftigen Basaren und einem reichen künstlerischen und literarischen Erbe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRN', 'it', 'Iran', 'Iran', 'Scopri una terra dell''antica civiltà persiana, con moschee meravigliose, bazar vivaci e un ricco patrimonio artistico e letterario.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRQ', 'en', 'Iraq', 'Iraq', 'Discover the cradle of civilization, a land of ancient Mesopotamian history, with iconic sites like Babylon and the Great Ziggurat of Ur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRQ', 'ar', 'العراق', 'العراق', 'اكتشف مهد الحضارة، أرض تاريخ بلاد ما بين النهرين القديم، مع مواقع شهيرة مثل بابل وزقورة أور العظيمة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRQ', 'es', 'Irak', 'Irak', 'Descubre la cuna de la civilización, una tierra de la antigua historia de Mesopotamia, con sitios icónicos como Babilonia y el Gran Zigurat de Ur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRQ', 'fr', 'Irak', 'Irak', 'Découvrez le berceau de la civilisation, une terre de l''ancienne histoire de la Mésopotamie, avec des sites emblématiques comme Babylone et la Grande Ziggourat d''Ur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRQ', 'pt', 'Iraque', 'Iraque', 'Descubra o berço da civilização, uma terra da antiga história da Mesopotâmia, com locais icónicos como a Babilónia e o Grande Zigurate de Ur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRQ', 'ru', 'Ирак', 'Ирак', 'Откройте для себя колыбель цивилизации, землю древней истории Месопотамии, с такими знаковыми местами, как Вавилон и Великий зиккурат Ура.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRQ', 'de', 'Irak', 'Irak', 'Entdecken Sie die Wiege der Zivilisation, ein Land der alten mesopotamischen Geschichte, mit ikonischen Stätten wie Babylon und der Großen Zikkurat von Ur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRQ', 'it', 'Iraq', 'Iraq', 'Scopri la culla della civiltà, una terra di antica storia mesopotamica, con siti iconici come Babilonia e la Grande Ziggurat di Ur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISR', 'en', 'Israel', 'Israel', 'Discover a land of ancient history and religious significance, with vibrant cities, stunning coastlines, and the unique experience of floating in the Dead Sea.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISR', 'ar', 'إسرائيل', 'إسرائيل', 'اكتشف أرضًا ذات تاريخ قديم وأهمية دينية، مع مدن نابضة بالحياة، وسواحل مذهلة، وتجربة فريدة من نوعها في الطفو في البحر الميت.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISR', 'es', 'Israel', 'Israel', 'Descubre una tierra de historia antigua y significado religioso, con ciudades vibrantes, costas impresionantes y la experiencia única de flotar en el Mar Muerto.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISR', 'fr', 'Israël', 'Israël', 'Découvrez une terre d''histoire ancienne et de signification religieuse, avec des villes dynamiques, des côtes magnifiques et l''expérience unique de flotter dans la mer Morte.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISR', 'pt', 'Israel', 'Israel', 'Descubra uma terra de história antiga e significado religioso, com cidades vibrantes, costas deslumbrantes e a experiência única de flutuar no Mar Morto.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISR', 'ru', 'Израиль', 'Израиль', 'Откройте для себя землю древней истории и религиозного значения, с яркими городами, потрясающими побережьями и уникальным опытом плавания в Мертвом море.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISR', 'de', 'Israel', 'Israel', 'Entdecken Sie ein Land mit alter Geschichte und religiöser Bedeutung, mit pulsierenden Städten, atemberaubenden Küsten und dem einzigartigen Erlebnis, im Toten Meer zu schweben.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISR', 'it', 'Israele', 'Israele', 'Scopri una terra di storia antica e significato religioso, con città vivaci, coste meravigliose e l''esperienza unica di galleggiare nel Mar Morto.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JPN', 'en', 'Japan', 'Japan', 'Experience where ancient traditions meet cutting-edge technology. Discover serene temples, vibrant cities, and breathtaking landscapes from Tokyo to Kyoto.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JPN', 'ar', 'اليابان', 'اليابان', 'اختبر حيث تلتقي التقاليد القديمة مع التكنولوجيا المتطورة. اكتشف المعابد الهادئة والمدن النابضة بالحياة والمناظر الطبيعية الخلابة من طوكيو إلى كيوتو.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JPN', 'es', 'Japón', 'Japón', 'Experimenta donde las tradiciones ancestrales se encuentran con la tecnología de vanguardia. Descubre templos serenos, ciudades vibrantes y paisajes impresionantes desde Tokio hasta Kioto.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JPN', 'fr', 'Japon', 'Japon', 'Découvrez où les traditions ancestrales rencontrent la technologie de pointe. Explorez des temples sereins, des villes dynamiques et des paysages à couper le souffle de Tokyo à Kyoto.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JPN', 'pt', 'Japão', 'Japão', 'Experimente onde tradições ancestrais encontram tecnologia de ponta. Descubra templos serenos, cidades vibrantes e paisagens deslumbrantes de Tóquio a Quioto.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JPN', 'ru', 'Япония', 'Япония', 'Ощутите, где древние традиции встречаются с передовыми технологиями. Откройте для себя безмятежные храмы, яркие города и захватывающие пейзажи от Токио до Киото.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JPN', 'de', 'Japan', 'Japan', 'Erleben Sie, wo alte Traditionen auf modernste Technologie treffen. Entdecken Sie ruhige Tempel, lebendige Städte und atemberaubende Landschaften von Tokyo bis Kyoto.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JPN', 'it', 'Giappone', 'Giappone', 'Scopri dove le antiche tradizioni incontrano la tecnologia all''avanguardia. Esplora templi sereni, città vibranti e paesaggi mozzafiato da Tokyo a Kyoto.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JOR', 'en', 'Jordan', 'Jordan', 'Explore ancient wonders and timeless beauty in this crossroads of civilizations. Discover Petra, the Dead Sea, and warm Jordanian hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JOR', 'ar', 'الأردن', 'الأردن', 'استكشف عجائب قديمة وجمالاً خالداً في هذا ملتقى الحضارات. اكتشف البتراء والبحر الميت والضيافة الأردنية الدافئة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JOR', 'es', 'Jordania', 'Jordania', 'Explora maravillas ancestrales y belleza atemporal en esta encrucijada de civilizaciones. Descubre Petra, el Mar Muerto y la cálida hospitalidad jordana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JOR', 'fr', 'Jordanie', 'Jordanie', 'Explorez des merveilles anciennes et une beauté intemporelle au carrefour des civilisations. Découvrez Pétra, la mer Morte et la chaleureuse hospitalité jordanienne.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JOR', 'pt', 'Jordânia', 'Jordânia', 'Explore maravilhas antigas e beleza atemporal nesta encruzilhada de civilizações. Descubra Petra, o Mar Morto e a calorosa hospitalidade jordaniana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JOR', 'ru', 'Иордания', 'Иордания', 'Исследуйте древние чудеса и вечную красоту на этом перекрёстке цивилизаций. Откройте Петру, Мёртвое море и тёплое иорданское гостеприимство.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JOR', 'de', 'Jordanien', 'Jordanien', 'Erkunden Sie antike Wunder und zeitlose Schönheit an diesem Scheideweg der Zivilisationen. Entdecken Sie Petra, das Tote Meer und die warme jordanische Gastfreundschaft.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('JOR', 'it', 'Giordania', 'Giordania', 'Esplora meraviglie antiche e bellezza senza tempo in questo crocevia di civiltà. Scopri Petra, il Mar Morto e la calorosa ospitalità giordana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KAZ', 'en', 'Kazakhstan', 'Kazakhstan', 'Discover the world''s largest landlocked country, a land of vast steppes, modern cities, and the stunning natural beauty of the Charyn Canyon.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KAZ', 'ar', 'كازاخستان', 'كازاخستان', 'اكتشف أكبر دولة غير ساحلية في العالم، أرض السهوب الشاسعة، والمدن الحديثة، والجمال الطبيعي المذهل لوادي تشارين.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KAZ', 'es', 'Kazajistán', 'Kazajistán', 'Descubre el país sin salida al mar más grande del mundo, una tierra de vastas estepas, ciudades modernas y la impresionante belleza natural del Cañón de Charyn.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KAZ', 'fr', 'Kazakhstan', 'Kazakhstan', 'Découvrez le plus grand pays enclavé du monde, une terre de vastes steppes, de villes modernes et de la beauté naturelle époustouflante du canyon de Charyn.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KAZ', 'pt', 'Cazaquistão', 'Cazaquistão', 'Descubra o maior país sem litoral do mundo, uma terra de vastas estepes, cidades modernas e a deslumbrante beleza natural do Cânion Charyn.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KAZ', 'ru', 'Казахстан', 'Казахстан', 'Откройте для себя самую большую страну в мире, не имеющую выхода к морю, страну бескрайних степей, современных городов и потрясающей природной красоты Чарынского каньона.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KAZ', 'de', 'Kasachstan', 'Kasachstan', 'Entdecken Sie das größte Binnenland der Welt, ein Land der weiten Steppen, modernen Städte und der atemberaubenden natürlichen Schönheit des Charyn-Canyons.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KAZ', 'it', 'Kazakistan', 'Kazakistan', 'Scopri il più grande paese senza sbocco sul mare del mondo, una terra di vaste steppe, città moderne e la straordinaria bellezza naturale del Canyon di Charyn.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'en', 'Kuwait', 'Kuwait', 'Experience the pearl of the Gulf with modern architecture, rich maritime heritage, and vibrant souks blending tradition with prosperity.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'ar', 'الكويت', 'الكويت', 'اختبر درة الخليج بهندستها المعمارية الحديثة وتراثها البحري الغني وأسواقها النابضة التي تمزج التراث مع الازدهار.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'es', 'Kuwait', 'Kuwait', 'Experimenta la perla del Golfo con arquitectura moderna, rico patrimonio marítimo y zocos vibrantes que combinan tradición con prosperidad.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'fr', 'Koweït', 'Koweït', 'Découvrez la perle du Golfe avec son architecture moderne, son riche patrimoine maritime et ses souks vibrants mélant tradition et prospérité.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'pt', 'Kuwait', 'Kuwait', 'Experimente a pérola do Golfo com arquitetura moderna, rico patrimônio marítimo e souks vibrantes que misturam tradição com prosperidade.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'ru', 'Кувейт', 'Кувейт', 'Ощутите жемчужину Персидского залива с современной архитектурой, богатым морским наследием и яркими базарами, сочетающими традиции с процветанием.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'de', 'Kuwait', 'Kuwait', 'Erleben Sie die Perle des Golfs mit moderner Architektur, reichem maritimen Erbe und lebendigen Souks, die Tradition mit Wohlstand verbinden.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KWT', 'it', 'Kuwait', 'Kuwait', 'Vivi la perla del Golfo con architettura moderna, ricco patrimonio marittimo e souk vibranti che fondono tradizione e prosperità.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KGZ', 'en', 'Kyrgyzstan', 'Kyrgyzstan', 'Discover the ''Switzerland of Central Asia'', a land of celestial mountains, alpine lakes, and a rich nomadic heritage.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KGZ', 'ar', 'قيرغيزستان', 'قيرغيزستان', 'اكتشف ''سويسرا آسيا الوسطى''، أرض الجبال السماوية، والبحيرات الألبية، والتراث البدوي الغني.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KGZ', 'es', 'Kirguistán', 'Kirguistán', 'Descubre la ''Suiza de Asia Central'', una tierra de montañas celestiales, lagos alpinos y una rica herencia nómada.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KGZ', 'fr', 'Kirghizistan', 'Kirghizistan', 'Découvrez la ''Suisse de l''Asie centrale'', une terre de montagnes célestes, de lacs alpins et d''un riche héritage nomade.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KGZ', 'pt', 'Quirguistão', 'Quirguistão', 'Descubra a ''Suíça da Ásia Central'', uma terra de montanhas celestiais, lagos alpinos e uma rica herança nómada.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KGZ', 'ru', 'Кыргызстан', 'Кыргызстан', 'Откройте для себя ''Швейцарию Центральной Азии'', страну небесных гор, альпийских озер и богатого кочевого наследия.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KGZ', 'de', 'Kirgisistan', 'Kirgisistan', 'Entdecken Sie die ''Schweiz Zentralasiens'', ein Land der himmlischen Berge, Alpenseen und eines reichen nomadischen Erbes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KGZ', 'it', 'Kirghizistan', 'Kirghizistan', 'Scopri la ''Svizzera dell''Asia centrale'', una terra di montagne celesti, laghi alpini e una ricca eredità nomade.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LAO', 'en', 'Laos', 'Laos', 'Discover a land of tranquil beauty, with ancient temples, French colonial architecture, and the mighty Mekong River. Experience the laid-back charm of Luang Prabang.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LAO', 'ar', 'لاوس', 'لاوس', 'اكتشف أرض الجمال الهادئ، بمعابدها القديمة، وهندستها المعمارية الاستعمارية الفرنسية، ونهر الميكونغ العظيم. استمتع بسحر لوانغ برابانغ المريح.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LAO', 'es', 'Laos', 'Laos', 'Descubre una tierra de belleza tranquila, con templos antiguos, arquitectura colonial francesa y el poderoso río Mekong. Experimenta el encanto relajado de Luang Prabang.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LAO', 'fr', 'Laos', 'Laos', 'Découvrez une terre d''une beauté tranquille, avec des temples anciens, une architecture coloniale française et le puissant fleuve Mékong. Découvrez le charme décontracté de Luang Prabang.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LAO', 'pt', 'Laos', 'Laos', 'Descubra uma terra de beleza tranquila, com templos antigos, arquitetura colonial francesa e o poderoso rio Mekong. Experimente o charme descontraído de Luang Prabang.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LAO', 'ru', 'Лаос', 'Лаос', 'Откройте для себя страну спокойной красоты с древними храмами, французской колониальной архитектурой и могучей рекой Меконг. Ощутите непринужденное очарование Луангпхабанга.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LAO', 'de', 'Laos', 'Laos', 'Entdecken Sie ein Land von ruhiger Schönheit mit alten Tempeln, französischer Kolonialarchitektur und dem mächtigen Mekong. Erleben Sie den entspannten Charme von Luang Prabang.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LAO', 'it', 'Laos', 'Laos', 'Scopri una terra di tranquilla bellezza, con antichi templi, architettura coloniale francese e il possente fiume Mekong. Vivi il fascino rilassato di Luang Prabang.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBN', 'en', 'Lebanon', 'Lebanon', 'Discover the Switzerland of the Middle East with stunning mountains, Mediterranean coastline, rich history, and legendary hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBN', 'ar', 'لبنان', 'لبنان', 'اكتشف سويسرا الشرق الأوسط بجبالها المذهلة وساحل البحر المتوسط وتاريخها الغني وضيافتها الأسطورية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBN', 'es', 'Líbano', 'Líbano', 'Descubre la Suiza del Medio Oriente con montañas impresionantes, costa mediterránea, rica historia y hospitalidad legendaria.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBN', 'fr', 'Liban', 'Liban', 'Découvrez la Suisse du Moyen-Orient avec ses montagnes époustouflantes, sa côte méditerranéenne, sa riche histoire et son hospitalité légendaire.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBN', 'pt', 'Líbano', 'Líbano', 'Descubra a Suíça do Oriente Médio com montanhas deslumbrantes, costa mediterrânea, rica história e hospitalidade lendária.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBN', 'ru', 'Ливан', 'Ливан', 'Откройте для себя Швейцарию Ближнего Востока с потрясающими горами, средиземноморским побережьем, богатой историей и легендарным гостеприимством.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBN', 'de', 'Libanon', 'Libanon', 'Entdecken Sie die Schweiz des Nahen Ostens mit atemberaubenden Bergen, mediterraner Küste, reicher Geschichte und legendärer Gastfreundschaft.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LBN', 'it', 'Libano', 'Libano', 'Scopri la Svizzera del Medio Oriente con montagne mozzafiato, costa mediterranea, ricca storia e ospitalità leggendaria.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MYS', 'en', 'Malaysia', 'Malaysia', 'Discover Malaysia where diverse cultures meet. From Kuala Lumpur skyscrapers to pristine beaches and rainforests, enjoy incredible food and warm hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MYS', 'ar', 'Malaysia', 'Malaysia', 'اكتشف ماليزيا حيث تلتقي الثقافات المتنوعة. من ناطحات سحاب كوالالمبور إلى الشواطئ النظيفة والغابات المطيرة، استمتع بالطعام الرائع والضيافة الدافئة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MYS', 'es', 'Malaysia', 'Malaysia', 'Descubre Malasia donde se encuentran culturas diversas. Desde los rascacielos de Kuala Lumpur hasta playas prístinas y selvas tropicales, disfruta comida increíble y cálida hospitalidad.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MYS', 'fr', 'Malaysia', 'Malaysia', 'Découvrez la Malaisie où se rencontrent diverses cultures. Des gratte-ciel de Kuala Lumpur aux plages immaculées et forêts tropicales, savourez une cuisine incroyable et une chaleureuse hospitalité.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MYS', 'pt', 'Malaysia', 'Malaysia', 'Descubra a Malásia onde culturas diversas se encontram. De arranha-céus de Kuala Lumpur a praias imaculadas e florestas tropicais, desfrute de comida incrível e hospitalidade calorosa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MYS', 'ru', 'Malaysia', 'Malaysia', 'Откройте Малайзию, где встречаются разнообразные культуры. От небоскрёбов Куала-Лумпур до нетронутых пляжей и тропических лесов, наслаждайтесь невероятной едой и тёплым гостеприимством.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MYS', 'de', 'Malaysia', 'Malaysia', 'Entdecken Sie Malaysia, wo vielfältige Kulturen aufeinandertreffen. Von Kuala Lumpurs Wolkenkratzern bis zu unberührten Stränden und Regenwäldern, genießen Sie unglaubliches Essen und warme Gastfreundschaft.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MYS', 'it', 'Malaysia', 'Malaysia', 'Scopri la Malesia dove si incontrano culture diverse. Dai grattacieli di Kuala Lumpur alle spiagge incontaminate e foreste pluviali, goditi cibo incredibile e calda ospitalità.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDV', 'en', 'Maldives', 'Maldives', 'Discover a paradise of over 1,000 coral islands, with white-sand beaches, turquoise lagoons, and vibrant underwater life. The ultimate luxury beach destination.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDV', 'ar', 'جزر المالديف', 'جزر المالديف', 'اكتشف جنة من أكثر من 1000 جزيرة مرجانية، بشواطئها ذات الرمال البيضاء، وبحيراتها الفيروزية، وحياتها المائية النابضة بالحياة. وجهة الشاطئ الفاخرة المطلقة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDV', 'es', 'Maldivas', 'Maldivas', 'Descubre un paraíso de más de 1.000 islas de coral, con playas de arena blanca, lagunas de color turquesa y una vibrante vida submarina. El destino de playa de lujo definitivo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDV', 'fr', 'Maldives', 'Maldives', 'Découvrez un paradis de plus de 1 000 îles coralliennes, avec des plages de sable blanc, des lagons turquoise et une vie sous-marine animée. La destination de plage de luxe par excellence.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDV', 'pt', 'Maldivas', 'Maldivas', 'Descubra um paraíso de mais de 1.000 ilhas de coral, com praias de areia branca, lagoas azul-turquesa e uma vida subaquática vibrante. O destino de praia de luxo por excelência.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDV', 'ru', 'Мальдивы', 'Мальдивы', 'Откройте для себя рай из более чем 1000 коралловых островов с белоснежными пляжами, бирюзовыми лагунами и яркой подводной жизнью. Идеальное место для роскошного пляжного отдыха.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDV', 'de', 'Malediven', 'Malediven', 'Entdecken Sie ein Paradies mit über 1.000 Koralleninseln, weißen Sandstränden, türkisfarbenen Lagunen und einer lebendigen Unterwasserwelt. Das ultimative Luxus-Strandziel.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDV', 'it', 'Maldive', 'Maldive', 'Scopri un paradiso di oltre 1.000 isole coralline, con spiagge di sabbia bianca, lagune turchesi e una vivace vita sottomarina. La destinazione balneare di lusso per eccellenza.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNG', 'en', 'Mongolia', 'Mongolia', 'Discover the land of Genghis Khan, with its vast steppes, nomadic culture, and the stunning Gobi Desert. Experience the raw beauty of the eternal blue sky.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNG', 'ar', 'منغوليا', 'منغوليا', 'اكتشف أرض جنكيز خان، بسهوبها الشاسعة، وثقافتها البدوية، وصحراء جوبي المذهلة. استمتع بالجمال الخام للسماء الزرقاء الأبدية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNG', 'es', 'Mongolia', 'Mongolia', 'Descubre la tierra de Gengis Kan, con sus vastas estepas, su cultura nómada y el impresionante desierto de Gobi. Experimenta la belleza cruda del cielo azul eterno.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNG', 'fr', 'Mongolie', 'Mongolie', 'Découvrez la terre de Gengis Khan, avec ses vastes steppes, sa culture nomade et le magnifique désert de Gobi. Découvrez la beauté brute du ciel bleu éternel.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNG', 'pt', 'Mongólia', 'Mongólia', 'Descubra a terra de Genghis Khan, com as suas vastas estepes, a sua cultura nómada e o deslumbrante Deserto de Gobi. Experimente a beleza crua do céu azul eterno.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNG', 'ru', 'Монголия', 'Монголия', 'Откройте для себя землю Чингисхана с ее бескрайними степями, кочевой культурой и потрясающей пустыней Гоби. Ощутите первозданную красоту вечного синего неба.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNG', 'de', 'Mongolei', 'Mongolei', 'Entdecken Sie das Land von Dschingis Khan mit seinen weiten Steppen, seiner Nomadenkultur und der atemberaubenden Wüste Gobi. Erleben Sie die raue Schönheit des ewigen blauen Himmels.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNG', 'it', 'Mongolia', 'Mongolia', 'Scopri la terra di Gengis Khan, con le sue vaste steppe, la cultura nomade e lo splendido deserto del Gobi. Vivi la bellezza selvaggia del cielo blu eterno.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MMR', 'en', 'Myanmar', 'Myanmar', 'Discover the ''Golden Land'', a country of ancient temples, serene landscapes, and diverse ethnic cultures. Explore the temples of Bagan and the floating villages of Inle Lake.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MMR', 'ar', 'ميانمار', 'ميانمار', 'اكتشف ''الأرض الذهبية''، بلد المعابد القديمة، والمناظر الطبيعية الهادئة، والثقافات العرقية المتنوعة. استكشف معابد باغان والقرى العائمة في بحيرة إينلي.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MMR', 'es', 'Myanmar', 'Myanmar', 'Descubre la ''Tierra Dorada'', un país de templos antiguos, paisajes serenos y diversas culturas étnicas. Explora los templos de Bagan y los pueblos flotantes del lago Inle.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MMR', 'fr', 'Myanmar', 'Myanmar', 'Découvrez la ''Terre d''Or'', un pays de temples anciens, de paysages sereins et de diverses cultures ethniques. Explorez les temples de Bagan et les villages flottants du lac Inle.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MMR', 'pt', 'Mianmar', 'Mianmar', 'Descubra a ''Terra Dourada'', um país de templos antigos, paisagens serenas e diversas culturas étnicas. Explore os templos de Bagan e as aldeias flutuantes do Lago Inle.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MMR', 'ru', 'Мьянма', 'Мьянма', 'Откройте для себя ''Золотую землю'', страну древних храмов, безмятежных пейзажей и разнообразных этнических культур. Исследуйте храмы Багана и плавучие деревни озера Инле.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MMR', 'de', 'Myanmar', 'Myanmar', 'Entdecken Sie das ''Goldene Land'', ein Land der alten Tempel, der ruhigen Landschaften und der vielfältigen ethnischen Kulturen. Erkunden Sie die Tempel von Bagan und die schwimmenden Dörfer des Inle-Sees.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MMR', 'it', 'Myanmar', 'Myanmar', 'Scopri la ''Terra d''oro'', un paese di antichi templi, paesaggi sereni e diverse culture etniche. Esplora i templi di Bagan e i villaggi galleggianti del Lago Inle.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'en', 'Nepal', 'Nepal', 'Experience the roof of the world with Mount Everest, ancient temples, and Himalayan adventures. Discover birthplace of Buddha, vibrant festivals, and warm mountain hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'ar', 'Nepal', 'Nepal', 'استمتع بسقف العالم مع جبل إفرست والمعابد القديمة ومغامرات هيمالايا. اكتشف موطن بوذا والمهرجانات النابضة وضيافة الجبل الدافئة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'es', 'Nepal', 'Nepal', 'Experimenta el techo del mundo con Monte Everest, templos ancestrales y aventuras himalayanas. Descubre cuna de Buda, festivales vibrantes y cálida hospitalidad montañesa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'fr', 'Nepal', 'Nepal', 'Vivez le toit du monde avec Mont Everest, temples antiques et aventures himalayennes. Découvrez berceau de Bouddha, festivals vibrants et chaleureuse hospitalité montagnarde.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'pt', 'Nepal', 'Nepal', 'Experimente o teto do mundo com Monte Everest, templos antigos e aventuras himalaias. Descubra berço de Buda, festivais vibrantes e hospitalidade montanhesa calorosa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'ru', 'Nepal', 'Nepal', 'Ощутите крышу мира с Эверестом, древними храмами и гималайскими приключениями. Откройте родину Будды, яркие фестивали и тёплое горное гостеприимство.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'de', 'Nepal', 'Nepal', 'Erleben Sie das Dach der Welt mit Mount Everest, antiken Tempeln und Himalaya-Abenteuern. Entdecken Sie Buddhas Geburtsort, lebendige Festivals und warme Berghospitalität.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NPL', 'it', 'Nepal', 'Nepal', 'Vivi il tetto del mondo con Monte Everest, templi antichi e avventure himalayane. Scopri culla del Buddha, festival vibranti e calda ospitalità montana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRK', 'en', 'North Korea', 'North Korea', 'Discover a land of immense political and cultural significance, with a unique and isolated society. Explore the monumental architecture of Pyongyang and the stunning natural beauty of Mount Paektu.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRK', 'ar', 'كوريا الشمالية', 'كوريا الشمالية', 'اكتشف أرضًا ذات أهمية سياسية وثقافية هائلة، مع مجتمع فريد ومعزول. استكشف العمارة الضخمة في بيونغ يانغ والجمال الطبيعي المذهل لجبل بايكتو.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRK', 'es', 'Corea del Norte', 'Corea del Norte', 'Descubre una tierra de inmensa importancia política y cultural, con una sociedad única y aislada. Explora la arquitectura monumental de Pyongyang y la impresionante belleza natural del monte Paektu.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRK', 'fr', 'Corée du Nord', 'Corée du Nord', 'Découvrez une terre d''une immense importance politique et culturelle, avec une société unique et isolée. Explorez l''architecture monumentale de Pyongyang et la beauté naturelle époustouflante du mont Paektu.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRK', 'pt', 'Coreia do Norte', 'Coreia do Norte', 'Descubra uma terra de imensa importância política e cultural, com uma sociedade única e isolada. Explore a arquitetura monumental de Pyongyang e a deslumbrante beleza natural do Monte Paektu.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRK', 'ru', 'Северная Корея', 'Северная Корея', 'Откройте для себя страну огромного политического и культурного значения с уникальным и изолированным обществом. Исследуйте монументальную архитектуру Пхеньяна и потрясающую природную красоту горы Пэкту.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRK', 'de', 'Nordkorea', 'Nordkorea', 'Entdecken Sie ein Land von immenser politischer und kultureller Bedeutung mit einer einzigartigen und isolierten Gesellschaft. Erkunden Sie die monumentale Architektur von Pjöngjang und die atemberaubende natürliche Schönheit des Mount Paektu.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRK', 'it', 'Corea del Nord', 'Corea del Nord', 'Scopri una terra di immensa importanza politica e culturale, con una società unica e isolata. Esplora l''architettura monumentale di Pyongyang e la straordinaria bellezza naturale del Monte Paektu.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'en', 'Oman', 'Oman', 'Experience authentic Arabia with dramatic landscapes, ancient forts, pristine beaches, and the warm hospitality of the Sultanate.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'ar', 'عمان', 'عمان', 'اختبر الجزيرة العربية الأصيلة بمناظرها الدرامية وحصونها القديمة وشواطئها البكر وضيافة السلطنة الدافئة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'es', 'Omán', 'Omán', 'Experimenta la Arabia auténtica con paisajes dramáticos, fuertes antiguos, playas prístinas y la cálida hospitalidad del Sultanato.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'fr', 'Oman', 'Oman', 'Découvrez l''''Arabie authentique avec des paysages dramatiques, des forts anciens, des plages immaculées et la chaleureuse hospitalité du Sultanat.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'pt', 'Omã', 'Omã', 'Experimente a Arábia autêntica com paisagens dramáticas, fortes antigos, praias imaculadas e a calorosa hospitalidade do Sultanato.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'ru', 'Оман', 'Оман', 'Ощутите подлинную Аравию с драматичными пейзажами, древними крепостями, нетронутыми пляжами и тёплым гостеприимством Султаната.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'de', 'Oman', 'Oman', 'Erleben Sie das authentische Arabien mit dramatischen Landschaften, alten Festungen, unberührten Stränden und der warmen Gastfreundschaft des Sultanats.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('OMN', 'it', 'Oman', 'Oman', 'Vivi l''''Arabia autentica con paesaggi drammatici, antichi forti, spiagge incontaminate e la calorosa ospitalità del Sultanato.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAK', 'en', 'Pakistan', 'Pakistan', 'Discover a land of dramatic mountain ranges, ancient civilizations, and vibrant cultures. Explore the Karakoram Highway and the ancient city of Mohenjo-daro.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAK', 'ar', 'باكستان', 'باكستان', 'اكتشف أرضًا من السلاسل الجبلية الدرامية، والحضارات القديمة، والثقافات النابضة بالحياة. استكشف طريق كاراكورام السريع ومدينة موهينجو دارو القديمة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAK', 'es', 'Pakistán', 'Pakistán', 'Descubre una tierra de espectaculares cadenas montañosas, civilizaciones antiguas y culturas vibrantes. Explora la autopista del Karakórum y la antigua ciudad de Mohenjo-daro.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAK', 'fr', 'Pakistan', 'Pakistan', 'Découvrez une terre de chaînes de montagnes spectaculaires, de civilisations anciennes et de cultures dynamiques. Explorez la route du Karakoram et l''ancienne ville de Mohenjo-daro.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAK', 'pt', 'Paquistão', 'Paquistão', 'Descubra uma terra de cadeias de montanhas dramáticas, civilizações antigas e culturas vibrantes. Explore a Autoestrada de Karakoram e a antiga cidade de Mohenjo-daro.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAK', 'ru', 'Пакистан', 'Пакистан', 'Откройте для себя страну драматических горных хребтов, древних цивилизаций и ярких культур. Исследуйте Каракорумское шоссе и древний город Мохенджо-Даро.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAK', 'de', 'Pakistan', 'Pakistan', 'Entdecken Sie ein Land mit dramatischen Gebirgszügen, alten Zivilisationen und lebendigen Kulturen. Erkunden Sie den Karakorum Highway und die antike Stadt Mohenjo-daro.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PAK', 'it', 'Pakistan', 'Pakistan', 'Scopri una terra di catene montuose spettacolari, antiche civiltà e culture vivaci. Esplora l''autostrada del Karakorum e l''antica città di Mohenjo-daro.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PSE', 'en', 'Palestine', 'Palestine', 'Discover a land of profound historical and religious significance, with ancient cities, sacred sites, and a resilient and vibrant culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PSE', 'ar', 'فلسطين', 'فلسطين', 'اكتشف أرضًا ذات أهمية تاريخية ودينية عميقة، بمدنها القديمة، ومواقعها المقدسة، وثقافتها الصامدة والنابضة بالحياة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PSE', 'es', 'Palestina', 'Palestina', 'Descubre una tierra de profundo significado histórico y religioso, con ciudades antiguas, lugares sagrados y una cultura resiliente y vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PSE', 'fr', 'Palestine', 'Palestine', 'Découvrez une terre d''une profonde signification historique et religieuse, avec des villes anciennes, des sites sacrés et une culture résiliente et dynamique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PSE', 'pt', 'Palestina', 'Palestina', 'Descubra uma terra de profundo significado histórico e religioso, com cidades antigas, locais sagrados e uma cultura resiliente e vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PSE', 'ru', 'Палестина', 'Палестина', 'Откройте для себя землю глубокого исторического и религиозного значения, с древними городами, святыми местами и стойкой и яркой культурой.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PSE', 'de', 'Palästina', 'Palästina', 'Entdecken Sie ein Land von tiefgreifender historischer und religiöser Bedeutung mit alten Städten, heiligen Stätten und einer widerstandsfähigen und lebendigen Kultur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PSE', 'it', 'Palestina', 'Palestina', 'Scopri una terra di profondo significato storico e religioso, con città antiche, luoghi sacri e una cultura resiliente e vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PHL', 'en', 'Philippines', 'Philippines', 'Discover 7,641 tropical islands of endless adventure. From Palawan''s pristine beaches to Manila''s vibrant energy, experience warm Filipino hospitality, diverse cultures, and breathtaking archipelago beauty.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PHL', 'ar', 'Philippines', 'Philippines', 'اكتشف 7,641 جزيرة استوائية من المغامرات اللا محدودة. من شواطئ بالاوان البكر إلى طاقة مانيلا النابضة، استمتع بالضيافة الفلبينية الدافئة والثقافات المتنوعة وجمال الأرخبيل الخلاب.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PHL', 'es', 'Philippines', 'Philippines', 'Descubre 7,641 islas tropicales de aventura sin fin. Desde playas prístinas de Palawan hasta energía vibrante de Manila, experimenta cálida hospitalidad filipina, culturas diversas y belleza impresionante del archipiélago.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PHL', 'fr', 'Philippines', 'Philippines', 'Découvrez 7,641 îles tropicales d''aventure sans fin. Des plages vierges de Palawan à l''énergie vibrante de Manille, vivez chaleureuse hospitalité philippine, cultures diverses et beauté époustouflante de l''archipel.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PHL', 'pt', 'Philippines', 'Philippines', 'Descubra 7,641 ilhas tropicais de aventura sem fim. Das praias imaculadas de Palawan à energia vibrante de Manila, experimente hospitalidade filipina calorosa, culturas diversas e beleza deslumbrante do arquipélago.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PHL', 'ru', 'Philippines', 'Philippines', 'Откройте 7,641 тропический остров бесконечных приключений. От нетронутых пляжей Палавана до яркой энергии Манилы, ощутите тёплое филиппинское гостеприимство, разнообразные культуры и захватывающую красоту архипелага.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PHL', 'de', 'Philippines', 'Philippines', 'Entdecken Sie 7,641 tropische Inseln endloser Abenteuer. Von Palawans unberührten Stränden bis zu Manilas lebendiger Energie, erleben Sie warme philippinische Gastfreundschaft, vielfältige Kulturen und atemberaubende Archipel-Schönheit.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PHL', 'it', 'Philippines', 'Philippines', 'Scopri 7,641 isole tropicali di avventure infinite. Dalle spiagge incontaminate di Palawan all''energia vibrante di Manila, vivi calda ospitalità filippina, culture diverse e bellezza mozzafiato dell''arcipelago.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('QAT', 'en', 'Qatar', 'Qatar', 'Experience luxury and innovation in this Gulf peninsula. Discover world-class museums, stunning architecture, and pristine beaches where tradition meets modernity.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('QAT', 'ar', 'قطر', 'قطر', 'اختبر الرفاهية والابتكار في هذه الجزيرة الخليجية. اكتشف متاحف عالمية المستوى وهندسة معمارية مذهلة وشواطئ نقية حيث يلتقي التراث مع الحداثة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('QAT', 'es', 'Catar', 'Catar', 'Experimenta lujo e innovación en esta península del Golfo. Descubre museos de clase mundial, arquitectura impresionante y playas prístinas donde la tradición se encuentra con la modernidad.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('QAT', 'fr', 'Qatar', 'Qatar', 'Découvrez le luxe et l''''innovation dans cette péninsule du Golfe. Explorez des musées de classe mondiale, une architecture époustouflante et des plages immaculées où la tradition rencontre la modernité.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('QAT', 'pt', 'Catar', 'Catar', 'Experimente luxo e inovação nesta península do Golfo. Descubra museus de classe mundial, arquitetura deslumbrante e praias imaculadas onde a tradição encontra a modernidade.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('QAT', 'ru', 'Катар', 'Катар', 'Ощутите роскошь и инновации на этом полуострове в Персидском заливе. Откройте музеи мирового класса, потрясающую архитектуру и нетронутые пляжи, где традиции встречаются с современностью.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('QAT', 'de', 'Katar', 'Katar', 'Erleben Sie Luxus und Innovation auf dieser Golfhalbinsel. Entdecken Sie Weltklasse-Museen, atemberaubende Architektur und unberührte Strände, wo Tradition auf Moderne trifft.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('QAT', 'it', 'Qatar', 'Qatar', 'Vivi lusso e innovazione in questa penisola del Golfo. Scopri musei di classe mondiale, architettura mozzafiato e spiagge incontaminate dove la tradizione incontra la modernità.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SAU', 'en', 'Saudi Arabia', 'Saudi Arabia', 'Discover the Kingdom where ancient heritage meets modern vision. Experience sacred sites, futuristic cities, and vast desert landscapes in the heart of Arabia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SAU', 'ar', 'المملكة العربية السعودية', 'المملكة العربية السعودية', 'اكتشف المملكة حيث يلتقي التراث العريق مع الرؤية الحديثة. اختبر المواقع المقدسة والمدن المستقبلية والمناظر الصحراوية الشاسعة في قلب الجزيرة العربية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SAU', 'es', 'Arabia Saudí', 'Arabia Saudí', 'Descubre el Reino donde el patrimonio ancestral se encuentra con la visión moderna. Experimenta sitios sagrados, ciudades futuristas y vastos paisajes desérticos en el corazón de Arabia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SAU', 'fr', 'Arabie saoudite', 'Arabie saoudite', 'Découvrez le Royaume où l''''héritage ancestral rencontre la vision moderne. Explorez des sites sacrés, des villes futuristes et de vastes paysages désertiques au cœur de l''''Arabie.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SAU', 'pt', 'Arábia Saudita', 'Arábia Saudita', 'Descubra o Reino onde o patrimônio ancestral encontra a visão moderna. Experimente locais sagrados, cidades futuristas e vastas paisagens desérticas no coração da Arábia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SAU', 'ru', 'Саудовская Аравия', 'Саудовская Аравия', 'Откройте для себя Королевство, где древнее наследие встречается с современным видением. Ощутите священные места, футуристические города и обширные пустынные пейзажи в сердце Аравии.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SAU', 'de', 'Saudi-Arabien', 'Saudi-Arabien', 'Entdecken Sie das Königreich, wo antikes Erbe auf moderne Vision trifft. Erleben Sie heilige Stätten, futuristische Städte und weite Wüstenlandschaften im Herzen Arabiens.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SAU', 'it', 'Arabia Saudita', 'Arabia Saudita', 'Scopri il Regno dove l''''eredità ancestrale incontra la visione moderna. Vivi siti sacri, città futuristiche e vasti paesaggi desertici nel cuore dell''''Arabia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SGP', 'en', 'Singapore', 'Singapore', 'Experience the Lion City where East meets West. Discover futuristic skylines, world-class dining, lush gardens, and multicultural harmony.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SGP', 'ar', 'سنغافورة', 'سنغافورة', 'اختبر مدينة الأسد حيث يلتقي الشرق مع الغرب. اكتشف الأفق المستقبلي والمأكولات عالمية المستوى والحدائق الخضراء والانسجام متعدد الثقافات.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SGP', 'es', 'Singapur', 'Singapur', 'Experimenta la Ciudad del León donde Oriente se encuentra con Occidente. Descubre horizontes futuristas, gastronomía de clase mundial, jardines exuberantes y armonía multicultural.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SGP', 'fr', 'Singapour', 'Singapour', 'Découvrez la Cité du Lion où l''''Orient rencontre l''''Occident. Explorez des horizons futuristes, une gastronomie de classe mondiale, des jardins luxuriants et l''''harmonie multiculturelle.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SGP', 'pt', 'Singapura', 'Singapura', 'Experimente a Cidade do Leão onde Oriente encontra Ocidente. Descubra horizontes futuristas, gastronomia de classe mundial, jardins exuberantes e harmonia multicultural.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SGP', 'ru', 'Сингапур', 'Сингапур', 'Ощутите Город Льва, где Восток встречается с Западом. Откройте футуристические горизонты, гастрономию мирового класса, пышные сады и мультикультурную гармонию.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SGP', 'de', 'Singapur', 'Singapur', 'Erleben Sie die Löwenstadt, wo Ost auf West trifft. Entdecken Sie futuristische Skylines, Weltklasse-Gastronomie, üppige Gärten und multikulturelle Harmonie.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SGP', 'it', 'Singapore', 'Singapore', 'Vivi la Città del Leone dove Oriente incontra Occidente. Scopri skyline futuristici, gastronomia di classe mondiale, giardini rigogliosi e armonia multiculturale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KOR', 'en', 'South Korea', 'South Korea', 'Experience the perfect blend of ancient traditions and cutting-edge technology. From palaces and temples to K-pop culture and innovative cities.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KOR', 'ar', 'South Korea', 'South Korea', 'استمتع بالمزيج المثالي من التقاليد القديمة والتكنولوجيا المتطورة. من القصور والمعابد إلى ثقافة الكيبوب والمدن المبتكرة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KOR', 'es', 'South Korea', 'South Korea', 'Experimenta la mezcla perfecta entre tradiciones ancestrales y tecnología de vanguardia. Desde palacios y templos hasta la cultura K-pop y ciudades innovadoras.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KOR', 'fr', 'South Korea', 'South Korea', 'Découvrez le mélange parfait entre traditions anciennes et technologie de pointe. Des palais et temples à la culture K-pop et aux villes innovantes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KOR', 'pt', 'South Korea', 'South Korea', 'Experimente a mistura perfeita entre tradições antigas e tecnologia de ponta. De palácios e templos à cultura K-pop e cidades inovadoras.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KOR', 'ru', 'South Korea', 'South Korea', 'Ощутите идеальное сочетание древних традиций и передовых технологий. От дворцов и храмов до культуры K-pop и инновационных городов.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KOR', 'de', 'South Korea', 'South Korea', 'Erleben Sie die perfekte Mischung aus alten Traditionen und modernster Technologie. Von Palästen und Tempeln bis zur K-Pop-Kultur und innovativen Städten.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KOR', 'it', 'South Korea', 'South Korea', 'Vivi la perfetta fusione tra tradizioni antiche e tecnologia all''avanguardia. Da palazzi e templi alla cultura K-pop e città innovative.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LKA', 'en', 'Sri Lanka', 'Sri Lanka', 'Discover the Pearl of the Indian Ocean with ancient temples, pristine beaches, and lush tea plantations. Experience rich Buddhist heritage, wildlife safaris, and warm island hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LKA', 'ar', 'Sri Lanka', 'Sri Lanka', 'اكتشف درة المحيط الهندي مع المعابد القديمة والشواطئ البكر ومزارع الشاي الخضراء. استمتع بالتراث البوذي الغني ورحلات السافاري لمشاهدة الحياة البرية والضيافة الدافئة للجزيرة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LKA', 'es', 'Sri Lanka', 'Sri Lanka', 'Descubre la Perla del Océano Índico con templos ancestrales, playas prístinas y exuberantes plantaciones de té. Experimenta rico patrimonio budista, safaris de vida silvestre y cálida hospitalidad isleña.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LKA', 'fr', 'Sri Lanka', 'Sri Lanka', 'Découvrez la Perle de l''Océan Indien avec temples antiques, plages vierges et plantations de thé luxuriantes. Vivez riche héritage bouddhiste, safaris faune et chaleureuse hospitalité insulaire.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LKA', 'pt', 'Sri Lanka', 'Sri Lanka', 'Descubra a Pérola do Oceano Índico com templos antigos, praias imaculadas e plantações de chá exuberantes. Experimente rico património budista, safaris da vida selvagem e hospitalidade insular calorosa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LKA', 'ru', 'Sri Lanka', 'Sri Lanka', 'Откройте Жемчужину Индийского океана с древними храмами, нетронутыми пляжами и пышными чайными плантациями. Ощутите богатое буддистское наследие, сафари с дикими животными и тёплое островное гостеприимство.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LKA', 'de', 'Sri Lanka', 'Sri Lanka', 'Entdecken Sie die Perle des Indischen Ozeans mit antiken Tempeln, unberührten Stränden und üppigen Teeplantagen. Erleben Sie reiches buddhistisches Erbe, Wildlife-Safaris und warme Inselhospitalität.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LKA', 'it', 'Sri Lanka', 'Sri Lanka', 'Scopri la Perla dell''Oceano Indiano con templi antichi, spiagge incontaminate e rigogliose piantagioni di tè. Vivi ricco patrimonio buddista, safari della fauna selvatica e calda ospitalità isolana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYR', 'en', 'Syria', 'Syria', 'Discover a land of ancient civilizations, with a rich history that includes the ancient city of Palmyra and the vibrant capital, Damascus.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYR', 'ar', 'سوريا', 'سوريا', 'اكتشف أرض الحضارات القديمة، بتاريخها الغني الذي يضم مدينة تدمر القديمة والعاصمة النابضة بالحياة دمشق.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYR', 'es', 'Siria', 'Siria', 'Descubre una tierra de civilizaciones antiguas, con una rica historia que incluye la antigua ciudad de Palmira y la vibrante capital, Damasco.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYR', 'fr', 'Syrie', 'Syrie', 'Découvrez une terre de civilisations anciennes, avec une histoire riche qui comprend l''ancienne ville de Palmyre et la vibrante capitale, Damas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYR', 'pt', 'Síria', 'Síria', 'Descubra uma terra de civilizações antigas, com uma história rica que inclui a antiga cidade de Palmira e a vibrante capital, Damasco.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYR', 'ru', 'Сирия', 'Сирия', 'Откройте для себя землю древних цивилизаций с богатой историей, включающей древний город Пальмиру и оживленную столицу Дамаск.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYR', 'de', 'Syrien', 'Syrien', 'Entdecken Sie ein Land der alten Zivilisationen mit einer reichen Geschichte, zu der die antike Stadt Palmyra und die pulsierende Hauptstadt Damaskus gehören.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SYR', 'it', 'Siria', 'Siria', 'Scopri una terra di antiche civiltà, con una ricca storia che include l''antica città di Palmira e la vibrante capitale, Damasco.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TJK', 'en', 'Tajikistan', 'Tajikistan', 'Discover a land of breathtaking mountain scenery, with the Pamir Highway offering one of the world''s most spectacular road trips.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TJK', 'ar', 'طاجيكستان', 'طاجيكستان', 'اكتشف أرضًا ذات مناظر جبلية خلابة، حيث يقدم طريق بامير السريع واحدة من أروع الرحلات البرية في العالم.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TJK', 'es', 'Tayikistán', 'Tayikistán', 'Descubre una tierra de impresionantes paisajes de montaña, con la carretera de Pamir que ofrece uno de los viajes por carretera más espectaculares del mundo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TJK', 'fr', 'Tadjikistan', 'Tadjikistan', 'Découvrez une terre de paysages de montagne à couper le souffle, avec la route du Pamir qui offre l''un des voyages en voiture les plus spectaculaires au monde.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TJK', 'pt', 'Tajiquistão', 'Tajiquistão', 'Descubra uma terra de paisagens montanhosas de tirar o fôlego, com a Rodovia Pamir a oferecer uma das viagens de carro mais espetaculares do mundo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TJK', 'ru', 'Таджикистан', 'Таджикистан', 'Откройте для себя страну захватывающих дух горных пейзажей, где Памирский тракт предлагает одно из самых зрелищных автомобильных путешествий в мире.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TJK', 'de', 'Tadschikistan', 'Tadschikistan', 'Entdecken Sie ein Land mit atemberaubender Berglandschaft, dessen Pamir Highway eine der spektakulärsten Autoreisen der Welt bietet.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TJK', 'it', 'Tagikistan', 'Tagikistan', 'Scopri una terra di paesaggi montani mozzafiato, con l''autostrada del Pamir che offre uno dei viaggi su strada più spettacolari del mondo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('THA', 'en', 'Thailand', 'Thailand', 'Experience the Land of Smiles with golden temples, pristine beaches, vibrant street food, and warm Thai hospitality in Southeast Asia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('THA', 'ar', 'تايلاند', 'تايلاند', 'اختبر أرض الابتسامات بمعابدها الذهبية وشواطئها البكر وأطعمة الشارع النابضة والضيافة التايلاندية الدافئة في جنوب شرق آسيا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('THA', 'es', 'Tailandia', 'Tailandia', 'Experimenta la Tierra de las Sonrisas con templos dorados, playas prístinas, vibrante comida callejera y la cálida hospitalidad tailandesa en el Sudeste Asiático.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('THA', 'fr', 'Thaïlande', 'Thaïlande', 'Découvrez le Pays du Sourire avec ses temples dorés, ses plages immaculées, sa street food vibrante et la chaleureuse hospitalité thaïlandaise en Asie du Sud-Est.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('THA', 'pt', 'Tailândia', 'Tailândia', 'Experimente a Terra dos Sorrisos com templos dourados, praias imaculadas, vibrante comida de rua e a calorosa hospitalidade tailandesa no Sudeste Asiático.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('THA', 'ru', 'Таиланд', 'Таиланд', 'Ощутите Страну Улыбок с золотыми храмами, нетронутыми пляжами, яркой уличной едой и тёплым тайским гостеприимством в Юго-Восточной Азии.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('THA', 'de', 'Thailand', 'Thailand', 'Erleben Sie das Land des Lächelns mit goldenen Tempeln, unberührten Stränden, lebendiger Straßenküche und warmer thailändischer Gastfreundschaft in Südostasien.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('THA', 'it', 'Thailandia', 'Thailandia', 'Vivi la Terra dei Sorrisi con templi dorati, spiagge incontaminate, vivace street food e la calorosa ospitalità thailandese nel Sud-est asiatico.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUR', 'en', 'Turkey', 'Turkey', 'Discover a country where East meets West, with a rich history of empires, stunning coastlines, and the vibrant city of Istanbul.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUR', 'ar', 'تركيا', 'تركيا', 'اكتشف بلدًا يلتقي فيه الشرق بالغرب، بتاريخ غني من الإمبراطوريات، وسواحل مذهلة، ومدينة إسطنبول النابضة بالحياة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUR', 'es', 'Turquía', 'Turquía', 'Descubre un país donde Oriente se encuentra con Occidente, con una rica historia de imperios, costas impresionantes y la vibrante ciudad de Estambul.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUR', 'fr', 'Turquie', 'Turquie', 'Découvrez un pays où l''Orient rencontre l''Occident, avec une riche histoire d''empires, des côtes magnifiques et la ville animée d''Istanbul.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUR', 'pt', 'Turquia', 'Turquia', 'Descubra um país onde o Oriente encontra o Ocidente, com uma rica história de impérios, costas deslumbrantes e a vibrante cidade de Istambul.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUR', 'ru', 'Турция', 'Турция', 'Откройте для себя страну, где Восток встречается с Западом, с богатой историей империй, потрясающими побережьями и оживленным городом Стамбулом.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUR', 'de', 'Türkei', 'Türkei', 'Entdecken Sie ein Land, in dem Ost auf West trifft, mit einer reichen Geschichte von Imperien, atemberaubenden Küsten und der pulsierenden Stadt Istanbul.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUR', 'it', 'Turchia', 'Turchia', 'Scopri un paese dove l''Oriente incontra l''Occidente, con una ricca storia di imperi, coste meravigliose e la vibrante città di Istanbul.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TKM', 'en', 'Turkmenistan', 'Turkmenistan', 'Discover a land of ancient history and vast deserts, with the futuristic city of Ashgabat and the fiery Darvaza gas crater, the ''Door to Hell''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TKM', 'ar', 'تركمانستان', 'تركمانستان', 'اكتشف أرضًا ذات تاريخ قديم وصحاري شاسعة، مع مدينة عشق أباد المستقبلية وحفرة غاز دارفازا المشتعلة، ''بوابة الجحيم''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TKM', 'es', 'Turkmenistán', 'Turkmenistán', 'Descubre una tierra de historia antigua y vastos desiertos, con la futurista ciudad de Asjabad y el ardiente cráter de gas de Darvaza, la ''Puerta al Infierno''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TKM', 'fr', 'Turkménistan', 'Turkménistan', 'Découvrez une terre d''histoire ancienne et de vastes déserts, avec la ville futuriste d''Achgabat et le cratère de gaz enflammé de Darvaza, la ''Porte de l''Enfer''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TKM', 'pt', 'Turquemenistão', 'Turquemenistão', 'Descubra uma terra de história antiga e vastos desertos, com a cidade futurista de Asgabate e a ardente cratera de gás de Darvaza, a ''Porta para o Inferno''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TKM', 'ru', 'Туркменистан', 'Туркменистан', 'Откройте для себя землю древней истории и бескрайних пустынь с футуристическим городом Ашхабадом и огненным газовым кратером Дарваза, ''Вратами в ад''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TKM', 'de', 'Turkmenistan', 'Turkmenistan', 'Entdecken Sie ein Land mit alter Geschichte und riesigen Wüsten, mit der futuristischen Stadt Aschgabat und dem feurigen Gaskrater von Derweze, dem ''Tor zur Hölle''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TKM', 'it', 'Turkmenistan', 'Turkmenistan', 'Scopri una terra di storia antica e vasti deserti, con la futuristica città di Ashgabat e l''infuocato cratere di gas di Darvaza, la ''Porta dell''Inferno''.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UZB', 'en', 'Uzbekistan', 'Uzbekistan', 'Discover the heart of the Silk Road, a land of stunning Islamic architecture, ancient cities like Samarkand and Bukhara, and a rich cultural heritage.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UZB', 'ar', 'أوزبكستان', 'أوزبكستان', 'اكتشف قلب طريق الحرير، أرض العمارة الإسلامية المذهلة، والمدن القديمة مثل سمرقند وبخارى، والتراث الثقافي الغني.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UZB', 'es', 'Uzbekistán', 'Uzbekistán', 'Descubre el corazón de la Ruta de la Seda, una tierra de impresionante arquitectura islámica, ciudades antiguas como Samarcanda y Bujará, y un rico patrimonio cultural.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UZB', 'fr', 'Ouzbékistan', 'Ouzbékistan', 'Découvrez le cœur de la Route de la Soie, une terre d''une architecture islamique époustouflante, de villes anciennes comme Samarcande et Boukhara, et d''un riche patrimoine culturel.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UZB', 'pt', 'Uzbequistão', 'Uzbequistão', 'Descubra o coração da Rota da Seda, uma terra de arquitetura islâmica deslumbrante, cidades antigas como Samarcanda e Bukhara, e um rico património cultural.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UZB', 'ru', 'Узбекистан', 'Узбекистан', 'Откройте для себя сердце Шелкового пути, страну потрясающей исламской архитектуры, древних городов, таких как Самарканд и Бухара, и богатого культурного наследия.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UZB', 'de', 'Usbekistan', 'Usbekistan', 'Entdecken Sie das Herz der Seidenstraße, ein Land mit atemberaubender islamischer Architektur, alten Städten wie Samarkand und Buchara und einem reichen kulturellen Erbe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UZB', 'it', 'Uzbekistan', 'Uzbekistan', 'Scopri il cuore della Via della Seta, una terra di straordinaria architettura islamica, antiche città come Samarcanda e Bukhara e un ricco patrimonio culturale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'en', 'Vietnam', 'Vietnam', 'Journey through Vietnam from bustling cities to serene landscapes. Experience rich history, breathtaking natural beauty, and extraordinary street food culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'ar', 'Vietnam', 'Vietnam', 'انطلق في رحلة عبر فيتنام من المدن النابضة بالحياة إلى المناظر الطبيعية الهادئة. استمتع بالتاريخ الغني والجمال الطبيعي الخلاب وثقافة طعام الشارع المميزة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'es', 'Vietnam', 'Vietnam', 'Viaja por Vietnam desde ciudades bulliciosas hasta paisajes serenos. Experimenta rica historia, belleza natural impresionante y extraordinaria cultura de comida callejera.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'fr', 'Vietnam', 'Vietnam', 'Voyagez à travers le Vietnam des villes animées aux paysages sereins. Découvrez une riche histoire, une beauté naturelle à couper le souffle et une extraordinaire culture de street food.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'pt', 'Vietnam', 'Vietnam', 'Viaje pelo Vietnã de cidades agitadas a paisagens serenas. Experimente história rica, beleza natural deslumbrante e extraordinária cultura de comida de rua.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'ru', 'Vietnam', 'Vietnam', 'Путешествуйте по Вьетнаму от шумных городов до спокойных пейзажей. Ощутите богатую историю, захватывающую природную красоту и необыкновенную культуру уличной еды.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'de', 'Vietnam', 'Vietnam', 'Reisen Sie durch Vietnam von lebendigen Städten zu ruhigen Landschaften. Erleben Sie reiche Geschichte, atemberaubende Naturschönheit und außergewöhnliche Straßenesskultur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VNM', 'it', 'Vietnam', 'Vietnam', 'Viaggia attraverso il Vietnam dalle città vivaci ai paesaggi sereni. Vivi una ricca storia, bellezza naturale mozzafiato e straordinaria cultura del cibo di strada.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('YEM', 'en', 'Yemen', 'Yemen', 'Discover a land of ancient history, unique architecture, and the stunning island of Socotra, a UNESCO World Heritage site.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('YEM', 'ar', 'اليمن', 'اليمن', 'اكتشف أرضًا ذات تاريخ قديم، وهندسة معمارية فريدة، وجزيرة سقطرى المذهلة، وهي أحد مواقع التراث العالمي لليونسكو.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('YEM', 'es', 'Yemen', 'Yemen', 'Descubre una tierra de historia antigua, arquitectura única y la impresionante isla de Socotra, declarada Patrimonio de la Humanidad por la UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('YEM', 'fr', 'Yémen', 'Yémen', 'Découvrez une terre d''histoire ancienne, d''architecture unique et de l''île époustouflante de Socotra, un site du patrimoine mondial de l''UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('YEM', 'pt', 'Iémen', 'Iémen', 'Descubra uma terra de história antiga, arquitetura única e a deslumbrante ilha de Socotorá, um Património Mundial da UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('YEM', 'ru', 'Йемен', 'Ймен', 'Откройте для себя землю древней истории, уникальной архитектуры и потрясающего острова Сокотра, объекта Всемирного наследия ЮНЕСКО.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('YEM', 'de', 'Jemen', 'Jemen', 'Entdecken Sie ein Land mit alter Geschichte, einzigartiger Architektur und der atemberaubenden Insel Sokotra, einem UNESCO-Weltkulturerbe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('YEM', 'it', 'Yemen', 'Yemen', 'Scopri una terra di storia antica, architettura unica e la splendida isola di Socotra, un sito del patrimonio mondiale dell''UNESCO.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ALB', 'en', 'Albania', 'Albania', 'Discover a hidden gem of the Balkans, with stunning Riviera beaches, Ottoman architecture, and the dramatic Albanian Alps.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ALB', 'ar', 'ألبانيا', 'ألبانيا', 'اكتشف جوهرة مخفية في البلقان، بشواطئ الريفيرا المذهلة، والهندسة المعمارية العثمانية، وجبال الألب الألبانية الدرامية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ALB', 'es', 'Albania', 'Albania', 'Descubre una joya escondida de los Balcanes, con impresionantes playas en la Riviera, arquitectura otomana y los espectaculares Alpes albaneses.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ALB', 'fr', 'Albanie', 'Albanie', 'Découvrez un joyau caché des Balkans, avec de superbes plages de la Riviera, une architecture ottomane et les spectaculaires Alpes albanaises.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ALB', 'pt', 'Albânia', 'Albânia', 'Descubra uma joia escondida dos Balcãs, com praias deslumbrantes na Riviera, arquitetura otomana e os dramáticos Alpes Albaneses.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ALB', 'ru', 'Албания', 'Албания', 'Откройте для себя скрытую жемчужину Балкан с потрясающими пляжами Ривьеры, османской архитектурой и впечатляющими Албанскими Альпами.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ALB', 'de', 'Albanien', 'Albanien', 'Entdecken Sie ein verstecktes Juwel des Balkans mit atemberaubenden Stränden an der Riviera, osmanischer Architektur und den dramatischen albanischen Alpen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ALB', 'it', 'Albania', 'Albania', 'Scopri una gemma nascosta dei Balcani, con splendide spiagge della Riviera, architettura ottomana e le spettacolari Alpi albanesi.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AND', 'en', 'Andorra', 'Andorra', 'Discover a tiny principality nestled in the Pyrenees mountains, known for its ski resorts, duty-free shopping, and stunning mountain landscapes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AND', 'ar', 'أندورا', 'أندورا', 'اكتشف إمارة صغيرة تقع في جبال البرانس، تشتهر بمنتجعات التزلج، والتسوق المعفى من الرسوم الجمركية، والمناظر الطبيعية الجبلية الخلابة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AND', 'es', 'Andorra', 'Andorra', 'Descubre un pequeño principado enclavado en los Pirineos, conocido por sus estaciones de esquí, sus compras libres de impuestos y sus impresionantes paisajes de montaña.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AND', 'fr', 'Andorre', 'Andorre', 'Découvrez une petite principauté nichée dans les Pyrénées, connue pour ses stations de ski, ses boutiques hors taxes et ses paysages de montagne magnifiques.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AND', 'pt', 'Andorra', 'Andorra', 'Descubra um pequeno principado aninhado nas montanhas dos Pirenéus, conhecido pelas suas estâncias de esqui, compras isentas de impostos e paisagens montanhosas deslumbrantes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AND', 'ru', 'Андорра', 'Андорра', 'Откройте для себя крошечное княжество, расположенное в Пиренейских горах, известное своими горнолыжными курортами, беспошлинной торговлей и потрясающими горными пейзажами.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AND', 'de', 'Andorra', 'Andorra', 'Entdecken Sie ein winziges Fürstentum in den Pyrenäen, das für seine Skigebiete, den zollfreien Einkauf und die atemberaubende Berglandschaft bekannt ist.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AND', 'it', 'Andorra', 'Andorra', 'Scopri un piccolo principato incastonato tra i Pirenei, noto per le sue località sciistiche, lo shopping duty-free e gli splendidi paesaggi montani.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUT', 'en', 'Austria', 'Austria', 'Experience Alpine splendor and imperial grandeur in Austria. From Vienna palaces to Salzburg music festivals and stunning mountain landscapes, discover classical elegance.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUT', 'ar', 'Austria', 'Austria', 'استمتع بالروعة الألبية والعظمة الإمبراطورية في النمسا. من قصور فييناإلى مهرجانات سالزبورغ الموسيقية والمناظر الجبلية الخلابة، اكتشف الأناقة الكلاسيكية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUT', 'es', 'Austria', 'Austria', 'Experimenta el esplendor alpino y la grandeza imperial en Austria. Desde palacios de Viena hasta festivales de música de Salzburgo y paisajes montañosos impresionantes, descubre elegancia clásica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUT', 'fr', 'Austria', 'Austria', 'Découvrez la splendeur alpine et la grandeur impériale en Autriche. Des palais de Vienne aux festivals de musique de Salzbourg et paysages montagneux époustouflants, explorez l''élégance classique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUT', 'pt', 'Austria', 'Austria', 'Experimente o esplendor alpino e a grandeza imperial na Áustria. Dos palácios de Viena aos festivais de música de Salzburgo e paisagens montanhosas deslumbrantes, descubra elegância clássica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUT', 'ru', 'Austria', 'Austria', 'Ощутите альпийское великолепие и имперское величие в Австрии. От венских дворцов до музыкальных фестивалей Зальцбурга и потрясающих горных пейзажей, откройте классическую элегантность.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUT', 'de', 'Austria', 'Austria', 'Erleben Sie alpine Pracht und kaiserliche Größe in Österreich. Von Wiener Palästen bis zu Salzburger Musikfestivals und atemberaubenden Berglandschaften, entdecken Sie klassische Eleganz.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('AUT', 'it', 'Austria', 'Austria', 'Vivi lo splendore alpino e la grandezza imperiale in Austria. Dai palazzi di Vienna ai festival musicali di Salisburgo e paesaggi montani mozzafiato, scopri eleganza classica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLR', 'en', 'Belarus', 'Belarus', 'Discover pristine forests, medieval castles, and Soviet-era architecture. Experience Belarusian hospitality in this fascinating Eastern European nation.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLR', 'ar', 'Belarus', 'Belarus', 'اكتشف الغابات البكر والقلاع القروسطية والعمارة السوفيتية. استمتع بالضيافة البيلاروسية في هذه الدولة الأوروبية الشرقية الساحرة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLR', 'es', 'Belarus', 'Belarus', 'Descubre bosques vírgenes, castillos medievales y arquitectura soviética. Experimenta la hospitalidad bielorrusa en esta fascinante nación de Europa del Este.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLR', 'fr', 'Belarus', 'Belarus', 'Découvrez des forêts vierges, châteaux médiévaux et architecture soviétique. Vivez l''hospitalité biélorusse dans cette nation fascinante d''Europe de l''Est.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLR', 'pt', 'Belarus', 'Belarus', 'Descubra florestas pristinas, castelos medievais e arquitetura soviética. Experimente hospitalidade bielorrussa nesta fascinante nação do Leste Europeu.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLR', 'ru', 'Belarus', 'Belarus', 'Откройте нетронутые леса, средневековые замки и советскую архитектуру. Ощутите белорусское гостеприимство в этой удивительной восточноевропейской стране.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLR', 'de', 'Belarus', 'Belarus', 'Entdecken Sie unberührte Wälder, mittelalterliche Schlösser und sowjetische Architektur. Erleben Sie weißrussische Gastfreundschaft in dieser faszinierenden osteuropäischen Nation.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BLR', 'it', 'Belarus', 'Belarus', 'Scopri foreste incontaminate, castelli medievali e architettura sovietica. Vivi l''ospitalità bielorussa in questa affascinante nazione dell''Europa orientale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEL', 'en', 'Belgium', 'Belgium', 'Discover Belgium charm with medieval cities, world-famous chocolates, and exceptional beers. From Brussels Grand Place to Bruges canals, experience European elegance.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEL', 'ar', 'Belgium', 'Belgium', 'اكتشف سحر بلجيكا مع المدن القروسطية والشوكولاته الشهيرة عالمياً والبيرة الاستثنائية. من ساحة بروكسل الكبرى إلى قنوات بروج، استمتع بالأناقة الأوروبية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEL', 'es', 'Belgium', 'Belgium', 'Descubre el encanto belga con ciudades medievales, chocolates mundialmente famosos y cervezas excepcionales. Desde la Grand Place de Bruselas hasta los canales de Brujas, experimenta elegancia europea.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEL', 'fr', 'Belgium', 'Belgium', 'Découvrez le charme belge avec villes médiévales, chocolats mondialement célèbres et bières exceptionnelles. De la Grand-Place de Bruxelles aux canaux de Bruges, vivez l''élégance européenne.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEL', 'pt', 'Belgium', 'Belgium', 'Descubra o charme belga com cidades medievais, chocolates mundialmente famosos e cervejas excepcionais. Da Grand Place de Bruxelas aos canais de Bruges, experimente elegância europeia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEL', 'ru', 'Belgium', 'Belgium', 'Откройте очарование Бельгии с средневековыми городами, всемирно известным шоколадом и исключительным пивом. От Гранд-Плас Брюсселя до каналов Брюгге, ощутите европейскую элегантность.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEL', 'de', 'Belgium', 'Belgium', 'Entdecken Sie belgischen Charme mit mittelalterlichen Städten, weltberühmter Schokolade und außergewöhnlichen Bieren. Vom Brüsseler Grand Place bis zu Brügger Kanälen, erleben Sie europäische Eleganz.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BEL', 'it', 'Belgium', 'Belgium', 'Scopri il fascino belga con città medievali, cioccolato famoso nel mondo e birre eccezionali. Dalla Grand Place di Bruxelles ai canali di Bruges, vivi eleganza europea.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BIH', 'en', 'Bosnia and Herzegovina', 'Bosnia and Herzegovina', 'Explore the historic bridge of Mostar, stunning mountains, and Ottoman heritage. Experience the crossroads of cultures in the heart of the Balkans.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BIH', 'ar', 'Bosnia and Herzegovina', 'Bosnia and Herzegovina', 'استكشف جسر موستار التاريخي والجبال الخلابة والتراث العثماني. اختبر ملتقى الثقافات في قلب البلقان.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BIH', 'es', 'Bosnia and Herzegovina', 'Bosnia and Herzegovina', 'Explora el puente histórico de Mostar, montañas impresionantes y patrimonio otomano. Experimenta el cruce de culturas en el corazón de los Balcanes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BIH', 'fr', 'Bosnia and Herzegovina', 'Bosnia and Herzegovina', 'Explorez le pont historique de Mostar, montagnes magnifiques et héritage ottoman. Vivez le carrefour des cultures au cœur des Balkans.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BIH', 'pt', 'Bosnia and Herzegovina', 'Bosnia and Herzegovina', 'Explore a ponte histórica de Mostar, montanhas deslumbrantes e herança otomana. Experimente o cruzamento de culturas no coração dos Bálcãs.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BIH', 'ru', 'Bosnia and Herzegovina', 'Bosnia and Herzegovina', 'Исследуйте исторический мост Мостара, потрясающие горы и османское наследие. Ощутите перекресток культур в сердце Балкан.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BIH', 'de', 'Bosnia and Herzegovina', 'Bosnia and Herzegovina', 'Erkunden Sie die historische Brücke von Mostar, atemberaubende Berge und osmanisches Erbe. Erleben Sie den Schnittpunkt der Kulturen im Herzen des Balkans.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BIH', 'it', 'Bosnia and Herzegovina', 'Bosnia and Herzegovina', 'Esplora il ponte storico di Mostar, montagne mozzafiato e patrimonio ottomano. Vivi l''incrocio di culture nel cuore dei Balcani.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGR', 'en', 'Bulgaria', 'Bulgaria', 'Experience ancient Thracian heritage, Black Sea resorts, and rose valleys. Discover Bulgaria rich history and stunning mountain landscapes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGR', 'ar', 'Bulgaria', 'Bulgaria', 'استمتع بالتراث التراقي القديم ومنتجعات البحر الأسود ووديان الورود. اكتشف تاريخ بلغاريا الغني والمناظر الجبلية الرائعة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGR', 'es', 'Bulgaria', 'Bulgaria', 'Experimenta el patrimonio tracio antiguo, resorts del Mar Negro y valles de rosas. Descubre la rica historia búlgara y paisajes montañosos impresionantes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGR', 'fr', 'Bulgaria', 'Bulgaria', 'Vivez l''héritage thrace ancien, stations balnéaires de la mer Noire et vallées de roses. Découvrez la riche histoire bulgare et paysages montagneux magnifiques.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGR', 'pt', 'Bulgaria', 'Bulgaria', 'Experimente herança trácia antiga, resorts do Mar Negro e vales de rosas. Descubra rica história búlgara e paisagens montanhosas deslumbrantes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGR', 'ru', 'Bulgaria', 'Bulgaria', 'Ощутите древнее фракийское наследие, курорты Черного моря и долины роз. Откройте богатую историю Болгарии и великолепные горные ландшафты.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGR', 'de', 'Bulgaria', 'Bulgaria', 'Erleben Sie antikes thrakisches Erbe, Schwarzmeerresorts und Rosentäler. Entdecken Sie Bulgariens reiche Geschichte und atemberaubende Berglandschaften.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('BGR', 'it', 'Bulgaria', 'Bulgaria', 'Vivi l''eredità tracia antica, resort del Mar Nero e valli delle rose. Scopri la ricca storia bulgara e paesaggi montani mozzafiato.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HRV', 'en', 'Croatia', 'Croatia', 'Discover the Adriatic pearl with stunning coastlines, historic Dubrovnik, and crystal-clear waters. Experience Croatian hospitality and Mediterranean charm.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HRV', 'ar', 'Croatia', 'Croatia', 'اكتشف لؤلؤة الأدرياتيكي ذات السواحل الرائعة ودوبروفنيك التاريخية والمياه الصافية. استمتع بالضيافة الكرواتية والسحر المتوسطي.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HRV', 'es', 'Croatia', 'Croatia', 'Descubre la perla del Adriático con costas impresionantes, la histórica Dubrovnik y aguas cristalinas. Experimenta hospitalidad croata y encanto mediterráneo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HRV', 'fr', 'Croatia', 'Croatia', 'Découvrez la perle adriatique avec côtes magnifiques, Dubrovnik historique et eaux cristallines. Vivez l''hospitalité croate et le charme méditerranéen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HRV', 'pt', 'Croatia', 'Croatia', 'Descubra pérola do Adriático com costas deslumbrantes, Dubrovnik histórica e águas cristalinas. Experimente hospitalidade croata e charme mediterrâneo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HRV', 'ru', 'Croatia', 'Croatia', 'Откройте жемчужину Адриатики с потрясающим побережьем, историческим Дубровником и кристально чистыми водами. Ощутите хорватское гостеприимство и средиземноморские чары.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HRV', 'de', 'Croatia', 'Croatia', 'Entdecken Sie die Adria-Perle mit atemberaubenden Küsten, historischem Dubrovnik und kristallklarem Wasser. Erleben Sie kroatische Gastfreundschaft und mediterranen Charme.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HRV', 'it', 'Croatia', 'Croatia', 'Scopri la perla dell''Adriatico con coste mozzafiato, Dubrovnik storica e acque cristalline. Vivi l''ospitalità croata e fascino mediterraneo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CYP', 'en', 'Cyprus', 'Cyprus', 'Experience ancient Greek mythology, stunning beaches, and medieval castles. Discover Cyprus where European and Middle Eastern cultures blend harmoniously.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CYP', 'ar', 'Cyprus', 'Cyprus', 'استمتع بأساطير اليونان القديمة والشواطئ الرائعة والقلاع القروسطية. اكتشف قبرص حيث تمتزج الثقافات الأوروبية والشرق أوسطية بانسجام.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CYP', 'es', 'Cyprus', 'Cyprus', 'Experimenta mitología griega antigua, playas impresionantes y castillos medievales. Descubre Chipre donde culturas europeas y de Oriente Medio se mezclan armoniosamente.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CYP', 'fr', 'Cyprus', 'Cyprus', 'Vivez mythologie grecque ancienne, plages magnifiques et châteaux médiévaux. Découvrez Chypre où cultures européennes et moyen-orientales se mélangent harmonieusement.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CYP', 'pt', 'Cyprus', 'Cyprus', 'Experimente mitologia grega antiga, praias deslumbrantes e castelos medievais. Descubra Chipre onde culturas europeias e do Oriente Médio se misturam harmoniosamente.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CYP', 'ru', 'Cyprus', 'Cyprus', 'Ощутите древнюю греческую мифологию, потрясающие пляжи и средневековые замки. Откройте Кипр, где европейские и ближневосточные культуры гармонично сливаются.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CYP', 'de', 'Cyprus', 'Cyprus', 'Erleben Sie antike griechische Mythologie, atemberaubende Strände und mittelalterliche Burgen. Entdecken Sie Zypern, wo europäische und nahostliche Kulturen harmonisch verschmelzen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CYP', 'it', 'Cyprus', 'Cyprus', 'Vivi mitologia greca antica, spiagge mozzafiato e castelli medievali. Scopri Cipro dove culture europee e mediorientali si fondono armoniosamente.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CZE', 'en', 'Czech Republic', 'Czech Republic', 'Discover the heart of Europe with Prague''s fairy-tale architecture, world-famous beer culture, and rich medieval history. Experience centuries of art, castles, and Czech hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CZE', 'ar', 'Czech Republic', 'Czech Republic', 'اكتشف قلب أوروبا مع هندسة براغ الخرافية وثقافة الجعة المشهورة عالمياً والتاريخ العريق في العصور الوسطى. استمتع بقرون من الفن والقلاع والضيافة التشيكية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CZE', 'es', 'Czech Republic', 'Czech Republic', 'Descubre el corazón de Europa con la arquitectura de cuento de hadas de Praga, cultura cervecera mundialmente famosa e historia medieval rica. Experimenta siglos de arte, castillos y hospitalidad checa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CZE', 'fr', 'Czech Republic', 'Czech Republic', 'Découvrez le cœur de l''Europe avec architecture de conte de fées de Prague, culture bière mondialement célèbre et riche histoire médiévale. Vivez des siècles d''art, châteaux et hospitalité tchèque.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CZE', 'pt', 'Czech Republic', 'Czech Republic', 'Descubra o coração da Europa com arquitetura de conto de fadas de Praga, cultura cervejeira mundialmente famosa e rica história medieval. Experimente séculos de arte, castelos e hospitalidade tcheca.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CZE', 'ru', 'Czech Republic', 'Czech Republic', 'Откройте сердце Европы с сказочной архитектурой Праги, всемирно известной пивной культурой и богатой средневековой историей. Ощутите века искусства, замки и чешское гостеприимство.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CZE', 'de', 'Czech Republic', 'Czech Republic', 'Entdecken Sie das Herz Europas mit Prags märchenhafter Architektur, weltberühmter Bierkultur und reicher mittelalterlicher Geschichte. Erleben Sie Jahrhunderte von Kunst, Schlössern und tschechischer Gastfreundschaft.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CZE', 'it', 'Czech Republic', 'Czech Republic', 'Scopri il cuore dell''Europa con l''architettura fiabesca di Praga, cultura della birra famosa nel mondo e ricca storia medievale. Vivi secoli di arte, castelli e ospitalità ceca.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DNK', 'en', 'Denmark', 'Denmark', 'Experience Danish hygge with Copenhagen''s colorful harbors, LEGO heritage, and Viking history. Discover modern design, sustainable living, and the happiest people on Earth.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DNK', 'ar', 'Denmark', 'Denmark', 'استمتع بالهيجة الدانماركية مع موانئ كوبنهاغن الملونة وتراث ليغو وتاريخ الفايكنغ. اكتشف التصميم الحديث والحياة المستدامة وأسعد الناس على الأرض.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DNK', 'es', 'Denmark', 'Denmark', 'Experimenta el hygge danés con puertos coloridos de Copenhague, patrimonio de LEGO e historia vikinga. Descubre diseño moderno, vida sostenible y las personas más felices de la Tierra.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DNK', 'fr', 'Denmark', 'Denmark', 'Vivez le hygge danois avec ports colorés de Copenhague, héritage LEGO et histoire viking. Découvrez design moderne, vie durable et les gens les plus heureux de la Terre.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DNK', 'pt', 'Denmark', 'Denmark', 'Experimente o hygge dinamarquês com portos coloridos de Copenhague, património LEGO e história viking. Descubra design moderno, vida sustentável e as pessoas mais felizes da Terra.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DNK', 'ru', 'Denmark', 'Denmark', 'Ощутите датский хюгге с цветными гаванями Копенгагена, наследием LEGO и историей викингов. Откройте современный дизайн, устойчивую жизнь и самых счастливых людей на Земле.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DNK', 'de', 'Denmark', 'Denmark', 'Erleben Sie dänische Hygge mit Kopenhagens bunten Häfen, LEGO-Erbe und Wikingergeschichte. Entdecken Sie modernes Design, nachhaltiges Leben und die glücklichsten Menschen der Erde.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DNK', 'it', 'Denmark', 'Denmark', 'Vivi il hygge danese con porti colorati di Copenaghen, patrimonio LEGO e storia vichinga. Scopri design moderno, vita sostenibile e le persone più felici della Terra.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EST', 'en', 'Estonia', 'Estonia', 'Wander medieval streets in Tallinn, explore pristine forests, and experience digital innovation in Estonia, where tradition meets technology.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EST', 'ar', 'Estonia', 'Estonia', 'تجول في شوارع تالين العتيقة، واستكشف الغابات البكر، واختبر الابتكار الرقمي في إستونيا حيث يلتقي التراث بالتكنولوجيا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EST', 'es', 'Estonia', 'Estonia', 'Recorre las calles medievales de Tallin, explora bosques vírgenes y vive la innovación digital en Estonia, donde la tradición se une a la tecnología.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EST', 'fr', 'Estonia', 'Estonia', 'Parcourez les rues médiévales de Tallinn, explorez des forêts préservées et découvrez l’innovation numérique en Estonie, où tradition et technologie se rencontrent.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EST', 'pt', 'Estonia', 'Estonia', 'Caminhe por ruas medievais em Tallinn, explore florestas intocadas e vivencie inovação digital na Estônia, onde tradição encontra tecnologia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EST', 'ru', 'Estonia', 'Estonia', 'Прогуляйтесь по средневековым улицам Таллина, исследуйте нетронутые леса и ощутите цифровые инновации в Эстонии, где традиции встречаются с технологиями.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EST', 'de', 'Estonia', 'Estonia', 'Schlendern Sie durch mittelalterliche Straßen in Tallinn, erkunden Sie unberührte Wälder und erleben Sie digitale Innovation in Estland, wo Tradition auf Technologie trifft.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('EST', 'it', 'Estonia', 'Estonia', 'Passeggia tra le vie medievali di Tallinn, esplora foreste incontaminate e vivi l’innovazione digitale in Estonia, dove la tradizione incontra la tecnologia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'en', 'Finland', 'Finland', 'Experience Finnish sisu with pristine lakes, Northern Lights magic, and sauna culture. Discover Helsinki''s design scene, Lapland''s reindeer, and the land of a thousand lakes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'ar', 'Finland', 'Finland', 'استمتع بالسيسو الفنلندي مع البحيرات النقية وسحر الأضواء الشمالية وثقافة الساونا. اكتشف مشهد التصميم في هلسنكي ورنة لابلاند وأرض الألف بحيرة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'es', 'Finland', 'Finland', 'Experimenta el sisu finlandés con lagos prístinos, magia de auroras boreales y cultura de sauna. Descubre la escena de diseño de Helsinki, renos de Laponia y la tierra de los mil lagos.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'fr', 'Finland', 'Finland', 'Vivez le sisu finlandais avec lacs vierges, magie des aurores boréales et culture sauna. Découvrez scène design d''Helsinki, rennes de Laponie et pays des mille lacs.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'pt', 'Finland', 'Finland', 'Experimente o sisu finlandês com lagos imaculados, magia da aurora boreal e cultura da sauna. Descubra cena de design de Helsínquia, renas da Lapônia e a terra dos mil lagos.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'ru', 'Finland', 'Finland', 'Ощутите финский сису с нетронутыми озерами, магией северного сияния и культурой сауны. Откройте дизайн-сцену Хельсинки, оленей Лапландии и страну тысячи озёр.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'de', 'Finland', 'Finland', 'Erleben Sie finnische Sisu mit unberührten Seen, Nordlichter-Magie und Sauna-Kultur. Entdecken Sie Helsinkis Designszene, Lapplands Rentiere und das Land der tausend Seen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FIN', 'it', 'Finland', 'Finland', 'Vivi il sisu finlandese con laghi incontaminati, magia dell''aurora boreale e cultura della sauna. Scopri la scena del design di Helsinki, renne della Lapponia e la terra dei mille laghi.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'en', 'France', 'France', 'Experience the epitome of elegance with world-class cuisine, iconic landmarks, and romantic countryside. From the Eiffel Tower to the French Riviera.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'ar', 'فرنسا', 'فرنسا', 'اختبر قمة الأناقة مع المأكولات الراقية والمعالم الشهيرة والريف الرومانسي. من برج إيفل إلى الريفييرا الفرنسية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'es', 'Francia', 'Francia', 'Experimenta el epítome de la elegancia con cocina de clase mundial, monumentos icónicos y paisajes románticos. Desde la Torre Eiffel hasta la Riviera Francesa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'fr', 'France', 'France', 'Découvrez l''''apogée de l''''élégance avec une cuisine de classe mondiale, des monuments emblématiques et des campagnes romantiques. De la Tour Eiffel à la Côte d''''Azur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'pt', 'França', 'França', 'Experimente o epítome da elegância com culinária de classe mundial, monumentos icônicos e paisagens românticas. Da Torre Eiffel à Riviera Francesa.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'ru', 'Франция', 'Франция', 'Откройте для себя воплощение элегантности с кухней мирового класса, знаковыми достопримечательностями и романтической сельской местностью. От Эйфелевой башни до Французской Ривьеры.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'de', 'Frankreich', 'Frankreich', 'Erleben Sie den Inbegriff der Eleganz mit Weltklasse-Küche, ikonischen Wahrzeichen und romantischer Landschaft. Vom Eiffelturm bis zur Côte d''''Azur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FRA', 'it', 'Francia', 'Francia', 'Scopri l''''epitome dell''''eleganza con cucina di classe mondiale, monumenti iconici e campagne romantiche. Dalla Torre Eiffel alla Costa Azzurra.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'en', 'Germany', 'Germany', 'Experience the heart of Europe with fairy-tale castles, vibrant cities, and world-renowned cultural heritage. From the Alps to the Baltic Sea.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'ar', 'ألمانيا', 'ألمانيا', 'اختبر قلب أوروبا بقلاعها الخيالية ومدنها النابضة بالحياة وتراثها الثقافي المشهور عالمياً. من جبال الألب إلى بحر البلطيق.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'es', 'Alemania', 'Alemania', 'Experimenta el corazón de Europa con castillos de cuento de hadas, ciudades vibrantes y patrimonio cultural de renombre mundial. Desde los Alpes hasta el Mar Báltico.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'fr', 'Allemagne', 'Allemagne', 'Découvrez le cœur de l''''Europe avec ses châteaux de conte de fées, ses villes dynamiques et son patrimoine culturel de renommée mondiale. Des Alpes à la mer Baltique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'pt', 'Alemanha', 'Alemanha', 'Experimente o coração da Europa com castelos de contos de fadas, cidades vibrantes e patrimônio cultural de renome mundial. Dos Alpes ao Mar Báltico.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'ru', 'Германия', 'Германия', 'Откройте для себя сердце Европы со сказочными замками, яркими городами и всемирно известным культурным наследием. От Альп до Балтийского моря.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'de', 'Deutschland', 'Deutschland', 'Erleben Sie das Herz Europas mit märchenhaften Schlössern, lebendigen Städten und weltberühmtem Kulturerbe. Von den Alpen bis zur Ostsee.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('DEU', 'it', 'Germania', 'Germania', 'Scopri il cuore dell''''Europa con castelli fiabeschi, città vivaci e patrimonio culturale di fama mondiale. Dalle Alpi al Mar Baltico.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'en', 'Greece', 'Greece', 'Discover ancient Greece with stunning islands, rich mythology, and Mediterranean charm. From Acropolis to Santorini sunsets, experience the cradle of Western civilization.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'ar', 'Greece', 'Greece', 'اكتشف اليونان القديمة مع الجزر الخلابة والأساطير الغنية والسحر المتوسطي. من الأكروبوليس إلى غروب الشمس في سانتوريني، استمتع بمهد الحضارة الغربية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'es', 'Greece', 'Greece', 'Descubre la antigua Grecia con islas impresionantes, rica mitología y encanto mediterráneo. Desde la Acrópolis hasta los atardeceres de Santorini, experimenta la cuna de la civilización occidental.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'fr', 'Greece', 'Greece', 'Découvrez la Grèce antique avec îles magnifiques, riche mythologie et charme méditerranéen. De l''Acropole aux couchers de soleil de Santorin, vivez le berceau de la civilisation occidentale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'pt', 'Greece', 'Greece', 'Descubra a Grécia antiga com ilhas deslumbrantes, rica mitologia e charme mediterrâneo. Da Acrópole aos pôres do sol de Santorini, experimente o berço da civilização ocidental.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'ru', 'Greece', 'Greece', 'Откройте древнюю Грецию с потрясающими островами, богатой мифологией и средиземноморским очарованием. От Акрополя до закатов Санторини, ощутите колыбель западной цивилизации.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'de', 'Greece', 'Greece', 'Entdecken Sie das antike Griechenland mit atemberaubenden Inseln, reicher Mythologie und mediterranem Charme. Von der Akropolis bis zu Santorins Sonnenuntergängen, erleben Sie die Wiege der westlichen Zivilisation.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('GRC', 'it', 'Greece', 'Greece', 'Scopri l''antica Grecia con isole mozzafiato, ricca mitologia e fascino mediterraneo. Dall''Acropoli ai tramonti di Santorini, vivi la culla della civiltà occidentale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HUN', 'en', 'Hungary', 'Hungary', 'Soak in Budapest’s thermal baths, cruise the Danube, and discover Hungary’s rich history, vibrant festivals, and hearty cuisine.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HUN', 'ar', 'Hungary', 'Hungary', 'استمتع بحمامات بودابست الحرارية، وجولة في نهر الدانوب، واكتشف تاريخ المجر الغني ومهرجاناتها النابضة ومأكولاتها الشهية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HUN', 'es', 'Hungary', 'Hungary', 'Relájate en los baños termales de Budapest, navega por el Danubio y descubre la historia, festivales y gastronomía de Hungría.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HUN', 'fr', 'Hungary', 'Hungary', 'Détendez-vous dans les bains thermaux de Budapest, naviguez sur le Danube et découvrez l’histoire, les festivals et la cuisine de la Hongrie.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HUN', 'pt', 'Hungary', 'Hungary', 'Relaxe nas termas de Budapeste, navegue pelo Danúbio e descubra a história, festivais e culinária da Hungria.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HUN', 'ru', 'Hungary', 'Hungary', 'Погрузитесь в термальные купальни Будапешта, прокатитесь по Дунаю и откройте для себя богатую историю, фестивали и кухню Венгрии.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HUN', 'de', 'Hungary', 'Hungary', 'Entspannen Sie in Budapests Thermalbädern, fahren Sie auf der Donau und entdecken Sie Geschichte, Festivals und Küche Ungarns.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('HUN', 'it', 'Hungary', 'Hungary', 'Rilassati nelle terme di Budapest, naviga sul Danubio e scopri la storia, i festival e la cucina dell’Ungheria.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISL', 'en', 'Iceland', 'Iceland', 'Witness geysers, waterfalls, and the northern lights in Iceland, a land of fire and ice with dramatic landscapes and rich Norse heritage.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISL', 'ar', 'Iceland', 'Iceland', 'شاهد الينابيع الحارة والشلالات والشفق القطبي في آيسلندا، أرض النار والجليد ذات المناظر الدرامية والتراث الإسكندنافي الغني.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISL', 'es', 'Iceland', 'Iceland', 'Contempla géiseres, cascadas y auroras boreales en Islandia, tierra de fuego y hielo con paisajes dramáticos y herencia nórdica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISL', 'fr', 'Iceland', 'Iceland', 'Admirez geysers, cascades et aurores boréales en Islande, terre de feu et de glace aux paysages spectaculaires et au riche héritage nordique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISL', 'pt', 'Iceland', 'Iceland', 'Veja gêiseres, cachoeiras e auroras boreais na Islândia, terra de fogo e gelo com paisagens dramáticas e herança nórdica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISL', 'ru', 'Iceland', 'Iceland', 'Полюбуйтесь гейзерами, водопадами и северным сиянием в Исландии — стране огня и льда с драматическими пейзажами и богатым нордическим наследием.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISL', 'de', 'Iceland', 'Iceland', 'Erleben Sie Geysire, Wasserfälle und Nordlichter in Island, einem Land aus Feuer und Eis mit dramatischen Landschaften und reichem nordischem Erbe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ISL', 'it', 'Iceland', 'Iceland', 'Ammira geyser, cascate e aurore boreali in Islanda, terra di fuoco e ghiaccio con paesaggi spettacolari e ricca eredità norrena.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRL', 'en', 'Ireland', 'Ireland', 'Experience the Emerald Isle’s rolling green hills, lively pubs, ancient castles, and warm Irish hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRL', 'ar', 'Ireland', 'Ireland', 'استمتع بتلال أيرلندا الخضراء، والحانات النابضة، والقلاع القديمة، وضيافة الأيرلنديين الدافئة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRL', 'es', 'Ireland', 'Ireland', 'Vive las colinas verdes, los pubs animados, los castillos antiguos y la hospitalidad cálida de Irlanda.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRL', 'fr', 'Ireland', 'Ireland', 'Découvrez les collines verdoyantes, les pubs animés, les châteaux anciens et l’hospitalité chaleureuse de l’Irlande.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRL', 'pt', 'Ireland', 'Ireland', 'Viva as colinas verdes, os pubs animados, os castelos antigos e a hospitalidade calorosa da Irlanda.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRL', 'ru', 'Ireland', 'Ireland', 'Ощутите зелёные холмы, оживлённые пабы, древние замки и тёплое ирландское гостеприимство.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRL', 'de', 'Ireland', 'Ireland', 'Erleben Sie grüne Hügel, lebhafte Pubs, alte Burgen und die herzliche Gastfreundschaft Irlands.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('IRL', 'it', 'Ireland', 'Ireland', 'Vivi le verdi colline, i pub animati, gli antichi castelli e la calorosa ospitalità irlandese.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'en', 'Italy', 'Italy', 'Immerse yourself in timeless beauty with Renaissance art, ancient ruins, and world-famous cuisine. From Rome to the Amalfi Coast.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'ar', 'إيطاليا', 'إيطاليا', 'انغمس في الجمال الخالد مع فن عصر النهضة والآثار القديمة والمأكولات المشهورة عالمياً. من روما إلى ساحل أمالفي.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'es', 'Italia', 'Italia', 'Sumérgete en la belleza atemporal con arte renacentista, ruinas antiguas y cocina mundialmente famosa. Desde Roma hasta la Costa Amalfitana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'fr', 'Italie', 'Italie', 'Plongez dans une beauté intemporelle avec l''''art de la Renaissance, des ruines antiques et une cuisine de renommée mondiale. De Rome à la côte amalfitaine.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'pt', 'Itália', 'Itália', 'Mergulhe na beleza atemporal com arte renascentista, ruínas antigas e culinária mundialmente famosa. De Roma à Costa Amalfitana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'ru', 'Италия', 'Италия', 'Погрузитесь в вечную красоту с искусством Возрождения, древними руинами и всемирно известной кухней. От Рима до побережья Амальфи.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'de', 'Italien', 'Italien', 'Tauchen Sie ein in zeitlose Schönheit mit Renaissance-Kunst, antiken Ruinen und weltberühmter Küche. Von Rom bis zur Amalfiküste.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ITA', 'it', 'Italia', 'Italia', 'Immergiti nella bellezza senza tempo con arte rinascimentale, rovine antiche e cucina di fama mondiale. Da Roma alla Costiera Amalfitana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LVA', 'en', 'Latvia', 'Latvia', 'Stroll through Riga’s Art Nouveau streets, explore Baltic beaches, and discover Latvia’s folk traditions and lush forests.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LVA', 'ar', 'Latvia', 'Latvia', 'تجول في شوارع ريغا الفنية، واستكشف شواطئ البلطيق، واكتشف التقاليد الشعبية والغابات الخضراء في لاتفيا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LVA', 'es', 'Latvia', 'Latvia', 'Pasea por las calles Art Nouveau de Riga, explora playas bálticas y descubre tradiciones populares y bosques de Letonia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LVA', 'fr', 'Latvia', 'Latvia', 'Promenez-vous dans les rues Art nouveau de Riga, explorez les plages baltes et découvrez les traditions populaires et forêts luxuriantes de la Lettonie.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LVA', 'pt', 'Latvia', 'Latvia', 'Caminhe pelas ruas Art Nouveau de Riga, explore praias bálticas e descubra tradições folclóricas e florestas exuberantes da Letônia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LVA', 'ru', 'Latvia', 'Latvia', 'Прогуляйтесь по улицам Ар-Нуво в Риге, исследуйте балтийские пляжи и откройте для себя народные традиции и леса Латвии.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LVA', 'de', 'Latvia', 'Latvia', 'Schlendern Sie durch Rigas Jugendstilstraßen, erkunden Sie baltische Strände und entdecken Sie lettische Volkstraditionen und Wälder.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LVA', 'it', 'Latvia', 'Latvia', 'Passeggia tra le vie Art Nouveau di Riga, esplora le spiagge baltiche e scopri le tradizioni popolari e le foreste della Lettonia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LIE', 'en', 'Liechtenstein', 'Liechtenstein', 'Discover alpine beauty, medieval castles, and charming villages in Liechtenstein, nestled between Switzerland and Austria.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LIE', 'ar', 'Liechtenstein', 'Liechtenstein', 'اكتشف الجمال الألبي، والقلاع القديمة، والقرى الساحرة في ليختنشتاين بين سويسرا والنمسا.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LIE', 'es', 'Liechtenstein', 'Liechtenstein', 'Descubre la belleza alpina, castillos medievales y pueblos encantadores en Liechtenstein, entre Suiza y Austria.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LIE', 'fr', 'Liechtenstein', 'Liechtenstein', 'Découvrez la beauté alpine, les châteaux médiévaux et les villages charmants du Liechtenstein, entre la Suisse et l’Autriche.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LIE', 'pt', 'Liechtenstein', 'Liechtenstein', 'Descubra a beleza alpina, castelos medievais e vilarejos encantadores em Liechtenstein, entre a Suíça e a Áustria.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LIE', 'ru', 'Liechtenstein', 'Liechtenstein', 'Откройте для себя альпийскую красоту, средневековые замки и очаровательные деревни Лихтенштейна между Швейцарией и Австрией.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LIE', 'de', 'Liechtenstein', 'Liechtenstein', 'Entdecken Sie alpine Schönheit, mittelalterliche Burgen und charmante Dörfer in Liechtenstein zwischen der Schweiz und Österreich.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LIE', 'it', 'Liechtenstein', 'Liechtenstein', 'Scopri la bellezza alpina, i castelli medievali e i villaggi incantevoli del Liechtenstein, tra Svizzera e Austria.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LTU', 'en', 'Lithuania', 'Lithuania', 'Lithuania is a Baltic gem known for its medieval old towns, lush forests, and vibrant cultural traditions. Explore Vilnius, Trakai Castle, and the Curonian Spit.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LTU', 'ar', 'ليتوانيا', 'ليتوانيا', 'ليتوانيا جوهرة البلطيق، تشتهر بمدنها القديمة وغاباتها الكثيفة وتقاليدها الثقافية الغنية. استكشف فيلنيوس وقلعة تراكي وشاطئ كورونيان.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LTU', 'es', 'Lituania', 'Lituania', 'Lituania es un tesoro báltico famoso por sus ciudades medievales, bosques y tradiciones culturales vibrantes. Descubre Vilna y el castillo de Trakai.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LTU', 'fr', 'Lituanie', 'Lituanie', 'La Lituanie est un joyau balte réputé pour ses villes médiévales, ses forêts et ses traditions culturelles. Explorez Vilnius et le château de Trakai.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LTU', 'pt', 'Lituânia', 'Lituânia', 'A Lituânia é um tesouro báltico com cidades medievais, florestas e tradições culturais vibrantes. Descubra Vilnius e o Castelo de Trakai.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LTU', 'ru', 'Литва', 'Литва', 'Литва — балтийская жемчужина с красивыми городами, лесами и богатой культурой. Откройте для себя Вильнюс и замок Тракай.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LTU', 'de', 'Litauen', 'Litauen', 'Litauen ist ein baltisches Juwel mit mittelalterlichen Städten, Wäldern und lebendigen Traditionen. Entdecken Sie Vilnius und die Burg Trakai.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LTU', 'it', 'Lituania', 'Lituania', 'La Lituania è una gemma baltica famosa per le sue città medievali, foreste e tradizioni culturali. Esplora Vilnius e il castello di Trakai.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LUX', 'en', 'Luxembourg', 'Luxembourg', 'Luxembourg is a small but wealthy country known for its medieval castles, scenic valleys, and cosmopolitan capital. Experience its unique blend of cultures.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LUX', 'ar', 'لوكسمبورغ', 'لوكسمبورغ', 'لوكسمبورغ دولة صغيرة غنية تشتهر بقلاعها ووديانها الخلابة وعاصمتها العالمية. استمتع بمزيج الثقافات الفريد.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LUX', 'es', 'Luxemburgo', 'Luxemburgo', 'Luxemburgo es un pequeño país famoso por sus castillos medievales, valles y su capital cosmopolita. Disfruta su mezcla cultural única.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LUX', 'fr', 'Luxembourg', 'Luxembourg', 'Le Luxembourg est un petit pays riche, réputé pour ses châteaux médiévaux, ses vallées et sa capitale cosmopolite. Découvrez son mélange culturel unique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LUX', 'pt', 'Luxemburgo', 'Luxemburgo', 'Luxemburgo é um pequeno país rico conhecido por seus castelos medievais, vales e capital cosmopolita. Experimente sua mistura única de culturas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LUX', 'ru', 'Люксембург', 'Люксембург', 'Люксембург — небольшая богатая страна, известная своими замками, долинами и космополитичной столицей. Ощутите уникальное смешение культур.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LUX', 'de', 'Luxemburg', 'Luxemburg', 'Luxemburg ist ein kleines, wohlhabendes Land, bekannt für seine Burgen, Täler und die kosmopolitische Hauptstadt. Erleben Sie die einzigartige Kulturmischung.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('LUX', 'it', 'Lussemburgo', 'Lussemburgo', 'Il Lussemburgo è un piccolo paese ricco noto per i suoi castelli medievali, valli e la capitale cosmopolita. Vivi la sua miscela culturale unica.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLT', 'en', 'Malta', 'Malta', 'Malta is a Mediterranean island nation with ancient temples, stunning coastlines, and a rich history. Enjoy its sunny climate and vibrant culture.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLT', 'ar', 'مالطا', 'مالطا', 'مالطا جزيرة متوسطية تشتهر بمعابدها القديمة وسواحلها الخلابة وتاريخها العريق. استمتع بمناخها المشمس وثقافتها النابضة بالحياة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLT', 'es', 'Malta', 'Malta', 'Malta es una isla mediterránea famosa por sus templos antiguos, costas impresionantes y su historia rica. Disfruta de su clima soleado y cultura vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLT', 'fr', 'Malte', 'Malte', 'Malte est une île méditerranéenne réputée pour ses temples anciens, ses côtes magnifiques et son histoire riche. Profitez de son climat ensoleillé et de sa culture dynamique.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLT', 'pt', 'Malta', 'Malta', 'Malta é uma ilha mediterrânea conhecida por seus templos antigos, belas costas e rica história. Aproveite o clima ensolarado e a cultura vibrante.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLT', 'ru', 'Мальта', 'Мальта', 'Мальта — средиземноморский остров с древними храмами, красивыми берегами и богатой историей. Наслаждайтесь солнечным климатом и яркой культурой.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLT', 'de', 'Malta', 'Malta', 'Malta ist eine Mittelmeerinsel mit antiken Tempeln, atemberaubenden Küsten und reicher Geschichte. Genießen Sie das sonnige Klima und die lebendige Kultur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MLT', 'it', 'Malta', 'Malta', 'Malta è un’isola mediterranea nota per i suoi templi antichi, le coste spettacolari e la storia ricca. Goditi il clima soleggiato e la cultura vivace.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDA', 'en', 'Moldova', 'Moldova', 'Moldova is known for its rolling vineyards, historic monasteries, and warm hospitality. Discover its charming countryside and rich traditions.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDA', 'ar', 'مولدوفا', 'مولدوفا', 'مولدوفا تشتهر بكروم العنب والأديرة التاريخية ودفء أهلها. اكتشف ريفها الساحر وتقاليدها الغنية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDA', 'es', 'Moldavia', 'Moldavia', 'Moldavia es famosa por sus viñedos, monasterios históricos y hospitalidad. Descubre su campo encantador y tradiciones ricas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDA', 'fr', 'Moldavie', 'Moldavie', 'La Moldavie est connue pour ses vignobles, monastères historiques et hospitalité. Découvrez sa campagne charmante et ses traditions.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDA', 'pt', 'Moldávia', 'Moldávia', 'A Moldávia é conhecida por seus vinhedos, mosteiros históricos e hospitalidade calorosa. Descubra o campo encantador e as tradições ricas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDA', 'ru', 'Молдова', 'Молдова', 'Молдова известна своими виноградниками, монастырями и гостеприимством. Откройте для себя её сельскую местность и богатые традиции.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDA', 'de', 'Moldawien', 'Moldawien', 'Moldawien ist bekannt für seine Weinberge, Klöster und herzliche Gastfreundschaft. Entdecken Sie die charmante Landschaft und Traditionen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MDA', 'it', 'Moldavia', 'Moldavia', 'La Moldavia è famosa per i suoi vigneti, monasteri storici e ospitalità. Scopri la campagna incantevole e le ricche tradizioni.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MCO', 'en', 'Monaco', 'Monaco', 'Monaco is a glamorous city-state on the French Riviera, famous for its casinos, luxury yachts, and the Grand Prix. Enjoy its Mediterranean charm.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MCO', 'ar', 'موناكو', 'موناكو', 'موناكو مدينة ساحرة على الريفييرا الفرنسية، تشتهر بكازينوهاتها ويخوتها الفاخرة وسباق الجائزة الكبرى. استمتع بسحر البحر المتوسط.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MCO', 'es', 'Mónaco', 'Mónaco', 'Mónaco es una ciudad-estado glamurosa famosa por sus casinos, yates de lujo y el Gran Premio. Disfruta su encanto mediterráneo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MCO', 'fr', 'Monaco', 'Monaco', 'Monaco est une cité-État glamour sur la Côte d’Azur, célèbre pour ses casinos, yachts de luxe et le Grand Prix. Profitez de son charme méditerranéen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MCO', 'pt', 'Mônaco', 'Mônaco', 'Mônaco é uma cidade-estado glamourosa famosa por seus cassinos, iates de luxo e o Grande Prêmio. Aproveite seu charme mediterrâneo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MCO', 'ru', 'Монако', 'Монако', 'Монако — гламурный город-государство на Лазурном берегу, известный казино, яхтами и Гран-при. Наслаждайтесь средиземноморским шармом.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MCO', 'de', 'Monaco', 'Monaco', 'Monaco ist eine glamouröse Stadt auf der Côte d’Azur, berühmt für Casinos, Luxusyachten und den Grand Prix. Genießen Sie mediterranen Charme.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MCO', 'it', 'Monaco', 'Monaco', 'Monaco è una città-stato glamour sulla Costa Azzurra, famosa per i casinò, gli yacht di lusso e il Gran Premio. Goditi il fascino mediterraneo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNE', 'en', 'Montenegro', 'Montenegro', 'Montenegro is a Balkan country with dramatic mountains, Adriatic beaches, and medieval villages. Explore Kotor Bay and Durmitor National Park.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNE', 'ar', 'الجبل الأسود', 'الجبل الأسود', 'الجبل الأسود بلد بلقاني يتميز بجباله الشاهقة وشواطئه على الأدرياتيكي وقراه القديمة. استكشف خليج كوتور وحديقة دورميتور الوطنية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNE', 'es', 'Montenegro', 'Montenegro', 'Montenegro es un país balcánico con montañas, playas adriáticas y pueblos medievales. Explora la bahía de Kotor y el Parque Nacional Durmitor.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNE', 'fr', 'Monténégro', 'Monténégro', 'Le Monténégro est un pays des Balkans avec montagnes, plages adriatiques et villages médiévaux. Découvrez la baie de Kotor et le parc Durmitor.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNE', 'pt', 'Montenegro', 'Montenegro', 'Montenegro é um país dos Bálcãs com montanhas, praias do Adriático e vilas medievais. Explore a Baía de Kotor e o Parque Nacional Durmitor.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNE', 'ru', 'Черногория', 'Черногория', 'Черногория — балканская страна с горами, пляжами Адриатики и средневековыми деревнями. Откройте для себя Котор и национальный парк Дурмитор.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNE', 'de', 'Montenegro', 'Montenegro', 'Montenegro ist ein Balkanstaat mit Bergen, Adriastränden und mittelalterlichen Dörfern. Entdecken Sie die Bucht von Kotor und den Durmitor-Nationalpark.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MNE', 'it', 'Montenegro', 'Montenegro', 'Il Montenegro è un paese balcanico con montagne, spiagge adriatiche e villaggi medievali. Esplora la baia di Kotor e il Parco Durmitor.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'en', 'Netherlands', 'Netherlands', 'Discover the Netherlands with iconic canals, vibrant tulip fields, and world-class museums. From Amsterdam charm to innovative design, experience Dutch culture and cycling paradise.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'ar', 'Netherlands', 'Netherlands', 'اكتشف هولندا مع القنوات الرمزية وحقول التوليب النابضة بالحياة والمتاحف عالمية المستوى. من سحر أمستردام إلى التصميم المبتكر، استمتع بالثقافة الهولندية وجنة الدراجات.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'es', 'Netherlands', 'Netherlands', 'Descubre los Países Bajos con canales icónicos, campos de tulipanes vibrantes y museos de clase mundial. Desde el encanto de Ámsterdam hasta el diseño innovador, experimenta la cultura holandesa y el paraíso ciclista.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'fr', 'Netherlands', 'Netherlands', 'Découvrez les Pays-Bas avec canaux iconiques, champs de tulipes vibrants et musées de classe mondiale. Du charme d''Amsterdam au design innovant, vivez la culture néerlandaise et le paradis du cyclisme.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'pt', 'Netherlands', 'Netherlands', 'Descubra os Países Baixos com canais icônicos, campos de tulipas vibrantes e museus de classe mundial. Do charme de Amsterdã ao design inovador, experimente a cultura holandesa e o paraíso das bicicletas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'ru', 'Netherlands', 'Netherlands', 'Откройте Нидерланды с иконическими каналами, яркими тюльпановыми полями и музеями мирового уровня. От очарования Амстердама до инновационного дизайна, ощутите голландскую культуру и велосипедный рай.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'de', 'Netherlands', 'Netherlands', 'Entdecken Sie die Niederlande mit ikonischen Kanälen, lebendigen Tulpenfeldern und Weltklasse-Museen. Von Amsterdams Charme bis zu innovativem Design, erleben Sie niederländische Kultur und Fahrradparadies.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NLD', 'it', 'Netherlands', 'Netherlands', 'Scopri i Paesi Bassi con canali iconici, campi di tulipani vibranti e musei di classe mondiale. Dal fascino di Amsterdam al design innovativo, vivi la cultura olandese e il paradiso delle biciclette.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MKD', 'en', 'North Macedonia', 'North Macedonia', 'North Macedonia is a land of lakes, mountains, and ancient history. Visit Lake Ohrid, Skopje, and explore its diverse cultural heritage.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MKD', 'ar', 'مقدونيا الشمالية', 'مقدونيا الشمالية', 'مقدونيا الشمالية أرض البحيرات والجبال والتاريخ العريق. زر بحيرة أوهريد وسكوبيا واكتشف تراثها الثقافي المتنوع.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MKD', 'es', 'Macedonia del Norte', 'Macedonia del Norte', 'Macedonia del Norte es tierra de lagos, montañas e historia antigua. Visita el lago Ohrid y Skopie.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MKD', 'fr', 'Macédoine du Nord', 'Macédoine du Nord', 'La Macédoine du Nord est un pays de lacs, montagnes et histoire ancienne. Visitez le lac Ohrid et Skopje.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MKD', 'pt', 'Macedônia do Norte', 'Macedônia do Norte', 'A Macedônia do Norte é uma terra de lagos, montanhas e história antiga. Visite o Lago Ohrid e Skopje.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MKD', 'ru', 'Северная Македония', 'Северная Македония', 'Северная Македония — страна озёр, гор и древней истории. Посетите Охрид и Скопье, откройте её культурное наследие.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MKD', 'de', 'Nordmazedonien', 'Nordmazedonien', 'Nordmazedonien ist ein Land der Seen, Berge und alten Geschichte. Besuchen Sie den Ohridsee und Skopje.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MKD', 'it', 'Macedonia del Nord', 'Macedonia del Nord', 'La Macedonia del Nord è una terra di laghi, montagne e storia antica. Visita il lago Ohrid e Skopje.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NOR', 'en', 'Norway', 'Norway', 'Experience Norway''s breathtaking fjords, Northern Lights, and midnight sun. Discover Viking heritage, modern cities like Oslo and Bergen, and pristine Arctic wilderness.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NOR', 'ar', 'Norway', 'Norway', 'استمتع بالمضايق البحرية الخلابة في النرويج والأضواء الشمالية وشمس منتصف الليل. اكتشف تراث الفايكنغ، والمدن الحديثة مثل أوسلو وبيرغن، والبرية القطبية النقية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NOR', 'es', 'Norway', 'Norway', 'Experimenta los impresionantes fiordos de Noruega, auroras boreales y sol de medianoche. Descubre el patrimonio vikingo, ciudades modernas como Oslo y Bergen, y naturaleza ártica prístina.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NOR', 'fr', 'Norway', 'Norway', 'Vivez les fjords à couper le souffle de la Norvège, aurores boréales et soleil de minuit. Découvrez héritage viking, villes modernes comme Oslo et Bergen, et nature arctique vierge.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NOR', 'pt', 'Norway', 'Norway', 'Experimente os fiordes de tirar o fôlego da Noruega, aurora boreal e sol da meia-noite. Descubra património viking, cidades modernas como Oslo e Bergen, e natureza ártica imaculada.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NOR', 'ru', 'Norway', 'Norway', 'Ощутите захватывающие фьорды Норвегии, северное сияние и полярное солнце. Откройте наследие викингов, современные города как Осло и Берген, и нетронутую арктическую природу.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NOR', 'de', 'Norway', 'Norway', 'Erleben Sie Norwegens atemberaubende Fjorde, Nordlichter und Mitternachtssonne. Entdecken Sie Wikinger-Erbe, moderne Städte wie Oslo und Bergen, und unberührte arktische Wildnis.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NOR', 'it', 'Norway', 'Norway', 'Vivi i fiordi mozzafiato della Norvegia, aurore boreali e sole di mezzanotte. Scopri patrimonio vichingo, città moderne come Oslo e Bergen, e natura artica incontaminata.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('POL', 'en', 'Poland', 'Poland', 'Poland is a country of medieval cities, castles, and natural beauty. Explore Kraków, Warsaw, the Tatra Mountains, and vibrant traditions.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('POL', 'ar', 'بولندا', 'بولندا', 'بولندا بلد المدن التاريخية والقلاع والطبيعة الخلابة. استكشف كراكوف ووارسو وجبال تاترا وتقاليدها النابضة بالحياة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('POL', 'es', 'Polonia', 'Polonia', 'Polonia es un país de ciudades medievales, castillos y belleza natural. Descubre Cracovia, Varsovia y los Montes Tatras.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('POL', 'fr', 'Pologne', 'Pologne', 'La Pologne est un pays de villes médiévales, châteaux et beautés naturelles. Explorez Cracovie, Varsovie et les Tatras.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('POL', 'pt', 'Polônia', 'Polônia', 'A Polônia é um país de cidades medievais, castelos e belezas naturais. Explore Cracóvia, Varsóvia e as Montanhas Tatra.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('POL', 'ru', 'Польша', 'Польша', 'Польша — страна средневековых городов, замков и природной красоты. Откройте для себя Краков, Варшаву и Татры.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('POL', 'de', 'Polen', 'Polen', 'Polen ist ein Land mittelalterlicher Städte, Burgen und Naturschönheiten. Entdecken Sie Krakau, Warschau und die Tatra.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('POL', 'it', 'Polonia', 'Polonia', 'La Polonia è un paese di città medievali, castelli e bellezze naturali. Esplora Cracovia, Varsavia e i Monti Tatra.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'en', 'Portugal', 'Portugal', 'Discover Portugal''s timeless charm from historic Lisbon to stunning Porto, sun-soaked Algarve beaches, and world-famous port wine valleys. Experience centuries of maritime heritage.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'ar', 'Portugal', 'Portugal', 'اكتشف سحر البرتغال الخالد من لشبونة التاريخية إلى بورتو الرائعة، وشواطئ الغارف المشمسة، ووديان نبيذ البورت الشهيرة عالمياً. استمتع بقرون من التراث البحري.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'es', 'Portugal', 'Portugal', 'Descubre el encanto eterno de Portugal desde la histórica Lisboa hasta la impresionante Oporto, playas soleadas del Algarve y famosos valles de vino de oporto. Experimenta siglos de patrimonio marítimo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'fr', 'Portugal', 'Portugal', 'Découvrez le charme intemporel du Portugal de Lisbonne historique à magnifique Porto, plages ensoleillées de l''Algarve et vallées de vin de Porto mondialement célèbres. Vivez des siècles d''héritage maritime.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'pt', 'Portugal', 'Portugal', 'Descubra o charme eterno de Portugal desde a histórica Lisboa ao deslumbrante Porto, praias ensolaradas do Algarve e famosos vales do vinho do Porto. Experimente séculos de património marítimo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'ru', 'Portugal', 'Portugal', 'Откройте вечное очарование Португалии от исторической Лиссабона до потрясающего Порту, солнечных пляжей Алгарви и всемирно известных долин портвейна. Ощутите века морского наследия.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'de', 'Portugal', 'Portugal', 'Entdecken Sie Portugals zeitlosen Charme vom historischen Lissabon bis zum atemberaubenden Porto, sonnigen Algarve-Stränden und weltberühmten Portwein-Tälern. Erleben Sie jahrhundertealtes Seefahrtserbe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PRT', 'it', 'Portugal', 'Portugal', 'Scopri il fascino senza tempo del Portogallo dalla storica Lisbona alla splendida Porto, spiagge soleggiate dell''Algarve e famose valli del vino di Porto. Vivi secoli di patrimonio marittimo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ROU', 'en', 'Romania', 'Romania', 'Romania is a land of castles, mountains, and folklore. Explore Transylvania, the Carpathians, and vibrant cities like Bucharest.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ROU', 'ar', 'رومانيا', 'رومانيا', 'رومانيا أرض القلاع والجبال والأساطير. استكشف ترانسيلفانيا وجبال الكاربات ومدنها النابضة بالحياة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ROU', 'es', 'Rumanía', 'Rumanía', 'Rumanía es tierra de castillos, montañas y folclore. Descubre Transilvania, los Cárpatos y Bucarest.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ROU', 'fr', 'Roumanie', 'Roumanie', 'La Roumanie est un pays de châteaux, montagnes et folklore. Explorez la Transylvanie, les Carpates et Bucarest.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ROU', 'pt', 'Romênia', 'Romênia', 'A Romênia é uma terra de castelos, montanhas e folclore. Explore a Transilvânia, os Cárpatos e Bucareste.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ROU', 'ru', 'Румыния', 'Румыния', 'Румыния — страна замков, гор и фольклора. Откройте для себя Трансильванию, Карпаты и Бухарест.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ROU', 'de', 'Rumänien', 'Rumänien', 'Rumänien ist ein Land der Burgen, Berge und Folklore. Entdecken Sie Transsilvanien, die Karpaten und Bukarest.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ROU', 'it', 'Romania', 'Romania', 'La Romania è una terra di castelli, montagne e folclore. Esplora la Transilvania, i Carpazi e Bucarest.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RUS', 'en', 'Russia', 'Russia', 'Russia is the world’s largest country, known for its vast landscapes, rich history, and iconic cities like Moscow and St. Petersburg.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RUS', 'ar', 'روسيا', 'روسيا', 'روسيا أكبر دولة في العالم، تشتهر بمناظرها الطبيعية وتاريخها الغني ومدنها الشهيرة مثل موسكو وسانت بطرسبرغ.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RUS', 'es', 'Rusia', 'Rusia', 'Rusia es el país más grande del mundo, famoso por sus paisajes, historia y ciudades icónicas como Moscú y San Petersburgo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RUS', 'fr', 'Russie', 'Russie', 'La Russie est le plus grand pays du monde, connue pour ses paysages, son histoire et ses villes emblématiques comme Moscou et Saint-Pétersbourg.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RUS', 'pt', 'Rússia', 'Rússia', 'A Rússia é o maior país do mundo, famosa por suas paisagens, história e cidades icônicas como Moscou e São Petersburgo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RUS', 'ru', 'Россия', 'Россия', 'Россия — крупнейшая страна мира, известная своими просторами, историей и городами, такими как Москва и Санкт-Петербург.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RUS', 'de', 'Russland', 'Russland', 'Russland ist das größte Land der Erde, bekannt für seine Landschaften, Geschichte und Städte wie Moskau und St. Petersburg.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('RUS', 'it', 'Russia', 'Russia', 'La Russia è il paese più grande del mondo, nota per i suoi paesaggi, la storia e città come Mosca e San Pietroburgo.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SMR', 'en', 'San Marino', 'San Marino', 'San Marino is one of the world’s oldest republics, perched atop Mount Titano. Enjoy medieval towers, panoramic views, and rich history.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SMR', 'ar', 'سان مارينو', 'سان مارينو', 'سان مارينو من أقدم الجمهوريات في العالم، تقع على جبل تيتانو. استمتع بالأبراج التاريخية والإطلالات البانورامية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SMR', 'es', 'San Marino', 'San Marino', 'San Marino es una de las repúblicas más antiguas del mundo, situada en el Monte Titano. Disfruta de sus torres medievales y vistas panorámicas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SMR', 'fr', 'Saint-Marin', 'Saint-Marin', 'Saint-Marin est l’une des plus anciennes républiques du monde, perchée sur le mont Titano. Profitez de ses tours médiévales et de ses panoramas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SMR', 'pt', 'San Marino', 'San Marino', 'San Marino é uma das repúblicas mais antigas do mundo, situada no Monte Titano. Aproveite as torres medievais e as vistas panorâmicas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SMR', 'ru', 'Сан-Марино', 'Сан-Марино', 'Сан-Марино — одна из старейших республик мира, расположенная на горе Титано. Наслаждайтесь средневековыми башнями и панорамными видами.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SMR', 'de', 'San Marino', 'San Marino', 'San Marino ist eine der ältesten Republiken der Welt, auf dem Monte Titano gelegen. Genießen Sie mittelalterliche Türme und Panoramablicke.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SMR', 'it', 'San Marino', 'San Marino', 'San Marino è una delle repubbliche più antiche del mondo, situata sul Monte Titano. Goditi le torri medievali e i panorami mozzafiato.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SRB', 'en', 'Serbia', 'Serbia', 'Serbia is a Balkan country with lively cities, ancient monasteries, and scenic rivers. Experience Belgrade’s nightlife and traditional cuisine.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SRB', 'ar', 'صربيا', 'صربيا', 'صربيا بلد بلقاني يتميز بمدنه النابضة بالحياة وأديرته القديمة وأنهاره الجميلة. استمتع بحياة بلغراد الليلية والمأكولات التقليدية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SRB', 'es', 'Serbia', 'Serbia', 'Serbia es un país balcánico con ciudades animadas, monasterios antiguos y ríos pintorescos. Disfruta la vida nocturna de Belgrado y la cocina tradicional.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SRB', 'fr', 'Serbie', 'Serbie', 'La Serbie est un pays des Balkans avec des villes animées, des monastères anciens et des rivières. Découvrez la vie nocturne de Belgrade et la cuisine traditionnelle.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SRB', 'pt', 'Sérvia', 'Sérvia', 'A Sérvia é um país dos Bálcãs com cidades animadas, mosteiros antigos e rios cênicos. Experimente a vida noturna de Belgrado e a culinária tradicional.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SRB', 'ru', 'Сербия', 'Сербия', 'Сербия — балканская страна с оживлёнными городами, древними монастырями и живописными реками. Ощутите ночную жизнь Белграда и традиционную кухню.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SRB', 'de', 'Serbien', 'Serbien', 'Serbien ist ein Balkanstaat mit lebhaften Städten, alten Klöstern und Flüssen. Erleben Sie Belgrads Nachtleben und traditionelle Küche.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SRB', 'it', 'Serbia', 'Serbia', 'La Serbia è un paese balcanico con città vivaci, antichi monasteri e fiumi pittoreschi. Vivi la vita notturna di Belgrado e la cucina tradizionale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVK', 'en', 'Slovakia', 'Slovakia', 'Slovakia is a country of castles, mountains, and charming towns. Discover the High Tatras, Bratislava, and rich folk traditions.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVK', 'ar', 'سلوفاكيا', 'سلوفاكيا', 'سلوفاكيا بلد القلاع والجبال والمدن الساحرة. اكتشف جبال تاترا وبراتيسلافا وتقاليدها الشعبية الغنية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVK', 'es', 'Eslovaquia', 'Eslovaquia', 'Eslovaquia es un país de castillos, montañas y pueblos encantadores. Descubre los Tatras, Bratislava y sus tradiciones.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVK', 'fr', 'Slovaquie', 'Slovaquie', 'La Slovaquie est un pays de châteaux, montagnes et villes charmantes. Découvrez les Tatras, Bratislava et ses traditions.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVK', 'pt', 'Eslováquia', 'Eslováquia', 'A Eslováquia é um país de castelos, montanhas e cidades encantadoras. Descubra os Tatras, Bratislava e tradições populares.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVK', 'ru', 'Словакия', 'Словакия', 'Словакия — страна замков, гор и очаровательных городков. Откройте для себя Татры, Братиславу и народные традиции.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVK', 'de', 'Slowakei', 'Slowakei', 'Die Slowakei ist ein Land der Burgen, Berge und charmanten Städte. Entdecken Sie die Hohen Tatra, Bratislava und Folklore.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVK', 'it', 'Slovacchia', 'Slovacchia', 'La Slovacchia è un paese di castelli, montagne e città affascinanti. Scopri i Tatra, Bratislava e le tradizioni popolari.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVN', 'en', 'Slovenia', 'Slovenia', 'Slovenia is a green country of lakes, mountains, and charming towns. Visit Lake Bled, Ljubljana, and explore its natural wonders.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVN', 'ar', 'سلوفينيا', 'سلوفينيا', 'سلوفينيا بلد أخضر يضم بحيرات وجبال ومدن ساحرة. زر بحيرة بليد وليوبليانا واكتشف عجائبها الطبيعية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVN', 'es', 'Eslovenia', 'Eslovenia', 'Eslovenia es un país verde de lagos, montañas y pueblos encantadores. Visita el lago Bled y Liubliana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVN', 'fr', 'Slovénie', 'Slovénie', 'La Slovénie est un pays vert de lacs, montagnes et villes charmantes. Visitez le lac de Bled et Ljubljana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVN', 'pt', 'Eslovênia', 'Eslovênia', 'A Eslovênia é um país verde de lagos, montanhas e cidades encantadoras. Visite o Lago Bled e Liubliana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVN', 'ru', 'Словения', 'Словения', 'Словения — зелёная страна озёр, гор и очаровательных городков. Посетите озеро Блед и Любляну.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVN', 'de', 'Slowenien', 'Slowenien', 'Slowenien ist ein grünes Land voller Seen, Berge und charmanter Städte. Besuchen Sie den Bleder See und Ljubljana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SVN', 'it', 'Slovenia', 'Slovenia', 'La Slovenia è un paese verde di laghi, montagne e città affascinanti. Visita il lago di Bled e Lubiana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'en', 'Spain', 'Spain', 'Discover passionate culture with flamenco rhythms, stunning architecture, and vibrant festivals. From Barcelona to Seville.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'ar', 'إسبانيا', 'إسبانيا', 'اكتشف الثقافة العاطفية مع إيقاعات الفلامنكو والهندسة المعمارية المذهلة والمهرجانات النابضة بالحياة. من برشلونة إلى إشبيلية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'es', 'España', 'España', 'Descubre la cultura apasionada con ritmos de flamenco, arquitectura impresionante y festivales vibrantes. Desde Barcelona hasta Sevilla.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'fr', 'Espagne', 'Espagne', 'Découvrez une culture passionnée avec les rythmes du flamenco, une architecture époustouflante et des festivals vibrants. De Barcelone à Séville.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'pt', 'Espanha', 'Espanha', 'Descubra a cultura apaixonada com ritmos de flamenco, arquitetura deslumbrante e festivais vibrantes. De Barcelona a Sevilha.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'ru', 'Испания', 'Испания', 'Откройте для себя страстную культуру с ритмами фламенко, потрясающей архитектурой и яркими фестивалями. От Барселоны до Севильи.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'de', 'Spanien', 'Spanien', 'Entdecken Sie leidenschaftliche Kultur mit Flamenco-Rhythmen, atemberaubender Architektur und lebendigen Festivals. Von Barcelona bis Sevilla.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('ESP', 'it', 'Spagna', 'Spagna', 'Scopri la cultura appassionata con ritmi di flamenco, architettura mozzafiato e festival vivaci. Da Barcellona a Siviglia.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'en', 'Sweden', 'Sweden', 'Discover Sweden''s perfect balance of innovation and nature. Experience Stockholm''s archipelago, Lapland''s wilderness, ABBA legacy, and renowned design culture with sustainable living.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'ar', 'Sweden', 'Sweden', 'اكتشف التوازن المثالي بين الابتكار والطبيعة في السويد. استمتع بأرخبيل ستوكهولم وبرية لابلاند وتراث أبا وثقافة التصميم المشهورة مع الحياة المستدامة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'es', 'Sweden', 'Sweden', 'Descubre el equilibrio perfecto de Suecia entre innovación y naturaleza. Experimenta el archipiélago de Estocolmo, naturaleza de Laponia, legado de ABBA y reconocida cultura de diseño con vida sostenible.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'fr', 'Sweden', 'Sweden', 'Découvrez l''équilibre parfait de la Suède entre innovation et nature. Vivez archipel de Stockholm, nature sauvage de Laponie, héritage ABBA et culture design renommée avec vie durable.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'pt', 'Sweden', 'Sweden', 'Descubra o equilíbrio perfeito da Suécia entre inovação e natureza. Experimente arquipélago de Estocolmo, natureza da Lapônia, legado do ABBA e renomada cultura de design com vida sustentável.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'ru', 'Sweden', 'Sweden', 'Откройте идеальный баланс Швеции между инновациями и природой. Ощутите архипелаг Стокгольма, дикую природу Лапландии, наследие ABBA и знаменитую культуру дизайна с устойчивой жизнью.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'de', 'Sweden', 'Sweden', 'Entdecken Sie Schwedens perfekte Balance zwischen Innovation und Natur. Erleben Sie Stockholms Schärengarten, Lapplands Wildnis, ABBA-Erbe und renommierte Designkultur mit nachhaltigem Leben.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SWE', 'it', 'Sweden', 'Sweden', 'Scopri il perfetto equilibrio della Svezia tra innovazione e natura. Vivi arcipelago di Stoccolma, natura selvaggia della Lapponia, eredità degli ABBA e rinomata cultura del design con vita sostenibile.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'en', 'Switzerland', 'Switzerland', 'Experience Swiss perfection with majestic Alps, pristine lakes, and world-renowned precision. From skiing adventures to luxury watches and chocolate, discover Alpine excellence.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'ar', 'Switzerland', 'Switzerland', 'استمتع بالكمال السويسري مع جبال الألب الهيبة والبحيرات البكر والدقة العالمية الشهرة. من مغامرات التزلج إلى الساعات الفاخرة والشوكولاته، اكتشف التميز الألبي.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'es', 'Switzerland', 'Switzerland', 'Experimenta la perfección suiza con majestuosos Alpes, lagos prístinos y precisión mundialmente reconocida. Desde aventuras de esquí hasta relojes de lujo y chocolate, descubre la excelencia alpina.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'fr', 'Switzerland', 'Switzerland', 'Découvrez la perfection suisse avec Alpes majestueuses, lacs immaculés et précision mondialement reconnue. Des aventures de ski aux montres de luxe et chocolat, explorez l''excellence alpine.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'pt', 'Switzerland', 'Switzerland', 'Experimente a perfeição suíça com Alpes majestosos, lagos imaculados e precisão mundialmente reconhecida. De aventuras de esqui a relógios de luxo e chocolate, descubra excelência alpina.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'ru', 'Switzerland', 'Switzerland', 'Ощутите швейцарское совершенство с величественными Альпами, пристинными озёрами и всемирно известной точностью. От лыжных приключений до роскошных часов и шоколада, откройте альпийское совершенство.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'de', 'Switzerland', 'Switzerland', 'Erleben Sie Schweizer Perfektion mit majestätischen Alpen, makellosen Seen und weltberühmter Präzision. Von Skiabenteuer bis zu Luxusuhren und Schokolade, entdecken Sie alpine Exzellenz.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('CHE', 'it', 'Switzerland', 'Switzerland', 'Vivi la perfezione svizzera con Alpi maestose, laghi incontaminati e precisione di fama mondiale. Dalle avventure sciistiche agli orologi di lusso e cioccolato, scopri eccellenza alpina.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UKR', 'en', 'Ukraine', 'Ukraine', 'Ukraine is a vast country of golden fields, historic cities, and vibrant culture. Explore Kyiv, Lviv, and the Carpathian Mountains.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UKR', 'ar', 'أوكرانيا', 'أوكرانيا', 'أوكرانيا بلد واسع يضم سهولاً ذهبية ومدناً تاريخية وثقافة نابضة بالحياة. استكشف كييف ولفيف وجبال الكاربات.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UKR', 'es', 'Ucrania', 'Ucrania', 'Ucrania es un país de campos dorados, ciudades históricas y cultura vibrante. Descubre Kiev, Leópolis y los Cárpatos.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UKR', 'fr', 'Ukraine', 'Ukraine', 'L’Ukraine est un vaste pays de champs dorés, villes historiques et culture vibrante. Explorez Kiev, Lviv et les Carpates.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UKR', 'pt', 'Ucrânia', 'Ucrânia', 'A Ucrânia é um país vasto de campos dourados, cidades históricas e cultura vibrante. Explore Kiev, Lviv e os Cárpatos.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UKR', 'ru', 'Украина', 'Украина', 'Украина — огромная страна золотых полей, исторических городов и яркой культуры. Откройте для себя Киев, Львов и Карпаты.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UKR', 'de', 'Ukraine', 'Ukraine', 'Die Ukraine ist ein weites Land mit goldenen Feldern, historischen Städten und lebendiger Kultur. Entdecken Sie Kiew, Lwiw und die Karpaten.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('UKR', 'it', 'Ucraina', 'Ucraina', 'L’Ucraina è un vasto paese di campi dorati, città storiche e cultura vibrante. Esplora Kiev, Leopoli e i Carpazi.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VAT', 'en', 'Vatican City', 'Vatican City', 'Vatican City is the world’s smallest independent state, home to St. Peter’s Basilica, the Vatican Museums, and priceless art.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VAT', 'ar', 'مدينة الفاتيكان', 'مدينة الفاتيكان', 'مدينة الفاتيكان أصغر دولة مستقلة في العالم، تضم كاتدرائية القديس بطرس والمتاحف الفنية الشهيرة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VAT', 'es', 'Ciudad del Vaticano', 'Ciudad del Vaticano', 'La Ciudad del Vaticano es el estado independiente más pequeño del mundo, hogar de la Basílica de San Pedro y los Museos Vaticanos.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VAT', 'fr', 'Cité du Vatican', 'Cité du Vatican', 'La Cité du Vatican est le plus petit État indépendant du monde, abritant la basilique Saint-Pierre et les musées du Vatican.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VAT', 'pt', 'Cidade do Vaticano', 'Cidade do Vaticano', 'A Cidade do Vaticano é o menor estado independente do mundo, lar da Basílica de São Pedro e dos Museus Vaticanos.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VAT', 'ru', 'Ватикан', 'Ватикан', 'Ватикан — самое маленькое независимое государство в мире, дом собора Святого Петра и Ватиканских музеев.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VAT', 'de', 'Vatikanstadt', 'Vatikanstadt', 'Die Vatikanstadt ist der kleinste unabhängige Staat der Welt, Heimat des Petersdoms und der Vatikanischen Museen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VAT', 'it', 'Città del Vaticano', 'Città del Vaticano', 'La Città del Vaticano è il più piccolo stato indipendente del mondo, sede della Basilica di San Pietro e dei Musei Vaticani.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FJI', 'en', 'Fiji', 'Fiji', 'Fiji is a tropical paradise of turquoise waters, white-sand beaches, and friendly island culture. Enjoy world-class diving and vibrant traditions.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FJI', 'ar', 'فيجي', 'فيجي', 'فيجي جنة استوائية بمياهها الفيروزية وشواطئها البيضاء وثقافتها الودودة. استمتع بالغوص العالمي والتقاليد النابضة بالحياة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FJI', 'es', 'Fiyi', 'Fiyi', 'Fiyi es un paraíso tropical de aguas turquesas, playas de arena blanca y cultura isleña. Disfruta del buceo y las tradiciones vibrantes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FJI', 'fr', 'Fidji', 'Fidji', 'Les Fidji sont un paradis tropical aux eaux turquoise, plages de sable blanc et culture chaleureuse. Profitez de la plongée et des traditions.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FJI', 'pt', 'Fiji', 'Fiji', 'Fiji é um paraíso tropical de águas azul-turquesa, praias de areia branca e cultura acolhedora. Aproveite o mergulho e as tradições vibrantes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FJI', 'ru', 'Фиджи', 'Фиджи', 'Фиджи — тропический рай с бирюзовой водой, белыми пляжами и дружелюбной культурой. Наслаждайтесь дайвингом и традициями.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FJI', 'de', 'Fidschi', 'Fidschi', 'Fidschi ist ein tropisches Paradies mit türkisfarbenem Wasser, weißen Stränden und herzlicher Kultur. Genießen Sie Tauchen und Traditionen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FJI', 'it', 'Figi', 'Figi', 'Le Figi sono un paradiso tropicale di acque turchesi, spiagge bianche e cultura accogliente. Goditi le immersioni e le tradizioni vivaci.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KIR', 'en', 'Kiribati', 'Kiribati', 'Kiribati is a Pacific island nation known for its coral atolls, turquoise lagoons, and unique culture. Experience untouched beaches and traditional life.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KIR', 'ar', 'كيريباس', 'كيريباس', 'كيريباس دولة جزرية في المحيط الهادئ تشتهر بجزرها المرجانية وبحيراتها وثقافتها الفريدة. استمتع بالشواطئ البكر والحياة التقليدية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KIR', 'es', 'Kiribati', 'Kiribati', 'Kiribati es una nación insular del Pacífico famosa por sus atolones de coral, lagunas y cultura única. Disfruta de playas vírgenes y vida tradicional.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KIR', 'fr', 'Kiribati', 'Kiribati', 'Kiribati est une nation insulaire du Pacifique connue pour ses atolls coralliens, lagons turquoise et culture unique. Profitez de plages vierges et de la vie traditionnelle.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KIR', 'pt', 'Kiribati', 'Kiribati', 'Kiribati é uma nação insular do Pacífico conhecida por seus atóis de coral, lagoas e cultura única. Aproveite praias intocadas e vida tradicional.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KIR', 'ru', 'Кирибати', 'Кирибати', 'Кирибати — тихоокеанское островное государство, известное коралловыми атоллами, лагунами и уникальной культурой. Наслаждайтесь нетронутыми пляжами и традиционной жизнью.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KIR', 'de', 'Kiribati', 'Kiribati', 'Kiribati ist eine pazifische Inselnation, bekannt für Korallenatolle, Lagunen und einzigartige Kultur. Erleben Sie unberührte Strände und traditionelles Leben.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('KIR', 'it', 'Kiribati', 'Kiribati', 'Kiribati è una nazione insulare del Pacifico nota per gli atolli corallini, le lagune e la cultura unica. Goditi spiagge incontaminate e vita tradizionale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MHL', 'en', 'Marshall Islands', 'Marshall Islands', 'The Marshall Islands are a Pacific paradise of coral atolls, crystal-clear waters, and rich marine life. Discover traditional villages and WWII history.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MHL', 'ar', 'جزر مارشال', 'جزر مارشال', 'جزر مارشال جنة في المحيط الهادئ تضم جزرًا مرجانية ومياهًا صافية وحياة بحرية غنية. اكتشف القرى التقليدية وتاريخ الحرب العالمية الثانية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MHL', 'es', 'Islas Marshall', 'Islas Marshall', 'Las Islas Marshall son un paraíso del Pacífico de atolones de coral, aguas cristalinas y vida marina. Descubre aldeas tradicionales e historia de la Segunda Guerra Mundial.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MHL', 'fr', 'Îles Marshall', 'Îles Marshall', 'Les Îles Marshall sont un paradis du Pacifique avec atolls coralliens, eaux claires et vie marine. Découvrez villages traditionnels et histoire de la Seconde Guerre mondiale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MHL', 'pt', 'Ilhas Marshall', 'Ilhas Marshall', 'As Ilhas Marshall são um paraíso do Pacífico de atóis de coral, águas cristalinas e vida marinha. Descubra vilarejos tradicionais e história da Segunda Guerra Mundial.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MHL', 'ru', 'Маршалловы Острова', 'Маршалловы Острова', 'Маршалловы Острова — тихоокеанский рай с коралловыми атоллами, чистой водой и богатой морской жизнью. Откройте для себя традиционные деревни и историю Второй мировой войны.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MHL', 'de', 'Marshallinseln', 'Marshallinseln', 'Die Marshallinseln sind ein pazifisches Paradies aus Korallenatollen, klarem Wasser und reichem Meeresleben. Entdecken Sie traditionelle Dörfer und Geschichte des Zweiten Weltkriegs.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('MHL', 'it', 'Isole Marshall', 'Isole Marshall', 'Le Isole Marshall sono un paradiso del Pacifico di atolli corallini, acque limpide e vita marina. Scopri villaggi tradizionali e storia della Seconda Guerra Mondiale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FSM', 'en', 'Micronesia', 'Micronesia', 'Micronesia is a region of hundreds of small islands in the Pacific, known for its clear waters, ancient ruins, and vibrant traditions. Explore Pohnpei and Chuuk Lagoon.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FSM', 'ar', 'ميكرونيزيا', 'ميكرونيزيا', 'ميكرونيزيا منطقة تضم مئات الجزر الصغيرة في المحيط الهادئ، تشتهر بمياهها الصافية وآثارها القديمة وتقاليدها النابضة بالحياة. استكشف بونبيي وبحيرة تشوك.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FSM', 'es', 'Micronesia', 'Micronesia', 'Micronesia es una región de cientos de pequeñas islas en el Pacífico, famosa por sus aguas claras, ruinas antiguas y tradiciones. Explora Pohnpei y la Laguna de Chuuk.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FSM', 'fr', 'Micronésie', 'Micronésie', 'La Micronésie est une région de centaines de petites îles du Pacifique, connue pour ses eaux claires, ruines anciennes et traditions. Explorez Pohnpei et le lagon de Chuuk.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FSM', 'pt', 'Micronésia', 'Micronésia', 'A Micronésia é uma região de centenas de pequenas ilhas no Pacífico, conhecida por águas claras, ruínas antigas e tradições. Explore Pohnpei e a Lagoa Chuuk.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FSM', 'ru', 'Микронезия', 'Микронезия', 'Микронезия — регион сотен небольших островов в Тихом океане, известный чистыми водами, древними руинами и яркими традициями. Исследуйте Понпеи и лагуну Чуук.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FSM', 'de', 'Mikronesien', 'Mikronesien', 'Mikronesien ist eine Region mit Hunderten kleiner Inseln im Pazifik, bekannt für klares Wasser, alte Ruinen und lebendige Traditionen. Entdecken Sie Pohnpei und die Chuuk-Lagune.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('FSM', 'it', 'Micronesia', 'Micronesia', 'La Micronesia è una regione di centinaia di piccole isole nel Pacifico, nota per le acque limpide, le rovine antiche e le tradizioni vivaci. Esplora Pohnpei e la laguna di Chuuk.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NRU', 'en', 'Nauru', 'Nauru', 'Nauru is a tiny island nation in the Pacific, known for its phosphate deposits, coastal cliffs, and friendly local culture. Experience a unique, off-the-beaten-path paradise.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NRU', 'ar', 'ناورو', 'ناورو', 'ناورو دولة جزرية صغيرة في المحيط الهادئ تشتهر بالفوسفات ومنحدراتها الساحلية وثقافتها المحلية الودودة. اكتشف جنة فريدة خارج المسار المعتاد.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NRU', 'es', 'Nauru', 'Nauru', 'Nauru es una pequeña nación insular del Pacífico famosa por sus depósitos de fosfato, acantilados y cultura local amigable. Descubre un paraíso único y poco conocido.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NRU', 'fr', 'Nauru', 'Nauru', 'Nauru est une petite nation insulaire du Pacifique, connue pour ses gisements de phosphate, falaises côtières et culture locale accueillante. Découvrez un paradis unique et méconnu.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NRU', 'pt', 'Nauru', 'Nauru', 'Nauru é uma pequena nação insular do Pacífico conhecida por seus depósitos de fosfato, falésias costeiras e cultura local amigável. Descubra um paraíso único e pouco explorado.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NRU', 'ru', 'Науру', 'Науру', 'Науру — крошечное островное государство в Тихом океане, известное фосфатами, прибрежными скалами и дружелюбной культурой. Откройте для себя уникальный рай.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NRU', 'de', 'Nauru', 'Nauru', 'Nauru ist eine winzige Inselnation im Pazifik, bekannt für Phosphatvorkommen, Küstenklippen und freundliche Kultur. Erleben Sie ein einzigartiges Paradies abseits der Pfade.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NRU', 'it', 'Nauru', 'Nauru', 'Nauru è una minuscola nazione insulare del Pacifico nota per i depositi di fosfato, le scogliere costiere e la cultura locale amichevole. Scopri un paradiso unico e poco conosciuto.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NZL', 'en', 'New Zealand', 'New Zealand', 'New Zealand is renowned for its breathtaking landscapes, Maori culture, adventure sports, and friendly people. Explore fjords, mountains, and vibrant cities.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NZL', 'ar', 'نيوزيلندا', 'نيوزيلندا', 'نيوزيلندا مشهورة بمناظرها الطبيعية الخلابة، وثقافة الماوري، والرياضات المغامرة، والشعب الودود. استكشف المضايق الجبلية والمدن النابضة بالحياة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NZL', 'es', 'Nueva Zelanda', 'Nueva Zelanda', 'Nueva Zelanda es famosa por sus paisajes impresionantes, cultura maorí, deportes de aventura y gente amable. Explora fiordos, montañas y ciudades vibrantes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NZL', 'fr', 'Nouvelle-Zélande', 'Nouvelle-Zélande', 'La Nouvelle-Zélande est réputée pour ses paysages à couper le souffle, sa culture maorie, ses sports d’aventure et son peuple accueillant. Explorez fjords, montagnes et villes dynamiques.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NZL', 'pt', 'Nova Zelândia', 'Nova Zelândia', 'A Nova Zelândia é famosa por suas paisagens deslumbrantes, cultura maori, esportes de aventura e povo acolhedor. Explore fiordes, montanhas e cidades vibrantes.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NZL', 'ru', 'Новая Зеландия', 'Новая Зеландия', 'Новая Зеландия славится потрясающими пейзажами, культурой маори, приключенческими видами спорта и гостеприимством. Исследуйте фьорды, горы и города.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NZL', 'de', 'Neuseeland', 'Neuseeland', 'Neuseeland ist bekannt für atemberaubende Landschaften, Maori-Kultur, Abenteuersport und freundliche Menschen. Entdecken Sie Fjorde, Berge und lebendige Städte.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('NZL', 'it', 'Nuova Zelanda', 'Nuova Zelanda', 'La Nuova Zelanda è famosa per i suoi paesaggi mozzafiato, la cultura maori, gli sport d’avventura e la gente cordiale. Esplora fiordi, montagne e città vivaci.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PLW', 'en', 'Palau', 'Palau', 'Palau is a tropical paradise in Micronesia, famous for its Rock Islands, pristine reefs, and world-class diving. Experience vibrant marine life and unique traditions.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PLW', 'ar', 'بالاو', 'بالاو', 'بالاو جنة استوائية في ميكرونيزيا تشتهر بجزرها الصخرية وشعابها المرجانية والغوص العالمي. استمتع بالحياة البحرية النابضة والتقاليد الفريدة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PLW', 'es', 'Palaos', 'Palaos', 'Palaos es un paraíso tropical en Micronesia famoso por sus islas rocosas, arrecifes y buceo de clase mundial. Disfruta de vida marina y tradiciones únicas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PLW', 'fr', 'Palaos', 'Palaos', 'Palaos est un paradis tropical en Micronésie, célèbre pour ses îles rocheuses, récifs et plongée. Profitez de la vie marine et des traditions uniques.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PLW', 'pt', 'Palau', 'Palau', 'Palau é um paraíso tropical na Micronésia famoso por suas ilhas rochosas, recifes e mergulho de classe mundial. Aproveite a vida marinha e tradições únicas.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PLW', 'ru', 'Палау', 'Палау', 'Палау — тропический рай в Микронезии, известный скалистыми островами, рифами и дайвингом. Откройте для себя морскую жизнь и уникальные традиции.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PLW', 'de', 'Palau', 'Palau', 'Palau ist ein tropisches Paradies in Mikronesien, bekannt für Felseninseln, Riffe und Tauchen. Erleben Sie Meeresleben und einzigartige Traditionen.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PLW', 'it', 'Palau', 'Palau', 'Palau è un paradiso tropicale in Micronesia famoso per le isole rocciose, le barriere coralline e le immersioni. Goditi la vita marina e le tradizioni uniche.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PNG', 'en', 'Papua New Guinea', 'Papua New Guinea', 'Papua New Guinea is a land of dramatic mountains, rainforests, and over 800 languages. Discover vibrant tribal cultures, adventure, and natural wonders.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PNG', 'ar', 'بابوا غينيا الجديدة', 'بابوا غينيا الجديدة', 'بابوا غينيا الجديدة أرض الجبال والغابات المطيرة وأكثر من 800 لغة. اكتشف الثقافات القبلية والمغامرة والعجائب الطبيعية.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PNG', 'es', 'Papúa Nueva Guinea', 'Papúa Nueva Guinea', 'Papúa Nueva Guinea es una tierra de montañas, selvas y más de 800 idiomas. Descubre culturas tribales, aventura y maravillas naturales.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PNG', 'fr', 'Papouasie-Nouvelle-Guinée', 'Papouasie-Nouvelle-Guinée', 'La Papouasie-Nouvelle-Guinée est une terre de montagnes, forêts tropicales et plus de 800 langues. Découvrez cultures tribales, aventure et merveilles naturelles.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PNG', 'pt', 'Papua-Nova Guiné', 'Papua-Nova Guiné', 'Papua-Nova Guiné é uma terra de montanhas, florestas tropicais e mais de 800 idiomas. Descubra culturas tribais, aventura e maravilhas naturais.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PNG', 'ru', 'Папуа — Новая Гвинея', 'Папуа — Новая Гвинея', 'Папуа — Новая Гвинея — страна гор, тропических лесов и более 800 языков. Откройте для себя племенные культуры, приключения и чудеса природы.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PNG', 'de', 'Papua-Neuguinea', 'Papua-Neuguinea', 'Papua-Neuguinea ist ein Land der Berge, Regenwälder und über 800 Sprachen. Erleben Sie Stammeskulturen, Abenteuer und Naturwunder.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('PNG', 'it', 'Papua Nuova Guinea', 'Papua Nuova Guinea', 'La Papua Nuova Guinea è una terra di montagne, foreste pluviali e oltre 800 lingue. Scopri culture tribali, avventura e meraviglie naturali.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('WSM', 'en', 'Samoa', 'Samoa', 'Samoa is a Polynesian island nation known for its lush rainforests, waterfalls, and strong cultural traditions. Enjoy warm hospitality and natural beauty.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('WSM', 'ar', 'ساموا', 'ساموا', 'ساموا دولة جزرية بولينيزية تشتهر بالغابات المطيرة والشلالات والتقاليد الثقافية القوية. استمتع بالضيافة الدافئة والجمال الطبيعي.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('WSM', 'es', 'Samoa', 'Samoa', 'Samoa es una nación polinesia famosa por sus selvas, cascadas y tradiciones culturales. Disfruta de hospitalidad cálida y belleza natural.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('WSM', 'fr', 'Samoa', 'Samoa', 'Samoa est une nation polynésienne connue pour ses forêts tropicales, cascades et traditions. Profitez de l’hospitalité et de la beauté naturelle.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('WSM', 'pt', 'Samoa', 'Samoa', 'Samoa é uma nação polinésia conhecida por suas florestas tropicais, cachoeiras e tradições culturais. Aproveite a hospitalidade e a beleza natural.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('WSM', 'ru', 'Самоа', 'Самоа', 'Самоа — полинезийское островное государство, известное тропическими лесами, водопадами и традициями. Наслаждайтесь гостеприимством и природой.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('WSM', 'de', 'Samoa', 'Samoa', 'Samoa ist eine polynesische Inselnation, bekannt für Regenwälder, Wasserfälle und Traditionen. Erleben Sie Gastfreundschaft und Natur.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('WSM', 'it', 'Samoa', 'Samoa', 'Samoa è una nazione polinesiana nota per le foreste pluviali, le cascate e le tradizioni. Goditi l’ospitalità e la bellezza naturale.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLB', 'en', 'Solomon Islands', 'Solomon Islands', 'The Solomon Islands are a Pacific archipelago with lush rainforests, WWII history, and vibrant Melanesian culture. Enjoy diving, hiking, and island hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLB', 'ar', 'جزر سليمان', 'جزر سليمان', 'جزر سليمان أرخبيل في المحيط الهادئ يتميز بالغابات المطيرة وتاريخ الحرب العالمية الثانية وثقافة ميلانيزية نابضة. استمتع بالغوص والمشي وضيافة الجزر.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLB', 'es', 'Islas Salomón', 'Islas Salomón', 'Las Islas Salomón son un archipiélago del Pacífico con selvas, historia de la Segunda Guerra Mundial y cultura melanesia. Disfruta de buceo, senderismo y hospitalidad.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLB', 'fr', 'Îles Salomon', 'Îles Salomon', 'Les Îles Salomon sont un archipel du Pacifique avec forêts tropicales, histoire de la Seconde Guerre mondiale et culture mélanésienne. Profitez de plongée et d’hospitalité.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLB', 'pt', 'Ilhas Salomão', 'Ilhas Salomão', 'As Ilhas Salomão são um arquipélago do Pacífico com florestas, história da Segunda Guerra Mundial e cultura melanésia. Aproveite mergulho e hospitalidade.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLB', 'ru', 'Соломоновы Острова', 'Соломоновы Острова', 'Соломоновы Острова — архипелаг в Тихом океане с тропическими лесами, историей Второй мировой войны и меланезийской культурой. Наслаждайтесь дайвингом и гостеприимством.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLB', 'de', 'Salomonen', 'Salomonen', 'Die Salomonen sind ein pazifischer Archipel mit Regenwäldern, Geschichte des Zweiten Weltkriegs und melanesischer Kultur. Erleben Sie Tauchen und Gastfreundschaft.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('SLB', 'it', 'Isole Salomone', 'Isole Salomone', 'Le Isole Salomone sono un arcipelago del Pacifico con foreste pluviali, storia della Seconda Guerra Mondiale e cultura melanesiana. Goditi immersioni e ospitalità.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TON', 'en', 'Tonga', 'Tonga', 'Tonga is a Polynesian kingdom of over 170 islands, known for its friendly people, whale watching, and unspoiled beaches. Experience authentic island life.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TON', 'ar', 'تونغا', 'تونغا', 'تونغا مملكة بولينيزية تضم أكثر من 170 جزيرة، تشتهر بالشعب الودود ومشاهدة الحيتان والشواطئ البكر. استمتع بالحياة الجزيرة الأصيلة.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TON', 'es', 'Tonga', 'Tonga', 'Tonga es un reino polinesio de más de 170 islas, famoso por su gente amable, avistamiento de ballenas y playas vírgenes. Vive la auténtica vida isleña.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TON', 'fr', 'Tonga', 'Tonga', 'Tonga est un royaume polynésien de plus de 170 îles, connu pour son peuple accueillant, observation des baleines et plages vierges. Vivez la vie authentique des îles.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TON', 'pt', 'Tonga', 'Tonga', 'Tonga é um reino polinésio com mais de 170 ilhas, conhecido pelo povo amigável, observação de baleias e praias intocadas. Viva a vida autêntica da ilha.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TON', 'ru', 'Тонга', 'Тонга', 'Тонга — полинезийское королевство из более чем 170 островов, известное гостеприимством, китами и пляжами. Ощутите настоящую островную жизнь.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TON', 'de', 'Tonga', 'Tonga', 'Tonga ist ein polynesisches Königreich mit über 170 Inseln, bekannt für freundliche Menschen, Wale und Strände. Erleben Sie authentisches Inselleben.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TON', 'it', 'Tonga', 'Tonga', 'Tonga è un regno polinesiano di oltre 170 isole, noto per la gente cordiale, le balene e le spiagge incontaminate. Vivi la vera vita isolana.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUV', 'en', 'Tuvalu', 'Tuvalu', 'Tuvalu is a small island country in the Pacific, known for its low-lying atolls, friendly people, and peaceful atmosphere. Experience true island serenity.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUV', 'ar', 'توفالو', 'توفالو', 'توفالو دولة جزرية صغيرة في المحيط الهادئ تشتهر بجزرها المنخفضة وشعبها الودود وأجوائها الهادئة. استمتع بالهدوء الحقيقي للجزر.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUV', 'es', 'Tuvalu', 'Tuvalu', 'Tuvalu es un pequeño país insular del Pacífico famoso por sus atolones bajos, gente amable y ambiente tranquilo. Vive la serenidad isleña.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUV', 'fr', 'Tuvalu', 'Tuvalu', 'Tuvalu est un petit pays insulaire du Pacifique, connu pour ses atolls bas, son peuple accueillant et son atmosphère paisible. Découvrez la sérénité des îles.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUV', 'pt', 'Tuvalu', 'Tuvalu', 'Tuvalu é um pequeno país insular do Pacífico conhecido por seus atóis baixos, povo amigável e atmosfera tranquila. Viva a serenidade da ilha.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUV', 'ru', 'Тувалу', 'Тувалу', 'Тувалу — небольшое островное государство в Тихом океане, известное низкими атоллами, дружелюбными людьми и спокойной атмосферой. Ощутите настоящую островную гармонию.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUV', 'de', 'Tuvalu', 'Tuvalu', 'Tuvalu ist ein kleines Inselstaat im Pazifik, bekannt für niedrige Atolle, freundliche Menschen und ruhige Atmosphäre. Erleben Sie wahre Inselruhe.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('TUV', 'it', 'Tuvalu', 'Tuvalu', 'Tuvalu è un piccolo paese insulare del Pacifico noto per gli atolli bassi, la gente cordiale e l’atmosfera tranquilla. Vivi la serenità dell’isola.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VUT', 'en', 'Vanuatu', 'Vanuatu', 'Vanuatu is a South Pacific nation of volcanic islands, rainforests, and vibrant Melanesian culture. Enjoy adventure, diving, and warm island hospitality.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VUT', 'ar', 'فانواتو', 'فانواتو', 'فانواتو دولة جزرية في جنوب المحيط الهادئ تضم جزرًا بركانية وغابات مطيرة وثقافة ميلانيزية نابضة. استمتع بالمغامرة والغوص وضيافة الجزر.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VUT', 'es', 'Vanuatu', 'Vanuatu', 'Vanuatu es una nación del Pacífico Sur de islas volcánicas, selvas y cultura melanesia. Disfruta de aventura, buceo y hospitalidad isleña.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VUT', 'fr', 'Vanuatu', 'Vanuatu', 'Vanuatu est une nation du Pacifique Sud composée d’îles volcaniques, de forêts tropicales et d’une culture mélanésienne. Profitez d’aventure et d’hospitalité.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VUT', 'pt', 'Vanuatu', 'Vanuatu', 'Vanuatu é uma nação do Pacífico Sul de ilhas vulcânicas, florestas tropicais e cultura melanésia. Aproveite aventura, mergulho e hospitalidade.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VUT', 'ru', 'Вануату', 'Вануату', 'Вануату — государство в южной части Тихого океана с вулканическими островами, тропическими лесами и меланезийской культурой. Наслаждайтесь приключениями и гостеприимством.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VUT', 'de', 'Vanuatu', 'Vanuatu', 'Vanuatu ist eine südpazifische Inselnation mit Vulkaninseln, Regenwäldern und melanesischer Kultur. Erleben Sie Abenteuer, Tauchen und Gastfreundschaft.');
INSERT INTO countries_i18n (country_code, locale, name, name_long, about) VALUES ('VUT', 'it', 'Vanuatu', 'Vanuatu', 'Vanuatu è una nazione del Pacifico meridionale di isole vulcaniche, foreste pluviali e cultura melanesiana. Goditi avventura, immersioni e ospitalità.');
